import React, { useState, useEffect } from 'react';
import { createDsp, getAllDsps, deleteDsp } from '../services/api';
import './DspCardPage.css';
import { useNavigate } from 'react-router-dom';

// Define interface for form data
interface FormData {
  firstName: string;
  lastName: string;
  phone: string;
  assignedMinutes: number;
}

// Define interface for DSP data
interface DspData extends FormData {
  id: number;
}

const DspCardPage = () => {
  const [formData, setFormData] = useState<FormData>({
    firstName: '',
    lastName: '',
    phone: '',
    assignedMinutes: 11900
  });
  const [dsps, setDsps] = useState<DspData[]>([]);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  
  const navigate = useNavigate();

  useEffect(() => {
    fetchDsps();
  }, []);

  const fetchDsps = async () => {
    try {
      const data = await getAllDsps();
      setDsps(data);
    } catch (err: any) {
      console.error('DSP\'ler getirilirken hata olu≈ütu:', err);
      setError(err.message || 'DSP\'ler getirilirken hata olu≈ütu');
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData((prev: FormData) => ({
      ...prev,
      [name]: name === 'assignedMinutes' ? parseInt(value) : value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (formData.assignedMinutes > 11900) {
      setError('Atanan dakika 11.900\'√º ge√ßemez');
      clearNotifications();
      return;
    }
    
    try {
      await createDsp(formData);
      setSuccess('DSP ba≈üarƒ±yla kaydedildi!');
      setFormData({
        firstName: '',
        lastName: '',
        phone: '',
        assignedMinutes: 11900
      });
      // Refresh the DSPs list
      fetchDsps();
      clearNotifications();
    } catch (err: any) {
      console.error('Create DSP error:', err);
      const errorMessage = err.message || 'DSP kaydedilirken hata olu≈ütu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  // Clear notifications after timeout
  const clearNotifications = () => {
    setTimeout(() => {
      setSuccess('');
      setError('');
    }, 3000);
  };

  const handleClose = () => {
    // Redirect to dashboard
    navigate('/dashboard');
  };

  const handleDelete = async (id: number) => {
    // Show confirmation dialog before deleting
    const confirmed = window.confirm('Bu DSP kaydƒ±nƒ± silmek istediƒüinize emin misiniz?');
    if (!confirmed) {
      return; // User cancelled the deletion
    }
    
    try {
      await deleteDsp(id);
      setSuccess('DSP ba≈üarƒ±yla silindi!');
      // Refresh the DSPs list
      fetchDsps();
      clearNotifications();
    } catch (err: any) {
      console.error('Delete error:', err);
      const errorMessage = err.message || 'DSP silinirken hata olu≈ütu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  return (
    <div className="dsp-card-page">
      {/* Popup notifications */}
      {success && (
        <div className="popup-notification alert alert-info">
          {success}
        </div>
      )}
      {error && (
        <div className="popup-notification alert alert-warning">
          {error}
        </div>
      )}
      
      {/* "Yeni DSP Kayƒ±t" card (fixed position and size) */}
      <div 
        className="draggable-card"
        style={{
          position: 'absolute',
          left: 0,
          top: -175,
          width: 400,
          height: 320,
          zIndex: 1000
        }}
      >
        <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
          <div 
            className="window-header"
            style={{ userSelect: 'none' }}
          >
            <span>Yeni DSP Kayƒ±t</span>
            <div className="window-controls">
              <button className="window-button" onClick={handleClose}>√ó</button>
            </div>
          </div>
          <div className="window-content" style={{ flex: 1, overflow: 'visible' }}>
            <form onSubmit={handleSubmit} className="registration-form">
              <div className="form-row">
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Ad:</label>
                    <input
                      type="text"
                      name="firstName"
                      className="form-input"
                      value={formData.firstName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Soyad:</label>
                    <input
                      type="text"
                      name="lastName"
                      className="form-input"
                      value={formData.lastName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
              </div>
              
              <div className="form-group">
                <label className="form-label">Telefon:</label>
                <input
                  type="text"
                  name="phone"
                  className="form-input"
                  value={formData.phone}
                  onChange={handleChange}
                  required
                />
              </div>
              
              <div className="form-group">
                <label className="form-label">Atanan Dakika:</label>
                <input
                  type="number"
                  name="assignedMinutes"
                  className="form-input"
                  value={formData.assignedMinutes}
                  onChange={handleChange}
                  min="0"
                  max="11900"
                  required
                />
              </div>
              
              <button type="submit" className="btn btn-primary">DSP Kaydet</button>
            </form>
          </div>
        </div>
      </div>
      
      {/* "Kayƒ±tlƒ± DSP'ler" card (same size and position as "Kayƒ±tlƒ± Uzmanlar") */}
      <div 
        className="draggable-card"
        style={{
          position: 'absolute',
          left: 165,
          top: -175,
          width: 895,
          height: 500,
          zIndex: 1000
        }}
      >
        <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
          <div 
            className="window-header"
            style={{ userSelect: 'none' }}
          >
            <span>Kayƒ±tlƒ± DSP'ler</span>
          </div>
          <div className="window-content" style={{ flex: 1, overflow: 'visible' }}>
            {dsps.length === 0 ? (
              <p>Kayƒ±tlƒ± DSP bulunmamaktadƒ±r.</p>
            ) : (
              <div className="dsp-list">
                <div className="dsp-header">
                  <div className="dsp-header-item">Ad Soyad</div>
                  <div className="dsp-header-item">ƒ∞leti≈üim</div>
                  <div className="dsp-header-item minutes-vertical">Tanƒ±mlƒ± Dakika</div>
                  <div className="dsp-header-item assigned-vertical">Atanan Dakika</div>
                  <div className="dsp-header-item minutes-shifted">Kalan</div>
                  <div className="dsp-header-item">ƒ∞≈ülemler</div>
                </div>
                {dsps.map((dsp) => (
                  <div key={dsp.id} className="dsp-row">
                    <div className="dsp-info">
                      <div className="dsp-name">{dsp.firstName} {dsp.lastName}</div>
                      <div className="dsp-contact">{dsp.phone}</div>
                      <div className="dsp-minutes">{dsp.assignedMinutes}</div>
                      <div className="dsp-assigned">0</div>
                      <div className="dsp-remaining">{dsp.assignedMinutes}</div>
                    </div>
                    <div className="dsp-actions">
                      <button className="btn-icon delete" onClick={(e) => { e.stopPropagation(); handleDelete(dsp.id); }}>üóëÔ∏è</button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DspCardPage;
