import React, { useState, useEffect } from 'react';
import { createWorkplace, getAllExperts, getAllDoctors, getAllDsps } from '../services/api';
import './WorkplaceRegistration.css';

const WorkplaceRegistration = () => {
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    sskRegistrationNo: '',
    taxOffice: '',
    taxNumber: '',
    price: '',
    riskLevel: 'low',
    employeeCount: 0,
    assignedExpertId: '',
    assignedDoctorId: '',
    assignedDspId: '',
    source: ''
  });
  
  const [experts, setExperts] = useState<any[]>([]);
  const [doctors, setDoctors] = useState<any[]>([]);
  const [dsps, setDsps] = useState<any[]>([]);
  const [availableExperts, setAvailableExperts] = useState<any[]>([]);
  const [availableDoctors, setAvailableDoctors] = useState<any[]>([]);
  const [availableDsps, setAvailableDsps] = useState<any[]>([]);
  const [showDsp, setShowDsp] = useState(false);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    fetchExperts();
    fetchDoctors();
    fetchDsps();
  }, []);

  const fetchExperts = async () => {
    try {
      const data = await getAllExperts();
      setExperts(data);
    } catch (err) {
      console.error('Error fetching experts:', err);
    }
  };

  const fetchDoctors = async () => {
    try {
      const data = await getAllDoctors();
      setDoctors(data);
    } catch (err) {
      console.error('Error fetching doctors:', err);
    }
  };

  const fetchDsps = async () => {
    try {
      const data = await getAllDsps();
      setDsps(data);
    } catch (err) {
      console.error('Error fetching DSPs:', err);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Check if we need to show DSP field
    if (name === 'riskLevel' || name === 'employeeCount') {
      const riskLevel = name === 'riskLevel' ? value : formData.riskLevel;
      const employeeCount = name === 'employeeCount' ? parseInt(value) : formData.employeeCount;
      
      if (riskLevel === 'veryDangerous' && employeeCount >= 10) {
        setShowDsp(true);
      } else {
        setShowDsp(false);
        setFormData(prev => ({ ...prev, assignedDspId: '' }));
      }
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    try {
      const dataToSend = {
        ...formData,
        price: parseFloat(formData.price),
        employeeCount: parseInt(formData.employeeCount.toString()),
        assignedExpertId: formData.assignedExpertId ? parseInt(formData.assignedExpertId) : null,
        assignedDoctorId: formData.assignedDoctorId ? parseInt(formData.assignedDoctorId) : null,
        assignedDspId: formData.assignedDspId ? parseInt(formData.assignedDspId) : null
      };
      
      await createWorkplace(dataToSend);
      setSuccess('Workplace registered successfully!');
      
      // Reset form
      setFormData({
        name: '',
        address: '',
        sskRegistrationNo: '',
        taxOffice: '',
        taxNumber: '',
        price: '',
        riskLevel: 'low',
        employeeCount: 0,
        assignedExpertId: '',
        assignedDoctorId: '',
        assignedDspId: '',
        source: ''
      });
      setShowDsp(false);
    } catch (err) {
      setError('Error registering workplace');
    }
  };

  const getRiskLevelText = (riskLevel: string) => {
    switch (riskLevel) {
      case 'low': return 'Low Risk';
      case 'dangerous': return 'Dangerous';
      case 'veryDangerous': return 'Very Dangerous';
      default: return riskLevel;
    }
  };

  return (
    <div className="workplace-registration-container">
      <div className="window">
        <div className="window-header">
          <span>Workplace Registration</span>
        </div>
        <div className="window-content">
          {success && <div className="alert alert-info">{success}</div>}
          {error && <div className="alert alert-warning">{error}</div>}
          
          <form onSubmit={handleSubmit} className="workplace-form">
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Workplace Name:</label>
                  <input
                    type="text"
                    name="name"
                    className="form-input"
                    value={formData.name}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">SSK Registration No:</label>
                  <input
                    type="text"
                    name="sskRegistrationNo"
                    className="form-input"
                    value={formData.sskRegistrationNo}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
            </div>
            
            <div className="form-group">
              <label className="form-label">Address:</label>
              <textarea
                name="address"
                className="form-textarea"
                value={formData.address}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Tax Office:</label>
                  <input
                    type="text"
                    name="taxOffice"
                    className="form-input"
                    value={formData.taxOffice}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Tax Number:</label>
                  <input
                    type="text"
                    name="taxNumber"
                    className="form-input"
                    value={formData.taxNumber}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Price:</label>
                  <input
                    type="number"
                    name="price"
                    className="form-input"
                    value={formData.price}
                    onChange={handleChange}
                    step="0.01"
                    required
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Risk Level:</label>
                  <select
                    name="riskLevel"
                    className="form-select"
                    value={formData.riskLevel}
                    onChange={handleChange}
                  >
                    <option value="low">Low Risk</option>
                    <option value="dangerous">Dangerous</option>
                    <option value="veryDangerous">Very Dangerous</option>
                  </select>
                </div>
              </div>
            </div>
            
            <div className="form-group">
              <label className="form-label">Employee Count:</label>
              <input
                type="number"
                name="employeeCount"
                className="form-input"
                value={formData.employeeCount}
                onChange={handleChange}
                min="0"
                required
              />
            </div>
            
            <div className="assignment-section">
              <h3>Assignments</h3>
              
              <div className="form-group">
                <label className="form-label">Assigned Expert:</label>
                <select
                  name="assignedExpertId"
                  className="form-select"
                  value={formData.assignedExpertId}
                  onChange={handleChange}
                >
                  <option value="">Select Expert</option>
                  {experts.map(expert => (
                    <option key={expert.id} value={expert.id}>
                      {expert.firstName} {expert.lastName} (Class {expert.expertiseClass})
                    </option>
                  ))}
                </select>
                {experts.length === 0 && (
                  <div className="assignment-warning">
                    No experts available. Please register experts first.
                  </div>
                )}
              </div>
              
              <div className="form-group">
                <label className="form-label">Assigned Doctor:</label>
                <select
                  name="assignedDoctorId"
                  className="form-select"
                  value={formData.assignedDoctorId}
                  onChange={handleChange}
                >
                  <option value="">Select Doctor</option>
                  {doctors.map(doctor => (
                    <option key={doctor.id} value={doctor.id}>
                      {doctor.firstName} {doctor.lastName}
                    </option>
                  ))}
                </select>
                {doctors.length === 0 && (
                  <div className="assignment-warning">
                    No doctors available. Please register doctors first.
                  </div>
                )}
              </div>
              
              {showDsp && (
                <div className="form-group">
                  <label className="form-label">Assigned DSP:</label>
                  <select
                    name="assignedDspId"
                    className="form-select"
                    value={formData.assignedDspId}
                    onChange={handleChange}
                  >
                    <option value="">Select DSP</option>
                    {dsps.map(dsp => (
                      <option key={dsp.id} value={dsp.id}>
                        {dsp.firstName} {dsp.lastName}
                      </option>
                    ))}
                  </select>
                  {dsps.length === 0 && (
                    <div className="assignment-warning">
                      No DSPs available. Please register DSPs first.
                    </div>
                  )}
                </div>
              )}
              
              {!showDsp && formData.riskLevel === 'veryDangerous' && formData.employeeCount >= 10 && (
                <div className="assignment-warning">
                  DSP assignment is required for very dangerous workplaces with 10+ employees
                </div>
              )}
            </div>
            
            <div className="form-group">
              <label className="form-label">Source:</label>
              <input
                type="text"
                name="source"
                className="form-input"
                value={formData.source}
                onChange={handleChange}
              />
            </div>
            
            <button type="submit" className="btn btn-primary">Register Workplace</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default WorkplaceRegistration;
