import React, { useState, useEffect } from 'react';
import { createExpert, getAllExperts, deleteExpert } from '../services/api';
import './ExpertCardPage.css';
import { useNavigate } from 'react-router-dom';

const ExpertCardPage = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    expertiseClass: 'A',
    assignedMinutes: 11900
  });
  const [experts, setExperts] = useState<any[]>([]);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  
  // State for "Yeni Uzman Kayıt" card
  const [newCard, setNewCard] = useState({
    x: 50,
    y: 150,
    width: 400,
    height: 500,
    isDragging: false,
    isResizing: false,
    resizeDirection: '',
    dragStartX: 0,
    dragStartY: 0,
    resizeStartX: 0,
    resizeStartY: 0,
    originalX: 50,
    originalY: 150,
    originalWidth: 400,
    originalHeight: 500
  });
  
  // State for "Kayıtlı Uzmanlar" card
  const [listCard, setListCard] = useState({
    x: 500,
    y: 150,
    width: 500,
    height: 500,
    isDragging: false,
    isResizing: false,
    resizeDirection: '',
    dragStartX: 0,
    dragStartY: 0,
    resizeStartX: 0,
    resizeStartY: 0,
    originalX: 500,
    originalY: 150,
    originalWidth: 500,
    originalHeight: 500
  });
  
  const navigate = useNavigate();

  useEffect(() => {
    fetchExperts();
  }, []);

  const fetchExperts = async () => {
    try {
      const data = await getAllExperts();
      setExperts(data);
    } catch (err) {
      console.error('Uzmanlar getirilirken hata oluştu:', err);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'assignedMinutes' ? parseInt(value) : value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (formData.assignedMinutes > 11900) {
      setError('Atanan dakika 11.900\'ü geçemez');
      return;
    }
    
    try {
      await createExpert(formData);
      setSuccess('Uzman başarıyla kaydedildi!');
      setFormData({
        firstName: '',
        lastName: '',
        phone: '',
        expertiseClass: 'A',
        assignedMinutes: 11900
      });
      // Refresh the experts list
      fetchExperts();
    } catch (err) {
      setError('Uzman kaydedilirken hata oluştu');
    }
  };

  const handleDelete = async (id: number) => {
    try {
      await deleteExpert(id);
      setSuccess('Uzman başarıyla silindi!');
      // Refresh the experts list
      fetchExperts();
    } catch (err) {
      setError('Uzman silinirken hata oluştu');
    }
  };

  const handleClose = () => {
    // Redirect to dashboard
    navigate('/dashboard');
  };

  // Mouse event handlers
  const handleMouseDown = (e: React.MouseEvent, cardType: 'new' | 'list', action: 'drag' | 'resize', direction: string = '') => {
    e.preventDefault();
    e.stopPropagation();
    
    if (action === 'drag') {
      if (cardType === 'new') {
        setNewCard(prev => ({
          ...prev,
          isDragging: true,
          dragStartX: e.clientX - prev.x,
          dragStartY: e.clientY - prev.y
        }));
      } else {
        setListCard(prev => ({
          ...prev,
          isDragging: true,
          dragStartX: e.clientX - prev.x,
          dragStartY: e.clientY - prev.y
        }));
      }
    } else if (action === 'resize') {
      if (cardType === 'new') {
        setNewCard(prev => ({
          ...prev,
          isResizing: true,
          resizeDirection: direction,
          resizeStartX: e.clientX,
          resizeStartY: e.clientY,
          originalX: prev.x,
          originalY: prev.y,
          originalWidth: prev.width,
          originalHeight: prev.height
        }));
      } else {
        setListCard(prev => ({
          ...prev,
          isResizing: true,
          resizeDirection: direction,
          resizeStartX: e.clientX,
          resizeStartY: e.clientY,
          originalX: prev.x,
          originalY: prev.y,
          originalWidth: prev.width,
          originalHeight: prev.height
        }));
      }
    }
  };

  const handleMouseMove = (e: MouseEvent) => {
    // Handle dragging for "Yeni Uzman Kayıt" card
    if (newCard.isDragging) {
      setNewCard(prev => ({
        ...prev,
        x: e.clientX - prev.dragStartX,
        y: e.clientY - prev.dragStartY
      }));
    }
    
    // Handle dragging for "Kayıtlı Uzmanlar" card
    if (listCard.isDragging) {
      setListCard(prev => ({
        ...prev,
        x: e.clientX - prev.dragStartX,
        y: e.clientY - prev.dragStartY
      }));
    }
    
    // Handle resizing for "Yeni Uzman Kayıt" card
    if (newCard.isResizing) {
      const deltaX = e.clientX - newCard.resizeStartX;
      const deltaY = e.clientY - newCard.resizeStartY;
      
      let newX = newCard.originalX;
      let newY = newCard.originalY;
      let newWidth = newCard.originalWidth;
      let newHeight = newCard.originalHeight;
      
      // Handle different resize directions
      if (newCard.resizeDirection.includes('right')) {
        newWidth = Math.max(300, newCard.originalWidth + deltaX);
      }
      if (newCard.resizeDirection.includes('left')) {
        newWidth = Math.max(300, newCard.originalWidth - deltaX);
        newX = newCard.originalX + deltaX;
      }
      if (newCard.resizeDirection.includes('bottom')) {
        newHeight = Math.max(200, newCard.originalHeight + deltaY);
      }
      if (newCard.resizeDirection.includes('top')) {
        newHeight = Math.max(200, newCard.originalHeight - deltaY);
        newY = newCard.originalY + deltaY;
      }
      
      setNewCard(prev => ({
        ...prev,
        x: newX,
        y: newY,
        width: newWidth,
        height: newHeight
      }));
    }
    
    // Handle resizing for "Kayıtlı Uzmanlar" card
    if (listCard.isResizing) {
      const deltaX = e.clientX - listCard.resizeStartX;
      const deltaY = e.clientY - listCard.resizeStartY;
      
      let newX = listCard.originalX;
      let newY = listCard.originalY;
      let newWidth = listCard.originalWidth;
      let newHeight = listCard.originalHeight;
      
      // Handle different resize directions
      if (listCard.resizeDirection.includes('right')) {
        newWidth = Math.max(300, listCard.originalWidth + deltaX);
      }
      if (listCard.resizeDirection.includes('left')) {
        newWidth = Math.max(300, listCard.originalWidth - deltaX);
        newX = listCard.originalX + deltaX;
      }
      if (listCard.resizeDirection.includes('bottom')) {
        newHeight = Math.max(200, listCard.originalHeight + deltaY);
      }
      if (listCard.resizeDirection.includes('top')) {
        newHeight = Math.max(200, listCard.originalHeight - deltaY);
        newY = listCard.originalY + deltaY;
      }
      
      setListCard(prev => ({
        ...prev,
        x: newX,
        y: newY,
        width: newWidth,
        height: newHeight
      }));
    }
  };

  const handleMouseUp = () => {
    setNewCard(prev => ({ ...prev, isDragging: false, isResizing: false, resizeDirection: '' }));
    setListCard(prev => ({ ...prev, isDragging: false, isResizing: false, resizeDirection: '' }));
  };

  useEffect(() => {
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
    
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [newCard.isDragging, listCard.isDragging, newCard.isResizing, listCard.isResizing]);

  return (
    <div className="expert-card-page">
      {/* "Yeni Uzman Kayıt" card (draggable and resizable) */}
      <div 
        className="draggable-card"
        style={{
          position: 'absolute',
          left: newCard.x,
          top: newCard.y,
          width: newCard.width,
          height: newCard.height,
          zIndex: 1000,
          cursor: newCard.isDragging ? 'grabbing' : 'default'
        }}
      >
        <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
          <div 
            className="window-header"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'drag')}
            style={{ cursor: 'move', userSelect: 'none' }}
          >
            <span>Yeni Uzman Kayıt</span>
            <div className="window-controls">
              <button className="window-button" onClick={handleClose}>×</button>
            </div>
          </div>
          <div className="window-content" style={{ flex: 1, overflow: 'auto' }}>
            {success && <div className="alert alert-info">{success}</div>}
            {error && <div className="alert alert-warning">{error}</div>}
            
            <form onSubmit={handleSubmit} className="registration-form">
              <div className="form-row">
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Ad:</label>
                    <input
                      type="text"
                      name="firstName"
                      className="form-input"
                      value={formData.firstName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Soyad:</label>
                    <input
                      type="text"
                      name="lastName"
                      className="form-input"
                      value={formData.lastName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
              </div>
              
              <div className="form-group">
                <label className="form-label">Telefon:</label>
                <input
                  type="text"
                  name="phone"
                  className="form-input"
                  value={formData.phone}
                  onChange={handleChange}
                  required
                />
              </div>
              
              <div className="form-row">
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Uzmanlık Sınıfı:</label>
                    <select
                      name="expertiseClass"
                      className="form-select"
                      value={formData.expertiseClass}
                      onChange={handleChange}
                    >
                      <option value="A">A</option>
                      <option value="B">B</option>
                      <option value="C">C</option>
                    </select>
                  </div>
                </div>
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Atanan Dakika:</label>
                    <input
                      type="number"
                      name="assignedMinutes"
                      className="form-input"
                      value={formData.assignedMinutes}
                      onChange={handleChange}
                      min="0"
                      max="11900"
                      required
                    />
                  </div>
                </div>
              </div>
              
              <button type="submit" className="btn btn-primary">Uzmanı Kaydet</button>
            </form>
          </div>
          {/* Resize handles */}
          <div 
            className="resize-handle resize-nw"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'top-left')}
          />
          <div 
            className="resize-handle resize-ne"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'top-right')}
          />
          <div 
            className="resize-handle resize-sw"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'bottom-left')}
          />
          <div 
            className="resize-handle resize-se"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'bottom-right')}
          />
          <div 
            className="resize-handle resize-n"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'top')}
          />
          <div 
            className="resize-handle resize-s"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'bottom')}
          />
          <div 
            className="resize-handle resize-w"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'left')}
          />
          <div 
            className="resize-handle resize-e"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'right')}
          />
        </div>
      </div>
      
      {/* "Kayıtlı Uzmanlar" card (draggable and resizable) */}
      <div 
        className="draggable-card"
        style={{
          position: 'absolute',
          left: listCard.x,
          top: listCard.y,
          width: listCard.width,
          height: listCard.height,
          zIndex: 1000,
          cursor: listCard.isDragging ? 'grabbing' : 'default'
        }}
      >
        <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
          <div 
            className="window-header"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'drag')}
            style={{ cursor: 'move', userSelect: 'none' }}
          >
            <span>Kayıtlı Uzmanlar</span>
          </div>
          <div className="window-content" style={{ flex: 1, overflow: 'auto' }}>
            {experts.length === 0 ? (
              <p>Kayıtlı uzman bulunmamaktadır.</p>
            ) : (
              <div className="expert-list">
                {experts.map((expert) => (
                  <div key={expert.id} className="expert-row">
                    <div className="expert-info-section">
                      <div className="expert-name">
                        <strong>Ad Soyad:</strong> {expert.firstName} {expert.lastName}
                      </div>
                      <div className="expert-contact">
                        <strong>İletişim:</strong> {expert.phone}
                      </div>
                      <div className="expert-class">
                        <strong>Sınıfı:</strong> {expert.expertiseClass}
                      </div>
                      <div className="expert-minutes">
                        <strong>Tanımlı Dakika:</strong> {expert.assignedMinutes}
                      </div>
                      <div className="expert-assignment">
                        <strong>Atanan Dakika:</strong> 0 | <strong>Kalan:</strong> {expert.assignedMinutes}
                      </div>
                    </div>
                    <div className="expert-actions">
                      <button className="btn-icon edit">✏️</button>
                      <button className="btn-icon delete" onClick={() => handleDelete(expert.id)}>🗑️</button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
          {/* Resize handles */}
          <div 
            className="resize-handle resize-nw"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'top-left')}
          />
          <div 
            className="resize-handle resize-ne"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'top-right')}
          />
          <div 
            className="resize-handle resize-sw"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'bottom-left')}
          />
          <div 
            className="resize-handle resize-se"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'bottom-right')}
          />
          <div 
            className="resize-handle resize-n"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'top')}
          />
          <div 
            className="resize-handle resize-s"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'bottom')}
          />
          <div 
            className="resize-handle resize-w"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'left')}
          />
          <div 
            className="resize-handle resize-e"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'right')}
          />
        </div>
      </div>
    </div>
  );
};

export default ExpertCardPage;
