import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { login } from '../services/api';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await login(username, password);
      if (response.token) {
        localStorage.setItem('token', response.token);
        navigate('/dashboard');
      } else {
        setError('Sunucudan geçersiz yanıt');
      }
    } catch (err: any) {
      setError(err.message || 'Geçersiz kullanıcı adı veya şifre');
    }
  };

  const handleClose = () => {
    // Close the application or redirect to a blank page
    window.close(); // Try to close the window
    // If that doesn't work, redirect to about:blank
    window.location.href = 'about:blank';
  };

  return (
    <div className="login-container">
      <div className="window login-window">
        <div className="window-header">
          <span>OSGB Sistemi - Giriş</span>
          <div className="window-controls">
            <button className="window-button" onClick={handleClose}>×</button>
          </div>
        </div>
        <div className="window-content login-content">
          <div className="login-logo">
            <h2 className="login-title">
              <span className="osgb-text">OSGB</span>
              <span className="gear-icon"></span>
            </h2>
            <p>İş Sağlığı ve Güvenliği</p>
          </div>
          {error && <div className="alert alert-warning">{error}</div>}
          <form onSubmit={handleSubmit} className="login-form">
            <div className="form-group login-input-group">
              <label className="form-label">Kullanıcı Adı:</label>
              <input
                type="text"
                className="form-input login-input"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div className="form-group login-input-group">
              <label className="form-label">Şifre:</label>
              <input
                type="password"
                className="form-input login-input"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="btn btn-primary login-button">Giriş</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
