import React, { useState, useEffect } from 'react';
import { getAllWorkplaces, createWorkplace } from '../services/api';

const DataPersistenceTest: React.FC = () => {
  const [workplaces, setWorkplaces] = useState<any[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      const data = await getAllWorkplaces();
      setWorkplaces(data);
      setError(null);
    } catch (err) {
      setError('Failed to fetch workplaces');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const createTestWorkplace = async () => {
    try {
      const testWorkplace = {
        name: `Test Workplace ${Date.now()}`,
        address: 'Test Address',
        sskRegistrationNo: '1234567890',
        taxOffice: 'Test Tax Office',
        taxNumber: '1234567890',
        price: 1000,
        riskLevel: 'low',
        employeeCount: 10,
        source: 'Test Source',
        approvalStatus: 'atama'
      };

      await createWorkplace(testWorkplace);
      await fetchData(); // Refresh the list
    } catch (err) {
      setError('Failed to create test workplace');
      console.error(err);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>Data Persistence Test</h2>
      <button onClick={createTestWorkplace} style={{ marginBottom: '20px' }}>
        Create Test Workplace
      </button>
      <div>
        <h3>Workplaces ({workplaces.length})</h3>
        {workplaces.map((workplace) => (
          <div key={workplace.id} style={{ border: '1px solid #ccc', margin: '10px', padding: '10px' }}>
            <p><strong>ID:</strong> {workplace.id}</p>
            <p><strong>Name:</strong> {workplace.name}</p>
            <p><strong>Created At:</strong> {new Date(workplace.createdAt).toLocaleString()}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DataPersistenceTest;
