import React, { useState, useEffect, useCallback } from 'react';
import { useLocation } from 'react-router-dom';
import './Dashboard.css';
import { createWorkplace, updateWorkplace, getAvailableExperts, getAvailableDoctors, getAvailableDsps, getAllWorkplaces, deleteWorkplace, getAllExperts, getAllDoctors, getAllDsps } from '../services/api';

// Define types
interface Workplace {
  id: number;
  name: string;
  sskRegistrationNo: string;
  address: string;
  price: string;
  taxOffice: string;
  taxNumber: string;
  riskLevel: string;
  employeeCount: string;
  source: string;
  assignedExpertId: string;
  assignedDoctorId: string;
  assignedDspId: string;
  registrationDate: string;
  approvalStatus: string;
}

const Dashboard: React.FC = () => {
  console.log('Dashboard component rendered at:', new Date().toISOString());
  const location = useLocation();
  
  // State declarations
  const [formData, setFormData] = useState({
    name: '',
    sskRegistrationNo: '',
    address: '',
    price: '',
    taxOffice: '',
    taxNumber: '',
    riskLevel: 'low',
    employeeCount: '',
    source: '',
    assignedExpertId: '',
    assignedDoctorId: '',
    assignedDspId: '',
    registrationDate: '',
  });
  
  const [isKdvIncluded, setIsKdvIncluded] = useState<boolean>(false);
  const [workplaces, setWorkplaces] = useState<Workplace[]>([]);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [isModalOpen, setIsModalOpen] = useState<boolean>(false);
  const [modalFormData, setModalFormData] = useState<Partial<Workplace>>({});
  const [showNoExpertPopup, setShowNoExpertPopup] = useState<boolean>(false);
  const [showNoDoctorPopup, setShowNoDoctorPopup] = useState<boolean>(false);
  const [showNoDspPopup, setShowNoDspPopup] = useState<boolean>(false);
  const [showDspNotAllowedPopup, setShowDspNotAllowedPopup] = useState<boolean>(false);
  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState<boolean>(false);
  const [allExperts, setAllExperts] = useState<any[]>([]);
  const [allDoctors, setAllDoctors] = useState<any[]>([]);
  const [allDsps, setAllDsps] = useState<any[]>([]);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  const [duplicateWarning, setDuplicateWarning] = useState<{show: boolean, workplace: Workplace | null}>({show: false, workplace: null});

  // Constants
  const workplacesPerPage = 10;
  
  // Derived state
  const totalPages = Math.ceil(workplaces.length / workplacesPerPage);
  
  // Add state for sorting
  const [sortConfig, setSortConfig] = useState<{ key: string; direction: 'asc' | 'desc' } | null>(null);
  
  // Function to sort workplaces
  const sortWorkplaces = (key: string) => {
    let direction: 'asc' | 'desc' = 'asc';
    if (sortConfig && sortConfig.key === key && sortConfig.direction === 'asc') {
      direction = 'desc';
    }
    
    setSortConfig({ key, direction });
  };
  
  // Apply sorting to workplaces
  const getSortedWorkplaces = () => {
    if (!sortConfig) return workplaces;
    
    return [...workplaces].sort((a, b) => {
      // Type guard to ensure key is a valid keyof Workplace
      const key = sortConfig.key as keyof Workplace;
      
      // Handle different data types for sorting
      let aValue: any, bValue: any;
      
      switch (key) {
        case 'name':
        case 'source':
          aValue = a[key] || '';
          bValue = b[key] || '';
          break;
        case 'registrationDate':
          aValue = new Date(a[key] || '').getTime();
          bValue = new Date(b[key] || '').getTime();
          break;
        case 'riskLevel':
          // Custom sorting for risk levels: 'low' < 'dangerous' < 'veryDangerous'
          const riskOrder: Record<string, number> = {
            'low': 1,
            'dangerous': 2,
            'veryDangerous': 3
          };
          aValue = riskOrder[a[key]] || 0;
          bValue = riskOrder[b[key]] || 0;
          break;
        case 'approvalStatus':
          // Custom sorting for approval status: 'atama' < 'bekliyor' < 'onaylandi'
          const approvalOrder: Record<string, number> = {
            'atama': 1,
            'bekliyor': 2,
            'onaylandi': 3
          };
          aValue = approvalOrder[a[key]] || 0;
          bValue = approvalOrder[b[key]] || 0;
          break;
        default:
          aValue = a[key];
          bValue = b[key];
      }
      
      if (aValue < bValue) {
        return sortConfig.direction === 'asc' ? -1 : 1;
      }
      if (aValue > bValue) {
        return sortConfig.direction === 'asc' ? 1 : -1;
      }
      return 0;
    });
  };
  
  // Add a useEffect to log when component mounts and unmounts
  useEffect(() => {
    console.log('Dashboard component mounted at:', new Date().toISOString());
    
    // Cleanup function
    return () => {
      console.log('Dashboard component unmounted at:', new Date().toISOString());
    };
  }, []);
  
  // Function to fetch all data - similar to ExpertCardPage approach
  const fetchAllData = useCallback(async () => {
    try {
      console.log('Fetching all data from backend...');
      
      // Fetch all data in parallel
      const [workplacesData, expertsData, doctorsData, dspsData] = await Promise.all([
        getAllWorkplaces(),
        getAllExperts(),
        getAllDoctors(),
        getAllDsps()
      ]);
      
      console.log('Received all data:', { 
        workplacesCount: workplacesData.length, 
        expertsCount: expertsData.length, 
        doctorsCount: doctorsData.length, 
        dspsCount: dspsData.length 
      });
      
      // Update all state
      setWorkplaces(workplacesData);
      setAllExperts(expertsData);
      setAllDoctors(doctorsData);
      setAllDsps(dspsData);
      console.log('State updated after data fetch');
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Veri alınırken hata oluştu');
    }
  }, []);

  // Load all data from backend on component mount - similar to ExpertCardPage approach
  useEffect(() => {
    console.log('Dashboard useEffect running - fetching all data at:', new Date().toISOString());
    fetchAllData();
  }, []);

  // Refresh data when location changes
  useEffect(() => {
    console.log('Location changed to:', location.pathname, 'at:', new Date().toISOString());
    fetchAllData();
  }, [location.pathname]);

  // Refresh data when window regains focus
  useEffect(() => {
    const handleFocus = () => {
      console.log('Window focused, refreshing all data...');
      fetchAllData();
    };

    window.addEventListener('focus', handleFocus);
    return () => {
      window.removeEventListener('focus', handleFocus);
    };
  }, []);

  // Clear notifications after timeout
  const clearNotifications = () => {
    setTimeout(() => {
      setSuccess('');
      setError('');
    }, 3000);
  };

  // Handlers
  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    
    // Check if user is trying to assign a DSP when it's not allowed
    if (name === 'assignedDspId' && value) {
      const employeeCount = parseInt(formData.employeeCount) || 0;
      const isAllowed = isDspAssignmentAllowed(formData.riskLevel, employeeCount);
      
      if (!isAllowed) {
        // Show warning popup
        setShowDspNotAllowedPopup(true);
        return; // Don't update the form data
      }
    }
    
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };
  
  const handlePriceChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    // Allow only numeric values
    if (value === '' || /^\d*\.?\d*$/.test(value)) {
      setFormData(prev => ({
        ...prev,
        price: value
      }));
    }
  };
  
  const handleTaxOfficeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    // Allow only letters and spaces, no numbers
    if (value === '' || /^[a-zA-ZğüşıöçĞÜŞİÖÇ\s]*$/.test(value)) {
      setFormData(prev => ({
        ...prev,
        taxOffice: value
      }));
    }
  };
  
  const handleTaxNumberChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    // Allow only numeric values
    if (value === '' || /^\d*$/.test(value)) {
      setFormData(prev => ({
        ...prev,
        taxNumber: value
      }));
    }
  };
  
  const handleEmployeeCountChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    // Allow only numeric values
    if (value === '' || /^\d*$/.test(value)) {
      setFormData(prev => ({
        ...prev,
        employeeCount: value
      }));
    }
  };
  
  const handleSourceChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    // Allow only letters and spaces, no numbers
    if (value === '' || /^[a-zA-ZğüşıöçĞÜŞİÖÇ\s]*$/.test(value)) {
      setFormData(prev => ({
        ...prev,
        source: value
      }));
    }
  };
  
  // Format price with thousands separator
  const formatPrice = (price: string | number): string => {
    if (!price) return '0';
    const num = typeof price === 'string' ? parseFloat(price) : price;
    if (isNaN(num)) return '0';
    return num.toLocaleString('tr-TR', { maximumFractionDigits: 2 });
  };
  
  const handleSSkRegistrationNoChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    let value = e.target.value.replace(/\D/g, ''); // Remove non-digit characters
    
    // Format as "x xxxx xx xx xxxxx xxx xx xx xxx"
    if (value.length > 21) value = value.substring(0, 21);
    
    let formattedValue = '';
    for (let i = 0; i < value.length; i++) {
      if (i === 1 || i === 5 || i === 7 || i === 9 || i === 14 || i === 17 || i === 19) {
        formattedValue += ' ' + value[i];
      } else {
        formattedValue += value[i];
      }
    }
    
    setFormData(prev => ({
      ...prev,
      sskRegistrationNo: formattedValue
    }));
  };
  
  const handleClose = () => {
    setFormData({
      name: '',
      sskRegistrationNo: '',
      address: '',
      price: '',
      taxOffice: '',
      taxNumber: '',
      riskLevel: 'low',
      employeeCount: '',
      source: '',
      assignedExpertId: '',
      assignedDoctorId: '',
      assignedDspId: '',
      registrationDate: '',
    });
    setIsKdvIncluded(false);
  };
  
  // Function to calculate required minutes based on risk level and employee count
  const calculateRequiredMinutes = (riskLevel: string, employeeCount: number): number => {
    switch (riskLevel) {
      case 'low':
        return employeeCount * 10;
      case 'dangerous':
        return employeeCount * 20;
      case 'veryDangerous':
        return employeeCount * 40;
      default:
        return 0;
    }
  };
  
  // Function to calculate required minutes for doctors based on risk level and employee count
  const calculateRequiredDoctorMinutes = (riskLevel: string, employeeCount: number): number => {
    switch (riskLevel) {
      case 'low':
        return employeeCount * 5;
      case 'dangerous':
        return employeeCount * 10;
      case 'veryDangerous':
        return employeeCount * 15;
      default:
        return 0;
    }
  };
  
  // Function to calculate required minutes for DSPs based on risk level and employee count
  const calculateRequiredDspMinutes = (riskLevel: string, employeeCount: number): number => {
    // DSP is only required for very dangerous workplaces with 10+ employees
    if (riskLevel !== 'veryDangerous' || employeeCount < 10) {
      return 0;
    }
    return employeeCount * 5; // 5 minutes per employee for DSP
  };
  
  // Function to filter experts based on risk level and required minutes
  const getFilteredExperts = (riskLevel: string, employeeCount: number) => {
    const requiredMinutes = calculateRequiredMinutes(riskLevel, employeeCount);
    
    return allExperts.filter(expert => {
      // Check if expert has enough minutes
      if (expert.assignedMinutes < requiredMinutes) {
        return false;
      }
      
      // Filter based on expertise class and risk level
      // A class experts can handle all risk levels
      // B class experts can handle low and dangerous risk levels
      // C class experts can only handle low risk level
      switch (expert.expertiseClass) {
        case 'A':
          return true; // A class can handle all risk levels
        case 'B':
          return riskLevel === 'low' || riskLevel === 'dangerous'; // B class can handle low and dangerous
        case 'C':
          return riskLevel === 'low'; // C class can only handle low risk
        default:
          return false;
      }
    });
  };
  
  // Function to filter doctors based on required minutes
  const getFilteredDoctors = (riskLevel: string, employeeCount: number) => {
    const requiredMinutes = calculateRequiredDoctorMinutes(riskLevel, employeeCount);
    
    return allDoctors.filter(doctor => {
      // Check if doctor has enough minutes
      return doctor.assignedMinutes >= requiredMinutes;
    });
  };
  
  // Function to filter DSPs based on required minutes
  const getFilteredDsps = (riskLevel: string, employeeCount: number) => {
    // Check if DSP assignment is allowed for this workplace
    const isAllowed = isDspAssignmentAllowed(riskLevel, employeeCount);
    
    // If DSP assignment is not allowed, return empty array
    if (!isAllowed) {
      return [];
    }
    
    // DSP minutes are deducted at 5 minutes per employee for very dangerous workplaces with 10+ employees
    const isDspRequired = riskLevel === 'veryDangerous' && employeeCount >= 10;
    
    // If DSP is not required, return all DSPs (optional assignment) that have at least 1 minute
    if (!isDspRequired) {
      return allDsps.filter(dsp => dsp.assignedMinutes > 0); // Return all DSPs with at least 1 minute
    }
    
    // If DSP is required, filter based on required minutes (5 minutes per employee)
    const requiredMinutes = employeeCount * 5;
    
    return allDsps.filter(dsp => {
      // Check if DSP has enough minutes
      return dsp.assignedMinutes >= requiredMinutes;
    });
  };
  
  // Function to check if expert assignment is valid
  const checkExpertAssignment = async (workplaceData: any) => {
    let tempWorkplaceId: number | null = null;
    try {
      // Create a temporary workplace to get its ID
      const tempWorkplace = await createWorkplace({
        ...workplaceData,
        employeeCount: parseInt(workplaceData.employeeCount) || 0,
        price: parseFloat(workplaceData.price) || 0,
        approvalStatus: 'atama',
        // Handle null values for assigned IDs
        assignedExpertId: workplaceData.assignedExpertId || null,
        assignedDoctorId: workplaceData.assignedDoctorId || null,
        assignedDspId: workplaceData.assignedDspId || null
      });
      
      tempWorkplaceId = tempWorkplace.id;
      
      // Get available experts for this workplace
      const availableExperts = await getAvailableExperts(tempWorkplace.id);
      
      // Delete the temporary workplace
      await deleteWorkplace(tempWorkplace.id);
      
      // If no experts available, show popup
      if (availableExperts.length === 0) {
        setShowNoExpertPopup(true);
        return false;
      }
      
      // If an expert is selected, check if it's still available after deduction
      if (workplaceData.assignedExpertId) {
        const selectedExpert = availableExperts.find((expert: any) => expert.id === parseInt(workplaceData.assignedExpertId));
        if (!selectedExpert) {
          setShowNoExpertPopup(true);
          return false;
        }
      }
      
      return true;
    } catch (error) {
      console.error('Error checking expert assignment:', error);
      // Try to delete the temporary workplace if it was created
      if (tempWorkplaceId) {
        try {
          await deleteWorkplace(tempWorkplaceId);
        } catch (deleteError) {
          console.error('Error deleting temporary workplace:', deleteError);
        }
      }
      return true; // Allow assignment if there's an error
    }
  };
  
  // Function to check if doctor assignment is valid
  const checkDoctorAssignment = async (workplaceData: any) => {
    let tempWorkplaceId: number | null = null;
    try {
      // Create a temporary workplace to get its ID
      const tempWorkplace = await createWorkplace({
        ...workplaceData,
        employeeCount: parseInt(workplaceData.employeeCount) || 0,
        price: parseFloat(workplaceData.price) || 0,
        approvalStatus: 'atama',
        // Handle null values for assigned IDs
        assignedExpertId: workplaceData.assignedExpertId || null,
        assignedDoctorId: workplaceData.assignedDoctorId || null,
        assignedDspId: workplaceData.assignedDspId || null
      });
      
      tempWorkplaceId = tempWorkplace.id;
      
      // Get available doctors for this workplace
      const availableDoctors = await getAvailableDoctors(tempWorkplace.id);
      
      // Delete the temporary workplace
      await deleteWorkplace(tempWorkplace.id);
      
      // If no doctors available, show popup
      if (availableDoctors.length === 0) {
        setShowNoDoctorPopup(true);
        return false;
      }
      
      // If a doctor is selected, check if it's still available after deduction
      if (workplaceData.assignedDoctorId) {
        const selectedDoctor = availableDoctors.find((doctor: any) => doctor.id === parseInt(workplaceData.assignedDoctorId));
        if (!selectedDoctor) {
          setShowNoDoctorPopup(true);
          return false;
        }
      }
      
      return true;
    } catch (error) {
      console.error('Error checking doctor assignment:', error);
      // Try to delete the temporary workplace if it was created
      if (tempWorkplaceId) {
        try {
          await deleteWorkplace(tempWorkplaceId);
        } catch (deleteError) {
          console.error('Error deleting temporary workplace:', deleteError);
        }
      }
      return true; // Allow assignment if there's an error
    }
  };
  
  // Function to check if DSP assignment is valid
  const checkDspAssignment = async (workplaceData: any) => {
    let tempWorkplaceId: number | null = null;
    try {
      // Create a temporary workplace to get its ID
      const tempWorkplace = await createWorkplace({
        ...workplaceData,
        employeeCount: parseInt(workplaceData.employeeCount) || 0,
        price: parseFloat(workplaceData.price) || 0,
        approvalStatus: 'atama',
        // Handle null values for assigned IDs
        assignedExpertId: workplaceData.assignedExpertId || null,
        assignedDoctorId: workplaceData.assignedDoctorId || null,
        assignedDspId: workplaceData.assignedDspId || null
      });
      
      tempWorkplaceId = tempWorkplace.id;
      
      // Check if DSP is actually required for this workplace
      const employeeCount = parseInt(workplaceData.employeeCount) || 0;
      const isDspRequired = workplaceData.riskLevel === 'veryDangerous' && employeeCount >= 10;
      
      // If DSP is not required, don't check availability
      if (!isDspRequired) {
        // Delete the temporary workplace
        await deleteWorkplace(tempWorkplace.id);
        return true; // DSP assignment is not required, so it's valid
      }
      
      // Get available DSPs for this workplace
      const availableDsps = await getAvailableDsps(tempWorkplace.id);
      
      // Delete the temporary workplace
      await deleteWorkplace(tempWorkplace.id);
      
      // If no DSPs available when required, show popup
      if (availableDsps.length === 0) {
        setShowNoDspPopup(true);
        return false;
      }
      
      // If a DSP is selected, check if it's still available after deduction
      if (workplaceData.assignedDspId) {
        const selectedDsp = availableDsps.find((dsp: any) => dsp.id === parseInt(workplaceData.assignedDspId));
        if (!selectedDsp) {
          setShowNoDspPopup(true);
          return false;
        }
      }
      
      return true;
    } catch (error) {
      console.error('Error checking DSP assignment:', error);
      // Try to delete the temporary workplace if it was created
      if (tempWorkplaceId) {
        try {
          await deleteWorkplace(tempWorkplaceId);
        } catch (deleteError) {
          console.error('Error deleting temporary workplace:', deleteError);
        }
      }
      return true; // Allow assignment if there's an error
    }
  };
  
  // Function to check if DSP assignment is allowed based on risk level and employee count
  const isDspAssignmentAllowed = (riskLevel: string, employeeCount: number): boolean => {
    // DSP can only be assigned to:
    // 1. Very dangerous workplaces with 10+ employees (required)
    // 2. Any workplace where user chooses to assign (optional, but only if not forbidden)
    
    // DSP assignment is NOT allowed for:
    // 1. Low risk (az tehlikeli) workplaces
    // 2. Dangerous (tehlikeli) workplaces
    // 3. Very dangerous (çok tehlikeli) workplaces with less than 10 employees
    
    if (riskLevel === 'low' || riskLevel === 'dangerous') {
      return false; // DSP assignment not allowed for low or dangerous risk levels
    }
    
    if (riskLevel === 'veryDangerous' && employeeCount < 10) {
      return false; // DSP assignment not allowed for very dangerous workplaces with less than 10 employees
    }
    
    return true; // DSP assignment allowed (and required for very dangerous with 10+ employees)
  };
  
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const submitTimestamp = Date.now();
    console.log('handleSubmit called at:', new Date().toISOString(), '(submit #', submitTimestamp, ') with formData:', formData);
    
    try {
      // Check for duplicate SGK registration number
      const formattedSskNo = formData.sskRegistrationNo.replace(/\s/g, ''); // Remove spaces for comparison
      const existingWorkplace = workplaces.find(workplace => 
        workplace.sskRegistrationNo && 
        workplace.sskRegistrationNo.replace(/\s/g, '') === formattedSskNo
      );
      
      if (existingWorkplace) {
        // Show duplicate warning popup
        setDuplicateWarning({show: true, workplace: existingWorkplace});
        
        // Clear the warning after 5 seconds
        setTimeout(() => {
          setDuplicateWarning({show: false, workplace: null});
        }, 5000);
        
        // Scroll to the existing workplace row
        setTimeout(() => {
          const element = document.getElementById(`workplace-row-${existingWorkplace.id}`);
          if (element) {
            element.scrollIntoView({ behavior: 'smooth', block: 'center' });
          }
        }, 100);
        
        return;
      }
      
      // Ensure all required fields are included and handle null values for assigned IDs
      const workplaceData = {
        ...formData,
        employeeCount: parseInt(formData.employeeCount) || 0,
        price: parseFloat(formData.price) || 0,
        approvalStatus: 'atama',
        // Handle null values for assigned IDs
        assignedExpertId: formData.assignedExpertId || null,
        assignedDoctorId: formData.assignedDoctorId || null,
        assignedDspId: formData.assignedDspId || null
      };
      
      console.log('Sending workplace data to backend (submit #', submitTimestamp, '):', workplaceData);
      
      // Check if personnel assignments are required
      const employeeCount = parseInt(formData.employeeCount) || 0;
      const isDspRequired = formData.riskLevel === 'veryDangerous' && employeeCount >= 10;
      
      // Only validate expert and doctor assignments (these are always required)
      const isExpertValid = await checkExpertAssignment(workplaceData);
      const isDoctorValid = await checkDoctorAssignment(workplaceData);
      
      // Only validate DSP assignment if it's required
      const isDspValid = isDspRequired ? await checkDspAssignment(workplaceData) : true;
      
      // If any required assignment is invalid, don't proceed with saving
      if (!isExpertValid || !isDoctorValid || !isDspValid) {
        console.log('Personnel assignment validation failed, not saving (submit #', submitTimestamp, ')');
        return;
      }
      
      // Save to backend
      const newWorkplace = await createWorkplace(workplaceData);
      console.log('Created new workplace response (submit #', submitTimestamp, '):', newWorkplace);
      
      // Show success message
      setSuccess('İş yeri başarıyla kaydedildi!');
      
      // Close the form first
      handleClose();
      console.log('Form closed (submit #', submitTimestamp, ')');
      
      // Refresh all data from backend to ensure consistency
      await fetchAllData();
      
      // Dispatch custom event to notify other components about data change
      window.dispatchEvent(new CustomEvent('workplaceCreated', { detail: { workplace: newWorkplace } }));
      
      // Reset to first page since we have new data
      setCurrentPage(1);
      console.log('Page reset to 1 (submit #', submitTimestamp, ')');
      
      // Clear notifications
      clearNotifications();
    } catch (error: any) {
      console.error('Error saving workplace (submit #', submitTimestamp, '):', error);
      // Provide more specific error message
      if (error.message) {
        setError(`İş yeri kaydedilirken hata oluştu: ${error.message}`);
      } else {
        setError('İş yeri kaydedilirken hata oluştu');
      }
      clearNotifications();
    }
  };
  
  const handlePageChange = (pageNumber: number) => {
    setCurrentPage(pageNumber);
  };
  
  const getCurrentPageWorkplaces = (): Workplace[] => {
    const startIndex = (currentPage - 1) * workplacesPerPage;
    const endIndex = startIndex + workplacesPerPage;
    const result = workplaces.slice(startIndex, endIndex);
    console.log('getCurrentPageWorkplaces called, returning:', result);
    return result;
  };
  
  const handleRowClick = (workplace: Workplace) => {
    setModalFormData(workplace);
    setIsModalOpen(true);
    // Prevent background scrolling when modal is open
    document.body.style.overflow = 'hidden';
  };
  
  const handleModalChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    
    // Apply the same formatting logic for SGK registration number in the modal
    if (name === 'sskRegistrationNo') {
      let formattedValue = value.replace(/\D/g, ''); // Remove non-digit characters
      
      // Format as "x xxxx xx xx xxxxx xxx xx xx xxx"
      if (formattedValue.length > 21) formattedValue = formattedValue.substring(0, 21);
      
      let finalFormattedValue = '';
      for (let i = 0; i < formattedValue.length; i++) {
        if (i === 1 || i === 5 || i === 7 || i === 9 || i === 14 || i === 17 || i === 19) {
          finalFormattedValue += ' ' + formattedValue[i];
        } else {
          finalFormattedValue += formattedValue[i];
        }
      }
      
      setModalFormData(prev => ({
        ...prev,
        [name]: finalFormattedValue
      }));
    } else {
      setModalFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };
  
  const handleModalSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    try {
      if (modalFormData.id) {
        // Ensure all required fields are included and handle null values for assigned IDs
        const workplaceData = {
          ...modalFormData,
          employeeCount: parseInt(modalFormData.employeeCount as string) || 0,
          price: parseFloat(modalFormData.price as string) || 0,
          // Handle null values for assigned IDs
          assignedExpertId: modalFormData.assignedExpertId || null,
          assignedDoctorId: modalFormData.assignedDoctorId || null,
          assignedDspId: modalFormData.assignedDspId || null
        };
        
        // Check if personnel assignments are required
        const employeeCount = parseInt(modalFormData.employeeCount as string) || 0;
        const riskLevel = (modalFormData.riskLevel as string) || 'low';
        const isDspRequired = riskLevel === 'veryDangerous' && employeeCount >= 10;
        
        // Only validate expert and doctor assignments (these are always required)
        const isExpertValid = await checkExpertAssignment(workplaceData);
        const isDoctorValid = await checkDoctorAssignment(workplaceData);
        
        // Only validate DSP assignment if it's required
        const isDspValid = isDspRequired ? await checkDspAssignment(workplaceData) : true;
        
        // If any required assignment is invalid, don't proceed with saving
        if (!isExpertValid || !isDoctorValid || !isDspValid) {
          return;
        }
        
        // Update existing workplace
        const updatedWorkplace = await updateWorkplace(modalFormData.id, workplaceData);
        
        // Show success message
        setSuccess('İş yeri başarıyla güncellendi!');
        
        // Close modal first
        setIsModalOpen(false);
        
        // Refresh all data from backend to ensure consistency
        await fetchAllData();
        
        // Clear notifications
        clearNotifications();
      }
    } catch (error: any) {
      console.error('Error updating workplace:', error);
      // Provide more specific error message
      if (error.message) {
        setError(`İş yeri güncellenirken hata oluştu: ${error.message}`);
      } else {
        setError('İş yeri güncellenirken hata oluştu');
      }
      clearNotifications();
    }
  };

  const handleDeleteWorkplace = () => {
    // Show confirmation dialog before deleting
    setShowDeleteConfirmation(true);
  };

  // Function to confirm deletion
  const confirmDeleteWorkplace = async () => {
    try {
      if (modalFormData.id) {
        // Delete from backend
        await deleteWorkplace(modalFormData.id);
        
        // Show success message
        setSuccess('İş yeri başarıyla silindi!');
        
        // Close confirmation dialog and modal
        setShowDeleteConfirmation(false);
        closeModal();
        
        // Refresh all data from backend to ensure consistency
        await fetchAllData();
        
        // Reset to first page if we're on an empty page
        const newTotalPages = Math.ceil(workplaces.length / workplacesPerPage);
        if (currentPage > newTotalPages && newTotalPages > 0) {
          setCurrentPage(newTotalPages);
        } else if (newTotalPages === 0) {
          setCurrentPage(1);
        }
        
        // Clear notifications
        clearNotifications();
      } else {
        setShowDeleteConfirmation(false);
        closeModal();
      }
    } catch (error: any) {
      console.error('Error deleting workplace:', error);
      // Provide more specific error message
      if (error.message) {
        setError(`İş yeri silinirken hata oluştu: ${error.message}`);
      } else {
        setError('İş yeri silinirken hata oluştu');
      }
      clearNotifications();
    }
  };

