import React, { useState, useEffect } from 'react';
import { getAllWorkplaces } from '../services/api';
import './Dashboard.css';

const Dashboard = () => {
  const [workplaces, setWorkplaces] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchWorkplaces();
  }, []);

  const fetchWorkplaces = async () => {
    try {
      const data = await getAllWorkplaces();
      setWorkplaces(data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching workplaces:', error);
      setLoading(false);
    }
  };

  const getRiskLevelText = (riskLevel: string) => {
    switch (riskLevel) {
      case 'low': return 'Low Risk';
      case 'dangerous': return 'Dangerous';
      case 'veryDangerous': return 'Very Dangerous';
      default: return riskLevel;
    }
  };

  return (
    <div className="dashboard-container">
      <div className="window">
        <div className="window-header">
          <span>Dashboard - Workplace Registration and Monitoring</span>
        </div>
        <div className="window-content">
          <button className="btn" onClick={() => window.location.hash = '/workplaces'}>
            Add New Workplace
          </button>
          
          {loading ? (
            <p>Loading workplaces...</p>
          ) : (
            <table className="table">
              <thead>
                <tr>
                  <th>Date</th>
                  <th>Workplace Name</th>
                  <th>Address</th>
                  <th>SSK Registration No</th>
                  <th>Tax Office</th>
                  <th>Tax Number</th>
                  <th>Price</th>
                  <th>Risk Level</th>
                  <th>Assigned Expert</th>
                  <th>Assigned Doctor</th>
                  <th>Source</th>
                </tr>
              </thead>
              <tbody>
                {workplaces.map((workplace) => (
                  <tr key={workplace.id}>
                    <td>{new Date(workplace.createdAt).toLocaleDateString()}</td>
                    <td>{workplace.name}</td>
                    <td>{workplace.address}</td>
                    <td>{workplace.sskRegistrationNo}</td>
                    <td>{workplace.taxOffice}</td>
                    <td>{workplace.taxNumber}</td>
                    <td>{workplace.price}</td>
                    <td>{getRiskLevelText(workplace.riskLevel)}</td>
                    <td>{workplace.assignedExpertId || 'Not assigned'}</td>
                    <td>{workplace.assignedDoctorId || 'Not assigned'}</td>
                    <td>{workplace.source || 'N/A'}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
