import React, { useState, useEffect } from 'react';
import { createExpert, getAllExperts, deleteExpert, updateExpert } from '../services/api';
import './ExpertCardPage.css';
import { useNavigate } from 'react-router-dom';

const ExpertCardPage = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    expertiseClass: 'A',
    assignedMinutes: 11900
  });
  const [experts, setExperts] = useState<any[]>([]);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  
  // Load saved positions from localStorage or use defaults
  const getSavedCardState = (cardType: 'new' | 'list') => {
    try {
      const savedState = localStorage.getItem(`expertCard_${cardType}_state`);
      if (savedState) {
        const parsedState = JSON.parse(savedState);
        // Validate that the parsed state has the required properties
        if (typeof parsedState === 'object' && parsedState !== null &&
            'x' in parsedState && 'y' in parsedState && 
            'width' in parsedState && 'height' in parsedState) {
          return {
            ...parsedState,
            isDragging: false,
            isResizing: false,
            resizeDirection: '',
            dragStartX: 0,
            dragStartY: 0,
            resizeStartX: 0,
            resizeStartY: 0
          };
        }
      }
    } catch (e) {
      // If there's an error parsing, we'll use the default state
      console.warn(`Failed to parse saved state for ${cardType} card, using defaults`);
    }
    
    // Default positions
    if (cardType === 'new') {
      return {
        x: 50,
        y: 150,
        width: 400,
        height: 500,
        isDragging: false,
        isResizing: false,
        resizeDirection: '',
        dragStartX: 0,
        dragStartY: 0,
        resizeStartX: 0,
        resizeStartY: 0,
        originalX: 50,
        originalY: 150,
        originalWidth: 400,
        originalHeight: 500
      };
    } else {
      return {
        x: 500,
        y: 150,
        width: 500,
        height: 500,
        isDragging: false,
        isResizing: false,
        resizeDirection: '',
        dragStartX: 0,
        dragStartY: 0,
        resizeStartX: 0,
        resizeStartY: 0,
        originalX: 500,
        originalY: 150,
        originalWidth: 500,
        originalHeight: 500
      };
    }
  };

  // Get saved modal state or use defaults
  const getSavedModalState = () => {
    try {
      const savedState = localStorage.getItem('expertModal_state');
      if (savedState) {
        const parsedState = JSON.parse(savedState);
        // Validate that the parsed state has the required properties
        if (typeof parsedState === 'object' && parsedState !== null &&
            'width' in parsedState && 'height' in parsedState) {
          return {
            ...parsedState,
            isResizing: false,
            resizeDirection: '',
            resizeStartX: 0,
            resizeStartY: 0,
            originalWidth: parsedState.width,
            originalHeight: parsedState.height
          };
        }
      }
    } catch (e) {
      console.warn('Failed to parse saved modal state, using defaults');
    }
    
    // Default modal size
    return {
      width: 400,
      height: 500,
      isResizing: false,
      resizeDirection: '',
      resizeStartX: 0,
      resizeStartY: 0,
      originalWidth: 400,
      originalHeight: 500
    };
  };

  // State for "Yeni Uzman Kayıt" card
  const [newCard, setNewCard] = useState(getSavedCardState('new'));
  
  // State for "Kayıtlı Uzmanlar" card
  const [listCard, setListCard] = useState(getSavedCardState('list'));
  
  // State for Expert Modal
  const [modalState, setModalState] = useState(getSavedModalState);
  
  const navigate = useNavigate();

  useEffect(() => {
    fetchExperts();
  }, []);

  const fetchExperts = async () => {
    try {
      const data = await getAllExperts();
      setExperts(data);
    } catch (err) {
      console.error('Uzmanlar getirilirken hata oluştu:', err);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'assignedMinutes' ? parseInt(value) : value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (formData.assignedMinutes > 11900) {
      setError('Atanan dakika 11.900\'ü geçemez');
      return;
    }
    
    try {
      await createExpert(formData);
      setSuccess('Uzman başarıyla kaydedildi!');
      setFormData({
        firstName: '',
        lastName: '',
        phone: '',
        expertiseClass: 'A',
        assignedMinutes: 11900
      });
      // Refresh the experts list
      fetchExperts();
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccess('');
      }, 3000);
    } catch (err: any) {
      console.error('Create expert error:', err);
      const errorMessage = err.message || 'Uzman kaydedilirken hata oluştu';
      setError(errorMessage);
      
      // Clear error message after 5 seconds
      setTimeout(() => {
        setError('');
      }, 5000);
    }
  };

  const handleDelete = async (id: number) => {
    // Show confirmation dialog before deleting
    const confirmed = window.confirm('Bu uzman kaydını silmek istediğinize emin misiniz?');
    if (!confirmed) {
      return; // User cancelled the deletion
    }
    
    try {
      await deleteExpert(id);
      setSuccess('Uzman başarıyla silindi!');
      // Refresh the experts list
      fetchExperts();
      
      // Close modal if it was open for this expert
      if (selectedExpert && selectedExpert.id === id) {
        setIsModalOpen(false);
        setSelectedExpert(null);
      }
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccess('');
      }, 3000);
    } catch (err: any) {
      console.error('Delete error:', err);
      const errorMessage = err.message || 'Uzman silinirken hata oluştu';
      setError(errorMessage);
      
      // Clear error message after 5 seconds
      setTimeout(() => {
        setError('');
      }, 5000);
    }
  };

  const handleClose = () => {
    // Redirect to dashboard
    navigate('/dashboard');
  };

  // Function to open modal with expert details
  const openExpertDetails = (expert: any) => {
    setSelectedExpert(expert);
    setIsEditMode(false);
    setIsModalOpen(true);
  };

  // Function to open modal in edit mode
  const openEditExpert = (expert: any) => {
    setSelectedExpert(expert);
    setEditFormData({
      firstName: expert.firstName || '',
      lastName: expert.lastName || '',
      phone: expert.phone || '',
      expertiseClass: expert.expertiseClass || 'A',
      assignedMinutes: expert.assignedMinutes || 11900
    });
    setIsEditMode(true);
    setIsModalOpen(true);
  };

  // Function to close modal
  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedExpert(null);
    setIsEditMode(false);
  };

  // Function to handle edit action from modal
  const handleEditExpert = async (expert: any) => {
    // This function was a placeholder, but we'll keep it for consistency
    // The actual update is handled by handleUpdateExpert
    console.log('Edit expert called for:', expert);
  };

  // State for modal dialog
  const [selectedExpert, setSelectedExpert] = useState<any>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isEditMode, setIsEditMode] = useState(false);
  
  // State for edit form data
  const [editFormData, setEditFormData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    expertiseClass: 'A',
    assignedMinutes: 11900
  });
  
  const handleEditFormDataChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setEditFormData(prev => ({
      ...prev,
      [name]: name === 'assignedMinutes' ? parseInt(value) : value
    }));
  };
  
  const handleUpdateExpert = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedExpert) return;
    
    try {
      await updateExpert(selectedExpert.id, editFormData);
      setSuccess('Uzman başarıyla güncellendi!');
      closeModal();
      fetchExperts(); // Refresh the experts list
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccess('');
      }, 3000);
    } catch (err: any) {
      console.error('Update error:', err);
      const errorMessage = err.message || 'Uzman güncellenirken hata oluştu';
      setError(errorMessage);
      
      // Clear error message after 5 seconds
      setTimeout(() => {
        setError('');
      }, 5000);
    }
  };

  const handleEdit = async (id: number) => {
    // Find the expert by id
    const expert = experts.find(exp => exp.id === id);
    if (expert) {
      openEditExpert(expert);
    } else {
      setError('Uzman bulunamadı');
    }
  };

  // Mouse event handlers
  const handleMouseDown = (e: React.MouseEvent, cardType: 'new' | 'list' | 'modal', action: 'drag' | 'resize', direction: string = '') => {
    e.preventDefault();
    e.stopPropagation();
    
    if (action === 'drag') {
      if (cardType === 'new') {
        setNewCard((prev: typeof newCard) => ({
          ...prev,
          isDragging: true,
          dragStartX: e.clientX - prev.x,
          dragStartY: e.clientY - prev.y
        }));
      } else if (cardType === 'list') {
        setListCard((prev: typeof listCard) => ({
          ...prev,
          isDragging: true,
          dragStartX: e.clientX - prev.x,
          dragStartY: e.clientY - prev.y
        }));
      }
    } else if (action === 'resize') {
      if (cardType === 'new') {
        setNewCard((prev: typeof newCard) => ({
          ...prev,
          isResizing: true,
          resizeDirection: direction,
          resizeStartX: e.clientX,
          resizeStartY: e.clientY,
          originalX: prev.x,
          originalY: prev.y,
          originalWidth: prev.width,
          originalHeight: prev.height
        }));
      } else if (cardType === 'list') {
        setListCard((prev: typeof listCard) => ({
          ...prev,
          isResizing: true,
          resizeDirection: direction,
          resizeStartX: e.clientX,
          resizeStartY: e.clientY,
          originalX: prev.x,
          originalY: prev.y,
          originalWidth: prev.width,
          originalHeight: prev.height
        }));
      } else if (cardType === 'modal') {
        // Get the current modal position relative to the viewport
        const modalElement = e.currentTarget.closest('.modal-content');
        if (modalElement) {
          const rect = modalElement.getBoundingClientRect();
          const currentX = rect.left;
          const currentY = rect.top;
          
          setModalState((prev: typeof modalState) => ({
            ...prev,
            isResizing: true,
            resizeDirection: direction,
            resizeStartX: e.clientX,
            resizeStartY: e.clientY,
            originalX: currentX,
            originalY: currentY,
            originalWidth: prev.width,
            originalHeight: prev.height
          }));
        }
      }
    }
  };

  const handleMouseMove = (e: MouseEvent) => {
    // Handle dragging for "Yeni Uzman Kayıt" card
    if (newCard.isDragging) {
      const newX = e.clientX - newCard.dragStartX;
      const newY = e.clientY - newCard.dragStartY;
      setNewCard((prev: typeof newCard) => ({
        ...prev,
        x: newX,
        y: newY,
        originalX: newX,
        originalY: newY
      }));
    }
    
    // Handle dragging for "Kayıtlı Uzmanlar" card
    if (listCard.isDragging) {
      const newX = e.clientX - listCard.dragStartX;
      const newY = e.clientY - listCard.dragStartY;
      setListCard((prev: typeof listCard) => ({
        ...prev,
        x: newX,
        y: newY,
        originalX: newX,
        originalY: newY
      }));
    }
    
    // Handle resizing for "Yeni Uzman Kayıt" card
    if (newCard.isResizing) {
      const deltaX = e.clientX - newCard.resizeStartX;
      const deltaY = e.clientY - newCard.resizeStartY;
      
      let newX = newCard.originalX;
      let newY = newCard.originalY;
      let newWidth = newCard.originalWidth;
      let newHeight = newCard.originalHeight;
      
      // Handle different resize directions
      if (newCard.resizeDirection.includes('right')) {
        newWidth = Math.max(300, newCard.originalWidth + deltaX);
      }
      if (newCard.resizeDirection.includes('left')) {
        newWidth = Math.max(300, newCard.originalWidth - deltaX);
        newX = newCard.originalX + deltaX;
      }
      if (newCard.resizeDirection.includes('bottom')) {
        newHeight = Math.max(200, newCard.originalHeight + deltaY);
      }
      if (newCard.resizeDirection.includes('top')) {
        newHeight = Math.max(200, newCard.originalHeight - deltaY);
        newY = newCard.originalY + deltaY;
      }
      
      setNewCard((prev: typeof newCard) => ({
        ...prev,
        x: newX,
        y: newY,
        width: newWidth,
        height: newHeight,
        originalX: newX,
        originalY: newY,
        originalWidth: newWidth,
        originalHeight: newHeight
      }));
    }
    
    // Handle resizing for "Kayıtlı Uzmanlar" card
    if (listCard.isResizing) {
      const deltaX = e.clientX - listCard.resizeStartX;
      const deltaY = e.clientY - listCard.resizeStartY;
      
      let newX = listCard.originalX;
      let newY = listCard.originalY;
      let newWidth = listCard.originalWidth;
      let newHeight = listCard.originalHeight;
      
      // Handle different resize directions
      if (listCard.resizeDirection.includes('right')) {
        newWidth = Math.max(300, listCard.originalWidth + deltaX);
      }
      if (listCard.resizeDirection.includes('left')) {
        newWidth = Math.max(300, listCard.originalWidth - deltaX);
        newX = listCard.originalX + deltaX;
      }
      if (listCard.resizeDirection.includes('bottom')) {
        newHeight = Math.max(200, listCard.originalHeight + deltaY);
      }
      if (listCard.resizeDirection.includes('top')) {
        newHeight = Math.max(200, listCard.originalHeight - deltaY);
        newY = listCard.originalY + deltaY;
      }
      
      setListCard((prev: typeof listCard) => ({
        ...prev,
        x: newX,
        y: newY,
        width: newWidth,
        height: newHeight,
        originalX: newX,
        originalY: newY,
        originalWidth: newWidth,
        originalHeight: newHeight
      }));
    }
    
    // Handle resizing for Expert Modal
    if (modalState.isResizing) {
      const deltaX = e.clientX - modalState.resizeStartX;
      const deltaY = e.clientY - modalState.resizeStartY;
      
      let newWidth = modalState.originalWidth;
      let newHeight = modalState.originalHeight;
      
      // Handle different resize directions
      if (modalState.resizeDirection.includes('right')) {
        newWidth = Math.max(300, modalState.originalWidth + deltaX);
      }
      if (modalState.resizeDirection.includes('left')) {
        newWidth = Math.max(300, modalState.originalWidth - deltaX);
      }
      if (modalState.resizeDirection.includes('bottom')) {
        newHeight = Math.max(200, modalState.originalHeight + deltaY);
      }
      if (modalState.resizeDirection.includes('top')) {
        newHeight = Math.max(200, modalState.originalHeight - deltaY);
      }
      
      setModalState((prev: typeof modalState) => ({
        ...prev,
        width: newWidth,
        height: newHeight,
        originalWidth: newWidth,
        originalHeight: newHeight
      }));
    }
  };

  const handleMouseUp = () => {
    setNewCard((prev: typeof newCard) => {
      const newState = { ...prev, isDragging: false, isResizing: false, resizeDirection: '' };
      // Save the new card state to localStorage
      localStorage.setItem('expertCard_new_state', JSON.stringify(newState));
      return newState;
    });
    
    setListCard((prev: typeof listCard) => {
      const newState = { ...prev, isDragging: false, isResizing: false, resizeDirection: '' };
      // Save the list card state to localStorage
      localStorage.setItem('expertCard_list_state', JSON.stringify(newState));
      return newState;
    });
    
    setModalState((prev: typeof modalState) => {
      const newState = { ...prev, isResizing: false, resizeDirection: '' };
      // Save the modal state to localStorage
      localStorage.setItem('expertModal_state', JSON.stringify(newState));
      return newState;
    });
  };

  useEffect(() => {
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
    
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [newCard.isDragging, listCard.isDragging, newCard.isResizing, listCard.isResizing, modalState.isResizing]);

  return (
    <div className="expert-card-page">
      {/* "Yeni Uzman Kayıt" card (draggable and resizable) */}
      <div 
        className="draggable-card"
        style={{
          position: 'absolute',
          left: newCard.x,
          top: newCard.y,
          width: newCard.width,
          height: newCard.height,
          zIndex: 1000,
          cursor: newCard.isDragging ? 'grabbing' : 'default'
        }}
      >
        <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
          <div 
            className="window-header"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'drag')}
            style={{ cursor: 'move', userSelect: 'none' }}
          >
            <span>Yeni Uzman Kayıt</span>
            <div className="window-controls">
              <button className="window-button" onClick={handleClose}>×</button>
            </div>
          </div>
          <div className="window-content" style={{ flex: 1, overflow: 'auto' }}>
            {success && <div className="alert alert-info">{success}</div>}
            {error && <div className="alert alert-warning">{error}</div>}
            
            <form onSubmit={handleSubmit} className="registration-form">
              <div className="form-row">
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Ad:</label>
                    <input
                      type="text"
                      name="firstName"
                      className="form-input"
                      value={formData.firstName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Soyad:</label>
                    <input
                      type="text"
                      name="lastName"
                      className="form-input"
                      value={formData.lastName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
              </div>
              
              <div className="form-group">
                <label className="form-label">Telefon:</label>
                <input
                  type="text"
                  name="phone"
                  className="form-input"
                  value={formData.phone}
                  onChange={handleChange}
                  required
                />
              </div>
              
              <div className="form-row">
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Uzmanlık Sınıfı:</label>
                    <select
                      name="expertiseClass"
                      className="form-select"
                      value={formData.expertiseClass}
                      onChange={handleChange}
                    >
                      <option value="A">A</option>
                      <option value="B">B</option>
                      <option value="C">C</option>
                    </select>
                  </div>
                </div>
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Atanan Dakika:</label>
                    <input
                      type="number"
                      name="assignedMinutes"
                      className="form-input"
                      value={formData.assignedMinutes}
                      onChange={handleChange}
                      min="0"
                      max="11900"
                      required
                    />
                  </div>
                </div>
              </div>
              
              <button type="submit" className="btn btn-primary">Uzmanı Kaydet</button>
            </form>
          </div>
          {/* Resize handles */}
          <div 
            className="resize-handle resize-nw"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'top-left')}
          />
          <div 
            className="resize-handle resize-ne"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'top-right')}
          />
          <div 
            className="resize-handle resize-sw"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'bottom-left')}
          />
          <div 
            className="resize-handle resize-se"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'bottom-right')}
          />
          <div 
            className="resize-handle resize-n"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'top')}
          />
          <div 
            className="resize-handle resize-s"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'bottom')}
          />
          <div 
            className="resize-handle resize-w"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'left')}
          />
          <div 
            className="resize-handle resize-e"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'right')}
          />
        </div>
      </div>
      
      {/* "Kayıtlı Uzmanlar" card (draggable and resizable) */}
      <div 
        className="draggable-card"
        style={{
          position: 'absolute',
          left: listCard.x,
          top: listCard.y,
          width: listCard.width,
          height: listCard.height,
          zIndex: 1000,
          cursor: listCard.isDragging ? 'grabbing' : 'default'
        }}
      >
        <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
          <div 
            className="window-header"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'drag')}
            style={{ cursor: 'move', userSelect: 'none' }}
          >
            <span>Kayıtlı Uzmanlar</span>
          </div>
          <div className="window-content" style={{ flex: 1, overflow: 'auto' }}>
            {experts.length === 0 ? (
              <p>Kayıtlı uzman bulunmamaktadır.</p>
            ) : (
              <div className="expert-list">
                <div className="expert-header">
                  <div className="expert-header-item">Ad Soyad</div>
                  <div className="expert-header-item">İletişim</div>
                  <div className="expert-header-item class-shifted">Sınıfı</div>
                  <div className="expert-header-item minutes-vertical">Tanımlı Dakika</div>
                  <div className="expert-header-item assigned-vertical">Atanan Dakika</div>
                  <div className="expert-header-item minutes-shifted">Kalan</div>
                  <div className="expert-header-item">İşlemler</div>
                </div>
                {experts.map((expert) => (
                  <div key={expert.id} className="expert-row" onClick={() => openExpertDetails(expert)}>
                    <div className="expert-info">
                      <div className="expert-name">{expert.firstName} {expert.lastName}</div>
                      <div className="expert-contact">{expert.phone}</div>
                      <div className="expert-class">{expert.expertiseClass}</div>
                      <div className="expert-minutes">{expert.assignedMinutes}</div>
                      <div className="expert-assigned">0</div>
                      <div className="expert-remaining">{expert.assignedMinutes}</div>
                    </div>
                    <div className="expert-actions">
                      <button className="btn-icon edit" onClick={(e) => { e.stopPropagation(); openEditExpert(expert); }}>✏️</button>
                      <button className="btn-icon delete" onClick={(e) => { e.stopPropagation(); handleDelete(expert.id); }}>🗑️</button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
          {/* Resize handles */}
          <div 
            className="resize-handle resize-nw"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'top-left')}
          />
          <div 
            className="resize-handle resize-ne"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'top-right')}
          />
          <div 
            className="resize-handle resize-sw"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'bottom-left')}
          />
          <div 
            className="resize-handle resize-se"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'bottom-right')}
          />
          <div 
            className="resize-handle resize-n"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'top')}
          />
          <div 
            className="resize-handle resize-s"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'bottom')}
          />
          <div 
            className="resize-handle resize-w"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'left')}
          />
          <div 
            className="resize-handle resize-e"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'right')}
          />
        </div>
      </div>
      
      {/* Expert Details Modal */}
      {isModalOpen && selectedExpert && (
        <div className="modal-overlay" onClick={(e) => {
          // Only close modal if clicking on the overlay (not the content)
          if (e.target === e.currentTarget) {
            closeModal();
          }
        }}>
          <div 
            className="modal-content" 
            onClick={(e) => e.stopPropagation()}
            style={{
              width: modalState.width,
              height: modalState.height,
              maxWidth: '90%',
              maxHeight: '90%',
            }}
          >
            <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
              <div 
                className="window-header"
                onMouseDown={(e) => handleMouseDown(e, 'modal', 'drag')}
                style={{ cursor: 'move', userSelect: 'none' }}
              >
                <span>{isEditMode ? 'Uzmanı Düzenle' : 'Uzman Detayları'}</span>
                <div className="window-controls">
                  <button className="window-button" onClick={closeModal} aria-label="Kapat">×</button>
                </div>
              </div>
              <div className="window-content">
                {isEditMode ? (
                  // Edit form
                  <form onSubmit={handleUpdateExpert} className="edit-form">
                    <div className="form-group">
                      <label className="form-label">Ad:</label>
                      <input
                        type="text"
                        name="firstName"
                        className="form-input"
                        value={editFormData.firstName}
                        onChange={handleEditFormDataChange}
                        required
                      />
                    </div>
                    
                    <div className="form-group">
                      <label className="form-label">Soyad:</label>
                      <input
                        type="text"
                        name="lastName"
                        className="form-input"
                        value={editFormData.lastName}
                        onChange={handleEditFormDataChange}
                        required
                      />
                    </div>
                    
                    <div className="form-group">
                      <label className="form-label">Telefon:</label>
                      <input
                        type="text"
                        name="phone"
                        className="form-input"
                        value={editFormData.phone}
                        onChange={handleEditFormDataChange}
                        required
                      />
                    </div>
                    
                    <div className="form-row">
                      <div className="form-col">
                        <div className="form-group">
                          <label className="form-label">Uzmanlık Sınıfı:</label>
                          <select
                            name="expertiseClass"
                            className="form-select"
                            value={editFormData.expertiseClass}
                            onChange={handleEditFormDataChange}
                          >
                            <option value="A">A</option>
                            <option value="B">B</option>
                            <option value="C">C</option>
                          </select>
                        </div>
                      </div>
                      <div className="form-col">
                        <div className="form-group">
                          <label className="form-label">Atanan Dakika:</label>
                          <input
                            type="number"
                            name="assignedMinutes"
                            className="form-input"
                            value={editFormData.assignedMinutes}
                            onChange={handleEditFormDataChange}
                            min="0"
                            max="11900"
                            required
                          />
                        </div>
                      </div>
                    </div>
                    
                    <div className="modal-actions">
                      <button type="submit" className="btn btn-primary">Güncelle</button>
                      <button type="button" className="btn" onClick={closeModal}>İptal</button>
                    </div>
                  </form>
                ) : (
                  // View mode
                  <>
                    <div className="expert-detail-info">
                      <div className="expert-detail-row">
                        <strong>Ad:</strong> {selectedExpert.firstName}
                      </div>
                      <div className="expert-detail-row">
                        <strong>Soyad:</strong> {selectedExpert.lastName}
                      </div>
                      <div className="expert-detail-row">
                        <strong>Telefon:</strong> {selectedExpert.phone}
                      </div>
                      <div className="expert-detail-row">
                        <strong>Uzmanlık Sınıfı:</strong> {selectedExpert.expertiseClass}
                      </div>
                      <div className="expert-detail-row">
                        <strong>Toplam Dakika:</strong> {selectedExpert.assignedMinutes}
                      </div>
                      <div className="expert-detail-row">
                        <strong>Atanan Dakika:</strong> 0
                      </div>
                      <div className="expert-detail-row">
                        <strong>Kalan Dakika:</strong> {selectedExpert.assignedMinutes}
                      </div>
                      
                      {/* Workplace assignment information */}
                      <div className="expert-detail-row">
                        <strong>Atandığı İş Yeri:</strong> {selectedExpert.assignedWorkplace ? selectedExpert.assignedWorkplace : 'Atanmamış'}
                      </div>
                    </div>
                    
                    <div className="modal-actions">
                      <button className="btn btn-primary" onClick={() => openEditExpert(selectedExpert)}>Düzenle</button>
                      <button className="btn btn-danger" onClick={() => {
                        const confirmed = window.confirm('Bu uzman kaydını silmek istediğinize emin misiniz?');
                        if (confirmed) {
                          handleDelete(selectedExpert.id);
                        }
                      }}>Sil</button>
                      <button className="btn" onClick={closeModal}>Kapat</button>
                    </div>
                  </>
                )}
              </div>
            </div>
            {/* Resize handles for modal */}
            <div 
              className="resize-handle resize-nw"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'top-left')}
            />
            <div 
              className="resize-handle resize-ne"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'top-right')}
            />
            <div 
              className="resize-handle resize-sw"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'bottom-left')}
            />
            <div 
              className="resize-handle resize-se"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'bottom-right')}
            />
            <div 
              className="resize-handle resize-n"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'top')}
            />
            <div 
              className="resize-handle resize-s"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'bottom')}
            />
            <div 
              className="resize-handle resize-w"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'left')}
            />
            <div 
              className="resize-handle resize-e"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'right')}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default ExpertCardPage;
