import React, { useState, useEffect } from 'react';
import './Dashboard.css';
import { useNavigate } from 'react-router-dom';
import { createWorkplace, getAllWorkplaces } from '../services/api';

const Dashboard = () => {
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    sskRegistrationNo: '',
    taxOffice: '',
    taxNumber: '',
    price: '',
    riskLevel: 'low',
    employeeCount: 0,
    assignedExpertId: '',
    assignedDoctorId: '',
    assignedDspId: '',
    source: ''
  });
  
  const [isChecked, setIsChecked] = useState(false);
  const [approvalStatus, setApprovalStatus] = useState('atama'); // atama, bekliyor, onaylandi
  const [workplaces, setWorkplaces] = useState<any[]>([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchWorkplaces();
  }, []);

  const fetchWorkplaces = async () => {
    try {
      const data = await getAllWorkplaces();
      setWorkplaces(data);
    } catch (err) {
      console.error('Error fetching workplaces:', err);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const formatSSKNumber = (value: string) => {
    // Remove all non-digit characters
    const digits = value.replace(/\D/g, '');
    
    // Apply formatting: x xxxx xxxx xxxxx xxx xx xx xxx xx (1+4+4+5+3+2+2+3+2 = 26 digits)
    let formatted = '';
    for (let i = 0; i < digits.length && i < 26; i++) {
      if (i === 1 || i === 5 || i === 9 || i === 14 || i === 17 || i === 19 || i === 21 || i === 24) {
        formatted += ' ';
      }
      formatted += digits[i];
    }
    
    return formatted;
  };

  const handleSSkRegistrationNoChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    // Allow only numeric input and apply formatting
    const value = e.target.value;
    const formattedValue = formatSSKNumber(value);
    setFormData(prev => ({
      ...prev,
      sskRegistrationNo: formattedValue
    }));
  };

  const handleCheckboxChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setIsChecked(e.target.checked);
  };

  const handleApprovalStatusChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const value = e.target.value;
    setApprovalStatus(value);
    
    // If approved, save the workplace data
    if (value === 'onaylandi') {
      saveWorkplace();
    }
  };

  const saveWorkplace = async () => {
    try {
      const dataToSend = {
        ...formData,
        price: parseFloat(formData.price),
        employeeCount: parseInt(formData.employeeCount.toString()),
        assignedExpertId: formData.assignedExpertId ? parseInt(formData.assignedExpertId) : null,
        assignedDoctorId: formData.assignedDoctorId ? parseInt(formData.assignedDoctorId) : null,
        assignedDspId: formData.assignedDspId ? parseInt(formData.assignedDspId) : null
      };
      
      await createWorkplace(dataToSend);
      console.log('Workplace saved successfully');
      
      // Refresh the workplaces list
      fetchWorkplaces();
      
      // Reset form
      setFormData({
        name: '',
        address: '',
        sskRegistrationNo: '',
        taxOffice: '',
        taxNumber: '',
        price: '',
        riskLevel: 'low',
        employeeCount: 0,
        assignedExpertId: '',
        assignedDoctorId: '',
        assignedDspId: '',
        source: ''
      });
      setApprovalStatus('atama');
    } catch (err) {
      console.error('Error saving workplace:', err);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // Form submission logic will be implemented here
    console.log('Form submitted:', formData);
  };

  const handleClose = () => {
    // Redirect to login page
    navigate('/');
  };

  const getRiskLevelText = (riskLevel: string) => {
    switch (riskLevel) {
      case 'low': return 'Az Tehlikeli';
      case 'dangerous': return 'Tehlikeli';
      case 'veryDangerous': return 'Çok Tehlikeli';
      default: return riskLevel;
    }
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h2 className="dashboard-title">Yeni İş Yeri Kaydı</h2>
      </div>
      
      {/* New compact single-line form */}
      <div className="compact-form-container">
        <div className="compact-form-row">
          <div className="compact-form-group">
            <label className="compact-form-label">İş Yeri Adı:</label>
            <input
              type="text"
              name="name"
              className="compact-form-input"
              value={formData.name}
              onChange={handleChange}
              placeholder="İş yeri adı"
            />
          </div>
          
          <div className="compact-form-group">
            <label className="compact-form-label">Uzman:</label>
            <input
              type="text"
              name="assignedExpertId"
              className="compact-form-input"
              value={formData.assignedExpertId}
              onChange={handleChange}
              placeholder="Uzman"
            />
          </div>
          
          <div className="compact-form-group">
            <label className="compact-form-label">Hekim:</label>
            <input
              type="text"
              name="assignedDoctorId"
              className="compact-form-input"
              value={formData.assignedDoctorId}
              onChange={handleChange}
              placeholder="Hekim"
            />
          </div>
          
          <div className="compact-form-group">
            <label className="compact-form-label">DSP:</label>
            <input
              type="text"
              name="assignedDspId"
              className="compact-form-input"
              value={formData.assignedDspId}
              onChange={handleChange}
              placeholder="DSP"
            />
          </div>
          
          <div className="compact-form-group">
            <label className="compact-form-label">Onay Durumu:</label>
            <select
              className="compact-form-select"
              value={approvalStatus}
              onChange={handleApprovalStatusChange}
            >
              <option value="atama">Atama yapılacak</option>
              <option value="bekliyor">Onay bekliyor</option>
              <option value="onaylandi">Onaylandı</option>
            </select>
          </div>
        </div>
      </div>
      
      {/* Registered Workplaces List */}
      <div className="workplaces-list-container">
        <h3 className="workplaces-list-title">Kayıtlı İş Yerleri</h3>
        <div className="workplaces-table-container">
          <table className="workplaces-table">
            <thead>
              <tr>
                <th>İş Yeri Adı</th>
                <th>SGK Kayıt No</th>
                <th>Fiyat</th>
                <th>Tehlike Sınıfı</th>
              </tr>
            </thead>
            <tbody>
              {workplaces.map((workplace) => (
                <tr key={workplace.id}>
                  <td>{workplace.name}</td>
                  <td>{workplace.sskRegistrationNo}</td>
                  <td>{workplace.price}</td>
                  <td>{getRiskLevelText(workplace.riskLevel)}</td>
                </tr>
              ))}
              {workplaces.length === 0 && (
                <tr>
                  <td colSpan={4} style={{ textAlign: 'center' }}>
                    Henüz kayıtlı iş yeri bulunmamaktadır.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
      
      {/* Existing form */}
      <div className="form-container">
        <form onSubmit={handleSubmit} className="workplace-form">
          <div className="form-row">
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">İş Yeri Adı:</label>
                <input
                  type="text"
                  name="name"
                  className="form-input small-input"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">SGK Kayıt No:</label>
                <input
                  type="text"
                  name="sskRegistrationNo"
                  className="form-input ssk-input"
                  value={formData.sskRegistrationNo}
                  onChange={handleSSkRegistrationNoChange}
                  placeholder="x xxxx xxxx xxxxx xxx xx xx xxx xx"
                  maxLength={30} // 26 digits + 4 spaces
                  required
                />
              </div>
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Adres:</label>
                <textarea
                  name="address"
                  className="form-textarea smaller-textarea"
                  value={formData.address}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Fiyat:</label>
                <input
                  type="number"
                  name="price"
                  className="form-input small-input"
                  value={formData.price}
                  onChange={handleChange}
                  step="0.01"
                  required
                />
              </div>
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Vergi Dairesi:</label>
                <input
                  type="text"
                  name="taxOffice"
                  className="form-input small-input"
                  value={formData.taxOffice}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Vergi No:</label>
                <input
                  type="text"
                  name="taxNumber"
                  className="form-input small-input"
                  value={formData.taxNumber}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Tehlike Sınıfı:</label>
                <select
                  name="riskLevel"
                  className="form-select small-select"
                  value={formData.riskLevel}
                  onChange={handleChange}
                >
                  <option value="low">Az Tehlikeli</option>
                  <option value="dangerous">Tehlikeli</option>
                  <option value="veryDangerous">Çok Tehlikeli</option>
                </select>
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Çalışan Sayısı:</label>
                <input
                  type="number"
                  name="employeeCount"
                  className="form-input small-input"
                  value={formData.employeeCount}
                  onChange={handleChange}
                  min="0"
                  required
                />
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Kaynak:</label>
                <input
                  type="text"
                  name="source"
                  className="form-input small-input"
                  value={formData.source}
                  onChange={handleChange}
                />
              </div>
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Atanan Uzman:</label>
                <input
                  type="text"
                  name="assignedExpertId"
                  className="form-input small-input"
                  value={formData.assignedExpertId}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Atanan Hekim:</label>
                <input
                  type="text"
                  name="assignedDoctorId"
                  className="form-input small-input"
                  value={formData.assignedDoctorId}
                  onChange={handleChange}
                />
              </div>
            </div>
          </div>
          
          <div className={`form-row ${isChecked ? 'completed' : ''}`}>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Atanan DSP:</label>
                <input
                  type="text"
                  name="assignedDspId"
                  className="form-input small-input"
                  value={formData.assignedDspId}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="form-col checkbox-col">
              <div className="form-group">
                <label className="form-label">Tamamlandı:</label>
                <input
                  type="checkbox"
                  name="completed"
                  className="form-checkbox"
                  checked={isChecked}
                  onChange={handleCheckboxChange}
                />
              </div>
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-col form-actions">
              <button type="button" className="btn btn-secondary" onClick={handleClose}>
                İptal
              </button>
              <button type="submit" className="btn btn-primary">
                Kaydet
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Dashboard;
