import React, { useState, useEffect } from 'react';
import { createDsp, getAllDsps, deleteDsp } from '../services/api';
import './DspCardPage.css';
import { useNavigate } from 'react-router-dom';

// Define interface for form data
interface FormData {
  firstName: string;
  lastName: string;
  phone: string;
  assignedMinutes: number;
}

// Define interface for DSP data
interface DspData extends FormData {
  id: number;
}

const DspCardPage = () => {
  const [formData, setFormData] = useState<FormData>({
    firstName: '',
    lastName: '',
    phone: '',
    assignedMinutes: 11900
  });
  const [dsps, setDsps] = useState<DspData[]>([]);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  
  const navigate = useNavigate();

  // Function to handle clicking on a DSP row (for future use)
  const handleDspClick = (dsp: DspData) => {
    // This can be implemented later if needed for showing DSP details
    console.log('DSP clicked:', dsp);
  };

  useEffect(() => {
    fetchDsps();
  }, []);

  const fetchDsps = async () => {
    try {
      const data = await getAllDsps();
      setDsps(data);
    } catch (err: any) {
      console.error('DSP\'ler getirilirken hata oluştu:', err);
      setError(err.message || 'DSP\'ler getirilirken hata oluştu');
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData((prev: FormData) => ({
      ...prev,
      [name]: name === 'assignedMinutes' ? parseInt(value) : value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (formData.assignedMinutes > 11900) {
      setError('Atanan dakika 11.900\'ü geçemez');
      clearNotifications();
      return;
    }
    
    try {
      await createDsp(formData);
      setSuccess('DSP başarıyla kaydedildi!');
      setFormData({
        firstName: '',
        lastName: '',
        phone: '',
        assignedMinutes: 11900
      });
      // Refresh the DSPs list
      fetchDsps();
      clearNotifications();
    } catch (err: any) {
      console.error('Create DSP error:', err);
      const errorMessage = err.message || 'DSP kaydedilirken hata oluştu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  // Clear notifications after timeout
  const clearNotifications = () => {
    setTimeout(() => {
      setSuccess('');
      setError('');
    }, 3000);
  };

  const handleClose = () => {
    // Redirect to dashboard
    navigate('/dashboard');
  };

  const handleDelete = async (id: number) => {
    // Show confirmation dialog before deleting
    const confirmed = window.confirm('Bu DSP kaydını silmek istediğinize emin misiniz?');
    if (!confirmed) {
      return; // User cancelled the deletion
    }
    
    try {
      await deleteDsp(id);
      setSuccess('DSP başarıyla silindi!');
      // Refresh the DSPs list
      fetchDsps();
      clearNotifications();
    } catch (err: any) {
      console.error('Delete error:', err);
      const errorMessage = err.message || 'DSP silinirken hata oluştu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  return (
    <div className="dsp-card-page">
      {/* Popup notifications */}
      {success && (
        <div className="popup-notification alert alert-info">
          {success}
        </div>
      )}
      {error && (
        <div className="popup-notification alert alert-warning">
          {error}
        </div>
      )}
      
      {/* "Yeni DSP Kayıt" card (fixed position and size) */}
      <div 
        className="draggable-card"
        style={{
          position: 'absolute',
          left: 0,
          top: -90,
          width: 400,
          height: 320,
          zIndex: 100
        }}
      >
        <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
          <div 
            className="window-header"
            style={{ userSelect: 'none' }}
          >
            <span>Yeni DSP Kayıt</span>
            <div className="window-controls">
              <button className="window-button" onClick={handleClose}>×</button>
            </div>
          </div>
          <div className="window-content" style={{ flex: 1, overflow: 'visible' }}>
            <form onSubmit={handleSubmit} className="registration-form">
              <div className="form-row">
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Ad:</label>
                    <input
                      type="text"
                      name="firstName"
                      className="form-input"
                      value={formData.firstName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Soyad:</label>
                    <input
                      type="text"
                      name="lastName"
                      className="form-input"
                      value={formData.lastName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
              </div>
              
              <div className="form-group">
                <label className="form-label">Telefon:</label>
                <input
                  type="text"
                  name="phone"
                  className="form-input"
                  value={formData.phone}
                  onChange={handleChange}
                  required
                />
              </div>
              
              <div className="form-group">
                <label className="form-label">Atanan Dakika:</label>
                <input
                  type="number"
                  name="assignedMinutes"
                  className="form-input"
                  value={formData.assignedMinutes}
                  onChange={handleChange}
                  min="0"
                  max="11900"
                  required
                />
              </div>
              
              <button type="submit" className="btn btn-primary">DSP Kaydet</button>
            </form>
          </div>
        </div>
      </div>
      
      {/* Display registered DSPs in individual rows positioned on the right side of the page */}
      <div className="registered-dsps-container" style={{ 
        position: 'absolute',
        right: '350px',
        top: '100px',
        width: '300px'
      }}>
        {dsps.map((dsp) => (
          <div
            key={dsp.id}
            onClick={() => handleDspClick(dsp)}
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              padding: '15px',
              margin: '10px 0',
              borderRadius: '8px',
              cursor: 'pointer',
              transition: 'all 0.3s ease',
              position: 'relative',
              top: '0px',
              left: '0px',
              width: '100%',
              // Using blue color scheme for all DSPs (similar to B class experts)
              backgroundColor: '#bbdefb' // Blue background
            }}
            onMouseEnter={(e) => {
              // Enhanced hover effect with more vibrant blue
              e.currentTarget.style.backgroundColor = '#90caf9'; // More vibrant blue on hover
              e.currentTarget.style.boxShadow = '0 4px 8px rgba(33, 150, 243, 0.3)';
              e.currentTarget.style.transform = 'translateY(-2px)';
            }}
            onMouseLeave={(e) => {
              // Reset to original blue color when not hovering
              e.currentTarget.style.backgroundColor = '#bbdefb';
              e.currentTarget.style.boxShadow = 'none';
              e.currentTarget.style.transform = 'translateY(0)';
            }}
          >
            <div className="dsp-info-display" style={{ fontSize: '14px' }}>
              <div className="dsp-name-display">{dsp.firstName} {dsp.lastName}</div>
              <div className="dsp-phone-display">{dsp.phone}</div>
            </div>
            <div 
              className="dsp-class-display"
              style={{
                // Blue badge for all DSPs
                backgroundColor: '#1976d2',
                color: 'white',
                fontSize: '12px',
                fontWeight: 'bold',
                padding: '2px 8px',
                borderRadius: '12px'
              }}
            >
              D
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DspCardPage;
