import React, { useState, useEffect } from 'react';
import { createDoctor, getAllDoctors, deleteDoctor, updateDoctor } from '../services/api';
import './DoctorCardPage.css';
import { useNavigate } from 'react-router-dom';

// Define interface for card state
interface CardState {
  x: number;
  y: number;
  width: number;
  height: number;
  isDragging: boolean;
  isResizing: boolean;
  resizeDirection: string;
  dragStartX: number;
  dragStartY: number;
  resizeStartX: number;
  resizeStartY: number;
  originalX: number;
  originalY: number;
  originalWidth: number;
  originalHeight: number;
}

// Define interface for modal state
interface ModalState {
  width: number;
  height: number;
  originalX?: number;
  originalY?: number;
  isResizing: boolean;
  resizeDirection: string;
  resizeStartX: number;
  resizeStartY: number;
  originalWidth: number;
  originalHeight: number;
  isDragging?: boolean;
  dragStartX?: number;
  dragStartY?: number;
}

// Define interface for form data
interface FormData {
  firstName: string;
  lastName: string;
  phone: string;
  assignedMinutes: number;
}

// Define interface for doctor data
interface Doctor {
  id: number;
  firstName: string;
  lastName: string;
  phone: string;
  assignedMinutes: number;
  assignedWorkplace?: string;
}

const DoctorCardPage = () => {
  const [formData, setFormData] = useState<FormData>({
    firstName: '',
    lastName: '',
    phone: '',
    assignedMinutes: 11900
  });
  const [doctors, setDoctors] = useState<Doctor[]>([]);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  
  // Validation functions
  const isValidName = (name: string) => {
    return /^[a-zA-ZğüşıöçĞÜŞİÖÇ\s]+$/.test(name);
  };

  const isValidPhone = (phone: string) => {
    return /^5\d{2}\s\d{3}\s\d{2}\s\d{2}$/.test(phone);
  };

  // Format phone number as user types
  const formatPhoneNumber = (value: string) => {
    // Remove all non-digit characters
    const digits = value.replace(/\D/g, '');
    
    // Limit to 10 digits
    const limitedDigits = digits.substring(0, 10);
    
    // Format as 5xx xxx xx xx
    let formatted = '';
    for (let i = 0; i < limitedDigits.length; i++) {
      if (i === 3 || i === 6 || i === 8) {
        formatted += ' ';
      }
      formatted += limitedDigits[i];
    }
    
    return formatted;
  };

  // Load saved positions from localStorage or use defaults
  const getSavedCardState = (cardType: 'new' | 'list'): CardState => {
    try {
      const savedState = localStorage.getItem(`doctorCard_${cardType}_state`);
      if (savedState) {
        const parsedState = JSON.parse(savedState);
        // Validate that the parsed state has the required properties
        if (typeof parsedState === 'object' && parsedState !== null &&
            'x' in parsedState && 'y' in parsedState && 
            'width' in parsedState && 'height' in parsedState) {
          return {
            ...parsedState,
            isDragging: false,
            isResizing: false,
            resizeDirection: '',
            dragStartX: 0,
            dragStartY: 0,
            resizeStartX: 0,
            resizeStartY: 0
          };
        }
      }
    } catch (e) {
      // If there's an error parsing, we'll use the default state
      console.warn(`Failed to parse saved state for ${cardType} card, using defaults`);
    }
    
    // Default positions - positioned at top: 0px as per user preference
    const width = 400;
    const height = 500;
    const x = (window.innerWidth - width) / 2;
    const y = 0; // Position at top as per user preference
    return {
      x,
      y,
      width,
      height,
      isDragging: false,
      isResizing: false,
      resizeDirection: '',
      dragStartX: 0,
      dragStartY: 0,
      resizeStartX: 0,
      resizeStartY: 0,
      originalX: x,
      originalY: y,
      originalWidth: width,
      originalHeight: height
    };
  };

  // Get saved modal state or use defaults
  const getSavedModalState = (): ModalState => {
    try {
      const savedState = localStorage.getItem('doctorModal_state');
      if (savedState) {
        const parsedState = JSON.parse(savedState);
        // Validate that the parsed state has the required properties
        if (typeof parsedState === 'object' && parsedState !== null &&
            'width' in parsedState && 'height' in parsedState) {
          return {
            ...parsedState,
            isResizing: false,
            resizeDirection: '',
            resizeStartX: 0,
            resizeStartY: 0,
            originalWidth: parsedState.width,
            originalHeight: parsedState.height
          };
        }
      }
    } catch (e) {
      console.warn('Failed to parse saved modal state, using defaults');
    }
    
    // Default modal size with centered position
    const defaultWidth = 400;
    const defaultHeight = 500;
    return {
      width: defaultWidth,
      height: defaultHeight,
      originalX: (window.innerWidth - defaultWidth) / 2,
      originalY: (window.innerHeight - defaultHeight) / 2,
      isResizing: false,
      resizeDirection: '',
      resizeStartX: 0,
      resizeStartY: 0,
      originalWidth: defaultWidth,
      originalHeight: defaultHeight
    };
  };

  // State for "Yeni Hekim Kayıt" card
  const [newCard, setNewCard] = useState<CardState>(getSavedCardState('new'));
  
  // State for Doctor Modal
  const [modalState, setModalState] = useState<ModalState>(getSavedModalState());
  
  const navigate = useNavigate();

  useEffect(() => {
    fetchDoctors();
  }, []);

  // Handle window resize to keep cards centered
  useEffect(() => {
    const handleResize = () => {
      // Update newCard position if it hasn't been moved by the user
      const savedNewCardState = localStorage.getItem('doctorCard_new_state');
      if (!savedNewCardState) {
        setNewCard((prev: CardState) => ({
          ...prev,
          x: (window.innerWidth - prev.width) / 2,
          y: 0, // Keep at top as per user preference
          originalX: (window.innerWidth - prev.width) / 2,
          originalY: 0 // Keep at top as per user preference
        }));
      }
      
      // List card has been removed, so no need to handle its positioning
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const fetchDoctors = async () => {
    try {
      const data = await getAllDoctors();
      setDoctors(data);
    } catch (err) {
      console.error('Hekimler getirilirken hata oluştu:', err);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    
    // Special handling for name fields - prevent numbers
    if (name === 'firstName' || name === 'lastName') {
      // Remove any numeric characters
      const cleanValue = value.replace(/\d/g, '');
      setFormData(prev => ({
        ...prev,
        [name]: cleanValue
      }));
    }
    // Special handling for phone number formatting
    else if (name === 'phone') {
      const formattedPhone = formatPhoneNumber(value);
      setFormData(prev => ({
        ...prev,
        [name]: formattedPhone
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: name === 'assignedMinutes' ? (value === '' ? 0 : parseInt(value) || 0) : value
      }));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    // Validation checks
    if (!isValidName(formData.firstName)) {
      setError('Ad sadece harf içermelidir');
      clearNotifications();
      return;
    }
    
    if (!isValidName(formData.lastName)) {
      setError('Soyad sadece harf içermelidir');
      clearNotifications();
      return;
    }
    
    if (!isValidPhone(formData.phone)) {
      setError('Telefon numarası 5xx xxx xx xx formatında olmalıdır');
      clearNotifications();
      return;
    }
    
    if (formData.assignedMinutes > 11900) {
      setError('Atanan dakika 11.900\'ü geçemez');
      clearNotifications();
      return;
    }
    
    try {
      await createDoctor(formData);
      setSuccess('Hekim başarıyla kaydedildi!');
      setFormData({
        firstName: '',
        lastName: '',
        phone: '',
        assignedMinutes: 11900
      });
      // Refresh the doctors list
      fetchDoctors();
      clearNotifications();
    } catch (err: any) {
      console.error('Create doctor error:', err);
      const errorMessage = err.message || 'Hekim kaydedilirken hata oluştu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  // Clear notifications after timeout
  const clearNotifications = () => {
    setTimeout(() => {
      setSuccess('');
      setError('');
    }, 3000);
  };

  const handleDelete = async (id: number) => {
    // Show confirmation dialog before deleting
    const confirmed = window.confirm('Bu hekim kaydını silmek istediğinize emin misiniz?');
    if (!confirmed) {
      return; // User cancelled the deletion
    }
    
    try {
      await deleteDoctor(id);
      setSuccess('Hekim başarıyla silindi!');
      // Refresh the doctors list
      fetchDoctors();
      
      // Close modal if it was open for this doctor
      if (selectedDoctor && selectedDoctor.id === id) {
        setIsModalOpen(false);
        setSelectedDoctor(null);
      }
      
      clearNotifications();
    } catch (err: any) {
      console.error('Delete error:', err);
      const errorMessage = err.message || 'Hekim silinirken hata oluştu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  const handleClose = () => {
    // Redirect to dashboard
    navigate('/dashboard');
  };

  // Function to open modal with doctor details
  const openDoctorDetails = (doctor: Doctor) => {
    setSelectedDoctor(doctor);
    setIsEditMode(false);
    setIsModalOpen(true);
    // Reset modal position to center when opening
    const defaultWidth = 400;
    const defaultHeight = 500;
    setModalState((prev: ModalState) => ({
      ...prev,
      width: defaultWidth,
      height: defaultHeight,
      originalX: (window.innerWidth - defaultWidth) / 2,
      originalY: (window.innerHeight - defaultHeight) / 2,
      originalWidth: defaultWidth,
      originalHeight: defaultHeight
    }));
  };

  // Function to open modal in edit mode
  const openEditDoctor = (doctor: Doctor) => {
    setSelectedDoctor(doctor);
    setEditFormData({
      firstName: doctor.firstName || '',
      lastName: doctor.lastName || '',
      phone: doctor.phone || '',
      assignedMinutes: doctor.assignedMinutes || 11900
    });
    setIsEditMode(true);
    setIsModalOpen(true);
    // Reset modal position to center when opening
    const defaultWidth = 400;
    const defaultHeight = 500;
    setModalState((prev: ModalState) => ({
      ...prev,
      width: defaultWidth,
      height: defaultHeight,
      originalX: (window.innerWidth - defaultWidth) / 2,
      originalY: (window.innerHeight - defaultHeight) / 2,
      originalWidth: defaultWidth,
      originalHeight: defaultHeight
    }));
  };

  // Function to close modal
  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedDoctor(null);
    setIsEditMode(false);
  };

  // State for modal dialog
  const [selectedDoctor, setSelectedDoctor] = useState<Doctor | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isEditMode, setIsEditMode] = useState(false);
  
  // State for edit form data
  const [editFormData, setEditFormData] = useState<FormData>({
    firstName: '',
    lastName: '',
    phone: '',
    assignedMinutes: 11900
  });
  
  const handleEditFormDataChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    
    // Special handling for name fields - prevent numbers
    if (name === 'firstName' || name === 'lastName') {
      // Remove any numeric characters
      const cleanValue = value.replace(/\d/g, '');
      setEditFormData((prev) => ({
        ...prev,
        [name]: cleanValue
      }));
    }
    // Special handling for phone number formatting
    else if (name === 'phone') {
      const formattedPhone = formatPhoneNumber(value);
      setEditFormData((prev) => ({
        ...prev,
        [name]: formattedPhone
      }));
    } else {
      setEditFormData((prev) => ({
        ...prev,
        [name]: name === 'assignedMinutes' ? (value === '' ? 0 : parseInt(value) || 0) : value
      }));
    }
  };
  
  const handleUpdateDoctor = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedDoctor) return;
    
    // Validation checks for edit form
    if (!isValidName(editFormData.firstName)) {
      setError('Ad sadece harf içermelidir');
      clearNotifications();
      return;
    }
    
    if (!isValidName(editFormData.lastName)) {
      setError('Soyad sadece harf içermelidir');
      clearNotifications();
      return;
    }
    
    if (!isValidPhone(editFormData.phone)) {
      setError('Telefon numarası 5xx xxx xx xx formatında olmalıdır');
      clearNotifications();
      return;
    }
    
    if (editFormData.assignedMinutes > 11900) {
      setError('Atanan dakika 11.900\'ü geçemez');
      clearNotifications();
      return;
    }
    
    try {
      await updateDoctor(selectedDoctor.id, editFormData);
      setSuccess('Hekim başarıyla güncellendi!');
      closeModal();
      fetchDoctors(); // Refresh the doctors list
      
      clearNotifications();
    } catch (err: any) {
      console.error('Update error:', err);
      const errorMessage = err.message || 'Hekim güncellenirken hata oluştu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  // Mouse event handlers
  const handleMouseDown = (e: React.MouseEvent, cardType: 'new' | 'list' | 'modal', action: 'drag' | 'resize', direction: string = '') => {
    e.preventDefault();
    e.stopPropagation();
    
    // Only support drag action for new card and modal since list card has been removed
    if (action === 'drag') {
      if (cardType === 'new') {
        setNewCard((prev: CardState) => ({
          ...prev,
          isDragging: true,
          dragStartX: e.clientX - prev.x,
          dragStartY: e.clientY - prev.y
        }));
      } 
      // Removed list card dragging functionality since it's been removed
      else if (cardType === 'modal') {
        // For modal dragging, we need to get the current position
        setModalState((prev: ModalState) => ({
          ...prev,
          isDragging: true,
          dragStartX: e.clientX - (prev.originalX || 0),
          dragStartY: e.clientY - (prev.originalY || 0)
        }));
      }
    }
    // Resize action is intentionally ignored since cards are fixed size
    
    // Ensure global event listeners are attached
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
  };

  const handleMouseMove = (e: MouseEvent) => {
    // Handle dragging for "Yeni Hekim Kayıt" card
    if (newCard.isDragging) {
      const newX = e.clientX - newCard.dragStartX;
      const newY = e.clientY - newCard.dragStartY;
      setNewCard((prev: CardState) => ({
        ...prev,
        x: newX,
        y: newY,
        originalX: newX,
        originalY: newY
      }));
    }
    
    // Removed list card dragging functionality since it's been removed
    
    // Handle dragging for Doctor Modal
    if (modalState.isDragging) {
      const newX = e.clientX - (modalState.dragStartX || 0);
      const newY = e.clientY - (modalState.dragStartY || 0);
      setModalState((prev: ModalState) => ({
        ...prev,
        originalX: newX,
        originalY: newY
      }));
    }
    
    // Resize functionality is removed since cards are fixed size
  };

  const handleMouseUp = () => {
    setNewCard((prev: CardState) => {
      const newState = { ...prev, isDragging: false };
      // Save the new card state to localStorage
      localStorage.setItem('doctorCard_new_state', JSON.stringify(newState));
      return newState;
    });
    
    // Removed list card state update since it's been removed
    
    setModalState((prev: ModalState) => {
      const newState = { ...prev, isDragging: false };
      // Save the modal state to localStorage
      localStorage.setItem('doctorModal_state', JSON.stringify(newState));
      return newState;
    });
    
    // Remove global event listeners
    document.removeEventListener('mousemove', handleMouseMove);
    document.removeEventListener('mouseup', handleMouseUp);
  };

  // Add useEffect to manage global event listeners
  useEffect(() => {
    // Only add global event listeners when actually needed
    if (newCard.isDragging || modalState.isDragging) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    }
    
    // Cleanup function to remove event listeners when component unmounts or dependencies change
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [newCard.isDragging, modalState.isDragging]);

  // Function to handle clicking on a doctor row
  const handleDoctorClick = (doctor: Doctor) => {
    openDoctorDetails(doctor);
  };

  return (
    <div className="doctor-card-page">
      {/* Popup notifications - only show at the top level */}
      {success && (
        <div className="popup-notification alert alert-info">
          {success}
        </div>
      )}
      {error && (
        <div className="popup-notification alert alert-warning">
          {error}
        </div>
      )}
      
      {/* Yeni Hekim Kayıt Card */}
      <div 
        className="doctor-card new-doctor-card"
        style={{
          left: `${newCard.x}px`,
          top: `${newCard.y}px`,
          width: `${newCard.width}px`,
          height: `${newCard.height}px`
        }}
      >
        <div className="card-header">
          <span>Yeni Hekim Kayıt</span>
          <div className="card-controls">
            <button className="card-button" onClick={handleClose}>×</button>
          </div>
        </div>
        <div className="card-content">
          {/* Removed duplicate notifications from here */}
          
          <form onSubmit={handleSubmit} className="doctor-form">
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Ad:</label>
                  <input
                    type="text"
                    name="firstName"
                    className="form-input"
                    value={formData.firstName}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Soyad:</label>
                  <input
                    type="text"
                    name="lastName"
                    className="form-input"
                    value={formData.lastName}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
            </div>
            
            <div className="form-group">
              <label className="form-label">Telefon:</label>
              <input
                type="text"
                name="phone"
                className="form-input"
                value={formData.phone}
                onChange={handleChange}
                placeholder="5xx xxx xx xx"
                required
              />
            </div>
            
            <div className="form-group">
              <label className="form-label">Atanan Dakika:</label>
              <input
                type="number"
                name="assignedMinutes"
                className="form-input"
                value={formData.assignedMinutes}
                onChange={handleChange}
                min="0"
                max="11900"
                required
              />
            </div>
            
            <div className="form-info">
              <p><strong>Hekim Dakika Hesaplama:</strong></p>
              <ul>
                <li>Düşük risk: 5 dakika/çalışan</li>
                <li>Tehlikeli: 10 dakika/çalışan</li>
                <li>Çok tehlikeli: 15 dakika/çalışan</li>
              </ul>
            </div>
            
            <button type="submit" className="btn btn-primary">Hekim Kaydet</button>
          </form>
        </div>
      </div>
      
      {/* Display registered doctors in individual rows positioned on the right side of the page */}
      <div className="registered-doctors-container" style={{ 
        position: 'absolute',
        right: '450px',
        top: '0px',
        width: '300px',
      
      }}>
        {doctors.map((doctor) => (
          <div
            key={doctor.id}
            onClick={() => handleDoctorClick(doctor)}
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              padding: '7px',
              margin: '10px 0',
              borderRadius: '8px',
              cursor: 'pointer',
              transition: 'all 0.3s ease',
              position: 'relative',
              top: '0px',
              left: '0px',
              width: '100%',
              // Using blue color scheme for all doctors (similar to B class experts)
              backgroundColor: '#bbdefb' // Blue background
            }}
            onMouseEnter={(e) => {
              // Enhanced hover effect with more vibrant blue
              e.currentTarget.style.backgroundColor = '#90caf9'; // More vibrant blue on hover
              e.currentTarget.style.boxShadow = '0 4px 8px rgba(33, 150, 243, 0.3)';
              e.currentTarget.style.transform = 'translateY(-2px)';
            }}
            onMouseLeave={(e) => {
              // Reset to original blue color when not hovering
              e.currentTarget.style.backgroundColor = '#bbdefb';
              e.currentTarget.style.boxShadow = 'none';
              e.currentTarget.style.transform = 'translateY(0)';
            }}
          >
            <div className="doctor-info-display">
              <div className="doctor-name-display">{doctor.firstName} {doctor.lastName}</div>
              <div className="doctor-phone-display">{doctor.phone}</div>
            </div>
            <div 
              className="doctor-class-display"
              style={{
                // Blue badge for all doctors
                backgroundColor: '#1976d2',
                color: 'white'
              }}
            >
              H
            </div>
          </div>
        ))}
      </div>
      
      {/* Doctor Details Modal */}
      {isModalOpen && selectedDoctor && (
        <div className="modal-overlay" onClick={(e) => {
          // Only close modal if clicking on the overlay (not the content)
          if (e.target === e.currentTarget) {
            closeModal();
          }
        }}>
          <div 
            className="modal-content draggable-card" 
            onClick={(e) => e.stopPropagation()}
            style={{
              width: modalState.width,
              height: modalState.height,
              maxWidth: '90vw',
              maxHeight: '90vh',
              position: 'absolute',
              left: '50%',
              top: '50%',
              transform: 'translate(-50%, -50%)',
              margin: 0,
              zIndex: 2001
            }}
          >
            <div className="modal-header">
              <h2>{isEditMode ? 'Hekim Düzenle' : 'Hekim Detayları'}</h2>
              <button className="modal-close" onClick={closeModal}>×</button>
            </div>
            <div className="modal-body">
              {isEditMode ? (
                <form onSubmit={handleUpdateDoctor} className="doctor-form">
                  <div className="form-row">
                    <div className="form-col">
                      <div className="form-group">
                        <label className="form-label">Ad:</label>
                        <input
                          type="text"
                          name="firstName"
                          className="form-input"
                          value={editFormData.firstName}
                          onChange={handleEditFormDataChange}
                          required
                        />
                      </div>
                    </div>
                    <div className="form-col">
                      <div className="form-group">
                        <label className="form-label">Soyad:</label>
                        <input
                          type="text"
                          name="lastName"
                          className="form-input"
                          value={editFormData.lastName}
                          onChange={handleEditFormDataChange}
                          required
                        />
                      </div>
                    </div>
                  </div>
                  
                  <div className="form-group">
                    <label className="form-label">Telefon:</label>
                    <input
                      type="text"
                      name="phone"
                      className="form-input"
                      value={editFormData.phone}
                      onChange={handleEditFormDataChange}
                      placeholder="5xx xxx xx xx"
                      required
                    />
                  </div>
                  
                  <div className="form-group">
                    <label className="form-label">Atanan Dakika:</label>
                    <input
                      type="number"
                      name="assignedMinutes"
                      className="form-input"
                      value={editFormData.assignedMinutes}
                      onChange={handleEditFormDataChange}
                      min="0"
                      max="11900"
                      required
                    />
                  </div>
                  
                  <div className="form-info">
                    <p><strong>Hekim Dakika Hesaplama:</strong></p>
                    <ul>
                      <li>Düşük risk: 5 dakika/çalışan</li>
                      <li>Tehlikeli: 10 dakika/çalışan</li>
                      <li>Çok tehlikeli: 15 dakika/çalışan</li>
                    </ul>
                  </div>
                  
                  <div className="modal-actions">
                    <button type="button" className="btn btn-secondary" onClick={closeModal}>
                      İptal
                    </button>
                    <button type="submit" className="btn btn-primary">
                      Güncelle
                    </button>
                  </div>
                </form>
              ) : (
                <div className="doctor-details">
                  <div className="detail-row">
                    <span className="detail-label">Ad:</span>
                    <span className="detail-value">{selectedDoctor.firstName}</span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Soyad:</span>
                    <span className="detail-value">{selectedDoctor.lastName}</span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Telefon:</span>
                    <span className="detail-value">{selectedDoctor.phone}</span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Atanan Dakika:</span>
                    <span className="detail-value">{selectedDoctor.assignedMinutes}</span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Atanan İşyeri:</span>
                    <span className="detail-value">{selectedDoctor.assignedWorkplace || 'Atanmamış'}</span>
                  </div>
                  <div className="modal-actions">
                    <button 
                      className="btn btn-secondary" 
                      onClick={() => openEditDoctor(selectedDoctor)}
                    >
                      Düzenle
                    </button>
                    <button 
                      className="btn btn-danger" 
                      onClick={() => handleDelete(selectedDoctor.id)}
                    >
                      Sil
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default DoctorCardPage;
