import React, { useState, useEffect, useCallback } from 'react';
import { useLocation } from 'react-router-dom';
import './Dashboard.css';
import { 
  createWorkplace, 
  updateWorkplace, 
  getAvailableExperts, 
  getAvailableDoctors, 
  getAvailableDsps, 
  getAllWorkplaces, 
  deleteWorkplace, 
  getAllExperts, 
  getAllDoctors, 
  getAllDsps 
} from '../services/api';

interface Workplace {
  id: number;
  name: string;
  sskRegistrationNo: string;
  address: string;
  price: string;
  taxOffice: string;
  taxNumber: string;
  riskLevel: string;
  employeeCount: string;
  source: string;
  assignedExpertId: string;
  assignedDoctorId: string;
  assignedDspId: string;
  trackingExpertId: string;
  registrationDate: string;
  approvalStatus: string;
  Expert?: {
    id: number;
    firstName: string;
    lastName: string;
  } | null;
  Doctor?: {
    id: number;
    firstName: string;
    lastName: string;
  } | null;
  Dsp?: {
    id: number;
    firstName: string;
    lastName: string;
  } | null;
}

const Dashboard: React.FC = () => {
  const location = useLocation();
  
  // State
  const [formData, setFormData] = useState({
    name: '',
    sskRegistrationNo: '',
    address: '',
    price: '',
    taxOffice: '',
    taxNumber: '',
    riskLevel: 'low',
    employeeCount: '',
    source: '',
    assignedExpertId: '',
    assignedDoctorId: '',
    assignedDspId: '',
    trackingExpertId: '',
    registrationDate: '',
  });
  
  const [workplaces, setWorkplaces] = useState<Workplace[]>([]);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [isModalOpen, setIsModalOpen] = useState<boolean>(false);
  const [modalFormData, setModalFormData] = useState<Partial<Workplace>>({});
  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState<boolean>(false);
  
  const [showNoExpertPopup, setShowNoExpertPopup] = useState<boolean>(false);
  const [showNoDoctorPopup, setShowNoDoctorPopup] = useState<boolean>(false);
  const [showNoDspPopup, setShowNoDspPopup] = useState<boolean>(false);
  const [showDspNotAllowedPopup, setShowDspNotAllowedPopup] = useState<boolean>(false);
  
  const [allExperts, setAllExperts] = useState<any[]>([]);
  const [allDoctors, setAllDoctors] = useState<any[]>([]);
  const [allDsps, setAllDsps] = useState<any[]>([]);
  
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  const [duplicateWarning, setDuplicateWarning] = useState<{show: boolean, workplace: Workplace | null}>({show: false, workplace: null});
  
  const [sortConfig, setSortConfig] = useState<{ key: string; direction: 'asc' | 'desc' } | null>(null);
  const [isPriceFocused, setIsPriceFocused] = useState(false);
  const [isModalPriceFocused, setIsModalPriceFocused] = useState(false);

  const workplacesPerPage = 10;
  const totalPages = Math.ceil(workplaces.length / workplacesPerPage);

  // Helper functions
  const isValidTurkishText = (text: string): boolean => {
    return /^([a-zA-ZğüşıöçĞÜŞİÖÇ\s]*)$/.test(text);
  };

  const isNumericOnly = (text: string): boolean => {
    return /^([0-9]*)$/.test(text);
  };

  const formatPriceForDisplay = (price: string): string => {
    if (!price) return '';
    if (!/^[0-9]*$/.test(price)) return price;
    return parseInt(price).toLocaleString('tr-TR');
  };

  const parsePrice = (formattedPrice: string): string => {
    if (!formattedPrice) return '';
    const cleaned = formattedPrice.replace(/\./g, '');
    return /^[0-9]*$/.test(cleaned) ? cleaned : '';
  };

  const formatDate = (dateString: string) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString('tr-TR');
  };

  const isDspAssignmentAllowed = (riskLevel: string, employeeCount: number): boolean => {
    if (riskLevel === 'low' || riskLevel === 'dangerous') {
      return false;
    }
    if (riskLevel === 'veryDangerous' && employeeCount < 10) {
      return false;
    }
    return true;
  };

  const getExpertName = (expertId: string) => {
    if (!expertId) return 'Atanmamış';
    const expert = allExperts.find((exp: any) => exp.id === parseInt(expertId));
    return expert ? `${expert.firstName} ${expert.lastName}` : 'Bilinmeyen Uzman';
  };

  const getTrackingExpertName = (trackingExpertId: string) => {
    if (!trackingExpertId) return 'Atanmamış';
    const expert = allExperts.find((exp: any) => exp.id === parseInt(trackingExpertId));
    return expert ? `${expert.firstName} ${expert.lastName}` : 'Atanmamış';
  };

  const getDoctorName = (doctorId: string) => {
    if (!doctorId) return 'Atanmamış';
    const doctor = allDoctors.find((doc: any) => doc.id === parseInt(doctorId));
    return doctor ? `${doctor.firstName} ${doctor.lastName}` : 'Bilinmeyen Hekim';
  };

  const getDspName = (dspId: string) => {
    if (!dspId) return 'Atanmamış';
    const dsp = allDsps.find((d: any) => d.id === parseInt(dspId));
    return dsp ? `${dsp.firstName} ${dsp.lastName}` : 'Bilinmeyen DSP';
  };

  const calculateRequiredMinutes = (riskLevel: string, employeeCount: number): number => {
    const multipliers: Record<string, number> = { 'low': 10, 'dangerous': 20, 'veryDangerous': 40 };
    return employeeCount * (multipliers[riskLevel] || 0);
  };

  const calculateRequiredDoctorMinutes = (riskLevel: string, employeeCount: number): number => {
    const multipliers: Record<string, number> = { 'low': 5, 'dangerous': 10, 'veryDangerous': 15 };
    return employeeCount * (multipliers[riskLevel] || 0);
  };

  const getFilteredExperts = (riskLevel: string, employeeCount: number) => {
    const requiredMinutes = calculateRequiredMinutes(riskLevel, employeeCount);
    return allExperts.filter(expert => {
      if (expert.assignedMinutes < requiredMinutes) return false;
      switch (expert.expertiseClass) {
        case 'A': return true;
        case 'B': return riskLevel === 'low' || riskLevel === 'dangerous';
        case 'C': return riskLevel === 'low';
        default: return false;
      }
    });
  };

  const getFilteredDoctors = (riskLevel: string, employeeCount: number) => {
    const requiredMinutes = calculateRequiredDoctorMinutes(riskLevel, employeeCount);
    return allDoctors.filter(doctor => doctor.assignedMinutes >= requiredMinutes);
  };

  const getFilteredDsps = (riskLevel: string, employeeCount: number) => {
    if (!isDspAssignmentAllowed(riskLevel, employeeCount)) {
      return [];
    }
    const isDspRequired = riskLevel === 'veryDangerous' && employeeCount >= 10;
    if (!isDspRequired) {
      return allDsps.filter(dsp => dsp.assignedMinutes > 0);
    }
    const requiredMinutes = employeeCount * 5;
    return allDsps.filter(dsp => dsp.assignedMinutes >= requiredMinutes);
  };

  const getSortedWorkplaces = () => {
    if (!sortConfig) {
      return [...workplaces].sort((a, b) => {
        return new Date(b.registrationDate || '').getTime() - new Date(a.registrationDate || '').getTime();
      });
    }

    return [...workplaces].sort((a, b) => {
      const key = sortConfig.key as keyof Workplace;
      let aValue: any, bValue: any;

      switch (key) {
        case 'name':
        case 'source':
          aValue = a[key] || '';
          bValue = b[key] || '';
          break;
        case 'registrationDate':
          aValue = new Date(a[key] || '').getTime();
          bValue = new Date(b[key] || '').getTime();
          break;
        case 'riskLevel':
          const riskOrder: Record<string, number> = { 'low': 1, 'dangerous': 2, 'veryDangerous': 3 };
          aValue = riskOrder[a[key]] || 0;
          bValue = riskOrder[b[key]] || 0;
          break;
        case 'approvalStatus':
          const approvalOrder: Record<string, number> = { 'atama': 1, 'bekliyor': 2, 'onaylandi': 3 };
          aValue = approvalOrder[a[key]] || 0;
          bValue = approvalOrder[b[key]] || 0;
          break;
        case 'assignedExpertId':
          aValue = getExpertName(a[key]);
          bValue = getExpertName(b[key]);
          break;
        case 'trackingExpertId':
          aValue = getTrackingExpertName(a[key]);
          bValue = getTrackingExpertName(b[key]);
          break;
        case 'assignedDoctorId':
          aValue = getDoctorName(a[key]);
          bValue = getDoctorName(b[key]);
          break;
        case 'assignedDspId':
          aValue = getDspName(a[key]);
          bValue = getDspName(b[key]);
          break;
        default:
          aValue = a[key];
          bValue = b[key];
      }

      if (aValue < bValue) return sortConfig.direction === 'asc' ? -1 : 1;
      if (aValue > bValue) return sortConfig.direction === 'asc' ? 1 : -1;
      return 0;
    });
  };

  // API calls
  const fetchAllData = useCallback(async () => {
    try {
      const [workplacesData, expertsData, doctorsData, dspsData] = await Promise.all([
        getAllWorkplaces(),
        getAllExperts(),
        getAllDoctors(),
        getAllDsps()
      ]);
      
      setWorkplaces(workplacesData);
      setAllExperts(expertsData);
      setAllDoctors(doctorsData);
      setAllDsps(dspsData);
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Veri alınırken hata oluştu');
    }
  }, []);

  useEffect(() => {
    fetchAllData();
  }, [fetchAllData]);

  useEffect(() => {
    fetchAllData();
  }, [location.pathname, fetchAllData]);

  useEffect(() => {
    const handleFocus = () => fetchAllData();
    window.addEventListener('focus', handleFocus);
    return () => window.removeEventListener('focus', handleFocus);
  }, [fetchAllData]);

  // Handlers
  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;

    if (name === 'assignedDspId' && value) {
      const employeeCount = parseInt(formData.employeeCount) || 0;
      if (!isDspAssignmentAllowed(formData.riskLevel, employeeCount)) {
        setShowDspNotAllowedPopup(true);
        return;
      }
    }

    if (name === 'taxOffice' && value && !isValidTurkishText(value)) return;
    if (name === 'taxNumber' && value && !isNumericOnly(value)) return;
    if (name === 'source' && value && !isValidTurkishText(value)) return;

    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handlePriceChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    if (value === '' || /^[0-9]*$/.test(value)) {
      setFormData(prev => ({ ...prev, price: value }));
    }
  };

  const handleModalPriceChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    if (value === '' || /^[0-9]*$/.test(value)) {
      setModalFormData(prev => ({ ...prev, price: value }));
    }
  };

  const handlePriceFocus = () => {
    setIsPriceFocused(true);
    setFormData(prev => ({ ...prev, price: parsePrice(prev.price) }));
  };

  const handlePriceBlur = () => {
    setIsPriceFocused(false);
    setFormData(prev => ({ ...prev, price: formatPriceForDisplay(prev.price) }));
  };

  const handleModalPriceFocus = () => {
    setIsModalPriceFocused(true);
    setModalFormData(prev => ({
      ...prev,
      price: typeof prev.price === 'string' ? parsePrice(prev.price) : String(prev.price || '')
    }));
  };

  const handleModalPriceBlur = () => {
    setIsModalPriceFocused(false);
    setModalFormData(prev => ({
      ...prev,
      price: typeof prev.price === 'string' ? formatPriceForDisplay(prev.price) : String(prev.price || '')
    }));
  };

  const handleSSkRegistrationNoChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    let value = e.target.value.replace(/\D/g, '');
    if (value.length > 21) value = value.substring(0, 21);
    
    let formattedValue = '';
    for (let i = 0; i < value.length; i++) {
      if ([1, 5, 7, 9, 14, 17, 19].includes(i)) {
        formattedValue += ' ' + value[i];
      } else {
        formattedValue += value[i];
      }
    }
    
    setFormData(prev => ({ ...prev, sskRegistrationNo: formattedValue }));
  };

  const handleClose = () => {
    setFormData({
      name: '', sskRegistrationNo: '', address: '', price: '', taxOffice: '',
      taxNumber: '', riskLevel: 'low', employeeCount: '', source: '',
      assignedExpertId: '', assignedDoctorId: '', assignedDspId: '', trackingExpertId: '', registrationDate: '',
    });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      const formattedSskNo = formData.sskRegistrationNo.replace(/\s/g, '');
      const existingWorkplace = workplaces.find(workplace => 
        workplace.sskRegistrationNo?.replace(/\s/g, '') === formattedSskNo
      );

      if (existingWorkplace) {
        setDuplicateWarning({ show: true, workplace: existingWorkplace });
        setTimeout(() => setDuplicateWarning({ show: false, workplace: null }), 5000);
        setTimeout(() => {
          const element = document.getElementById(`workplace-row-${existingWorkplace.id}`);
          if (element) {
            element.scrollIntoView({ behavior: 'smooth', block: 'center' });
          }
        }, 100);
        return;
      }

      const workplaceData = {
        ...formData,
        employeeCount: parseInt(formData.employeeCount) || 0,
        price: parseInt(parsePrice(formData.price)) || 0,
        approvalStatus: 'atama',
        assignedExpertId: formData.assignedExpertId || null,
        assignedDoctorId: formData.assignedDoctorId || null,
        assignedDspId: formData.assignedDspId || null,
        trackingExpertId: formData.trackingExpertId || null
      };

      const newWorkplace = await createWorkplace(workplaceData);
      setSuccess('İş yeri başarıyla kaydedildi!');
      handleClose();
      await fetchAllData();
      setCurrentPage(1);
      setTimeout(() => setSuccess(''), 3000);
    } catch (error: any) {
      setError(`İş yeri kaydedilirken hata oluştu: ${error.message || ''}`);
      setTimeout(() => setError(''), 3000);
    }
  };

  const handleRowClick = (workplace: Workplace) => {
    setModalFormData(workplace);
    setIsModalOpen(true);
    document.body.style.overflow = 'hidden';
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setModalFormData({});
    document.body.style.overflow = 'auto';
  };

  const handleModalChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;

    if (name === 'taxOffice' && value && !isValidTurkishText(value)) return;
    if (name === 'taxNumber' && value && !isNumericOnly(value)) return;
    if (name === 'source' && value && !isValidTurkishText(value)) return;

    if (name === 'sskRegistrationNo') {
      let formattedValue = value.replace(/\D/g, '');
      if (formattedValue.length > 21) formattedValue = formattedValue.substring(0, 21);
      let finalValue = '';
      for (let i = 0; i < formattedValue.length; i++) {
        if ([1, 5, 7, 9, 14, 17, 19].includes(i)) {
          finalValue += ' ' + formattedValue[i];
        } else {
          finalValue += formattedValue[i];
        }
      }
      setModalFormData(prev => ({ ...prev, [name]: finalValue }));
    } else {
      setModalFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleModalSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      if (modalFormData.id) {
        const workplaceData: Record<string, any> = {
          ...modalFormData,
          employeeCount: parseInt(modalFormData.employeeCount as string) || 0,
          price: parseInt(parsePrice(modalFormData.price as string)) || 0,
          assignedExpertId: modalFormData.assignedExpertId || null,
          assignedDoctorId: modalFormData.assignedDoctorId || null,
          assignedDspId: modalFormData.assignedDspId || null,
          trackingExpertId: modalFormData.trackingExpertId || null
        };

        // Remove any undefined values
        Object.keys(workplaceData).forEach(key => {
          if (workplaceData[key] === undefined) {
            delete workplaceData[key];
          }
        });

        await updateWorkplace(modalFormData.id, workplaceData);
        setSuccess('İş yeri başarıyla güncellendi!');
        closeModal();
        await fetchAllData();
        setTimeout(() => setSuccess(''), 3000);
      }
    } catch (error: any) {
      setError(`İş yeri güncellenirken hata oluştu: ${error.message || ''}`);
      setTimeout(() => setError(''), 3000);
    }
  };

  const handleDelete = async (id: number) => {
    try {
      await deleteWorkplace(id);
      setSuccess('İş yeri başarıyla silindi!');
      closeModal();
      await fetchAllData();
      setTimeout(() => setSuccess(''), 3000);
    } catch (error: any) {
      setError(`İş yeri silinirken hata oluştu: ${error.message || ''}`);
      setTimeout(() => setError(''), 3000);
    }
  };

  const handleApprovalStatusChange = async (id: number, status: string) => {
    try {
      setWorkplaces(prev => 
        prev.map(w => w.id === id ? { ...w, approvalStatus: status } : w)
      );
      const workplace = workplaces.find(w => w.id === id);
      if (workplace) {
        await updateWorkplace(id, { ...workplace, approvalStatus: status });
      }
    } catch (error) {
      await fetchAllData();
    }
  };

  const sortWorkplaces = (key: string) => {
    let direction: 'asc' | 'desc' = 'asc';
    if (sortConfig?.key === key && sortConfig.direction === 'asc') {
      direction = 'desc';
    }
    setSortConfig({ key, direction });
  };

  const handlePageChange = (pageNumber: number) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div className="dashboard-container">
      {/* Popup notifications */}
      {success && <div className="popup-notification alert alert-info">{success}</div>}
      {error && <div className="popup-notification alert alert-warning">{error}</div>}
      
      {/* Duplicate warning popup */}
      {duplicateWarning.show && duplicateWarning.workplace && (
        <div className="duplicate-warning-popup">
          <h3>Kayıt Zaten Mevcut</h3>
          <p>Bu kayıt daha önce girilmiş. Tekrar kayıt yapmak için ilgili satırı düzenleyebilirsiniz.</p>
        </div>
      )}

      {/* No Expert Popup */}
      {showNoExpertPopup && (
        <div className="popup-overlay" onClick={() => setShowNoExpertPopup(false)}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()}>
            <div className="popup-header">
              <h3>Uygun Uzman Bulunmuyor</h3>
              <button className="popup-close" onClick={() => setShowNoExpertPopup(false)}>×</button>
            </div>
            <div className="popup-body">
              <p>Atanacak uygun uzman bulunmamaktadır.</p>
            </div>
            <div className="popup-footer">
              <button className="btn btn-primary" onClick={() => setShowNoExpertPopup(false)}>Tamam</button>
            </div>
          </div>
        </div>
      )}
      
      {/* No Doctor Popup */}
      {showNoDoctorPopup && (
        <div className="popup-overlay" onClick={() => setShowNoDoctorPopup(false)}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()}>
            <div className="popup-header">
              <h3>Uygun Hekim Bulunmuyor</h3>
              <button className="popup-close" onClick={() => setShowNoDoctorPopup(false)}>×</button>
            </div>
            <div className="popup-body">
              <p>Atanacak uygun hekim bulunmamaktadır.</p>
            </div>
            <div className="popup-footer">
              <button className="btn btn-primary" onClick={() => setShowNoDoctorPopup(false)}>Tamam</button>
            </div>
          </div>
        </div>
      )}
      
      {/* No DSP Popup */}
      {showNoDspPopup && (
        <div className="popup-overlay" onClick={() => setShowNoDspPopup(false)}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()}>
            <div className="popup-header">
              <h3>Uygun DSP Bulunmuyor</h3>
              <button className="popup-close" onClick={() => setShowNoDspPopup(false)}>×</button>
            </div>
            <div className="popup-body">
              <p>Atanacak uygun DSP bulunmamaktadır.</p>
            </div>
            <div className="popup-footer">
              <button className="btn btn-primary" onClick={() => setShowNoDspPopup(false)}>Tamam</button>
            </div>
          </div>
        </div>
      )}
      
      {/* DSP Not Allowed Popup */}
      {showDspNotAllowedPopup && (
        <div className="popup-overlay" onClick={() => setShowDspNotAllowedPopup(false)}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()}>
            <div className="popup-header">
              <h3>DSP Atanamaz</h3>
              <button className="popup-close" onClick={() => setShowDspNotAllowedPopup(false)}>×</button>
            </div>
            <div className="popup-body">
              <p>Seçilen iş yerine DSP atanamaz.</p>
            </div>
            <div className="popup-footer">
              <button className="btn btn-primary" onClick={() => setShowDspNotAllowedPopup(false)}>Tamam</button>
            </div>
          </div>
        </div>
      )}
      
      {/* Delete Confirmation Popup */}
      {showDeleteConfirmation && (
        <div className="popup-overlay" onClick={() => setShowDeleteConfirmation(false)}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()}>
            <div className="popup-header">
              <h3>Kayıt Silme Onayı</h3>
              <button className="popup-close" onClick={() => setShowDeleteConfirmation(false)}>×</button>
            </div>
            <div className="popup-body">
              <p>Kayıdı gerçekten silmek istiyor musunuz?</p>
            </div>
            <div className="popup-footer">
              <button className="btn btn-secondary" onClick={() => setShowDeleteConfirmation(false)}>İptal</button>
              <button className="btn btn-danger" onClick={() => {
                if (modalFormData.id) {
                  handleDelete(modalFormData.id);
                }
                setShowDeleteConfirmation(false);
              }}>Sil</button>
            </div>
          </div>
        </div>
      )}

      <div className="dashboard-content">
        <div className="form-container">
          <form onSubmit={handleSubmit} className="workplace-form">
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">İş Yeri Adı:</label>
                  <input 
                    type="text" 
                    name="name" 
                    value={formData.name} 
                    onChange={handleChange} 
                    className="form-input"
                    required 
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">SGK İşyeri Sicil No:</label>
                  <input 
                    type="text" 
                    name="sskRegistrationNo" 
                    value={formData.sskRegistrationNo} 
                    onChange={handleSSkRegistrationNoChange} 
                    className="form-input"
                    required 
                  />
                </div>
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Adres:</label>
                  <textarea 
                    name="address" 
                    value={formData.address} 
                    onChange={handleChange} 
                    className="form-textarea"
                    required 
                  />
                </div>
              </div>
            </div>

            <div className="form-row price-employee-tracking">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Fiyat:</label>
                  <div className="price-input-container">
                    <input
                      type="text"
                      name="price"
                      value={isPriceFocused ? formData.price : formatPriceForDisplay(formData.price)}
                      onChange={handlePriceChange}
                      onFocus={handlePriceFocus}
                      onBlur={handlePriceBlur}
                      className="form-input price-input"
                      required
                    />
                  </div>
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Çalışan Sayısı:</label>
                  <input 
                    type="text" 
                    name="employeeCount" 
                    value={formData.employeeCount} 
                    onChange={handleChange} 
                    className="form-input"
                    required 
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Takip Edecek Uzman:</label>
                  <select 
                    name="trackingExpertId" 
                    value={formData.trackingExpertId} 
                    onChange={handleChange}
                    className="form-select"
                  >
                    <option value="">Seçiniz</option>
                    {allExperts.map(e => <option key={`tracking-${e.id}`} value={e.id}>{e.firstName} {e.lastName}</option>)}
                  </select>
                </div>
              </div>
            </div>

            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Vergi Dairesi:</label>
                  <input 
                    type="text" 
                    name="taxOffice" 
                    value={formData.taxOffice} 
                    onChange={handleChange} 
                    className="form-input"
                    required 
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Vergi Numarası:</label>
                  <input 
                    type="text" 
                    name="taxNumber" 
                    value={formData.taxNumber} 
                    onChange={handleChange} 
                    className="form-input"
                    required 
                  />
                </div>
              </div>
            </div>

            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Tehlike Sınıfı:</label>
                  <select 
                    name="riskLevel" 
                    value={formData.riskLevel} 
                    onChange={handleChange} 
                    className="form-select"
                    required
                  >
                    <option value="low">Az Tehlikeli</option>
                    <option value="dangerous">Tehlikeli</option>
                    <option value="veryDangerous">Çok Tehlikeli</option>
                  </select>
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Kaynak:</label>
                  <input 
                    type="text" 
                    name="source" 
                    value={formData.source} 
                    onChange={handleChange} 
                    className="form-input"
                    required 
                  />
                </div>
              </div>
            </div>

            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Atanan Uzman:</label>
                  <select 
                    name="assignedExpertId" 
                    value={formData.assignedExpertId} 
                    onChange={handleChange}
                    className="form-select"
                  >
                    <option value="">Seçiniz</option>
                    {getFilteredExperts(formData.riskLevel, parseInt(formData.employeeCount) || 0).map(e => 
                      <option key={e.id} value={e.id}>{e.firstName} {e.lastName} ({e.expertiseClass} - {e.assignedMinutes} dk)</option>
                    )}
                  </select>
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Atanan Hekim:</label>
                  <select 
                    name="assignedDoctorId" 
                    value={formData.assignedDoctorId} 
                    onChange={handleChange}
                    className="form-select"
                  >
                    <option value="">Seçiniz</option>
                    {getFilteredDoctors(formData.riskLevel, parseInt(formData.employeeCount) || 0).map(d => 
                      <option key={d.id} value={d.id}>{d.firstName} {d.lastName} ({d.assignedMinutes} dk)</option>
                    )}
                  </select>
                </div>
              </div>
            </div>

            <div className="form-row">
              <div className="form-col">
                <div className="form-group dsp-selection-container">
                  <label className="form-label">Atanan DSP:</label>
                  <select 
                    name="assignedDspId" 
                    value={formData.assignedDspId} 
                    onChange={handleChange}
                    className="form-select"
                    disabled={!isDspAssignmentAllowed(formData.riskLevel, parseInt(formData.employeeCount) || 0)}
                  >
                    <option value="">Seçiniz</option>
                    {getFilteredDsps(formData.riskLevel, parseInt(formData.employeeCount) || 0).map(d => 
                      <option key={d.id} value={d.id}>{d.firstName} {d.lastName} ({d.assignedMinutes} dk)</option>
                    )}
                  </select>
                  {!isDspAssignmentAllowed(formData.riskLevel, parseInt(formData.employeeCount) || 0) && (
                    <div className="form-hint">DSP ataması bu iş yeri için yapılamaz</div>
                  )}
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Kayıt Tarihi:</label>
                  <input 
                    type="date" 
                    name="registrationDate" 
                    value={formData.registrationDate} 
                    onChange={handleChange} 
                    className="form-input"
                    required 
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">&nbsp;</label>
                  <div className="form-actions-inline">
                    <button type="button" className="btn btn-secondary" onClick={handleClose}>
                      Temizle
                    </button>
                    <button type="submit" className="btn btn-primary">
                      Kaydet
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </form>
        </div>

        <div className="rows-container">
          <div className="header-row-no-box">
            <button className="row-header-label" onClick={() => sortWorkplaces('name')}>
              İş Yeri Adı
              <span className="sort-arrows">
                <span className={`sort-arrow sort-arrow-up ${sortConfig?.key === 'name' && sortConfig.direction === 'asc' ? 'active' : ''}`}></span>
                <span className={`sort-arrow sort-arrow-down ${sortConfig?.key === 'name' && sortConfig.direction === 'desc' ? 'active' : ''}`}></span>
              </span>
            </button>
            <button className="row-header-source" onClick={() => sortWorkplaces('source')}>
              Kaynak
              <span className="sort-arrows">
                <span className={`sort-arrow sort-arrow-up ${sortConfig?.key === 'source' && sortConfig.direction === 'asc' ? 'active' : ''}`}></span>
                <span className={`sort-arrow sort-arrow-down ${sortConfig?.key === 'source' && sortConfig.direction === 'desc' ? 'active' : ''}`}></span>
              </span>
            </button>
            <button className="row-header-date" onClick={() => sortWorkplaces('registrationDate')}>
              Kayıt Tarihi
              <span className="sort-arrows">
                <span className={`sort-arrow sort-arrow-up ${sortConfig?.key === 'registrationDate' && sortConfig.direction === 'asc' ? 'active' : ''}`}></span>
                <span className={`sort-arrow sort-arrow-down ${sortConfig?.key === 'registrationDate' && sortConfig.direction === 'desc' ? 'active' : ''}`}></span>
              </span>
            </button>
            <button className="row-header-expert" onClick={() => sortWorkplaces('trackingExpertId')}>
              Takip Edecek
              <span className="sort-arrows">
                <span className={`sort-arrow sort-arrow-up ${sortConfig?.key === 'trackingExpertId' && sortConfig.direction === 'asc' ? 'active' : ''}`}></span>
                <span className={`sort-arrow sort-arrow-down ${sortConfig?.key === 'trackingExpertId' && sortConfig.direction === 'desc' ? 'active' : ''}`}></span>
              </span>
            </button>
            <button className="row-header-status" onClick={() => sortWorkplaces('approvalStatus')}>
              Durum
              <span className="sort-arrows">
                <span className={`sort-arrow sort-arrow-up ${sortConfig?.key === 'approvalStatus' && sortConfig.direction === 'asc' ? 'active' : ''}`}></span>
                <span className={`sort-arrow sort-arrow-down ${sortConfig?.key === 'approvalStatus' && sortConfig.direction === 'desc' ? 'active' : ''}`}></span>
              </span>
            </button>
          </div>

          <div className="workplaces-list">
            {getSortedWorkplaces().slice((currentPage - 1) * workplacesPerPage, currentPage * workplacesPerPage).map(workplace => (
              <div 
                key={workplace.id} 
                id={`workplace-row-${workplace.id}`}
                className={`row-item ${duplicateWarning.show && duplicateWarning.workplace?.id === workplace.id ? 'duplicate-highlight' : ''}`}
                onClick={(e) => {
                  if (!(e.target as HTMLElement).closest('.inline-approval-status')) {
                    handleRowClick(workplace);
                  }
                }}
              >
                <div className="row-label">{workplace.name}</div>
                <div className="row-source">{workplace.source}</div>
                <div className="row-date">{formatDate(workplace.registrationDate)}</div>
                <div className="row-expert">{getTrackingExpertName(workplace.trackingExpertId)}</div>
                <select 
                  className={`inline-approval-status approval-status-${workplace.approvalStatus}`}
                  value={workplace.approvalStatus}
                  onChange={(e) => handleApprovalStatusChange(workplace.id, e.target.value)}
                  onClick={(e) => e.stopPropagation()}
                >
                  <option value="atama">Atama yapılacak</option>
                  <option value="bekliyor">Onay bekleniyor</option>
                  <option value="onaylandi">Onaylandı</option>
                </select>
              </div>
            ))}
          </div>

          {totalPages > 1 && (
            <div className="pagination">
              <span 
                className={`pagination-link ${currentPage === 1 ? 'disabled' : ''}`}
                onClick={() => handlePageChange(Math.max(1, currentPage - 1))}
              >
                Önceki
              </span>
              <span className="page-info">
                Sayfa {currentPage} / {totalPages}
              </span>
              <span 
                className={`pagination-link ${currentPage === totalPages ? 'disabled' : ''}`}
                onClick={() => handlePageChange(Math.min(totalPages, currentPage + 1))}
              >
                Sonraki
              </span>
            </div>
          )}
        </div>
      </div>

      {isModalOpen && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>İş Yeri Düzenle</h3>
              <button className="modal-close" onClick={closeModal}>×</button>
            </div>
            <div className="modal-body">
              <form onSubmit={handleModalSubmit}>
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">İş Yeri Adı:</label>
                      <input 
                        type="text" 
                        name="name" 
                        value={modalFormData.name || ''} 
                        onChange={handleModalChange} 
                        className="form-input"
                        required 
                      />
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">SGK İşyeri Sicil No:</label>
                      <input 
                        type="text" 
                        name="sskRegistrationNo" 
                        value={modalFormData.sskRegistrationNo || ''} 
                        onChange={handleModalChange} 
                        className="form-input"
                        required 
                      />
                    </div>
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Adres:</label>
                      <textarea 
                        name="address" 
                        value={modalFormData.address || ''} 
                        onChange={handleModalChange} 
                        className="form-textarea"
                        required 
                      />
                    </div>
                  </div>
                </div>

                <div className="form-row price-employee-tracking">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Fiyat:</label>
                      <div className="price-input-container">
                        <input
                          type="text"
                          name="price"
                          value={isModalPriceFocused ? (modalFormData.price || '') : formatPriceForDisplay(modalFormData.price as string || '')}
                          onChange={handleModalPriceChange}
                          onFocus={handleModalPriceFocus}
                          onBlur={handleModalPriceBlur}
                          className="form-input price-input"
                          required
                        />
                      </div>
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Çalışan Sayısı:</label>
                      <input 
                        type="text" 
                        name="employeeCount" 
                        value={modalFormData.employeeCount || ''} 
                        onChange={handleModalChange} 
                        className="form-input"
                        required 
                      />
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Takip Edecek:</label>
                      <select 
                        name="trackingExpertId" 
                        value={modalFormData.trackingExpertId || ''} 
                        onChange={handleModalChange}
                        className="form-select"
                      >
                        <option value="">Seçiniz</option>
                        {allExperts.map(e => <option key={`modal-tracking-${e.id}`} value={e.id}>{e.firstName} {e.lastName}</option>)}
                      </select>
                    </div>
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Vergi Dairesi:</label>
                      <input 
                        type="text" 
                        name="taxOffice" 
                        value={modalFormData.taxOffice || ''} 
                        onChange={handleModalChange} 
                        className="form-input"
                        required 
                      />
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Vergi Numarası:</label>
                      <input 
                        type="text" 
                        name="taxNumber" 
                        value={modalFormData.taxNumber || ''} 
                        onChange={handleModalChange} 
                        className="form-input"
                        required 
                      />
                    </div>
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Tehlike Sınıfı:</label>
                      <select 
                        name="riskLevel" 
                        value={modalFormData.riskLevel || ''} 
                        onChange={handleModalChange} 
                        className="form-select"
                        required
                      >
                        <option value="low">Az Tehlikeli</option>
                        <option value="dangerous">Tehlikeli</option>
                        <option value="veryDangerous">Çok Tehlikeli</option>
                      </select>
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Kaynak:</label>
                      <input 
                        type="text" 
                        name="source" 
                        value={modalFormData.source || ''} 
                        onChange={handleModalChange} 
                        className="form-input"
                        required 
                      />
                    </div>
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Atanan Uzman:</label>
                      <select 
                        name="assignedExpertId" 
                        value={modalFormData.assignedExpertId || ''} 
                        onChange={handleModalChange}
                        className="form-select"
                      >
                        <option value="">Seçiniz</option>
                        {allExperts.map(e => <option key={e.id} value={e.id}>{e.firstName} {e.lastName} ({e.expertiseClass})</option>)}
                      </select>
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Atanan Hekim:</label>
                      <select 
                        name="assignedDoctorId" 
                        value={modalFormData.assignedDoctorId || ''} 
                        onChange={handleModalChange}
                        className="form-select"
                      >
                        <option value="">Seçiniz</option>
                        {allDoctors.map(d => <option key={d.id} value={d.id}>{d.firstName} {d.lastName}</option>)}
                      </select>
                    </div>
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Atanan DSP:</label>
                      <select 
                        name="assignedDspId" 
                        value={modalFormData.assignedDspId || ''} 
                        onChange={handleModalChange}
                        className="form-select"
                      >
                        <option value="">Seçiniz</option>
                        {allDsps.map(d => <option key={d.id} value={d.id}>{d.firstName} {d.lastName}</option>)}
                      </select>
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Kayıt Tarihi:</label>
                      <input 
                        type="date" 
                        name="registrationDate" 
                        value={modalFormData.registrationDate || ''} 
                        onChange={handleModalChange} 
                        className="form-input"
                        required 
                      />
                    </div>
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Onay Durumu:</label>
                      <select 
                        name="approvalStatus" 
                        value={modalFormData.approvalStatus || ''} 
                        onChange={handleModalChange}
                        className="form-select"
                      >
                        <option value="atama">Atama yapılacak</option>
                        <option value="bekliyor">Onay bekleniyor</option>
                        <option value="onaylandi">Onaylandı</option>
                      </select>
                    </div>
                  </div>
                </div>

                <div className="modal-actions">
                  <button type="button" className="btn btn-danger" onClick={() => setShowDeleteConfirmation(true)}>
                    Sil
                  </button>
                  <button type="button" className="btn btn-secondary" onClick={closeModal}>
                    İptal
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Güncelle
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
