import React, { useState } from 'react';
import './DataImport.css';
import { useNavigate } from 'react-router-dom';

const DataImport = () => {
  const [importSource, setImportSource] = useState('web');
  const [webUrl, setWebUrl] = useState('');
  const [file, setFile] = useState<File | null>(null);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setFile(e.target.files[0]);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (importSource === 'web' && !webUrl) {
      setError('Lütfen geçerli bir URL girin');
      return;
    }
    
    if (importSource === 'file' && !file) {
      setError('Lütfen bir dosya seçin');
      return;
    }
    
    // Simulate import process
    setSuccess('Veri aktarımı başlatıldı. Bu işlem birkaç dakika sürebilir...');
    
    // Reset form after success
    setTimeout(() => {
      setWebUrl('');
      setFile(null);
      setSuccess('');
    }, 3000);
  };

  const handleClose = () => {
    // Redirect to dashboard
    navigate('/');
  };

  return (
    <div className="data-import-container">
      <div className="window">
        <div className="window-header">
          <span>Veri Aktar</span>
          <div className="window-controls">
            <button className="window-button" onClick={handleClose}>×</button>
          </div>
        </div>
        <div className="window-content">
          {success && <div className="alert alert-info">{success}</div>}
          {error && <div className="alert alert-warning">{error}</div>}
          
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">Aktarım Kaynağı:</label>
              <div>
                <label>
                  <input
                    type="radio"
                    name="importSource"
                    value="web"
                    checked={importSource === 'web'}
                    onChange={() => setImportSource('web')}
                  />
                  Web URL
                </label>
                <label style={{ marginLeft: '20px' }}>
                  <input
                    type="radio"
                    name="importSource"
                    value="file"
                    checked={importSource === 'file'}
                    onChange={() => setImportSource('file')}
                  />
                  Excel Dosyası
                </label>
              </div>
            </div>
            
            {importSource === 'web' ? (
              <div className="form-group">
                <label className="form-label">Web URL:</label>
                <input
                  type="url"
                  className="form-input"
                  value={webUrl}
                  onChange={(e) => setWebUrl(e.target.value)}
                  placeholder="https://example.com/data.xlsx"
                />
              </div>
            ) : (
              <div className="form-group">
                <label className="form-label">Excel Dosyası:</label>
                <input
                  type="file"
                  className="form-input"
                  accept=".xlsx,.xls"
                  onChange={handleFileChange}
                />
              </div>
            )}
            
            <button type="submit" className="btn btn-primary">Veri Aktar</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default DataImport;
