import React, { useState, useEffect, useRef } from 'react';
import { createPortal } from 'react-dom';
import './Dashboard.css';
import { useNavigate } from 'react-router-dom';
import { createWorkplace, getAllWorkplaces, updateWorkplace, deleteWorkplace, getWorkplaceById } from '../services/api';

const Dashboard = () => {
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    sskRegistrationNo: '',
    taxOffice: '',
    taxNumber: '',
    price: '',
    riskLevel: 'low',
    employeeCount: 0,
    assignedExpertId: '',
    assignedDoctorId: '',
    assignedDspId: '',
    source: '',
    approvalStatus: 'atama',
    registrationDate: new Date().toISOString().split('T')[0] // Added registration date
  });
  
  const [isChecked, setIsChecked] = useState(false);
  const [workplaces, setWorkplaces] = useState<any[]>([]);
  const [selectedWorkplace, setSelectedWorkplace] = useState<any>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalFormData, setModalFormData] = useState<any>({});
  const [currentPage, setCurrentPage] = useState(1);
  const [sortMenuOpen, setSortMenuOpen] = useState(false);
  const sortButtonRef = useRef<HTMLButtonElement>(null);
  const navigate = useNavigate();

  // Fetch workplaces on component mount
  useEffect(() => {
    fetchWorkplaces();
  }, []);

  const fetchWorkplaces = async () => {
    try {
      const data = await getAllWorkplaces();
      // Sort workplaces by ID in descending order (newest first)
      const sortedData = data.sort((a: any, b: any) => b.id - a.id);
      setWorkplaces(sortedData);
      // Reset to first page when new data is loaded
      setCurrentPage(1);
    } catch (err) {
      console.error('Error fetching workplaces:', err);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const formatSSKNumber = (value: string) => {
    // Remove all non-digit characters
    const digits = value.replace(/\D/g, '');
    
    // Apply formatting: x xxxx xxxx xxxxx xxx xx xx xxx xx (1+4+4+5+3+2+2+3+2 = 26 digits)
    let formatted = '';
    for (let i = 0; i < digits.length && i < 26; i++) {
      if (i === 1 || i === 5 || i === 9 || i === 14 || i === 17 || i === 19 || i === 21 || i === 24) {
        formatted += ' ';
      }
      formatted += digits[i];
    }
    
    return formatted;
  };

  const handleSSkRegistrationNoChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    // Allow only numeric input and apply formatting
    const value = e.target.value;
    const formattedValue = formatSSKNumber(value);
    setFormData(prev => ({
      ...prev,
      sskRegistrationNo: formattedValue
    }));
  };

  const handleCheckboxChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setIsChecked(e.target.checked);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const dataToSend = {
        ...formData,
        price: parseFloat(formData.price),
        employeeCount: parseInt(formData.employeeCount.toString()),
        assignedExpertId: formData.assignedExpertId ? parseInt(formData.assignedExpertId) : null,
        assignedDoctorId: formData.assignedDoctorId ? parseInt(formData.assignedDoctorId) : null,
        assignedDspId: formData.assignedDspId ? parseInt(formData.assignedDspId) : null,
        registrationDate: formData.registrationDate
      };
      
      await createWorkplace(dataToSend);
      console.log('Workplace saved successfully');
      
      // Refresh workplaces list to show the new entry
      fetchWorkplaces();
      
      // Reset form
      setFormData({
        name: '',
        address: '',
        sskRegistrationNo: '',
        taxOffice: '',
        taxNumber: '',
        price: '',
        riskLevel: 'low',
        employeeCount: 0,
        assignedExpertId: '',
        assignedDoctorId: '',
        assignedDspId: '',
        source: '',
        approvalStatus: 'atama',
        registrationDate: new Date().toISOString().split('T')[0] // Reset to today's date
      });
    } catch (err) {
      console.error('Error saving workplace:', err);
    }
  };

  const handleClose = () => {
    // Redirect to login page
    navigate('/');
  };

  const getRiskLevelText = (riskLevel: string) => {
    switch (riskLevel) {
      case 'low': return 'Az Tehlikeli';
      case 'dangerous': return 'Tehlikeli';
      case 'veryDangerous': return 'Çok Tehlikeli';
      default: return riskLevel;
    }
  };

  // Handle row click to open modal
  const handleRowClick = async (workplace: any) => {
    try {
      const detailedWorkplace = await getWorkplaceById(workplace.id);
      setSelectedWorkplace(detailedWorkplace);
      setModalFormData(detailedWorkplace);
      setIsModalOpen(true);
    } catch (err) {
      console.error('Error fetching workplace details:', err);
    }
  };

  // Handle modal form changes
  const handleModalChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setModalFormData((prev: any) => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle modal form submission
  const handleModalSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const dataToSend = {
        ...modalFormData,
        price: parseFloat(modalFormData.price),
        employeeCount: parseInt(modalFormData.employeeCount.toString()),
        assignedExpertId: modalFormData.assignedExpertId ? parseInt(modalFormData.assignedExpertId) : null,
        assignedDoctorId: modalFormData.assignedDoctorId ? parseInt(modalFormData.assignedDoctorId) : null,
        assignedDspId: modalFormData.assignedDspId ? parseInt(modalFormData.assignedDspId) : null,
        registrationDate: modalFormData.registrationDate
      };
      
      await updateWorkplace(selectedWorkplace.id, dataToSend);
      console.log('Workplace updated successfully');
      
      // Refresh workplaces list
      fetchWorkplaces();
      
      // Close modal
      setIsModalOpen(false);
    } catch (err) {
      console.error('Error updating workplace:', err);
    }
  };

  // Handle workplace deletion
  const handleDeleteWorkplace = async () => {
    if (window.confirm('Bu iş yerini silmek istediğinizden emin misiniz?')) {
      try {
        await deleteWorkplace(selectedWorkplace.id);
        console.log('Workplace deleted successfully');
        
        // Refresh workplaces list
        fetchWorkplaces();
        
        // Close modal
        setIsModalOpen(false);
      } catch (err) {
        console.error('Error deleting workplace:', err);
      }
    }
  };

  // Handle approval status change
  const handleApprovalStatusChange = async (workplaceId: number, newStatus: string) => {
    try {
      // Get the current workplace data
      const workplace = await getWorkplaceById(workplaceId);
      
      // Update only the approval status
      const updatedWorkplace = {
        ...workplace,
        approvalStatus: newStatus
      };
      
      // Send update to backend
      await updateWorkplace(workplaceId, updatedWorkplace);
      
      // Refresh the workplaces list
      fetchWorkplaces();
      
      console.log(`Workplace ${workplaceId} approval status updated to ${newStatus}`);
    } catch (err) {
      console.error('Error updating approval status:', err);
    }
  };

  // Pagination functions
  const handlePageChange = (page: number) => {
    setCurrentPage(page);
  };

  // Calculate total pages
  const totalPages = Math.ceil(workplaces.length / 10);

  // Get workplaces for current page
  const getCurrentPageWorkplaces = () => {
    const startIndex = (currentPage - 1) * 10;
    const endIndex = startIndex + 10;
    return workplaces.slice(startIndex, endIndex);
  };

  // Format date for display
  const formatDate = (dateString: string) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString('tr-TR');
  };

  // Sorting function
  const sortWorkplaces = (criteria: string) => {
    let sortedWorkplaces = [...workplaces];
    
    switch (criteria) {
      case 'name':
        sortedWorkplaces.sort((a, b) => 
          (a.name || '').localeCompare(b.name || '')
        );
        break;
      case 'date':
        sortedWorkplaces.sort((a, b) => {
          const dateA = new Date(a.registrationDate || '').getTime();
          const dateB = new Date(b.registrationDate || '').getTime();
          return dateB - dateA; // Newest first
        });
        break;
      case 'approval':
        const statusOrder: Record<string, number> = {
          'atama': 1,    // Atama yapılacak
          'bekliyor': 2, // Onay bekliyor
          'onaylandi': 3 // Onaylandı
        };
        sortedWorkplaces.sort((a, b) => {
          const statusA = statusOrder[a.approvalStatus || 'atama'] || 0;
          const statusB = statusOrder[b.approvalStatus || 'atama'] || 0;
          return statusA - statusB;
        });
        break;
      default:
        // Default sorting by ID (descending)
        sortedWorkplaces.sort((a, b) => b.id - a.id);
    }
    
    setWorkplaces(sortedWorkplaces);
    setSortMenuOpen(false);
    setCurrentPage(1); // Reset to first page when sorting
  };

  // Close sort menu when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (sortButtonRef.current && !sortButtonRef.current.contains(event.target as Node)) {
        setSortMenuOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h2 className="dashboard-title">Yeni İş Yeri Kaydı</h2>
      </div>
      
      {/* Existing form */}
      <div className="form-container">
        <form onSubmit={handleSubmit} className="workplace-form">
          <div className="form-row">
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">İş Yeri Adı:</label>
                <input
                  type="text"
                  name="name"
                  className="form-input small-input"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">SGK Kayıt No:</label>
                <input
                  type="text"
                  name="sskRegistrationNo"
                  className="form-input ssk-input"
                  value={formData.sskRegistrationNo}
                  onChange={handleSSkRegistrationNoChange}
                  placeholder="x xxxx xxxx xxxxx xxx xx xx xxx xx"
                  maxLength={30} // 26 digits + 4 spaces
                  required
                />
              </div>
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Adres:</label>
                <textarea
                  name="address"
                  className="form-textarea smaller-textarea"
                  value={formData.address}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Fiyat:</label>
                <input
                  type="number"
                  name="price"
                  className="form-input small-input"
                  value={formData.price}
                  onChange={handleChange}
                  step="0.01"
                  required
                />
              </div>
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Vergi Dairesi:</label>
                <input
                  type="text"
                  name="taxOffice"
                  className="form-input small-input"
                  value={formData.taxOffice}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Vergi No:</label>
                <input
                  type="text"
                  name="taxNumber"
                  className="form-input small-input"
                  value={formData.taxNumber}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Tehlike Sınıfı:</label>
                <select
                  name="riskLevel"
                  className="form-select small-select"
                  value={formData.riskLevel}
                  onChange={handleChange}
                >
                  <option value="low">Az Tehlikeli</option>
                  <option value="dangerous">Tehlikeli</option>
                  <option value="veryDangerous">Çok Tehlikeli</option>
                </select>
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Çalışan Sayısı:</label>
                <input
                  type="number"
                  name="employeeCount"
                  className="form-input small-input"
                  value={formData.employeeCount}
                  onChange={handleChange}
                  min="0"
                  required
                />
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Kaynak:</label>
                <input
                  type="text"
                  name="source"
                  className="form-input small-input"
                  value={formData.source}
                  onChange={handleChange}
                />
              </div>
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Atanan Uzman:</label>
                <input
                  type="text"
                  name="assignedExpertId"
                  className="form-input small-input"
                  value={formData.assignedExpertId}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Atanan Hekim:</label>
                <input
                  type="text"
                  name="assignedDoctorId"
                  className="form-input small-input"
                  value={formData.assignedDoctorId}
                  onChange={handleChange}
                />
              </div>
            </div>
          </div>
          
          <div className={`form-row ${isChecked ? 'completed' : ''}`}>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Atanan DSP:</label>
                <input
                  type="text"
                  name="assignedDspId"
                  className="form-input small-input"
                  value={formData.assignedDspId}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Kayıt Tarihi:</label>
                <input
                  type="date"
                  name="registrationDate"
                  className="form-input small-input"
                  value={formData.registrationDate}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="form-col checkbox-col">
              <div className="form-group">
                <label className="form-label">Tamamlandı:</label>
                <input
                  type="checkbox"
                  name="completed"
                  className="form-checkbox"
                  checked={isChecked}
                  onChange={handleCheckboxChange}
                />
              </div>
            </div>
            <div className="form-col sort-button-col">
              <button
                type="button"
                className="btn btn-danger sort-button"
                onClick={() => setSortMenuOpen(!sortMenuOpen)}
                ref={sortButtonRef}
              >
                Sırala
              </button>
              {sortMenuOpen && (
                <div className="sort-menu">
                  <button 
                    className="sort-option" 
                    onClick={() => sortWorkplaces('name')}
                  >
                    İş Yeri Adına Göre
                  </button>
                  <button 
                    className="sort-option" 
                    onClick={() => sortWorkplaces('date')}
                  >
                    Tarihe Göre
                  </button>
                  <button 
                    className="sort-option" 
                    onClick={() => sortWorkplaces('approval')}
                  >
                    Onay Durumuna Göre
                  </button>
                </div>
              )}
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-col form-actions">
              <button type="button" className="btn btn-secondary" onClick={handleClose}>
                İptal
              </button>
              <button type="submit" className="btn btn-primary">
                Kaydet
              </button>
            </div>
          </div>
        </form>
      </div>
      
      {/* Modal for workplace details */}
      {isModalOpen && createPortal(
        <div className="modal-overlay" onClick={() => setIsModalOpen(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>İş Yeri Detayları</h3>
              <button className="modal-close" onClick={() => setIsModalOpen(false)}>×</button>
            </div>
            <div className="modal-body">
              <form onSubmit={handleModalSubmit}>
                <div className="form-group">
                  <label className="form-label">İş Yeri Adı:</label>
                  <input
                    type="text"
                    name="name"
                    className="form-input"
                    value={modalFormData.name || ''}
                    onChange={handleModalChange}
                    required
                  />
                </div>
                
                <div className="form-group">
                  <label className="form-label">Adres:</label>
                  <textarea
                    name="address"
                    className="form-textarea"
                    value={modalFormData.address || ''}
                    onChange={handleModalChange}
                    required
                  />
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">SGK Kayıt No:</label>
                      <input
                        type="text"
                        name="sskRegistrationNo"
                        className="form-input"
                        value={modalFormData.sskRegistrationNo || ''}
                        onChange={handleModalChange}
                        required
                      />
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Fiyat:</label>
                      <input
                        type="number"
                        name="price"
                        className="form-input"
                        value={modalFormData.price || ''}
                        onChange={handleModalChange}
                        step="0.01"
                        required
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Vergi Dairesi:</label>
                      <input
                        type="text"
                        name="taxOffice"
                        className="form-input"
                        value={modalFormData.taxOffice || ''}
                        onChange={handleModalChange}
                        required
                      />
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Vergi No:</label>
                      <input
                        type="text"
                        name="taxNumber"
                        className="form-input"
                        value={modalFormData.taxNumber || ''}
                        onChange={handleModalChange}
                        required
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Tehlike Sınıfı:</label>
                      <select
                        name="riskLevel"
                        className="form-select"
                        value={modalFormData.riskLevel || 'low'}
                        onChange={handleModalChange}
                      >
                        <option value="low">Az Tehlikeli</option>
                        <option value="dangerous">Tehlikeli</option>
                        <option value="veryDangerous">Çok Tehlikeli</option>
                      </select>
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Çalışan Sayısı:</label>
                      <input
                        type="number"
                        name="employeeCount"
                        className="form-input"
                        value={modalFormData.employeeCount || 0}
                        onChange={handleModalChange}
                        min="0"
                        required
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Atanan Uzman:</label>
                      <input
                        type="text"
                        name="assignedExpertId"
                        className="form-input"
                        value={modalFormData.assignedExpertId || ''}
                        onChange={handleModalChange}
                      />
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Atanan Hekim:</label>
                      <input
                        type="text"
                        name="assignedDoctorId"
                        className="form-input"
                        value={modalFormData.assignedDoctorId || ''}
                        onChange={handleModalChange}
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-group">
                  <label className="form-label">Atanan DSP:</label>
                  <input
                    type="text"
                    name="assignedDspId"
                    className="form-input"
                    value={modalFormData.assignedDspId || ''}
                    onChange={handleModalChange}
                  />
                </div>
                
                <div className="form-group">
                  <label className="form-label">Onay Durumu:</label>
                  <select
                    name="approvalStatus"
                    className="form-select"
                    value={modalFormData.approvalStatus || 'atama'}
                    onChange={handleModalChange}
                  >
                    <option value="atama">Atama yapılacak</option>
                    <option value="bekliyor">Onay bekliyor</option>
                    <option value="onaylandi">Onaylandı</option>
                  </select>
                </div>
                
                <div className="form-group">
                  <label className="form-label">Kaynak:</label>
                  <input
                    type="text"
                    name="source"
                    className="form-input"
                    value={modalFormData.source || ''}
                    onChange={handleModalChange}
                  />
                </div>
                
                <div className="form-group">
                  <label className="form-label">Kayıt Tarihi:</label>
                  <input
                    type="date"
                    name="registrationDate"
                    className="form-input"
                    value={modalFormData.registrationDate || ''}
                    onChange={handleModalChange}
                  />
                </div>
                
                <div className="modal-actions">
                  <button type="button" className="btn btn-danger" onClick={handleDeleteWorkplace}>
                    Sil
                  </button>
                  <button type="button" className="btn btn-secondary" onClick={() => setIsModalOpen(false)}>
                    İptal
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Güncelle
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>,
        document.body
      )}
      
      {/* Rows with pagination - Approval status selection outside of compact row */}
      <div className="rows-container">
        <div className="dashboard-content-wrapper">
          {/* Display current page rows */}
          <div className="workplaces-list">
            {getCurrentPageWorkplaces().map((workplace, index) => (
              <div 
                key={(currentPage - 1) * 10 + index} 
                className="row-item"
                onClick={() => handleRowClick(workplace)}
              >
                <span className="row-number">{(currentPage - 1) * 10 + index + 1}.</span>
                <span className="row-label">{workplace.name || 'İş Yeri Adı'}</span>
                <span className="row-date">{formatDate(workplace.registrationDate)}</span>
                <select
                  className={`inline-approval-status approval-status-${workplace.approvalStatus || 'atama'}`}
                  value={workplace.approvalStatus || 'atama'}
                  onClick={(e) => e.stopPropagation()}
                  onChange={(e) => handleApprovalStatusChange(workplace.id, e.target.value)}
                >
                  <option value="atama">Atama yapılacak</option>
                  <option value="bekliyor">Onay bekleniyor</option>
                  <option value="onaylandi">Onaylandı</option>
                </select>
              </div>
            ))}
          </div>
        </div>
        
        {/* Page numbers */}
        {totalPages > 1 && (
          <div className="pagination">
            {[...Array(totalPages)].map((_, index) => (
              <button
                key={index}
                className={`page-number ${currentPage === index + 1 ? 'active' : ''}`}
                onClick={() => handlePageChange(index + 1)}
              >
                {index + 1}
              </button>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
