import React, { useState } from 'react';
import { createDsp } from '../services/api';
import './Registration.css';

const DspRegistration = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    assignedMinutes: 11900
  });
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'assignedMinutes' ? parseInt(value) : value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (formData.assignedMinutes > 11900) {
      setError('Atanan dakika 11.900\'ü geçemez');
      return;
    }
    
    try {
      await createDsp(formData);
      setSuccess('DSP başarıyla kaydedildi!');
      setFormData({
        firstName: '',
        lastName: '',
        phone: '',
        assignedMinutes: 11900
      });
    } catch (err) {
      setError('DSP kaydedilirken hata oluştu');
    }
  };

  return (
    <div className="registration-container">
      <div className="window">
        <div className="window-header">
          <span>DSP Kayıt</span>
        </div>
        <div className="window-content">
          {success && <div className="alert alert-info">{success}</div>}
          {error && <div className="alert alert-warning">{error}</div>}
          
          <form onSubmit={handleSubmit} className="registration-form">
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Ad:</label>
                  <input
                    type="text"
                    name="firstName"
                    className="form-input"
                    value={formData.firstName}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Soyad:</label>
                  <input
                    type="text"
                    name="lastName"
                    className="form-input"
                    value={formData.lastName}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
            </div>
            
            <div className="form-group">
              <label className="form-label">Telefon:</label>
              <input
                type="text"
                name="phone"
                className="form-input"
                value={formData.phone}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="form-group">
              <label className="form-label">Atanan Dakika:</label>
              <input
                type="number"
                name="assignedMinutes"
                className="form-input"
                value={formData.assignedMinutes}
                onChange={handleChange}
                min="0"
                max="11900"
                required
              />
            </div>
            
            <button type="submit" className="btn btn-primary">DSP Kaydet</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default DspRegistration;
