import React, { useState, useEffect } from 'react';
import { createDsp, getAllDsps, deleteDsp, updateDsp } from '../services/api';
import './DspCardPage.css';
import { useNavigate, useLocation } from 'react-router-dom';

// Define interface for form data
interface FormData {
  firstName: string;
  lastName: string;
  phone: string;
  assignedMinutes: number;
}

// Define interface for DSP data
interface DspData extends FormData {
  id: number;
}

const DspCardPage = () => {
  // DSP Card Page Component
  const [formData, setFormData] = useState<FormData>({
    firstName: '',
    lastName: '',
    phone: '',
    assignedMinutes: 11900
  });
  const [dsps, setDsps] = useState<DspData[]>([]);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  
  const navigate = useNavigate();
  const location = useLocation();

  // Validation functions
  const isValidName = (name: string) => {
    return /^[a-zA-ZğüşıöçĞÜŞİÖÇ\s]+$/.test(name);
  };

  const isValidPhone = (phone: string) => {
    return /^5\d{2}\s\d{3}\s\d{2}\s\d{2}$/.test(phone);
  };

  // Format phone number as user types
  const formatPhoneNumber = (value: string) => {
    // Remove all non-digit characters
    const digits = value.replace(/\D/g, '');
    
    // Limit to 10 digits
    const limitedDigits = digits.substring(0, 10);
    
    // Format as 5xx xxx xx xx
    let formatted = '';
    for (let i = 0; i < limitedDigits.length; i++) {
      if (i === 3 || i === 6 || i === 8) {
        formatted += ' ';
      }
      formatted += limitedDigits[i];
    }
    
    return formatted;
  };

  // Function to handle clicking on a DSP row
  const handleDspClick = (dsp: DspData) => {
    openDspDetails(dsp);
  };

  useEffect(() => {
    fetchDsps();
  }, []);

  // Add useEffect to refresh data when location changes (navigation)
  useEffect(() => {
    console.log('DspCardPage: Location changed to:', location.pathname, 'at:', new Date().toISOString());
    fetchDsps();
  }, [location.pathname]);

  // Add useEffect to refresh data when window regains focus
  useEffect(() => {
    const handleFocus = () => {
      console.log('DspCardPage: Window focused, refreshing DSPs...');
      fetchDsps();
    };

    window.addEventListener('focus', handleFocus);
    return () => {
      window.removeEventListener('focus', handleFocus);
    };
  }, []);

  // Add useEffect to refresh data when workplace is created
  useEffect(() => {
    const handleWorkplaceCreated = () => {
      console.log('DspCardPage: Workplace created, refreshing DSPs...');
      fetchDsps();
    };

    window.addEventListener('workplaceCreated', handleWorkplaceCreated);
    return () => {
      window.removeEventListener('workplaceCreated', handleWorkplaceCreated);
    };
  }, []);

  const fetchDsps = async () => {
    try {
      const data = await getAllDsps();
      setDsps(data);
    } catch (err: any) {
      console.error('DSP\'ler getirilirken hata oluştu:', err);
      setError(err.message || 'DSP\'ler getirilirken hata oluştu');
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    
    // Special handling for name fields - prevent numbers
    if (name === 'firstName' || name === 'lastName') {
      // Remove any numeric characters
      const cleanValue = value.replace(/\d/g, '');
      setFormData((prev: FormData) => ({
        ...prev,
        [name]: cleanValue
      }));
    }
    // Special handling for phone number formatting
    else if (name === 'phone') {
      const formattedPhone = formatPhoneNumber(value);
      setFormData((prev: FormData) => ({
        ...prev,
        [name]: formattedPhone
      }));
    } else {
      setFormData((prev: FormData) => ({
        ...prev,
        [name]: name === 'assignedMinutes' ? (value === '' ? 0 : parseInt(value) || 0) : value
      }));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    // Validation checks
    if (!isValidName(formData.firstName)) {
      setError('Ad sadece harf içermelidir');
      clearNotifications();
      return;
    }
    
    if (!isValidName(formData.lastName)) {
      setError('Soyad sadece harf içermelidir');
      clearNotifications();
      return;
    }
    
    if (!isValidPhone(formData.phone)) {
      setError('Telefon numarası 5xx xxx xx xx formatında olmalıdır');
      clearNotifications();
      return;
    }
    
    if (formData.assignedMinutes > 11900) {
      setError('Atanan dakika 11.900\'ü geçemez');
      clearNotifications();
      return;
    }
    
    try {
      await createDsp(formData);
      setSuccess('DSP başarıyla kaydedildi!');
      setFormData({
        firstName: '',
        lastName: '',
        phone: '',
        assignedMinutes: 11900
      });
      // Refresh the DSPs list
      fetchDsps();
      clearNotifications();
    } catch (err: any) {
      console.error('Create DSP error:', err);
      const errorMessage = err.message || 'DSP kaydedilirken hata oluştu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  // Clear notifications after timeout
  const clearNotifications = () => {
    setTimeout(() => {
      setSuccess('');
      setError('');
    }, 3000);
  };

  const handleClose = () => {
    // Redirect to dashboard
    navigate('/');
  };

  // Function to open modal with DSP details
  const openDspDetails = (dsp: DspData) => {
    setSelectedDsp(dsp);
    setIsEditMode(false);
    setIsModalOpen(true);
  };

  // Function to open modal in edit mode
  const openEditDsp = (dsp: DspData) => {
    setSelectedDsp(dsp);
    setEditFormData({
      firstName: dsp.firstName || '',
      lastName: dsp.lastName || '',
      phone: dsp.phone || '',
      assignedMinutes: dsp.assignedMinutes || 11900
    });
    setIsEditMode(true);
    setIsModalOpen(true);
  };

  // Function to close modal
  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedDsp(null);
    setIsEditMode(false);
  };

  // State for modal dialog
  const [selectedDsp, setSelectedDsp] = useState<DspData | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isEditMode, setIsEditMode] = useState(false);
  
  // State for edit form data
  const [editFormData, setEditFormData] = useState<FormData>({
    firstName: '',
    lastName: '',
    phone: '',
    assignedMinutes: 11900
  });
  
  const handleEditFormDataChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    
    // Special handling for name fields - prevent numbers
    if (name === 'firstName' || name === 'lastName') {
      // Remove any numeric characters
      const cleanValue = value.replace(/\d/g, '');
      setEditFormData((prev: FormData) => ({
        ...prev,
        [name]: cleanValue
      }));
    }
    // Special handling for phone number formatting
    else if (name === 'phone') {
      const formattedPhone = formatPhoneNumber(value);
      setEditFormData((prev: FormData) => ({
        ...prev,
        [name]: formattedPhone
      }));
    } else {
      setEditFormData((prev: FormData) => ({
        ...prev,
        [name]: name === 'assignedMinutes' ? (value === '' ? 0 : parseInt(value) || 0) : value
      }));
    }
  };
  
  const handleUpdateDsp = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedDsp) return;
    
    // Validation checks for edit form
    if (!isValidName(editFormData.firstName)) {
      setError('Ad sadece harf içermelidir');
      clearNotifications();
      return;
    }
    
    if (!isValidName(editFormData.lastName)) {
      setError('Soyad sadece harf içermelidir');
      clearNotifications();
      return;
    }
    
    if (!isValidPhone(editFormData.phone)) {
      setError('Telefon numarası 5xx xxx xx xx formatında olmalıdır');
      clearNotifications();
      return;
    }
    
    if (editFormData.assignedMinutes > 11900) {
      setError('Atanan dakika 11.900\'ü geçemez');
      clearNotifications();
      return;
    }
    
    try {
      await updateDsp(selectedDsp.id, editFormData);
      setSuccess('DSP başarıyla güncellendi!');
      closeModal();
      fetchDsps(); // Refresh the DSPs list
      
      clearNotifications();
    } catch (err: any) {
      console.error('Update error:', err);
      const errorMessage = err.message || 'DSP güncellenirken hata oluştu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  const handleDelete = async (id: number) => {
    // Show confirmation dialog before deleting
    const confirmed = window.confirm('Bu DSP kaydını silmek istediğinize emin misiniz?');
    if (!confirmed) {
      return; // User cancelled the deletion
    }
    
    try {
      await deleteDsp(id);
      setSuccess('DSP başarıyla silindi!');
      // Refresh the DSPs list
      fetchDsps();
      
      // Close modal if it was open for this DSP
      if (selectedDsp && selectedDsp.id === id) {
        setIsModalOpen(false);
        setSelectedDsp(null);
      }
      
      clearNotifications();
    } catch (err: any) {
      console.error('Delete error:', err);
      const errorMessage = err.message || 'DSP silinirken hata oluştu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  return (
    <div className="dsp-card-page">
      {/* Popup notifications */}
      {success && (
        <div className="popup-notification alert alert-info">
          {success}
        </div>
      )}
      {error && (
        <div className="popup-notification alert alert-warning">
          {error}
        </div>
      )}
      
      {/* "Yeni DSP Kayıt" card (fixed position and size) */}
      <div 
        className="draggable-card"
        style={{
          position: 'absolute',
          left: 0,
          top: -90,
          width: 400,
          height: 320,
          zIndex: 100
        }}
      >
        <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
          <div 
            className="window-header"
            style={{ userSelect: 'none' }}
          >
            <span>Yeni DSP Kayıt</span>
            <div className="window-controls">
              <button className="window-button" onClick={handleClose}>×</button>
            </div>
          </div>
          <div className="window-content" style={{ flex: 1, overflow: 'visible' }}>
            <form onSubmit={handleSubmit} className="registration-form">
              <div className="form-row">
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Ad:</label>
                    <input
                      type="text"
                      name="firstName"
                      className="form-input"
                      value={formData.firstName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Soyad:</label>
                    <input
                      type="text"
                      name="lastName"
                      className="form-input"
                      value={formData.lastName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
              </div>
              
              <div className="form-group">
                <label className="form-label">Telefon:</label>
                <input
                  type="text"
                  name="phone"
                  className="form-input"
                  value={formData.phone}
                  onChange={handleChange}
                  required
                />
              </div>
              
              <div className="form-group">
                <label className="form-label">Atanan Dakika:</label>
                <input
                  type="number"
                  name="assignedMinutes"
                  className="form-input"
                  value={formData.assignedMinutes}
                  onChange={handleChange}
                  min="0"
                  max="11900"
                  required
                />
              </div>
              
              <button type="submit" className="btn btn-primary">DSP Kaydet</button>
            </form>
          </div>
        </div>
      </div>
      
      {/* Display registered DSPs in individual rows positioned on the right side of the page */}
      <div className="registered-dsps-container" style={{ 
        position: 'absolute',
        right: '450px',
        top: '0px',
        width: '300px'
      }}>
        {dsps.map((dsp) => (
          <div
            key={dsp.id}
            onClick={() => handleDspClick(dsp)}
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              padding: '15px',
              margin: '10px 0',
              borderRadius: '8px',
              cursor: 'pointer',
              transition: 'all 0.3s ease',
              position: 'relative',
              top: '0px',
              left: '0px',
              width: '100%',
              // Using blue color scheme for all DSPs (similar to B class experts)
              backgroundColor: '#bbdefb' // Blue background
            }}
            onMouseEnter={(e) => {
              // Enhanced hover effect with more vibrant blue
              e.currentTarget.style.backgroundColor = '#90caf9'; // More vibrant blue on hover
              e.currentTarget.style.boxShadow = '0 4px 8px rgba(33, 150, 243, 0.3)';
              e.currentTarget.style.transform = 'translateY(-2px)';
            }}
            onMouseLeave={(e) => {
              // Reset to original blue color when not hovering
              e.currentTarget.style.backgroundColor = '#bbdefb';
              e.currentTarget.style.boxShadow = 'none';
              e.currentTarget.style.transform = 'translateY(0)';
            }}
          >
            <div className="dsp-info-display" style={{ fontSize: '14px' }}>
              <div className="dsp-name-display">{dsp.firstName} {dsp.lastName}</div>
              <div className="dsp-phone-display">{dsp.phone}</div>
            </div>
            <div 
              className="dsp-class-display"
              style={{
                // Blue badge for all DSPs
                backgroundColor: '#1976d2',
                color: 'white',
                fontSize: '12px',
                fontWeight: 'bold',
                padding: '2px 8px',
                borderRadius: '12px'
              }}
            >
              D
            </div>
          </div>
        ))}
      </div>
      
      {/* DSP Details Modal */}
      {isModalOpen && selectedDsp && (
        <div className="modal-overlay" onClick={(e) => {
          // Only close modal if clicking on the overlay (not the content)
          if (e.target === e.currentTarget) {
            closeModal();
          }
        }}>
          <div 
            className="modal-content draggable-card" 
            onClick={(e) => e.stopPropagation()}
            style={{
              width: 400,
              height: 500,
              maxWidth: '90vw',
              maxHeight: '90vh',
              position: 'absolute',
              left: '50%',
              top: '50%',
              transform: 'translate(-50%, -50%)',
              margin: 0,
              zIndex: 2001
            }}
          >
            <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
              <div 
                className="window-header"
                style={{ userSelect: 'none' }}
              >
                <span>{isEditMode ? 'DSP Düzenle' : 'DSP Detayları'}</span>
                <div className="window-controls">
                  <button className="window-button" onClick={closeModal} aria-label="Kapat">×</button>
                </div>
              </div>
              <div className="window-content">
                {isEditMode ? (
                  // Edit form
                  <form onSubmit={handleUpdateDsp} className="edit-form">
                    <div className="form-row">
                      <div className="form-col">
                        <div className="form-group">
                          <label className="form-label">Ad:</label>
                          <input
                            type="text"
                            name="firstName"
                            className="form-input"
                            value={editFormData.firstName}
                            onChange={handleEditFormDataChange}
                            required
                          />
                        </div>
                      </div>
                      <div className="form-col">
                        <div className="form-group">
                          <label className="form-label">Soyad:</label>
                          <input
                            type="text"
                            name="lastName"
                            className="form-input"
                            value={editFormData.lastName}
                            onChange={handleEditFormDataChange}
                            required
                          />
                        </div>
                      </div>
                    </div>
                    
                    <div className="form-group">
                      <label className="form-label">Telefon:</label>
                      <input
                        type="text"
                        name="phone"
                        className="form-input"
                        value={editFormData.phone}
                        onChange={handleEditFormDataChange}
                        placeholder="5xx xxx xx xx"
                        required
                      />
                    </div>
                    
                    <div className="form-group">
                      <label className="form-label">Atanan Dakika:</label>
                      <input
                        type="number"
                        name="assignedMinutes"
                        className="form-input"
                        value={editFormData.assignedMinutes}
                        onChange={handleEditFormDataChange}
                        min="0"
                        max="11900"
                        required
                      />
                    </div>
                    
                    <div className="modal-actions">
                      <button type="submit" className="btn btn-primary">Güncelle</button>
                      <button type="button" className="btn" onClick={closeModal}>İptal</button>
                    </div>
                  </form>
                ) : (
                  // View mode
                  <>
                    <div className="dsp-detail-info">
                      <div className="dsp-detail-row">
                        <strong>Ad:</strong> {selectedDsp.firstName}
                      </div>
                      <div className="dsp-detail-row">
                        <strong>Soyad:</strong> {selectedDsp.lastName}
                      </div>
                      <div className="dsp-detail-row">
                        <strong>Telefon:</strong> {selectedDsp.phone}
                      </div>
                      <div className="dsp-detail-row">
                        <strong>Toplam Dakika:</strong> {selectedDsp.assignedMinutes}
                      </div>
                      <div className="dsp-detail-row">
                        <strong>Atanan Dakika:</strong> 0
                      </div>
                      <div className="dsp-detail-row">
                        <strong>Kalan Dakika:</strong> {selectedDsp.assignedMinutes}
                      </div>
                      
                      {/* Workplace assignment information */}
                    </div>
                    
                    <div className="modal-actions">
                      <button className="btn btn-primary" onClick={() => openEditDsp(selectedDsp)}>Düzenle</button>
                      <button className="btn btn-danger" onClick={() => {
                        const confirmed = window.confirm('Bu DSP kaydını silmek istediğinize emin misiniz?');
                        if (confirmed) {
                          handleDelete(selectedDsp.id);
                        }
                      }}>Sil</button>
                      <button className="btn btn-close" onClick={closeModal}>Kapat</button>
                    </div>
                  </>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default DspCardPage;
