      if (error.message) {
        setError(`İş yeri güncellenirken hata oluştu: ${error.message}`);
      } else {
        setError('İş yeri güncellenirken hata oluştu');
      }
      clearNotifications();
    }
  };

  const handleDeleteWorkplace = () => {
    // Show confirmation dialog before deleting
    setShowDeleteConfirmation(true);
  };

  // Function to confirm deletion
  const confirmDeleteWorkplace = async () => {
    try {
      if (modalFormData.id) {
        // Delete from backend
        await deleteWorkplace(modalFormData.id);
        
        // Show success message
        setSuccess('İş yeri başarıyla silindi!');
        
        // Close confirmation dialog and modal
        setShowDeleteConfirmation(false);
        closeModal();
        
        // Refresh all data from backend to ensure consistency
        await fetchAllData();
        
        // Reset to first page if we're on an empty page
        const newTotalPages = Math.ceil(workplaces.length / workplacesPerPage);
        if (currentPage > newTotalPages && newTotalPages > 0) {
          setCurrentPage(newTotalPages);
        } else if (newTotalPages === 0) {
          setCurrentPage(1);
        }
        
        // Clear notifications
        clearNotifications();
      } else {
        setShowDeleteConfirmation(false);
        closeModal();
      }
    } catch (error: any) {
      console.error('Error deleting workplace:', error);
      // Provide more specific error message
      if (error.message) {
        setError(`İş yeri silinirken hata oluştu: ${error.message}`);
      } else {
        setError('İş yeri silinirken hata oluştu');
      }
      clearNotifications();
    }
  };

  // Function to cancel deletion
  const cancelDeleteWorkplace = () => {
    setShowDeleteConfirmation(false);
  };

  const handleApprovalStatusChange = async (id: number, status: string) => {
    try {
      // Update local state first for immediate UI feedback
      setWorkplaces(prev => 
        prev.map(workplace => 
          workplace.id === id ? { ...workplace, approvalStatus: status } : workplace
        )
      );
      
      // Update the backend
      await updateWorkplace(id, { ...workplaces.find(w => w.id === id), approvalStatus: status });
      
      // Show success message
      setSuccess('Onay durumu başarıyla güncellendi!');
      clearNotifications();
    } catch (error) {
      console.error('Error updating approval status:', error);
      setError('Onay durumu güncellenirken hata oluştu');
      clearNotifications();
      
      // Revert the local state change if backend update fails
      await fetchAllData();
    }
  };
  
  const formatDate = (dateString: string) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString('tr-TR');
  };
  
  // Function to format risk level in Turkish
  const formatRiskLevel = (riskLevel: string): string => {
    switch (riskLevel) {
      case 'low':
        return 'Az Tehlikeli';
      case 'dangerous':
        return 'Tehlikeli';
      case 'veryDangerous':
        return 'Çok Tehlikeli';
      default:
        return 'Belirtilmemiş';
    }
  };
  
  // Function to get expert name by ID
  const getExpertName = (expertId: string, workplace?: Workplace) => {
    // First try to get name from included data in workplace
    if (workplace && workplace.Expert) {
      console.log('Using included Expert data:', workplace.Expert);
      // Check if firstName and lastName exist
      if (workplace.Expert.firstName && workplace.Expert.lastName) {
        return `${workplace.Expert.firstName} ${workplace.Expert.lastName}`;
      }
    }
    
    // Fallback to lookup in allExperts array
    if (!expertId) return 'Atanmamış';
    const expert = allExperts.find(expert => expert.id === parseInt(expertId));
    if (expert && expert.firstName && expert.lastName) {
      return `${expert.firstName} ${expert.lastName}`;
    }
    return 'Bilinmeyen Uzman';
  };
  
  // Function to get doctor name by ID
  const getDoctorName = (doctorId: string, workplace?: Workplace) => {
    // First try to get name from included data in workplace
    if (workplace && workplace.Doctor) {
      console.log('Using included Doctor data:', workplace.Doctor);
      // Check if firstName and lastName exist
      if (workplace.Doctor.firstName && workplace.Doctor.lastName) {
        return `${workplace.Doctor.firstName} ${workplace.Doctor.lastName}`;
      }
    }
    
    // Fallback to lookup in allDoctors array
    if (!doctorId) return 'Atanmamış';
    const doctor = allDoctors.find(doctor => doctor.id === parseInt(doctorId));
    if (doctor && doctor.firstName && doctor.lastName) {
      return `${doctor.firstName} ${doctor.lastName}`;
    }
    return 'Bilinmeyen Hekim';
  };
  
  // Function to get DSP name by ID
  const getDspName = (dspId: string, workplace?: Workplace) => {
    // First try to get name from included data in workplace
    if (workplace && workplace.Dsp) {
      console.log('Using included Dsp data:', workplace.Dsp);
      // Check if firstName and lastName exist
      if (workplace.Dsp.firstName && workplace.Dsp.lastName) {
        return `${workplace.Dsp.firstName} ${workplace.Dsp.lastName}`;
      }
    }
    
    // Fallback to lookup in allDsps array
    if (!dspId) return 'Atanmamış';
    const dsp = allDsps.find(dsp => dsp.id === parseInt(dspId));
    if (dsp && dsp.firstName && dsp.lastName) {
      return `${dsp.firstName} ${dsp.lastName}`;
    }
    return 'Bilinmeyen DSP';
  };
  
  const handleEditWorkplace = () => {
    setWorkplaces(prev => 
      prev.map(workplace => 
        workplace.id === modalFormData.id ? modalFormData as Workplace : workplace
      )
    );
    closeModal();
  };
  
  // Function to close modal
  const closeModal = () => {
    setIsModalOpen(false);
    setModalFormData({});
    // Allow background scrolling when modal is closed
    document.body.style.overflow = 'auto';
  };
  
  return (
    <div className="dashboard-container">
      {/* Popup notifications */}
      {success && (
        <div className="popup-notification alert alert-info">
          {success}
        </div>
      )}
      {error && (
        <div className="popup-notification alert alert-warning">
          {error}
        </div>
      )}
      
      {/* No Expert Popup */}
      {showNoExpertPopup && (
        <div className="popup-overlay" onClick={() => setShowNoExpertPopup(false)}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()}>
            <div className="popup-header">
              <h3>Uygun Uzman Bulunmuyor</h3>
              <button className="popup-close" onClick={() => setShowNoExpertPopup(false)}>×</button>
            </div>
            <div className="popup-body">
              <p>Atanacak uygun uzman bulunmamaktadır.</p>
            </div>
            <div className="popup-footer">
              <button className="btn btn-primary" onClick={() => setShowNoExpertPopup(false)}>Tamam</button>
            </div>
          </div>
        </div>
      )}
      
      {/* No Doctor Popup */}
      {showNoDoctorPopup && (
        <div className="popup-overlay" onClick={() => setShowNoDoctorPopup(false)}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()}>
            <div className="popup-header">
              <h3>Uygun Hekim Bulunmuyor</h3>
              <button className="popup-close" onClick={() => setShowNoDoctorPopup(false)}>×</button>
            </div>
            <div className="popup-body">
              <p>Atanacak uygun hekim bulunmamaktadır.</p>
            </div>
            <div className="popup-footer">
              <button className="btn btn-primary" onClick={() => setShowNoDoctorPopup(false)}>Tamam</button>
            </div>
          </div>
        </div>
      )}
      
      {/* No DSP Popup */}
      {showNoDspPopup && (
        <div className="popup-overlay" onClick={() => setShowNoDspPopup(false)}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()}>
            <div className="popup-header">
              <h3>Uygun DSP Bulunmuyor</h3>
              <button className="popup-close" onClick={() => setShowNoDspPopup(false)}>×</button>
            </div>
            <div className="popup-body">
              <p>Atanacak uygun DSP bulunmamaktadır.</p>
            </div>
            <div className="popup-footer">
              <button className="btn btn-primary" onClick={() => setShowNoDspPopup(false)}>Tamam</button>
            </div>
          </div>
        </div>
      )}
      
      {/* DSP Not Allowed Popup */}
      {showDspNotAllowedPopup && (
        <div className="popup-overlay" onClick={() => setShowDspNotAllowedPopup(false)}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()}>
            <div className="popup-header">
              <h3>DSP Atanamaz</h3>
              <button className="popup-close" onClick={() => setShowDspNotAllowedPopup(false)}>×</button>
            </div>
            <div className="popup-body">
              <p>Seçilen iş yerine DSP atanamaz.</p>
            </div>
            <div className="popup-footer">
              <button className="btn btn-primary" onClick={() => setShowDspNotAllowedPopup(false)}>Tamam</button>
            </div>
          </div>
        </div>
      )}
      
      {/* Delete Confirmation Popup */}
      {showDeleteConfirmation && (
        <div className="popup-overlay" onClick={cancelDeleteWorkplace}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()}>
            <div className="popup-header">
              <h3>Kayıt Silme Onayı</h3>
              <button className="popup-close" onClick={cancelDeleteWorkplace}>×</button>
            </div>
            <div className="popup-body">
              <p>Kayıdı gerçekten silmek istiyor musunuz?</p>
            </div>
            <div className="popup-footer">
              <button className="btn btn-secondary" onClick={cancelDeleteWorkplace}>İptal</button>
              <button className="btn btn-danger" onClick={confirmDeleteWorkplace}>Sil</button>
            </div>
          </div>
        </div>
      )}

      <div className="dashboard-content">
        {/* Existing form */}
        <div className="form-container">
          {/* Popup notifications */}
          {success && (
            <div className="popup-notification alert alert-info">
              {success}
            </div>
          )}
          {error && (
            <div className="popup-notification alert alert-warning">
              {error}
            </div>
          )}
          
          {/* Duplicate warning popup */}
          {duplicateWarning.show && (
            <div className="duplicate-warning-popup">
              <h3>Kayıt Zaten Mevcut</h3>
              <p>Bu kayıt daha önce girilmiş. Tekrar kayıt yapmak için ilgili satırı düzenleyebilirsiniz.</p>
            </div>
          )}
          
          {/* No Expert Popup */}
          {showNoExpertPopup && (
            <div className="popup-overlay" onClick={() => setShowNoExpertPopup(false)}>
              <div className="popup-content" onClick={(e) => e.stopPropagation()}>
                <div className="popup-header">
                  <h3>Uygun Uzman Bulunmuyor</h3>
                  <button className="popup-close" onClick={() => setShowNoExpertPopup(false)}>×</button>
                </div>
                <div className="popup-body">
                  <p>Atanacak uygun uzman bulunmamaktadır.</p>
                </div>
                <div className="popup-footer">
                  <button className="btn btn-primary" onClick={() => setShowNoExpertPopup(false)}>Tamam</button>
                </div>
              </div>
            </div>
          )}
          
          {/* No Doctor Popup */}
          {showNoDoctorPopup && (
            <div className="popup-overlay" onClick={() => setShowNoDoctorPopup(false)}>
              <div className="popup-content" onClick={(e) => e.stopPropagation()}>
                <div className="popup-header">
                  <h3>Uygun Hekim Bulunmuyor</h3>
                  <button className="popup-close" onClick={() => setShowNoDoctorPopup(false)}>×</button>
                </div>
                <div className="popup-body">
                  <p>Atanacak uygun hekim bulunmamaktadır.</p>
                </div>
                <div className="popup-footer">
                  <button className="btn btn-primary" onClick={() => setShowNoDoctorPopup(false)}>Tamam</button>
                </div>
              </div>
            </div>
          )}
          
          {/* No DSP Popup */}
          {showNoDspPopup && (
            <div className="popup-overlay" onClick={() => setShowNoDspPopup(false)}>
              <div className="popup-content" onClick={(e) => e.stopPropagation()}>
                <div className="popup-header">
                  <h3>Uygun DSP Bulunmuyor</h3>
                  <button className="popup-close" onClick={() => setShowNoDspPopup(false)}>×</button>
                </div>
                <div className="popup-body">
                  <p>Atanacak uygun DSP bulunmamaktadır.</p>
                </div>
                <div className="popup-footer">
                  <button className="btn btn-primary" onClick={() => setShowNoDspPopup(false)}>Tamam</button>
                </div>
              </div>
            </div>
          )}
          
          {/* DSP Not Allowed Popup */}
          {showDspNotAllowedPopup && (
            <div className="popup-overlay" onClick={() => setShowDspNotAllowedPopup(false)}>
              <div className="popup-content" onClick={(e) => e.stopPropagation()}>
                <div className="popup-header">
                  <h3>DSP Atanamaz</h3>
                  <button className="popup-close" onClick={() => setShowDspNotAllowedPopup(false)}>×</button>
                </div>
                <div className="popup-body">
                  <p>Seçilen iş yerine DSP atanamaz.</p>
                </div>
                <div className="popup-footer">
                  <button className="btn btn-primary" onClick={() => setShowDspNotAllowedPopup(false)}>Tamam</button>
                </div>
              </div>
            </div>
          )}
          
          {/* Delete Confirmation Popup */}
          {showDeleteConfirmation && (
            <div className="popup-overlay" onClick={cancelDeleteWorkplace}>
              <div className="popup-content" onClick={(e) => e.stopPropagation()}>
                <div className="popup-header">
                  <h3>Kayıt Silme Onayı</h3>
                  <button className="popup-close" onClick={cancelDeleteWorkplace}>×</button>
                </div>
                <div className="popup-body">
                  <p>Kayıdı gerçekten silmek istiyor musunuz?</p>
                </div>
                <div className="popup-footer">
                  <button className="btn btn-secondary" onClick={cancelDeleteWorkplace}>İptal</button>
                  <button className="btn btn-danger" onClick={confirmDeleteWorkplace}>Sil</button>
                </div>
              </div>
            </div>
          )}

          <form onSubmit={handleSubmit} className="workplace-form">
            {/* Compact single-line form layout */}
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">İş Yeri Adı:</label>
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    className="form-input"
                    required
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">SGK İşyeri Sicil No:</label>
                  <input
                    type="text"
                    name="sskRegistrationNo"
                    value={formData.sskRegistrationNo}
                    onChange={handleSSkRegistrationNoChange}
                    className="form-input ssk-input"
                    placeholder="x xxxx xx xx xxxxx xxx xx xx xxx"
                    required
                  />
                </div>
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Adres:</label>
                  <textarea
                    name="address"
                    value={formData.address}
                    onChange={handleChange}
                    className="form-textarea"
                    required
                  />
                </div>
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Fiyat:</label>
                  <div className="price-input-container">
                    <input
                      type="text"
                      name="price"
                      value={isPriceFocused ? formData.price : formatPriceForDisplay(formData.price)}
                      onChange={handlePriceChange}
                      onFocus={handlePriceFocus}
                      onBlur={handlePriceBlur}
                      className="form-input price-input"
                      required
                    />
                  </div>
                  <div className="kdv-checkbox-container">
                    <input
                      type="checkbox"
                      id="kdv-included"
                      className="kdv-checkbox"
                      checked={isKdvIncluded}
                      onChange={(e) => setIsKdvIncluded(e.target.checked)}
                    />
                    <label htmlFor="kdv-included" className="kdv-label">KDV Dahil</label>
                  </div>
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Vergi Dairesi:</label>
                  <input
                    type="text"
                    name="taxOffice"
                    value={formData.taxOffice}
                    onChange={handleChange}
                    className="form-input"
                    required
                  />
                </div>
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Vergi Numarası:</label>
                  <input
                    type="text"
                    name="taxNumber"
                    value={formData.taxNumber}
                    onChange={handleTaxNumberChange}
                    className="form-input"
                    required
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Tehlike Sınıfı:</label>
                  <select
                    name="riskLevel"
                    value={formData.riskLevel}
                    onChange={handleChange}
                    className="form-select"
                    required
                  >
                    <option value="low">Az Tehlikeli</option>
                    <option value="dangerous">Tehlikeli</option>
                    <option value="veryDangerous">Çok Tehlikeli</option>
                  </select>
                </div>
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Çalışan Sayısı:</label>
                  <input
                    type="text"
                    name="employeeCount"
                    value={formData.employeeCount}
                    onChange={handleEmployeeCountChange}
                    className="form-input"
                    required
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Kaynak:</label>
                  <input
                    type="text"
                    name="source"
                    value={formData.source}
                    onChange={handleSourceChange}
                    className="form-input"
                    required
                  />
                </div>
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Atanan Uzman:</label>
                  <select
                    name="assignedExpertId"
                    value={formData.assignedExpertId}
                    onChange={handleChange}
                    className="form-select"
                  >
                    <option value="">Seçiniz</option>
                    {getFilteredExperts(formData.riskLevel, parseInt(formData.employeeCount) || 0).map(expert => (
                      <option key={expert.id} value={expert.id}>
                        {expert.firstName} {expert.lastName} ({expert.expertiseClass} - {expert.assignedMinutes} dk)
                      </option>
                    ))}
                  </select>
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Atanan Hekim:</label>
                  <select
                    name="assignedDoctorId"
                    value={formData.assignedDoctorId}
                    onChange={handleChange}
                    className="form-select"
                  >
                    <option value="">Seçiniz</option>
                    {getFilteredDoctors(formData.riskLevel, parseInt(formData.employeeCount) || 0).map(doctor => (
                      <option key={doctor.id} value={doctor.id}>
                        {doctor.firstName} {doctor.lastName} ({doctor.assignedMinutes} dk)
                      </option>
                    ))}
                  </select>
                </div>
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-col">
                <div className="form-group dsp-selection-container">
                  <label className="form-label">Atanan DSP:</label>
                  <select
                    name="assignedDspId"
                    value={formData.assignedDspId}
                    onChange={handleChange}
                    className="form-select"
                    disabled={!isDspAssignmentAllowed(formData.riskLevel, parseInt(formData.employeeCount) || 0)}
                  >
                    <option value="">Seçiniz</option>
                    {getFilteredDsps(formData.riskLevel, parseInt(formData.employeeCount) || 0).map(dsp => (
                      <option key={dsp.id} value={dsp.id}>
                        {dsp.firstName} {dsp.lastName} ({dsp.assignedMinutes} dk)
                      </option>
                    ))}
                  </select>
                  {!isDspAssignmentAllowed(formData.riskLevel, parseInt(formData.employeeCount) || 0) && (
                    <div className="form-hint">DSP ataması bu iş yeri için yapılamaz</div>
                  )}
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Kayıt Tarihi:</label>
                  <input
                    type="date"
                    name="registrationDate"
                    value={formData.registrationDate}
                    onChange={handleChange}
                    className="form-input"
                    required
                  />
                </div>
              </div>
              {/* Move buttons to this row to save space */}
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">&nbsp;</label>
                  <div className="form-actions-inline">
                    <button type="button" className="btn btn-secondary" onClick={handleClose}>
                      Temizle
                    </button>
                    <button type="submit" className="btn btn-primary">
                      Kaydet
                    </button>
                  </div>
                </div>
              </div>
            </div>
            
            {/* Remove the original form-actions div since buttons are now inline */}
          </form>
        </div>
        
        {/* Workplaces list */}
        <div className="rows-container">
          <div className="header-row-no-box">
            <button className="row-header-label" onClick={() => sortWorkplaces('name')}>
              İş Yeri Adı
              <span className="sort-arrows">
                <span className={`sort-arrow sort-arrow-up ${sortConfig?.key === 'name' && sortConfig.direction === 'asc' ? 'active' : ''}`}></span>
                <span className={`sort-arrow sort-arrow-down ${sortConfig?.key === 'name' && sortConfig.direction === 'desc' ? 'active' : ''}`}></span>
              </span>
            </button>
            <button className="row-header-source" onClick={() => sortWorkplaces('source')}>
              Kaynak
              <span className="sort-arrows">
                <span className={`sort-arrow sort-arrow-up ${sortConfig?.key === 'source' && sortConfig.direction === 'asc' ? 'active' : ''}`}></span>
                <span className={`sort-arrow sort-arrow-down ${sortConfig?.key === 'source' && sortConfig.direction === 'desc' ? 'active' : ''}`}></span>
              </span>
            </button>
            <button className="row-header-date" onClick={() => sortWorkplaces('registrationDate')}>
              Kayıt Tarihi
              <span className="sort-arrows">
                <span className={`sort-arrow sort-arrow-up ${sortConfig?.key === 'registrationDate' && sortConfig.direction === 'asc' ? 'active' : ''}`}></span>
                <span className={`sort-arrow sort-arrow-down ${sortConfig?.key === 'registrationDate' && sortConfig.direction === 'desc' ? 'active' : ''}`}></span>
              </span>
            </button>
            <button className="row-header-expert" onClick={() => sortWorkplaces('assignedExpertId')}>
              Uzman
              <span className="sort-arrows">
                <span className={`sort-arrow sort-arrow-up ${sortConfig?.key === 'assignedExpertId' && sortConfig.direction === 'asc' ? 'active' : ''}`}></span>
                <span className={`sort-arrow sort-arrow-down ${sortConfig?.key === 'assignedExpertId' && sortConfig.direction === 'desc' ? 'active' : ''}`}></span>
              </span>
            </button>
            <button className="row-header-status" onClick={() => sortWorkplaces('approvalStatus')}>
              Durum
              <span className="sort-arrows">
                <span className={`sort-arrow sort-arrow-up ${sortConfig?.key === 'approvalStatus' && sortConfig.direction === 'asc' ? 'active' : ''}`}></span>
                <span className={`sort-arrow sort-arrow-down ${sortConfig?.key === 'approvalStatus' && sortConfig.direction === 'desc' ? 'active' : ''}`}></span>
              </span>
            </button>
          </div>
          
          <div className="workplaces-list">
            {getSortedWorkplaces().slice((currentPage - 1) * workplacesPerPage, currentPage * workplacesPerPage).map(workplace => (
              <div 
                key={workplace.id} 
                id={`workplace-row-${workplace.id}`}
                className={`row-item ${duplicateWarning.show && duplicateWarning.workplace?.id === workplace.id ? 'duplicate-highlight' : ''}`}
                onClick={(e) => {
                  // Only open modal if the click is not on the status dropdown
                  if (!(e.target as HTMLElement).closest('.inline-approval-status')) {
                    handleRowClick(workplace);
                  }
                }}
              >
                <div className="row-label">{workplace.name}</div>
                <div className="row-source">{workplace.source}</div>
                <div className="row-date">{formatDate(workplace.registrationDate)}</div>
                <div className="row-expert">{getExpertName(workplace.assignedExpertId, workplace)}</div>
                <select
                  className={`inline-approval-status approval-status-${workplace.approvalStatus}`}
                  value={workplace.approvalStatus}
                  onChange={(e) => handleApprovalStatusChange(workplace.id, e.target.value)}
                  onClick={(e) => e.stopPropagation()} // Prevent row click when clicking on dropdown
                >
                  <option value="atama">Atama yapılacak</option>
                  <option value="bekliyor">Onay bekleniyor</option>
                  <option value="onaylandi">Onaylandı</option>
                </select>
              </div>
            ))}
          </div>
          
          {totalPages > 1 && (
            <div className="pagination">
              <span 
                className={`pagination-link ${workplaces.length === 0 ? 'disabled' : ''}`}
                onClick={() => workplaces.length > 0 && handlePageChange(currentPage > 1 ? currentPage - 1 : totalPages)}
              >
                Önceki
              </span>
              <span className="page-info">
                Sayfa {currentPage} / {totalPages}
              </span>
              <span 
                className={`pagination-link ${workplaces.length === 0 ? 'disabled' : ''}`}
                onClick={() => workplaces.length > 0 && handlePageChange(currentPage < totalPages ? currentPage + 1 : 1)}
              >
                Sonraki
              </span>
            </div>
          )}
        </div>
      </div>
      
      {/* Modal for editing workplace */}
      {isModalOpen && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>İş Yeri Düzenle</h3>
              <button className="modal-close" onClick={closeModal}>×</button>
            </div>
            <div className="modal-body">
              <form onSubmit={handleModalSubmit}>
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">İş Yeri Adı:</label>
                      <input
                        type="text"
                        name="name"
                        value={modalFormData.name || ''}
                        onChange={handleModalChange}
                        className="form-input"
                        required
                      />
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">SGK İşyeri Sicil No:</label>
                      <input
                        type="text"
                        name="sskRegistrationNo"
                        value={modalFormData.sskRegistrationNo || ''}
                        onChange={handleModalChange}
                        className="form-input ssk-input"
                        placeholder="x xxxx xx xx xxxxx xxx xx xx xxx"
                        required
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Adres:</label>
                      <textarea
                        name="address"
                        value={modalFormData.address || ''}
                        onChange={handleModalChange}
                        className="form-textarea"
                        required
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Fiyat:</label>
                      <div className="price-input-container">
                        <input
                          type="text"
                          name="price"
                          value={isModalPriceFocused ? (modalFormData.price || '') : formatPriceForDisplay(modalFormData.price as string || '')}
                          onChange={handleModalPriceChange}
                          onFocus={handleModalPriceFocus}
                          onBlur={handleModalPriceBlur}
                          className="form-input price-input"
                          required
                        />
                      </div>
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Vergi Dairesi:</label>
                      <input
                        type="text"
                        name="taxOffice"
                        value={modalFormData.taxOffice || ''}
                        onChange={handleModalChange}
                        className="form-input"
                        required
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Vergi Numarası:</label>
                      <input
                        type="text"
                        name="taxNumber"
                        value={modalFormData.taxNumber || ''}
                        onChange={handleModalChange}
                        className="form-input"
                        required
                      />
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Tehlike Sınıfı:</label>
                      <select
                        name="riskLevel"
                        value={modalFormData.riskLevel || ''}
                        onChange={handleModalChange}
                        className="form-select"
                        required
                      >
                        <option value="low">Az Tehlikeli</option>
                        <option value="dangerous">Tehlikeli</option>
                        <option value="veryDangerous">Çok Tehlikeli</option>
                      </select>
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Çalışan Sayısı:</label>
                      <input
                        type="text"
                        name="employeeCount"
                        value={modalFormData.employeeCount || ''}
                        onChange={handleModalChange}
                        className="form-input"
                        required
                      />
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Kaynak:</label>
                      <input
                        type="text"
                        name="source"
                        value={modalFormData.source || ''}
                        onChange={handleModalChange}
                        className="form-input"
                        required
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Atanan Uzman:</label>
                      <select
                        name="assignedExpertId"
                        value={modalFormData.assignedExpertId || ''}
                        onChange={handleModalChange}
                        className="form-select"
                      >
                        <option value="">Seçiniz</option>
                        {allExperts.map(expert => (
                          <option key={expert.id} value={expert.id}>
                            {expert.firstName} {expert.lastName} ({expert.expertiseClass})
                          </option>
                        ))}
                      </select>
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Atanan Hekim:</label>
                      <select
                        name="assignedDoctorId"
                        value={modalFormData.assignedDoctorId || ''}
                        onChange={handleModalChange}
                        className="form-select"
                      >
                        <option value="">Seçiniz</option>
                        {allDoctors.map(doctor => (
                          <option key={doctor.id} value={doctor.id}>
                            {doctor.firstName} {doctor.lastName}
                          </option>
                        ))}
                      </select>
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Atanan DSP:</label>
                      <select
                        name="assignedDspId"
                        value={modalFormData.assignedDspId || ''}
                        onChange={handleModalChange}
                        className="form-select"
                      >
                        <option value="">Seçiniz</option>
                        {allDsps.map(dsp => (
                          <option key={dsp.id} value={dsp.id}>
                            {dsp.firstName} {dsp.lastName}
                          </option>
                        ))}
                      </select>
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Kayıt Tarihi:</label>
                      <input
                        type="date"
                        name="registrationDate"
                        value={modalFormData.registrationDate || ''}
                        onChange={handleModalChange}
                        className="form-input"
                        required
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Onay Durumu:</label>
                      <select
                        name="approvalStatus"
                        value={modalFormData.approvalStatus || ''}
                        onChange={handleModalChange}
                        className="form-select"
                      >
                        <option value="atama">Atama yapılacak</option>
                        <option value="bekliyor">Onay bekleniyor</option>
                        <option value="onaylandi">Onaylandı</option>
                      </select>
                    </div>
                  </div>
                </div>
                
                <div className="modal-actions">
                  <button type="button" className="btn btn-danger" onClick={handleDeleteWorkplace}>
                    Sil
                  </button>
                  <button type="button" className="btn btn-secondary" onClick={closeModal}>
                    İptal
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Güncelle
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
