import React, { useState, useEffect } from 'react';
import { getAllWorkplaces } from '../services/api';
import './WorkplaceRegistration.css';
import { useNavigate } from 'react-router-dom';

const WorkplaceRegistration = () => {
  const [workplaces, setWorkplaces] = useState<any[]>([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchApprovedWorkplaces();
  }, []);

  const fetchApprovedWorkplaces = async () => {
    try {
      const allWorkplaces = await getAllWorkplaces();
      // Filter only approved ("onaylandi") workplaces
      const approvedWorkplaces = allWorkplaces.filter(
        (workplace: any) => workplace.approvalStatus === 'onaylandi'
      );
      
      setWorkplaces(approvedWorkplaces);
    } catch (err) {
      console.error('Error fetching approved workplaces:', err);
    }
  };

  const getRiskLevelText = (riskLevel: string) => {
    switch (riskLevel) {
      case 'low': return 'Düşük Risk';
      case 'dangerous': return 'Tehlikeli';
      case 'veryDangerous': return 'Çok Tehlikeli';
      default: return riskLevel;
    }
  };

  const handleClose = () => {
    // Redirect to dashboard
    navigate('/');
  };

  // Format date for display
  const formatDate = (dateString: string) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString('tr-TR');
  };

  return (
    <div className="workplace-registration-container">
      <div className="window">
        <div className="window-header">
          <span>Onaylanmış İş Yerleri</span>
          <div className="window-controls">
            <button className="window-button" onClick={handleClose}>×</button>
          </div>
        </div>
        <div className="window-content">
          {/* Approved Workplaces List in Row Format */}
          <div className="approved-workplaces-container">
            {workplaces.length === 0 ? (
              <div className="no-data-message">Onaylanmış iş yeri bulunmamaktadır.</div>
            ) : (
              workplaces.map((workplace, index) => (
                <div key={workplace.id} className="workplace-row approved-workplace">
                  <div className="row-header">
                    <span className="row-number">{index + 1}.</span>
                    <span className="workplace-name">{workplace.name}</span>
                  </div>
                  <div className="row-details">
                    <div className="detail-item">
                      <span className="detail-label">SGK Kayıt No:</span>
                      <span className="detail-value">{workplace.sskRegistrationNo}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Fiyat:</span>
                      <span className="detail-value">{workplace.price} ₺</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Tehlike Sınıfı:</span>
                      <span className="detail-value">{getRiskLevelText(workplace.riskLevel)}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Kayıt Tarihi:</span>
                      <span className="detail-value">{formatDate(workplace.registrationDate)}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Atanan Uzman:</span>
                      <span className="detail-value">{workplace.assignedExpertId || 'Atanmamış'}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Atanan Hekim:</span>
                      <span className="detail-value">{workplace.assignedDoctorId || 'Atanmamış'}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Atanan DSP:</span>
                      <span className="detail-value">{workplace.assignedDspId || 'Atanmamış'}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Kaynak:</span>
                      <span className="detail-value">{workplace.source || 'Belirtilmemiş'}</span>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default WorkplaceRegistration;
