import React, { useState, useEffect } from 'react';
import { getAllWorkplaces } from '../services/api';
import './Dashboard.css';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [workplaces, setWorkplaces] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetchWorkplaces();
  }, []);

  const fetchWorkplaces = async () => {
    try {
      const data = await getAllWorkplaces();
      setWorkplaces(data);
      setLoading(false);
    } catch (error) {
      console.error('İş yerleri getirilirken hata oluştu:', error);
      setLoading(false);
    }
  };

  const getRiskLevelText = (riskLevel: string) => {
    switch (riskLevel) {
      case 'low': return 'Düşük Risk';
      case 'dangerous': return 'Tehlikeli';
      case 'veryDangerous': return 'Çok Tehlikeli';
      default: return riskLevel;
    }
  };

  const handleClose = () => {
    // Redirect to login page
    navigate('/');
  };

  return (
    <div className="dashboard-container">
      <div className="window">
        <div className="window-header">
          <span>CRM - İş Yeri Kayıt ve İzleme Paneli</span>
          <div className="window-controls">
            <button className="window-button" onClick={handleClose}>×</button>
          </div>
        </div>
        <div className="window-content">
          <div className="dashboard-header">
            <h2 className="dashboard-title">Kayıtlı İş Yerleri</h2>
            <button className="btn btn-primary" onClick={() => navigate('/workplaces')}>
              Yeni İş Yeri Ekle
            </button>
          </div>
          
          {loading ? (
            <div className="loading-container">
              <p>İş yerleri yükleniyor...</p>
            </div>
          ) : (
            <div className="dashboard-content">
              {workplaces.length === 0 ? (
                <div className="empty-state">
                  <p>Henüz kayıtlı iş yeri bulunmamaktadır.</p>
                  <button className="btn btn-primary" onClick={() => navigate('/workplaces')}>
                    İlk İş Yerini Ekle
                  </button>
                </div>
              ) : (
                <div className="workplace-table-container">
                  <table className="table workplace-table">
                    <thead>
                      <tr>
                        <th>Tarih</th>
                        <th>İş Yeri Adı</th>
                        <th>Adres</th>
                        <th>SGK Kayıt No</th>
                        <th>Vergi Dairesi</th>
                        <th>Vergi No</th>
                        <th>Fiyat</th>
                        <th>Tehlike Sınıfı</th>
                        <th>Atanan Uzman</th>
                        <th>Atanan Hekim</th>
                        <th>Kaynak</th>
                      </tr>
                    </thead>
                    <tbody>
                      {workplaces.map((workplace) => (
                        <tr key={workplace.id}>
                          <td>{new Date(workplace.createdAt).toLocaleDateString()}</td>
                          <td>{workplace.name}</td>
                          <td className="address-cell">{workplace.address}</td>
                          <td>{workplace.sskRegistrationNo}</td>
                          <td>{workplace.taxOffice}</td>
                          <td>{workplace.taxNumber}</td>
                          <td>{workplace.price} ₺</td>
                          <td>{getRiskLevelText(workplace.riskLevel)}</td>
                          <td>{workplace.assignedExpertId || 'Atanmadı'}</td>
                          <td>{workplace.assignedDoctorId || 'Atanmadı'}</td>
                          <td>{workplace.source || 'N/A'}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                  <div className="table-summary">
                    Toplam {workplaces.length} iş yeri kaydı
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
