import React, { useState, useEffect } from 'react';
import { createExpert, getAllExperts, deleteExpert } from '../services/api';
import './ExpertCardPage.css';
import { useNavigate } from 'react-router-dom';

const ExpertCardPage = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    expertiseClass: 'A',
    assignedMinutes: 11900
  });
  const [experts, setExperts] = useState<any[]>([]);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  
  // State for "Yeni Uzman Kayƒ±t" card
  const [newCard, setNewCard] = useState({
    x: 50,
    y: 150,
    width: 400,
    height: 500,
    isDragging: false,
    isResizing: false,
    dragStartX: 0,
    dragStartY: 0,
    resizeStartX: 0,
    resizeStartY: 0,
    originalWidth: 400,
    originalHeight: 500
  });
  
  // State for "Kayƒ±tlƒ± Uzmanlar" card
  const [listCard, setListCard] = useState({
    x: 500,
    y: 150,
    width: 500,
    height: 500,
    isDragging: false,
    isResizing: false,
    dragStartX: 0,
    dragStartY: 0,
    resizeStartX: 0,
    resizeStartY: 0,
    originalWidth: 500,
    originalHeight: 500
  });
  
  const navigate = useNavigate();

  useEffect(() => {
    fetchExperts();
  }, []);

  const fetchExperts = async () => {
    try {
      const data = await getAllExperts();
      setExperts(data);
    } catch (err) {
      console.error('Uzmanlar getirilirken hata olu≈ütu:', err);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'assignedMinutes' ? parseInt(value) : value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (formData.assignedMinutes > 11900) {
      setError('Atanan dakika 11.900\'√º ge√ßemez');
      return;
    }
    
    try {
      await createExpert(formData);
      setSuccess('Uzman ba≈üarƒ±yla kaydedildi!');
      setFormData({
        firstName: '',
        lastName: '',
        phone: '',
        expertiseClass: 'A',
        assignedMinutes: 11900
      });
      // Refresh the experts list
      fetchExperts();
    } catch (err) {
      setError('Uzman kaydedilirken hata olu≈ütu');
    }
  };

  const handleDelete = async (id: number) => {
    try {
      await deleteExpert(id);
      setSuccess('Uzman ba≈üarƒ±yla silindi!');
      // Refresh the experts list
      fetchExperts();
    } catch (err) {
      setError('Uzman silinirken hata olu≈ütu');
    }
  };

  const handleClose = () => {
    // Redirect to dashboard
    navigate('/dashboard');
  };

  // Mouse event handlers
  const handleMouseDown = (e: React.MouseEvent, cardType: 'new' | 'list', action: 'drag' | 'resize') => {
    e.preventDefault();
    e.stopPropagation();
    
    if (action === 'drag') {
      if (cardType === 'new') {
        setNewCard(prev => ({
          ...prev,
          isDragging: true,
          dragStartX: e.clientX - prev.x,
          dragStartY: e.clientY - prev.y
        }));
      } else {
        setListCard(prev => ({
          ...prev,
          isDragging: true,
          dragStartX: e.clientX - prev.x,
          dragStartY: e.clientY - prev.y
        }));
      }
    } else if (action === 'resize') {
      if (cardType === 'new') {
        setNewCard(prev => ({
          ...prev,
          isResizing: true,
          resizeStartX: e.clientX,
          resizeStartY: e.clientY,
          originalWidth: prev.width,
          originalHeight: prev.height
        }));
      } else {
        setListCard(prev => ({
          ...prev,
          isResizing: true,
          resizeStartX: e.clientX,
          resizeStartY: e.clientY,
          originalWidth: prev.width,
          originalHeight: prev.height
        }));
      }
    }
  };

  const handleMouseMove = (e: MouseEvent) => {
    // Handle dragging for "Yeni Uzman Kayƒ±t" card
    if (newCard.isDragging) {
      setNewCard(prev => ({
        ...prev,
        x: e.clientX - prev.dragStartX,
        y: e.clientY - prev.dragStartY
      }));
    }
    
    // Handle dragging for "Kayƒ±tlƒ± Uzmanlar" card
    if (listCard.isDragging) {
      setListCard(prev => ({
        ...prev,
        x: e.clientX - prev.dragStartX,
        y: e.clientY - prev.dragStartY
      }));
    }
    
    // Handle resizing for "Yeni Uzman Kayƒ±t" card
    if (newCard.isResizing) {
      const newWidth = Math.max(300, newCard.originalWidth + (e.clientX - newCard.resizeStartX));
      const newHeight = Math.max(200, newCard.originalHeight + (e.clientY - newCard.resizeStartY));
      setNewCard(prev => ({
        ...prev,
        width: newWidth,
        height: newHeight
      }));
    }
    
    // Handle resizing for "Kayƒ±tlƒ± Uzmanlar" card
    if (listCard.isResizing) {
      const newWidth = Math.max(300, listCard.originalWidth + (e.clientX - listCard.resizeStartX));
      const newHeight = Math.max(200, listCard.originalHeight + (e.clientY - listCard.resizeStartY));
      setListCard(prev => ({
        ...prev,
        width: newWidth,
        height: newHeight
      }));
    }
  };

  const handleMouseUp = () => {
    setNewCard(prev => ({ ...prev, isDragging: false, isResizing: false }));
    setListCard(prev => ({ ...prev, isDragging: false, isResizing: false }));
  };

  useEffect(() => {
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
    
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [newCard.isDragging, listCard.isDragging, newCard.isResizing, listCard.isResizing]);

  return (
    <div className="expert-card-page">
      {/* "Yeni Uzman Kayƒ±t" card (draggable and resizable) */}
      <div 
        className="draggable-card"
        style={{
          position: 'absolute',
          left: newCard.x,
          top: newCard.y,
          width: newCard.width,
          height: newCard.height,
          zIndex: 1000,
          cursor: newCard.isDragging ? 'grabbing' : 'default'
        }}
      >
        <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
          <div 
            className="window-header"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'drag')}
            style={{ cursor: 'move', userSelect: 'none' }}
          >
            <span>Yeni Uzman Kayƒ±t</span>
            <div className="window-controls">
              <button className="window-button" onClick={handleClose}>√ó</button>
            </div>
          </div>
          <div className="window-content" style={{ flex: 1, overflow: 'auto' }}>
            {success && <div className="alert alert-info">{success}</div>}
            {error && <div className="alert alert-warning">{error}</div>}
            
            <form onSubmit={handleSubmit} className="registration-form">
              <div className="form-row">
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Ad:</label>
                    <input
                      type="text"
                      name="firstName"
                      className="form-input"
                      value={formData.firstName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Soyad:</label>
                    <input
                      type="text"
                      name="lastName"
                      className="form-input"
                      value={formData.lastName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
              </div>
              
              <div className="form-group">
                <label className="form-label">Telefon:</label>
                <input
                  type="text"
                  name="phone"
                  className="form-input"
                  value={formData.phone}
                  onChange={handleChange}
                  required
                />
              </div>
              
              <div className="form-row">
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Uzmanlƒ±k Sƒ±nƒ±fƒ±:</label>
                    <select
                      name="expertiseClass"
                      className="form-select"
                      value={formData.expertiseClass}
                      onChange={handleChange}
                    >
                      <option value="A">A</option>
                      <option value="B">B</option>
                      <option value="C">C</option>
                    </select>
                  </div>
                </div>
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Atanan Dakika:</label>
                    <input
                      type="number"
                      name="assignedMinutes"
                      className="form-input"
                      value={formData.assignedMinutes}
                      onChange={handleChange}
                      min="0"
                      max="11900"
                      required
                    />
                  </div>
                </div>
              </div>
              
              <button type="submit" className="btn btn-primary">Uzmanƒ± Kaydet</button>
            </form>
          </div>
          <div 
            className="resize-handle"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize')}
          />
        </div>
      </div>
      
      {/* "Kayƒ±tlƒ± Uzmanlar" card (draggable and resizable) */}
      <div 
        className="draggable-card"
        style={{
          position: 'absolute',
          left: listCard.x,
          top: listCard.y,
          width: listCard.width,
          height: listCard.height,
          zIndex: 1000,
          cursor: listCard.isDragging ? 'grabbing' : 'default'
        }}
      >
        <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
          <div 
            className="window-header"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'drag')}
            style={{ cursor: 'move', userSelect: 'none' }}
          >
            <span>Kayƒ±tlƒ± Uzmanlar</span>
          </div>
          <div className="window-content" style={{ flex: 1, overflow: 'auto' }}>
            {experts.length === 0 ? (
              <p>Kayƒ±tlƒ± uzman bulunmamaktadƒ±r.</p>
            ) : (
              <div className="expert-list">
                {experts.map((expert) => (
                  <div key={expert.id} className="expert-card">
                    <div className="expert-info">
                      <span>{expert.firstName} {expert.lastName} - {expert.phone} - Sƒ±nƒ±f {expert.expertiseClass} - {expert.assignedMinutes} dakika</span>
                    </div>
                    <div className="expert-actions">
                      <button className="btn-icon edit">‚úèÔ∏è</button>
                      <button className="btn-icon delete" onClick={() => handleDelete(expert.id)}>üóëÔ∏è</button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
          <div 
            className="resize-handle"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize')}
          />
        </div>
      </div>
    </div>
  );
};

export default ExpertCardPage;
