import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getAllExperts, getAllDoctors, getAllDsps, getAllWorkplaces } from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import './Dashboard.css';

const Dashboard = () => {
  const [expertCount, setExpertCount] = useState(0);
  const [doctorCount, setDoctorCount] = useState(0);
  const [dspCount, setDspCount] = useState(0);
  const [workplaceCount, setWorkplaceCount] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  
  const { user } = useAuth();

  useEffect(() => {
    fetchCounts();
  }, []);

  const fetchCounts = async () => {
    try {
      setLoading(true);
      const [experts, doctors, dsps, workplaces] = await Promise.all([
        getAllExperts(),
        getAllDoctors(),
        getAllDsps(),
        getAllWorkplaces()
      ]);
      
      setExpertCount(experts.length);
      setDoctorCount(doctors.length);
      setDspCount(dsps.length);
      setWorkplaceCount(workplaces.length);
      setError('');
    } catch (err: any) {
      setError('Failed to fetch dashboard data: ' + err.message);
      console.error('Dashboard error:', err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="dashboard">Loading...</div>;
  }

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>
      
      {user && (
        <div className="welcome-message">
          <h2>Welcome, {user.fullName}!</h2>
          {user.organizationId && (
            <p>Organization ID: {user.organizationId}</p>
          )}
        </div>
      )}
      
      {error && <div className="error-message">{error}</div>}
      
      <div className="dashboard-stats">
        <div className="stat-card">
          <h3>Experts</h3>
          <p className="stat-number">{expertCount}</p>
          <Link to="/experts" className="stat-link">View Experts</Link>
        </div>
        
        <div className="stat-card">
          <h3>Doctors</h3>
          <p className="stat-number">{doctorCount}</p>
          <Link to="/doctors" className="stat-link">View Doctors</Link>
        </div>
        
        <div className="stat-card">
          <h3>DSPs</h3>
          <p className="stat-number">{dspCount}</p>
          <Link to="/dsps" className="stat-link">View DSPs</Link>
        </div>
        
        <div className="stat-card">
          <h3>Workplaces</h3>
          <p className="stat-number">{workplaceCount}</p>
          <Link to="/workplaces" className="stat-link">View Workplaces</Link>
        </div>
      </div>
      
      <div className="dashboard-actions">
        <Link to="/visits" className="action-button">Manage Visits</Link>
      </div>
    </div>
  );
};

export default Dashboard;
