import React, { useState } from 'react';
import './DataImport.css';

const DataImport = () => {
  const [importSource, setImportSource] = useState('web');
  const [webUrl, setWebUrl] = useState('');
  const [file, setFile] = useState<File | null>(null);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setFile(e.target.files[0]);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (importSource === 'web' && !webUrl) {
      setError('Please enter a valid URL');
      return;
    }
    
    if (importSource === 'file' && !file) {
      setError('Please select a file');
      return;
    }
    
    // Simulate import process
    setSuccess('Data import started successfully. This may take a few moments...');
    
    // Reset form after success
    setTimeout(() => {
      setWebUrl('');
      setFile(null);
      setSuccess('');
    }, 3000);
  };

  return (
    <div className="data-import-container">
      <div className="window">
        <div className="window-header">
          <span>Data Import</span>
        </div>
        <div className="window-content">
          {success && <div className="alert alert-info">{success}</div>}
          {error && <div className="alert alert-warning">{error}</div>}
          
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">Import Source:</label>
              <div>
                <label>
                  <input
                    type="radio"
                    name="importSource"
                    value="web"
                    checked={importSource === 'web'}
                    onChange={() => setImportSource('web')}
                  />
                  Web URL
                </label>
                <label style={{ marginLeft: '20px' }}>
                  <input
                    type="radio"
                    name="importSource"
                    value="file"
                    checked={importSource === 'file'}
                    onChange={() => setImportSource('file')}
                  />
                  Excel File
                </label>
              </div>
            </div>
            
            {importSource === 'web' ? (
              <div className="form-group">
                <label className="form-label">Web URL:</label>
                <input
                  type="url"
                  className="form-input"
                  value={webUrl}
                  onChange={(e) => setWebUrl(e.target.value)}
                  placeholder="https://example.com/data.xlsx"
                />
              </div>
            ) : (
              <div className="form-group">
                <label className="form-label">Excel File:</label>
                <input
                  type="file"
                  className="form-input"
                  accept=".xlsx,.xls"
                  onChange={handleFileChange}
                />
              </div>
            )}
            
            <button type="submit" className="btn btn-primary">Import Data</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default DataImport;
