import React, { useState, useEffect } from 'react';
import { createWorkplace, getAllWorkplaces } from '../services/api';

interface Workplace {
  id: number;
  name: string;
  address: string;
  sskRegistrationNo: string;
  taxOffice: string;
  taxNumber: string;
  price: string;
  riskLevel: string;
  employeeCount: string;
  source: string;
  assignedExpertId: string;
  assignedDoctorId: string;
  assignedDspId: string;
  registrationDate: string;
  approvalStatus: string;
}

const DashboardTest: React.FC = () => {
  const [workplaces, setWorkplaces] = useState<Workplace[]>([]);
  const [testStatus, setTestStatus] = useState<string>('Not started');
  const [testWorkplaceId, setTestWorkplaceId] = useState<number | null>(null);

  // Function to simulate the user workflow
  const runTest = async () => {
    try {
      setTestStatus('Starting test...');
      
      // 1. Get initial workplaces count
      setTestStatus('1. Getting initial workplaces...');
      const initialWorkplaces = await getAllWorkplaces();
      const initialCount = initialWorkplaces.length;
      console.log('Initial workplaces count:', initialCount);
      
      // 2. Create a new workplace
      setTestStatus('2. Creating test workplace...');
      const newWorkplace = await createWorkplace({
        name: 'Test Workplace for Navigation Issue',
        address: 'Test Address',
        sskRegistrationNo: '1234567890',
        taxOffice: 'Test Tax Office',
        taxNumber: '1234567890',
        price: 1000,
        riskLevel: 'low',
        employeeCount: 10,
        source: 'Test Source',
        approvalStatus: 'atama'
      });
      
      setTestWorkplaceId(newWorkplace.id);
      console.log('Created workplace with ID:', newWorkplace.id);
      
      // 3. Get workplaces after creation
      setTestStatus('3. Getting workplaces after creation...');
      const workplacesAfterCreation = await getAllWorkplaces();
      const countAfterCreation = workplacesAfterCreation.length;
      console.log('Workplaces count after creation:', countAfterCreation);
      
      const createdWorkplaceFound = workplacesAfterCreation.find(w => w.id === newWorkplace.id);
      if (createdWorkplaceFound) {
        setTestStatus('✓ Test workplace found after creation');
        console.log('Test workplace found after creation:', createdWorkplaceFound.name);
      } else {
        setTestStatus('✗ ERROR: Test workplace NOT found after creation');
        console.log('ERROR: Test workplace NOT found after creation');
        console.log('All workplaces:', workplacesAfterCreation.map(w => ({id: w.id, name: w.name})));
        return;
      }
      
      // 4. Simulate "navigation away" by clearing state
      setTestStatus('4. Simulating navigation away...');
      setWorkplaces([]);
      await new Promise(resolve => setTimeout(resolve, 1000)); // Wait 1 second
      
      // 5. Simulate "navigation back" by fetching data again
      setTestStatus('5. Simulating navigation back...');
      const workplacesAfterNavigation = await getAllWorkplaces();
      const countAfterNavigation = workplacesAfterNavigation.length;
      console.log('Workplaces count after navigation:', countAfterNavigation);
      
      const workplaceAfterNavigation = workplacesAfterNavigation.find(w => w.id === newWorkplace.id);
      if (workplaceAfterNavigation) {
        setTestStatus('✓ SUCCESS: Test workplace still found after navigation');
        console.log('SUCCESS: Test workplace still found after navigation:', workplaceAfterNavigation.name);
        setWorkplaces(workplacesAfterNavigation);
      } else {
        setTestStatus('✗ FAILURE: Test workplace disappeared after navigation');
        console.log('FAILURE: Test workplace disappeared after navigation');
        console.log('All workplaces after navigation:', workplacesAfterNavigation.map(w => ({id: w.id, name: w.name})));
        setWorkplaces(workplacesAfterNavigation);
        return;
      }
      
      // 6. Clean up
      setTestStatus('6. Cleaning up test workplace...');
      // In a real test, we would delete the workplace here
      // But for this simulation, we'll just report success
      setTestStatus('✓ TEST COMPLETED SUCCESSFULLY');
      
    } catch (error) {
      console.error('Test error:', error);
      setTestStatus('✗ TEST FAILED: ' + (error as Error).message);
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h2>Dashboard Navigation Test</h2>
      <p>Status: {testStatus}</p>
      <button onClick={runTest} style={{ padding: '10px 20px', fontSize: '16px' }}>
        Run Navigation Test
      </button>
      
      {workplaces.length > 0 && (
        <div style={{ marginTop: '20px' }}>
          <h3>Current Workplaces ({workplaces.length}):</h3>
          <ul>
            {workplaces.map(workplace => (
              <li key={workplace.id}>
                {workplace.name} (ID: {workplace.id})
                {testWorkplaceId === workplace.id && ' ← TEST WORKPLACE'}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default DashboardTest;
