import React, { useState, useEffect } from 'react';
import { createDoctor, getAllDoctors, deleteDoctor, updateDoctor } from '../services/api';
import './DoctorCardPage.css';
import { useNavigate, useLocation } from 'react-router-dom';

// Define interface for form data
interface FormData {
  firstName: string;
  lastName: string;
  phone: string;
  assignedMinutes: number;
}

// Define interface for doctor data
interface Doctor {
  id: number;

  firstName: string;
  lastName: string;
  phone: string;
  assignedMinutes: number;
  usedMinutes?: number;
  assignedWorkplace?: string;
}

const DoctorCardPage = () => {
  const [formData, setFormData] = useState<FormData>({
    firstName: '',
    lastName: '',
    phone: '',
    assignedMinutes: 11900
  });
  const [doctors, setDoctors] = useState<Doctor[]>([]);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  
  // Validation functions
  const isValidName = (name: string) => {
    return /^[a-zA-ZğüşıöçĞÜŞİÖÇ\s]+$/.test(name);
  };

  const isValidPhone = (phone: string) => {
    return /^5\d{2}\s\d{3}\s\d{2}\s\d{2}$/.test(phone);
  };

  // Format phone number as user types
  const formatPhoneNumber = (value: string) => {
    // Remove all non-digit characters
    const digits = value.replace(/\D/g, '');
    
    // Limit to 10 digits
    const limitedDigits = digits.substring(0, 10);
    
    // Format as 5xx xxx xx xx
    let formatted = '';
    for (let i = 0; i < limitedDigits.length; i++) {
      if (i === 3 || i === 6 || i === 8) {
        formatted += ' ';
      }
      formatted += limitedDigits[i];
    }
    
    return formatted;
  };

  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    fetchDoctors();
  }, []);

  // Add useEffect to refresh data when location changes (navigation)
  useEffect(() => {
    console.log('DoctorCardPage: Location changed to:', location.pathname, 'at:', new Date().toISOString());
    fetchDoctors();
  }, [location.pathname]);

  // Add useEffect to refresh data when window regains focus
  useEffect(() => {
    const handleFocus = () => {
      console.log('DoctorCardPage: Window focused, refreshing doctors...');
      fetchDoctors();
    };

    window.addEventListener('focus', handleFocus);
    return () => {
      window.removeEventListener('focus', handleFocus);
    };
  }, []);

  // Add useEffect to refresh data when workplace is created
  useEffect(() => {
    const handleWorkplaceCreated = () => {
      console.log('DoctorCardPage: Workplace created, refreshing doctors...');
      fetchDoctors();
    };

    window.addEventListener('workplaceCreated', handleWorkplaceCreated);
    return () => {
      window.removeEventListener('workplaceCreated', handleWorkplaceCreated);
    };
  }, []);

  const fetchDoctors = async () => {
    try {
      const data = await getAllDoctors();
      setDoctors(data);
    } catch (err) {
      console.error('Hekimler getirilirken hata oluştu:', err);
    }
  };

  // Function to get assigned workplace names for a doctor
  const getAssignedWorkplaceNames = (doctorId: number) => {
    // This would require a new endpoint to get workplaces assigned to a doctor
    // For now, we'll return a placeholder
    return 'Atanmamış';
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    
    // Special handling for name fields - prevent numbers
    if (name === 'firstName' || name === 'lastName') {
      // Remove any numeric characters
      const cleanValue = value.replace(/\d/g, '');
      setFormData(prev => ({
        ...prev,
        [name]: cleanValue
      }));
    }
    // Special handling for phone number formatting
    else if (name === 'phone') {
      const formattedPhone = formatPhoneNumber(value);
      setFormData(prev => ({
        ...prev,
        [name]: formattedPhone
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: name === 'assignedMinutes' ? (value === '' ? 0 : parseInt(value) || 0) : value
      }));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    // Validation checks
    if (!isValidName(formData.firstName)) {
      setError('Ad sadece harf içermelidir');
      clearNotifications();
      return;
    }
    
    if (!isValidName(formData.lastName)) {
      setError('Soyad sadece harf içermelidir');
      clearNotifications();
      return;
    }
    
    if (!isValidPhone(formData.phone)) {
      setError('Telefon numarası 5xx xxx xx xx formatında olmalıdır');
      clearNotifications();
      return;
    }
    
    if (formData.assignedMinutes > 11900) {
      setError('Atanan dakika 11.900\'ü geçemez');
      clearNotifications();
      return;
    }
    
    try {
      await createDoctor(formData);
      setSuccess('Hekim başarıyla kaydedildi!');
      setFormData({
        firstName: '',
        lastName: '',
        phone: '',
        assignedMinutes: 11900
      });
      // Refresh the doctors list
      fetchDoctors();
      clearNotifications();
    } catch (err: any) {
      console.error('Create doctor error:', err);
      const errorMessage = err.message || 'Hekim kaydedilirken hata oluştu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  // Clear notifications after timeout
  const clearNotifications = () => {
    setTimeout(() => {
      setSuccess('');
      setError('');
    }, 3000);
  };

  const handleDelete = async (id: number) => {
    // Show confirmation dialog before deleting
    const confirmed = window.confirm('Bu hekim kaydını silmek istediğinize emin misiniz?');
    if (!confirmed) {
      return; // User cancelled the deletion
    }
    
    try {
      await deleteDoctor(id);
      setSuccess('Hekim başarıyla silindi!');
      // Refresh the doctors list
      fetchDoctors();
      
      // Close modal if it was open for this doctor
      if (selectedDoctor && selectedDoctor.id === id) {
        setIsModalOpen(false);
        setSelectedDoctor(null);
      }
      
      clearNotifications();
    } catch (err: any) {
      console.error('Delete error:', err);
      const errorMessage = err.message || 'Hekim silinirken hata oluştu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  const handleClose = () => {
    // Redirect to dashboard
    navigate('/');
  };

  // Function to open modal with doctor details
  const openDoctorDetails = (doctor: Doctor) => {
    setSelectedDoctor(doctor);
    setIsEditMode(false);
    setIsModalOpen(true);
  };

  // Function to open modal in edit mode
  const openEditDoctor = (doctor: Doctor) => {
    setSelectedDoctor(doctor);
    setEditFormData({
      firstName: doctor.firstName || '',
      lastName: doctor.lastName || '',
      phone: doctor.phone || '',
      assignedMinutes: doctor.assignedMinutes || 11900
    });
    setIsEditMode(true);
    setIsModalOpen(true);
  };

  // Function to close modal
  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedDoctor(null);
    setIsEditMode(false);
  };

  // State for modal dialog
  const [selectedDoctor, setSelectedDoctor] = useState<Doctor | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isEditMode, setIsEditMode] = useState(false);
  
  // State for edit form data
  const [editFormData, setEditFormData] = useState<FormData>({
    firstName: '',
    lastName: '',
    phone: '',
    assignedMinutes: 11900
  });
  
  const handleEditFormDataChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    
    // Special handling for name fields - prevent numbers
    if (name === 'firstName' || name === 'lastName') {
      // Remove any numeric characters
      const cleanValue = value.replace(/\d/g, '');
      setEditFormData((prev) => ({
        ...prev,
        [name]: cleanValue
      }));
    }
    // Special handling for phone number formatting
    else if (name === 'phone') {
      const formattedPhone = formatPhoneNumber(value);
      setEditFormData((prev) => ({
        ...prev,
        [name]: formattedPhone
      }));
    } else {
      setEditFormData((prev) => ({
        ...prev,
        [name]: name === 'assignedMinutes' ? (value === '' ? 0 : parseInt(value) || 0) : value
      }));
    }
  };
  
  const handleUpdateDoctor = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedDoctor) return;
    
    // Validation checks for edit form
    if (!isValidName(editFormData.firstName)) {
      setError('Ad sadece harf içermelidir');
      clearNotifications();
      return;
    }
    
    if (!isValidName(editFormData.lastName)) {
      setError('Soyad sadece harf içermelidir');
      clearNotifications();
      return;
    }
    
    if (!isValidPhone(editFormData.phone)) {
      setError('Telefon numarası 5xx xxx xx xx formatında olmalıdır');
      clearNotifications();
      return;
    }
    
    if (editFormData.assignedMinutes > 11900) {
      setError('Atanan dakika 11.900\'ü geçemez');
      clearNotifications();
      return;
    }
    
    try {
      await updateDoctor(selectedDoctor.id, editFormData);
      setSuccess('Hekim başarıyla güncellendi!');
      closeModal();
      fetchDoctors(); // Refresh the doctors list
      
      clearNotifications();
    } catch (err: any) {
      console.error('Update error:', err);
      const errorMessage = err.message || 'Hekim güncellenirken hata oluştu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  // Function to handle clicking on a doctor row
  const handleDoctorClick = (doctor: Doctor) => {
    openDoctorDetails(doctor);
  };

  return (
    <div className="doctor-card-page">
      {/* Popup notifications - only show at the top level */}
      {success && (
        <div className="popup-notification alert alert-info">
          {success}
        </div>
      )}
      {error && (
        <div className="popup-notification alert alert-warning">
          {error}
        </div>
      )}
      
      {/* "Yeni Hekim Kayıt" card (fixed position and size like DSP card) */}
      <div 
        className="draggable-card"
        style={{
          position: 'absolute',
          left: 0,
          top: -90,
          width: 400,
          height: 320,
          zIndex: 100
        }}
      >
        <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
          <div 
            className="window-header"
            style={{ userSelect: 'none' }}
          >
            <span>Yeni Hekim Kayıt</span>
            <div className="window-controls">
              <button className="window-button" onClick={handleClose}>×</button>
            </div>
          </div>
          <div className="window-content" style={{ flex: 1, overflow: 'visible' }}>
            <form onSubmit={handleSubmit} className="registration-form">
              <div className="form-row">
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Ad:</label>
                    <input
                      type="text"
                      name="firstName"
                      className="form-input"
                      value={formData.firstName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Soyad:</label>
                    <input
                      type="text"
                      name="lastName"
                      className="form-input"
                      value={formData.lastName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
              </div>
              
              <div className="form-group">
                <label className="form-label">Telefon:</label>
                <input
                  type="text"
                  name="phone"
                  className="form-input"
                  value={formData.phone}
                  onChange={handleChange}
                  placeholder="5xx xxx xx xx"
                  required
                />
              </div>
              
              <div className="form-group">
                <label className="form-label">Atanan Dakika:</label>
                <input
                  type="number"
                  name="assignedMinutes"
                  className="form-input"
                  value={formData.assignedMinutes}
                  onChange={handleChange}
                  min="0"
                  max="11900"
                  required
                />
              </div>
              
              <button type="submit" className="btn btn-primary">Hekim Kaydet</button>
            </form>
          </div>
        </div>
      </div>
      
      {/* Display registered doctors in individual rows positioned on the right side of the page */}
      <div className="registered-doctors-container" style={{ 
        position: 'absolute',
        right: '450px',
        top: '0px',
        width: '300px',
      
      }}>
        {doctors.map((doctor) => (
          <div
            key={doctor.id}
            onClick={() => handleDoctorClick(doctor)}
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              padding: '15px',
              margin: '10px 0',
              borderRadius: '8px',
              cursor: 'pointer',
              transition: 'all 0.3s ease',
              position: 'relative',
              top: '0px',
              left: '0px',
              width: '100%',
              // Using blue color scheme for all doctors (similar to B class experts)
              backgroundColor: '#bbdefb' // Blue background
            }}
            onMouseEnter={(e) => {
              // Enhanced hover effect with more vibrant blue
              e.currentTarget.style.backgroundColor = '#90caf9'; // More vibrant blue on hover
              e.currentTarget.style.boxShadow = '0 4px 8px rgba(33, 150, 243, 0.3)';
              e.currentTarget.style.transform = 'translateY(-2px)';
            }}
            onMouseLeave={(e) => {
              // Reset to original blue color when not hovering
              e.currentTarget.style.backgroundColor = '#bbdefb';
              e.currentTarget.style.boxShadow = 'none';
              e.currentTarget.style.transform = 'translateY(0)';
            }}
          >
            <div className="doctor-info-display" style={{ fontSize: '14px' }}>
              <div className="doctor-name-display">{doctor.firstName} {doctor.lastName}</div>
              <div className="doctor-phone-display">{doctor.phone}</div>
            </div>
            <div 
              className="doctor-class-display"
              style={{
                // Blue badge for all doctors
                backgroundColor: '#1976d2',
                color: 'white',
                fontSize: '12px',
                fontWeight: 'bold',
                padding: '2px 8px',
                borderRadius: '12px'
              }}
            >
              H
            </div>
          </div>
        ))}
      </div>
      
      {/* Doctor Details Modal */}
      {isModalOpen && selectedDoctor && (
        <div className="modal-overlay" onClick={(e) => {
          // Only close modal if clicking on the overlay (not the content)
          if (e.target === e.currentTarget) {
            closeModal();
          }
        }}>
          <div 
            className="modal-content draggable-card" 
            onClick={(e) => e.stopPropagation()}
            style={{
              width: 400,
              height: 500,
              maxWidth: '90vw',
              maxHeight: '90vh',
              position: 'absolute',
              left: '50%',
              top: '50%',
              transform: 'translate(-50%, -50%)',
              margin: 0,
              zIndex: 2001
            }}
          >
            <div className="modal-header">
              <h2>{isEditMode ? 'Hekim Düzenle' : 'Hekim Detayları'}</h2>
              <button className="modal-close" onClick={closeModal}>×</button>
            </div>
            <div className="modal-body">
              {isEditMode ? (
                <form onSubmit={handleUpdateDoctor} className="edit-form">
                  <div className="form-row">
                    <div className="form-col">
                      <div className="form-group">
                        <label className="form-label">Ad:</label>
                        <input
                          type="text"
                          name="firstName"
                          className="form-input"
                          value={editFormData.firstName}
                          onChange={handleEditFormDataChange}
                          required
                        />
                      </div>
                    </div>
                    <div className="form-col">
                      <div className="form-group">
                        <label className="form-label">Soyad:</label>
                        <input
                          type="text"
                          name="lastName"
                          className="form-input"
                          value={editFormData.lastName}
                          onChange={handleEditFormDataChange}
                          required
                        />
                      </div>
                    </div>
                  </div>
                  
                  <div className="form-group">
                    <label className="form-label">Telefon:</label>
                    <input
                      type="text"
                      name="phone"
                      className="form-input"
                      value={editFormData.phone}
                      onChange={handleEditFormDataChange}
                      placeholder="5xx xxx xx xx"
                      required
                    />
                  </div>
                  
                  <div className="form-group">
                    <label className="form-label">Atanan Dakika:</label>
                    <input
                      type="number"
                      name="assignedMinutes"
                      className="form-input"
                      value={editFormData.assignedMinutes}
                      onChange={handleEditFormDataChange}
                      min="0"
                      max="11900"
                      required
                    />
                  </div>
                  
                  <div className="modal-actions">
                    <button type="submit" className="btn btn-primary">Güncelle</button>
                    <button type="button" className="btn" onClick={closeModal}>İptal</button>
                  </div>
                </form>
              ) : (
                // View mode
                <>
                  <div className="doctor-detail-info">
                    <div className="doctor-detail-row">
                      <strong>Ad:</strong> {selectedDoctor.firstName}
                    </div>
                    <div className="doctor-detail-row">
                      <strong>Soyad:</strong> {selectedDoctor.lastName}
                    </div>
                    <div className="doctor-detail-row">
                      <strong>Telefon:</strong> {selectedDoctor.phone}
                    </div>
                    <div className="doctor-detail-row">
                      <strong>Toplam Dakika:</strong> {selectedDoctor.assignedMinutes}
                    </div>
                    <div className="doctor-detail-row">
                      <strong>Atanan Dakika:</strong> {selectedDoctor.usedMinutes || 0}
                    </div>
                    <div className="doctor-detail-row">
                      <strong>Kalan Dakika:</strong> {selectedDoctor.assignedMinutes - (selectedDoctor.usedMinutes || 0)}
                    </div>
                    
                    {/* Workplace assignment information */}
                    <div className="doctor-detail-row">
                      <strong>Atandığı İş Yeri:</strong> {getAssignedWorkplaceNames(selectedDoctor.id)}
                    </div>
                  </div>
                  
                  <div className="modal-actions">
                    <button 
                      className="btn btn-primary" 
                      onClick={() => openEditDoctor(selectedDoctor)}
                    >
                      Düzenle
                    </button>
                    <button 
                      className="btn btn-danger" 
                      onClick={() => {
                        const confirmed = window.confirm('Bu hekim kaydını silmek istediğinize emin misiniz?');
                        if (confirmed) {
                          handleDelete(selectedDoctor.id);
                        }
                      }}
                    >
                      Sil
                    </button>
                    <button className="btn btn-close" onClick={closeModal}>Kapat</button>
                  </div>
                </>
              )}

            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default DoctorCardPage;
