import React, { useState } from 'react';
import { createExpert } from '../services/api';
import './Registration.css';

const ExpertRegistration = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    expertiseClass: 'A',
    assignedMinutes: 11900
  });
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'assignedMinutes' ? parseInt(value) : value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (formData.assignedMinutes > 11900) {
      setError('Assigned minutes cannot exceed 11,900');
      return;
    }
    
    try {
      await createExpert(formData);
      setSuccess('Expert registered successfully!');
      setFormData({
        firstName: '',
        lastName: '',
        phone: '',
        expertiseClass: 'A',
        assignedMinutes: 11900
      });
    } catch (err) {
      setError('Error registering expert');
    }
  };

  return (
    <div className="registration-container">
      <div className="window">
        <div className="window-header">
          <span>Expert Registration</span>
        </div>
        <div className="window-content">
          {success && <div className="alert alert-info">{success}</div>}
          {error && <div className="alert alert-warning">{error}</div>}
          
          <form onSubmit={handleSubmit} className="registration-form">
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">First Name:</label>
                  <input
                    type="text"
                    name="firstName"
                    className="form-input"
                    value={formData.firstName}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Last Name:</label>
                  <input
                    type="text"
                    name="lastName"
                    className="form-input"
                    value={formData.lastName}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
            </div>
            
            <div className="form-group">
              <label className="form-label">Phone:</label>
              <input
                type="text"
                name="phone"
                className="form-input"
                value={formData.phone}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Expertise Class:</label>
                  <select
                    name="expertiseClass"
                    className="form-select"
                    value={formData.expertiseClass}
                    onChange={handleChange}
                  >
                    <option value="A">A</option>
                    <option value="B">B</option>
                    <option value="C">C</option>
                  </select>
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Assigned Minutes:</label>
                  <input
                    type="number"
                    name="assignedMinutes"
                    className="form-input"
                    value={formData.assignedMinutes}
                    onChange={handleChange}
                    min="0"
                    max="11900"
                    required
                  />
                </div>
              </div>
            </div>
            
            <button type="submit" className="btn btn-primary">Register Expert</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ExpertRegistration;
