import React, { useState, useEffect } from 'react';
import './Dashboard.css';
import { useNavigate } from 'react-router-dom';
import { createWorkplace, getAllWorkplaces, updateWorkplace, deleteWorkplace, getWorkplaceById } from '../services/api';

const Dashboard = () => {
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    sskRegistrationNo: '',
    taxOffice: '',
    taxNumber: '',
    price: '',
    riskLevel: 'low',
    employeeCount: 0,
    assignedExpertId: '',
    assignedDoctorId: '',
    assignedDspId: '',
    source: '',
    approvalStatus: 'atama'
  });
  
  const [isChecked, setIsChecked] = useState(false);
  const [workplaces, setWorkplaces] = useState<any[]>([]);
  const [selectedWorkplace, setSelectedWorkplace] = useState<any>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalFormData, setModalFormData] = useState<any>({});
  const navigate = useNavigate();

  // Fetch workplaces on component mount
  useEffect(() => {
    fetchWorkplaces();
  }, []);

  const fetchWorkplaces = async () => {
    try {
      const data = await getAllWorkplaces();
      setWorkplaces(data);
    } catch (err) {
      console.error('Error fetching workplaces:', err);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const formatSSKNumber = (value: string) => {
    // Remove all non-digit characters
    const digits = value.replace(/\D/g, '');
    
    // Apply formatting: x xxxx xxxx xxxxx xxx xx xx xxx xx (1+4+4+5+3+2+2+3+2 = 26 digits)
    let formatted = '';
    for (let i = 0; i < digits.length && i < 26; i++) {
      if (i === 1 || i === 5 || i === 9 || i === 14 || i === 17 || i === 19 || i === 21 || i === 24) {
        formatted += ' ';
      }
      formatted += digits[i];
    }
    
    return formatted;
  };

  const handleSSkRegistrationNoChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    // Allow only numeric input and apply formatting
    const value = e.target.value;
    const formattedValue = formatSSKNumber(value);
    setFormData(prev => ({
      ...prev,
      sskRegistrationNo: formattedValue
    }));
  };

  const handleCheckboxChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setIsChecked(e.target.checked);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // Form submission logic will be implemented here
    console.log('Form submitted:', formData);
  };

  const handleClose = () => {
    // Redirect to login page
    navigate('/');
  };

  const getRiskLevelText = (riskLevel: string) => {
    switch (riskLevel) {
      case 'low': return 'Az Tehlikeli';
      case 'dangerous': return 'Tehlikeli';
      case 'veryDangerous': return 'Çok Tehlikeli';
      default: return riskLevel;
    }
  };

  // Handle row click to open modal
  const handleRowClick = async (workplace: any) => {
    try {
      const detailedWorkplace = await getWorkplaceById(workplace.id);
      setSelectedWorkplace(detailedWorkplace);
      setModalFormData(detailedWorkplace);
      setIsModalOpen(true);
    } catch (err) {
      console.error('Error fetching workplace details:', err);
    }
  };

  // Handle modal form changes
  const handleModalChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setModalFormData((prev: any) => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle modal form submission
  const handleModalSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const dataToSend = {
        ...modalFormData,
        price: parseFloat(modalFormData.price),
        employeeCount: parseInt(modalFormData.employeeCount.toString()),
        assignedExpertId: modalFormData.assignedExpertId ? parseInt(modalFormData.assignedExpertId) : null,
        assignedDoctorId: modalFormData.assignedDoctorId ? parseInt(modalFormData.assignedDoctorId) : null,
        assignedDspId: modalFormData.assignedDspId ? parseInt(modalFormData.assignedDspId) : null
      };
      
      await updateWorkplace(selectedWorkplace.id, dataToSend);
      console.log('Workplace updated successfully');
      
      // Refresh workplaces list
      fetchWorkplaces();
      
      // Close modal
      setIsModalOpen(false);
    } catch (err) {
      console.error('Error updating workplace:', err);
    }
  };

  // Handle workplace deletion
  const handleDeleteWorkplace = async () => {
    if (window.confirm('Bu iş yerini silmek istediğinizden emin misiniz?')) {
      try {
        await deleteWorkplace(selectedWorkplace.id);
        console.log('Workplace deleted successfully');
        
        // Refresh workplaces list
        fetchWorkplaces();
        
        // Close modal
        setIsModalOpen(false);
      } catch (err) {
        console.error('Error deleting workplace:', err);
      }
    }
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h2 className="dashboard-title">Yeni İş Yeri Kaydı</h2>
      </div>
      
      {/* Existing form */}
      <div className="form-container">
        <form onSubmit={handleSubmit} className="workplace-form">
          <div className="form-row">
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">İş Yeri Adı:</label>
                <input
                  type="text"
                  name="name"
                  className="form-input small-input"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">SGK Kayıt No:</label>
                <input
                  type="text"
                  name="sskRegistrationNo"
                  className="form-input ssk-input"
                  value={formData.sskRegistrationNo}
                  onChange={handleSSkRegistrationNoChange}
                  placeholder="x xxxx xxxx xxxxx xxx xx xx xxx xx"
                  maxLength={30} // 26 digits + 4 spaces
                  required
                />
              </div>
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Adres:</label>
                <textarea
                  name="address"
                  className="form-textarea smaller-textarea"
                  value={formData.address}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Fiyat:</label>
                <input
                  type="number"
                  name="price"
                  className="form-input small-input"
                  value={formData.price}
                  onChange={handleChange}
                  step="0.01"
                  required
                />
              </div>
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Vergi Dairesi:</label>
                <input
                  type="text"
                  name="taxOffice"
                  className="form-input small-input"
                  value={formData.taxOffice}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Vergi No:</label>
                <input
                  type="text"
                  name="taxNumber"
                  className="form-input small-input"
                  value={formData.taxNumber}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Tehlike Sınıfı:</label>
                <select
                  name="riskLevel"
                  className="form-select small-select"
                  value={formData.riskLevel}
                  onChange={handleChange}
                >
                  <option value="low">Az Tehlikeli</option>
                  <option value="dangerous">Tehlikeli</option>
                  <option value="veryDangerous">Çok Tehlikeli</option>
                </select>
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Çalışan Sayısı:</label>
                <input
                  type="number"
                  name="employeeCount"
                  className="form-input small-input"
                  value={formData.employeeCount}
                  onChange={handleChange}
                  min="0"
                  required
                />
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Kaynak:</label>
                <input
                  type="text"
                  name="source"
                  className="form-input small-input"
                  value={formData.source}
                  onChange={handleChange}
                />
              </div>
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Atanan Uzman:</label>
                <input
                  type="text"
                  name="assignedExpertId"
                  className="form-input small-input"
                  value={formData.assignedExpertId}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Atanan Hekim:</label>
                <input
                  type="text"
                  name="assignedDoctorId"
                  className="form-input small-input"
                  value={formData.assignedDoctorId}
                  onChange={handleChange}
                />
              </div>
            </div>
          </div>
          
          <div className={`form-row ${isChecked ? 'completed' : ''}`}>
            <div className="form-col">
              <div className="form-group">
                <label className="form-label">Atanan DSP:</label>
                <input
                  type="text"
                  name="assignedDspId"
                  className="form-input small-input"
                  value={formData.assignedDspId}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="form-col checkbox-col">
              <div className="form-group">
                <label className="form-label">Tamamlandı:</label>
                <input
                  type="checkbox"
                  name="completed"
                  className="form-checkbox"
                  checked={isChecked}
                  onChange={handleCheckboxChange}
                />
              </div>
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-col form-actions">
              <button type="button" className="btn btn-secondary" onClick={handleClose}>
                İptal
              </button>
              <button type="submit" className="btn btn-primary">
                Kaydet
              </button>
            </div>
          </div>
        </form>
      </div>
      
      {/* Modal for workplace details */}
      {isModalOpen && (
        <div className="modal-overlay" onClick={() => setIsModalOpen(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>İş Yeri Detayları</h3>
              <button className="modal-close" onClick={() => setIsModalOpen(false)}>×</button>
            </div>
            <div className="modal-body">
              <form onSubmit={handleModalSubmit}>
                <div className="form-group">
                  <label className="form-label">İş Yeri Adı:</label>
                  <input
                    type="text"
                    name="name"
                    className="form-input"
                    value={modalFormData.name || ''}
                    onChange={handleModalChange}
                    required
                  />
                </div>
                
                <div className="form-group">
                  <label className="form-label">Adres:</label>
                  <textarea
                    name="address"
                    className="form-textarea"
                    value={modalFormData.address || ''}
                    onChange={handleModalChange}
                    required
                  />
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">SGK Kayıt No:</label>
                      <input
                        type="text"
                        name="sskRegistrationNo"
                        className="form-input"
                        value={modalFormData.sskRegistrationNo || ''}
                        onChange={handleModalChange}
                        required
                      />
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Fiyat:</label>
                      <input
                        type="number"
                        name="price"
                        className="form-input"
                        value={modalFormData.price || ''}
                        onChange={handleModalChange}
                        step="0.01"
                        required
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Vergi Dairesi:</label>
                      <input
                        type="text"
                        name="taxOffice"
                        className="form-input"
                        value={modalFormData.taxOffice || ''}
                        onChange={handleModalChange}
                        required
                      />
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Vergi No:</label>
                      <input
                        type="text"
                        name="taxNumber"
                        className="form-input"
                        value={modalFormData.taxNumber || ''}
                        onChange={handleModalChange}
                        required
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Tehlike Sınıfı:</label>
                      <select
                        name="riskLevel"
                        className="form-select"
                        value={modalFormData.riskLevel || 'low'}
                        onChange={handleModalChange}
                      >
                        <option value="low">Az Tehlikeli</option>
                        <option value="dangerous">Tehlikeli</option>
                        <option value="veryDangerous">Çok Tehlikeli</option>
                      </select>
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Çalışan Sayısı:</label>
                      <input
                        type="number"
                        name="employeeCount"
                        className="form-input"
                        value={modalFormData.employeeCount || 0}
                        onChange={handleModalChange}
                        min="0"
                        required
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Atanan Uzman:</label>
                      <input
                        type="text"
                        name="assignedExpertId"
                        className="form-input"
                        value={modalFormData.assignedExpertId || ''}
                        onChange={handleModalChange}
                      />
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Atanan Hekim:</label>
                      <input
                        type="text"
                        name="assignedDoctorId"
                        className="form-input"
                        value={modalFormData.assignedDoctorId || ''}
                        onChange={handleModalChange}
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-group">
                  <label className="form-label">Atanan DSP:</label>
                  <input
                    type="text"
                    name="assignedDspId"
                    className="form-input"
                    value={modalFormData.assignedDspId || ''}
                    onChange={handleModalChange}
                  />
                </div>
                
                <div className="form-group">
                  <label className="form-label">Onay Durumu:</label>
                  <select
                    name="approvalStatus"
                    className="form-select"
                    value={modalFormData.approvalStatus || 'atama'}
                    onChange={handleModalChange}
                  >
                    <option value="atama">Atama yapılacak</option>
                    <option value="bekliyor">Onay bekliyor</option>
                    <option value="onaylandi">Onaylandı</option>
                  </select>
                </div>
                
                <div className="form-group">
                  <label className="form-label">Kaynak:</label>
                  <input
                    type="text"
                    name="source"
                    className="form-input"
                    value={modalFormData.source || ''}
                    onChange={handleModalChange}
                  />
                </div>
                
                <div className="modal-actions">
                  <button type="button" className="btn btn-danger" onClick={handleDeleteWorkplace}>
                    Sil
                  </button>
                  <button type="button" className="btn btn-secondary" onClick={() => setIsModalOpen(false)}>
                    İptal
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Güncelle
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
      
      {/* 10 rows with numbers to the right of the registration card */}
      <div className="rows-container">
        {[...Array(10)].map((_, index) => (
          <div key={index} className="row-item">
            <span className="row-number">{index + 1}.</span>
            <span className="row-label">İş Yeri Adı</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
