import React, { useState, useEffect } from 'react';
import { createExpert, getAllExperts, deleteExpert, updateExpert } from '../services/api';
import './ExpertCardPage.css';
import { useNavigate } from 'react-router-dom';

const ExpertCardPage = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    expertiseClass: 'A',
    assignedMinutes: 11900
  });
  const [experts, setExperts] = useState<any[]>([]);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  
  // Load saved positions from localStorage or use defaults
  const getSavedCardState = (cardType: 'new' | 'list') => {
    try {
      const savedState = localStorage.getItem(`expertCard_${cardType}_state`);
      if (savedState) {
        const parsedState = JSON.parse(savedState);
        // Validate that the parsed state has the required properties
        if (typeof parsedState === 'object' && parsedState !== null &&
            'x' in parsedState && 'y' in parsedState && 
            'width' in parsedState && 'height' in parsedState) {
          return {
            ...parsedState,
            isDragging: false,
            isResizing: false,
            resizeDirection: '',
            dragStartX: 0,
            dragStartY: 0,
            resizeStartX: 0,
            resizeStartY: 0
          };
        }
      }
    } catch (e) {
      // If there's an error parsing, we'll use the default state
      console.warn(`Failed to parse saved state for ${cardType} card, using defaults`);
    }
    
    // Default positions
    if (cardType === 'new') {
      return {
        x: 50,
        y: 50,  // Changed from -175 to 50 to avoid covering navbar
        width: 400,
        height: 500,
        isDragging: false,
        isResizing: false,
        resizeDirection: '',
        dragStartX: 0,
        dragStartY: 0,
        resizeStartX: 0,
        resizeStartY: 0,
        originalX: 50,
        originalY: 50,  // Changed from -175 to 50 to avoid covering navbar
        originalWidth: 400,
        originalHeight: 500
      };
    } else {
      return {
        x: 500,
        y: 50,  // Changed from -175 to 50 to avoid covering navbar
        width: 500,
        height: 500,
        isDragging: false,
        isResizing: false,
        resizeDirection: '',
        dragStartX: 0,
        dragStartY: 0,
        resizeStartX: 0,
        resizeStartY: 0,
        originalX: 500,
        originalY: 50,  // Changed from -175 to 50 to avoid covering navbar
        originalWidth: 500,
        originalHeight: 500
      };
    }
  };

  // Get saved modal state or use defaults
  const getSavedModalState = () => {
    try {
      const savedState = localStorage.getItem('expertModal_state');
      if (savedState) {
        const parsedState = JSON.parse(savedState);
        // Validate that the parsed state has the required properties
        if (typeof parsedState === 'object' && parsedState !== null &&
            'width' in parsedState && 'height' in parsedState) {
          return {
            ...parsedState,
            isResizing: false,
            resizeDirection: '',
            resizeStartX: 0,
            resizeStartY: 0,
            originalWidth: parsedState.width,
            originalHeight: parsedState.height
          };
        }
      }
    } catch (e) {
      console.warn('Failed to parse saved modal state, using defaults');
    }
    
    // Default modal size with centered position
    const defaultWidth = 400;
    const defaultHeight = 500;
    return {
      width: defaultWidth,
      height: defaultHeight,
      originalX: (window.innerWidth - defaultWidth) / 2,
      originalY: (window.innerHeight - defaultHeight) / 2,
      isResizing: false,
      resizeDirection: '',
      resizeStartX: 0,
      resizeStartY: 0,
      originalWidth: defaultWidth,
      originalHeight: defaultHeight
    };
  };

  // State for Expert Modal
  const [modalState, setModalState] = useState(getSavedModalState());
  
  const navigate = useNavigate();

  // Validation functions
  const isValidName = (name: string) => {
    return /^[a-zA-ZğüşıöçĞÜŞİÖÇ\s]+$/.test(name);
  };

  const isValidPhone = (phone: string) => {
    return /^5\d{2}\s\d{3}\s\d{2}\s\d{2}$/.test(phone);
  };

  // Format phone number as user types
  const formatPhoneNumber = (value: string) => {
    // Remove all non-digit characters
    const digits = value.replace(/\D/g, '');
    
    // Limit to 10 digits
    const limitedDigits = digits.substring(0, 10);
    
    // Format as 5xx xxx xx xx
    let formatted = '';
    for (let i = 0; i < limitedDigits.length; i++) {
      if (i === 3 || i === 6 || i === 8) {
        formatted += ' ';
      }
      formatted += limitedDigits[i];
    }
    
    return formatted;
  };

  // Function to handle clicking on an expert row
  // This function opens the expert details modal when an expert row is clicked
  const handleExpertClick = (expert: any) => {
    openExpertDetails(expert);
  };

  useEffect(() => {
    fetchExperts();
  }, []);

  const fetchExperts = async () => {
    try {
      const data = await getAllExperts();
      setExperts(data);
    } catch (err: any) {
      console.error('Uzmanlar getirilirken hata oluştu:', err);
      setError(err.message || 'Uzmanlar getirilirken hata oluştu');
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    
    // Special handling for name fields - prevent numbers
    if (name === 'firstName' || name === 'lastName') {
      // Remove any numeric characters
      const cleanValue = value.replace(/\d/g, '');
      setFormData((prev: typeof formData) => ({
        ...prev,
        [name]: cleanValue
      }));
    }
    // Special handling for phone number formatting
    else if (name === 'phone') {
      const formattedPhone = formatPhoneNumber(value);
      setFormData((prev: typeof formData) => ({
        ...prev,
        [name]: formattedPhone
      }));
    } else {
      setFormData((prev: typeof formData) => ({
        ...prev,
        [name]: name === 'assignedMinutes' ? parseInt(value) : value
      }));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    // Validation checks
    if (!isValidName(formData.firstName)) {
      setError('Ad sadece harf içermelidir');
      clearNotifications();
      return;
    }
    
    if (!isValidName(formData.lastName)) {
      setError('Soyad sadece harf içermelidir');
      clearNotifications();
      return;
    }
    
    if (!isValidPhone(formData.phone)) {
      setError('Telefon numarası 5xx xxx xx xx formatında olmalıdır');
      clearNotifications();
      return;
    }
    
    if (formData.assignedMinutes > 11900) {
      setError('Atanan dakika 11.900\'ü geçemez');
      clearNotifications();
      return;
    }
    
    try {
      await createExpert(formData);
      setSuccess('Uzman başarıyla kaydedildi!');
      setFormData({
        firstName: '',
        lastName: '',
        phone: '',
        expertiseClass: 'A',
        assignedMinutes: 11900
      });
      // Refresh the experts list
      fetchExperts();
      clearNotifications();
    } catch (err: any) {
      console.error('Create expert error:', err);
      const errorMessage = err.message || 'Uzman kaydedilirken hata oluştu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  // Clear notifications after timeout
  const clearNotifications = () => {
    setTimeout(() => {
      setSuccess('');
      setError('');
    }, 3000);
  };

  const handleDelete = async (id: number) => {
    // Show confirmation dialog before deleting
    const confirmed = window.confirm('Bu uzman kaydını silmek istediğinize emin misiniz?');
    if (!confirmed) {
      return; // User cancelled the deletion
    }
    
    try {
      await deleteExpert(id);
      setSuccess('Uzman başarıyla silindi!');
      // Refresh the experts list
      fetchExperts();
      
      // Close modal if it was open for this expert
      if (selectedExpert && selectedExpert.id === id) {
        setIsModalOpen(false);
        setSelectedExpert(null);
      }
      
      clearNotifications();
    } catch (err: any) {
      console.error('Delete error:', err);
      const errorMessage = err.message || 'Uzman silinirken hata oluştu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  const handleClose = () => {
    // Redirect to dashboard
    navigate('/dashboard');
  };

  // Function to open modal with expert details
  const openExpertDetails = (expert: any) => {
    setSelectedExpert(expert);
    setIsEditMode(false);
    setIsModalOpen(true);
    // Reset modal position to center when opening
    const defaultWidth = 400;
    const defaultHeight = 500;
    setModalState((prev: typeof modalState) => ({
      ...prev,
      width: defaultWidth,
      height: defaultHeight,
      originalX: (window.innerWidth - defaultWidth) / 2,
      originalY: (window.innerHeight - defaultHeight) / 2,
      originalWidth: defaultWidth,
      originalHeight: defaultHeight
    }));
  };

  // Function to open modal in edit mode
  const openEditExpert = (expert: any) => {
    setSelectedExpert(expert);
    setEditFormData({
      firstName: expert.firstName || '',
      lastName: expert.lastName || '',
      phone: expert.phone || '',
      expertiseClass: expert.expertiseClass || 'A',
      assignedMinutes: expert.assignedMinutes || 11900
    });
    setIsEditMode(true);
    setIsModalOpen(true);
    // Reset modal position to center when opening
    const defaultWidth = 400;
    const defaultHeight = 500;
    setModalState((prev: typeof modalState) => ({
      ...prev,
      width: defaultWidth,
      height: defaultHeight,
      originalX: (window.innerWidth - defaultWidth) / 2,
      originalY: (window.innerHeight - defaultHeight) / 2,
      originalWidth: defaultWidth,
      originalHeight: defaultHeight
    }));
  };

  // Function to close modal
  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedExpert(null);
    setIsEditMode(false);
  };

  // Function to handle edit action from modal
  const handleEditExpert = async (expert: any) => {
    // This function was a placeholder, but we'll keep it for consistency
    // The actual update is handled by handleUpdateExpert
    console.log('Edit expert called for:', expert);
  };

  // State for modal dialog
  const [selectedExpert, setSelectedExpert] = useState<any>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isEditMode, setIsEditMode] = useState(false);
  
  // State for edit form data
  const [editFormData, setEditFormData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    expertiseClass: 'A',
    assignedMinutes: 11900
  });
  
  const handleEditFormDataChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    
    // Special handling for name fields in edit form - prevent numbers
    if (name === 'firstName' || name === 'lastName') {
      // Remove any numeric characters
      const cleanValue = value.replace(/\d/g, '');
      setEditFormData((prev: typeof editFormData) => ({
        ...prev,
        [name]: cleanValue
      }));
    }
    // Special handling for phone number formatting in edit form
    else if (name === 'phone') {
      const formattedPhone = formatPhoneNumber(value);
      setEditFormData((prev: typeof editFormData) => ({
        ...prev,
        [name]: formattedPhone
      }));
    } else {
      setEditFormData((prev: typeof editFormData) => ({
        ...prev,
        [name]: name === 'assignedMinutes' ? parseInt(value) : value
      }));
    }
  };
  
  const handleUpdateExpert = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedExpert) return;
    
    // Validation checks for edit form
    if (!isValidName(editFormData.firstName)) {
      setError('Ad sadece harf içermelidir');
      clearNotifications();
      return;
    }
    
    if (!isValidName(editFormData.lastName)) {
      setError('Soyad sadece harf içermelidir');
      clearNotifications();
      return;
    }
    
    if (!isValidPhone(editFormData.phone)) {
      setError('Telefon numarası 5xx xxx xx xx formatında olmalıdır');
      clearNotifications();
      return;
    }
    
    try {
      await updateExpert(selectedExpert.id, editFormData);
      setSuccess('Uzman başarıyla güncellendi!');
      closeModal();
      fetchExperts(); // Refresh the experts list
      
      clearNotifications();
    } catch (err: any) {
      console.error('Update error:', err);
      const errorMessage = err.message || 'Uzman güncellenirken hata oluştu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  const handleEdit = async (id: number) => {
    // Find the expert by id
    const expert = experts.find(exp => exp.id === id);
    if (expert) {
      openEditExpert(expert);
    } else {
      setError('Uzman bulunamadı');
    }
  };

  // Mouse event handlers
  const handleMouseDown = (e: React.MouseEvent, cardType: 'new' | 'list' | 'modal', action: 'drag' | 'resize', direction: string = '') => {
    e.preventDefault();
    e.stopPropagation();
    
    // Only support drag and resize for modal since main cards are fixed
    if (cardType === 'modal') {
      if (action === 'drag') {
        // For modal dragging, we need to get the current position
        setModalState((prev: typeof modalState) => ({
          ...prev,
          isDragging: true,
          dragStartX: e.clientX - (prev.originalX || 0),
          dragStartY: e.clientY - (prev.originalY || 0)
        }));
      } else if (action === 'resize') {
        // Get the current modal position relative to the viewport
        const modalElement = e.currentTarget.closest('.modal-content');
        if (modalElement) {
          const rect = modalElement.getBoundingClientRect();
          const currentX = rect.left;
          const currentY = rect.top;
          
          setModalState((prev: typeof modalState) => ({
            ...prev,
            isResizing: true,
            resizeDirection: direction,
            resizeStartX: e.clientX,
            resizeStartY: e.clientY,
            originalX: currentX,
            originalY: currentY,
            originalWidth: prev.width,
            originalHeight: prev.height
          }));
        }
      }
      
      // Ensure global event listeners are attached
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    }
    // Ignore drag and resize for 'new' and 'list' cards since they are fixed
  };

  const handleMouseMove = (e: MouseEvent) => {
    // Handle resizing for Expert Modal
    if (modalState.isResizing) {
      const deltaX = e.clientX - modalState.resizeStartX;
      const deltaY = e.clientY - modalState.resizeStartY;
      
      let newWidth = modalState.originalWidth;
      let newHeight = modalState.originalHeight;
      let newX = modalState.originalX || 0;
      let newY = modalState.originalY || 0;
      
      // Handle different resize directions
      if (modalState.resizeDirection.includes('right')) {
        newWidth = Math.max(300, modalState.originalWidth + deltaX);
      }
      if (modalState.resizeDirection.includes('left')) {
        newWidth = Math.max(300, modalState.originalWidth - deltaX);
        newX = modalState.originalX + deltaX;
      }
      if (modalState.resizeDirection.includes('bottom')) {
        newHeight = Math.max(200, modalState.originalHeight + deltaY);
      }
      if (modalState.resizeDirection.includes('top')) {
        newHeight = Math.max(200, modalState.originalHeight - deltaY);
        newY = modalState.originalY + deltaY;
      }
      
      setModalState((prev: typeof modalState) => ({
        ...prev,
        width: newWidth,
        height: newHeight,
        originalWidth: newWidth,
        originalHeight: newHeight,
        originalX: newX,
        originalY: newY
      }));
    }
    
    // Handle dragging for Expert Modal
    if (modalState.isDragging) {
      const newX = e.clientX - modalState.dragStartX;
      const newY = e.clientY - modalState.dragStartY;
      setModalState((prev: typeof modalState) => ({
        ...prev,
        originalX: newX,
        originalY: newY
      }));
    }
  };

  const handleMouseUp = () => {
    setModalState((prev: typeof modalState) => {
      const newState = { ...prev, isDragging: false, isResizing: false, resizeDirection: '' };
      // Save the modal state to localStorage
      localStorage.setItem('expertModal_state', JSON.stringify(newState));
      return newState;
    });
  };

  useEffect(() => {
    // Define cleanup function
    const cleanup = () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
    
    // Always remove any existing listeners first to ensure clean state
    cleanup();
    
    // Only add global event listeners when actually needed
    if (modalState.isDragging || modalState.isResizing) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    }
    
    // Cleanup function to remove event listeners when component unmounts or dependencies change
    return cleanup;
  }, [modalState.isDragging, modalState.isResizing]);

  // Calculate statistics for the new card
  const calculateExpertStats = () => {
    const stats = {
      aClassCount: 0,
      bClassCount: 0,
      cClassCount: 0,
      aClassMinutes: 0,
      bClassMinutes: 0,
      cClassMinutes: 0,
      aClassRemaining: 0,
      bClassRemaining: 0,
      cClassRemaining: 0
    };

    experts.forEach(expert => {
      switch (expert.expertiseClass) {
        case 'A':
          stats.aClassCount++;
          stats.aClassMinutes += expert.assignedMinutes || 0;
          stats.aClassRemaining += expert.assignedMinutes || 0;
          break;
        case 'B':
          stats.bClassCount++;
          stats.bClassMinutes += expert.assignedMinutes || 0;
          stats.bClassRemaining += expert.assignedMinutes || 0;
          break;
        case 'C':
          stats.cClassCount++;
          stats.cClassMinutes += expert.assignedMinutes || 0;
          stats.cClassRemaining += expert.assignedMinutes || 0;
          break;
      }
    });

    return stats;
  };

  const expertStats = calculateExpertStats();

  return (
    <div className="expert-card-page">
      {/* Popup notifications */}
      {success && (
        <div className="popup-notification alert alert-info">
          {success}
        </div>
      )}
      {error && (
        <div className="popup-notification alert alert-warning">
          {error}
        </div>
      )}
      
      {/* "Yeni Uzman Kayıt" card (fixed position and size) */}
      <div 
        className="draggable-card"
        style={{
          position: 'absolute',
          left: 0,
          top: -90,
          width: 400,
          height: 375,
          zIndex: 100
        }}
      >
        <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
          <div 
            className="window-header"
            style={{ userSelect: 'none' }}
          >
            <span>Yeni Uzman Kayıt</span>
          </div>
          <div className="window-content" style={{ flex: 1, overflow: 'visible' }}>
            <form onSubmit={handleSubmit} className="registration-form">
              <div className="form-row">
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Ad:</label>
                    <input
                      type="text"
                      name="firstName"
                      className="form-input"
                      value={formData.firstName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Soyad:</label>
                    <input
                      type="text"
                      name="lastName"
                      className="form-input"
                      value={formData.lastName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
              </div>
              
              <div className="form-group">
                <label className="form-label">Telefon:</label>
                <input
                  type="text"
                  name="phone"
                  className="form-input"
                  value={formData.phone}
                  onChange={handleChange}
                  placeholder="5xx xxx xx xx"
                  required
                />
              </div>
              
              <div className="form-group">
                <label className="form-label">Uzmanlık Sınıfı:</label>
                <select
                  name="expertiseClass"
                  className="form-select"
                  value={formData.expertiseClass}
                  onChange={handleChange}
                  required
                >
                  <option value="A">A Sınıfı</option>
                  <option value="B">B Sınıfı</option>
                  <option value="C">C Sınıfı</option>
                </select>
              </div>
              
              <div className="form-group">
                <label className="form-label">Atanan Dakika:</label>
                <input
                  type="number"
                  name="assignedMinutes"
                  className="form-input"
                  value={formData.assignedMinutes}
                  onChange={handleChange}
                  min="0"
                  max="11900"
                  required
                />
              </div>
              
              <button type="submit" className="btn btn-primary">Uzmanı Kaydet</button>
            </form>
          </div>
        </div>
      </div>
      
      {/* Statistics card for expert classes */}
      <div 
        className="draggable-card"
        style={{
          position: 'absolute',
          right: 0, // Positioned to the right edge of the page
          top: -90,
          width: 300,
          height: 375,
          zIndex: 100
        }}
      >
        <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
          <div 
            className="window-header"
            style={{ userSelect: 'none' }}
          >
            <span>Uzman İstatistikleri</span>
          </div>
          <div className="window-content" style={{ flex: 1, overflow: 'visible', padding: '15px' }}>
            <div className="stats-container">
              <div className="stats-section">
                <h3>Kayıtlı Uzman Sayıları</h3>
                <div className="stat-item">
                  <span className="stat-label">A Sınıfı:</span>
                  <span className="stat-value">{expertStats.aClassCount}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">B Sınıfı:</span>
                  <span className="stat-value">{expertStats.bClassCount}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">C Sınıfı:</span>
                  <span className="stat-value">{expertStats.cClassCount}</span>
                </div>
              </div>
              
              <div className="stats-section">
                <h3>Tanımlanan Dakikalar</h3>
                <div className="stat-item">
                  <span className="stat-label">A Sınıfı:</span>
                  <span className="stat-value">{expertStats.aClassMinutes}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">B Sınıfı:</span>
                  <span className="stat-value">{expertStats.bClassMinutes}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">C Sınıfı:</span>
                  <span className="stat-value">{expertStats.cClassMinutes}</span>
                </div>
              </div>
              
              <div className="stats-section">
                <h3>Kalan Dakikalar</h3>
                <div className="stat-item">
                  <span className="stat-label">A Sınıfı:</span>
                  <span className="stat-value">{expertStats.aClassRemaining}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">B Sınıfı:</span>
                  <span className="stat-value">{expertStats.bClassRemaining}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">C Sınıfı:</span>
                  <span className="stat-value">{expertStats.cClassRemaining}</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {/* Display registered experts in individual rows positioned on the right side of the page */}
      <div className="registered-experts-container" style={{ 
        position: 'absolute',
        right: '50px',
        top: '100px',
        width: '300px'  // Reduced width from 400px to 300px
      }}>
        {experts.map((expert) => (
          <div
            key={expert.id}
            onClick={() => handleExpertClick(expert)}
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              padding: '15px',
              margin: '10px 0',
              borderRadius: '8px',
              cursor: 'pointer',
              transition: 'all 0.3s ease',
              // Reset positioning to normal size and remove previous offsets
              position: 'relative',
              top: '-100px',
              left: '-400px',
              width: '100%',  // Full width within the container
              // Color coding based on expertise class - B class is now blue
              ...(expert.expertiseClass === 'A' && { 
                backgroundColor: '#ffcdd2' // More vibrant red for A class
              }),
              ...(expert.expertiseClass === 'B' && { 
                backgroundColor: '#bbdefb' // Blue for B class
              }),
              ...(expert.expertiseClass === 'C' && { 
                backgroundColor: '#c8e6c9' // More vibrant green for C class
              })
            }}
            onMouseEnter={(e) => {
              // Enhanced hover effect with color changes
              if (expert.expertiseClass === 'A') {
                e.currentTarget.style.backgroundColor = '#ef9a9a'; // Even more vibrant red on hover
                e.currentTarget.style.boxShadow = '0 4px 8px rgba(211, 47, 47, 0.3)';
                e.currentTarget.style.transform = 'translateY(-2px)';
              } else if (expert.expertiseClass === 'B') {
                e.currentTarget.style.backgroundColor = '#90caf9'; // More vibrant blue on hover
                e.currentTarget.style.boxShadow = '0 4px 8px rgba(33, 150, 243, 0.3)';
                e.currentTarget.style.transform = 'translateY(-2px)';
              } else if (expert.expertiseClass === 'C') {
                e.currentTarget.style.backgroundColor = '#a5d6a7'; // Even more vibrant green on hover
                e.currentTarget.style.boxShadow = '0 4px 8px rgba(56, 142, 60, 0.3)';
                e.currentTarget.style.transform = 'translateY(-2px)';
              }
            }}
            onMouseLeave={(e) => {
              // Reset to original colors when not hovering
              if (expert.expertiseClass === 'A') {
                e.currentTarget.style.backgroundColor = '#ffcdd2';
                e.currentTarget.style.boxShadow = 'none';
                e.currentTarget.style.transform = 'translateY(0)';
              } else if (expert.expertiseClass === 'B') {
                e.currentTarget.style.backgroundColor = '#bbdefb';
                e.currentTarget.style.boxShadow = 'none';
                e.currentTarget.style.transform = 'translateY(0)';
              } else if (expert.expertiseClass === 'C') {
                e.currentTarget.style.backgroundColor = '#c8e6c9';
                e.currentTarget.style.boxShadow = 'none';
                e.currentTarget.style.transform = 'translateY(0)';
              }
            }}
          >
            <div className="expert-info-display" style={{ fontSize: '15px' }}>
              <div className="expert-name-display">{expert.firstName} {expert.lastName}</div>
              <div className="expert-phone-display">{expert.phone}</div>
            </div>
            <div 
              className="expert-class-display"
              style={{
                // Color coding for expertise class badge - B class is now blue
                ...(expert.expertiseClass === 'A' && { 
                  backgroundColor: '#d32f2f',
                  color: 'white'
                }),
                ...(expert.expertiseClass === 'B' && { 
                  backgroundColor: '#1976d2',
                  color: 'white'
                }),
                ...(expert.expertiseClass === 'C' && { 
                  backgroundColor: '#388e3c',
                  color: 'white'
                })
              }}
            >
              {expert.expertiseClass}
            </div>
          </div>
        ))}
      </div>
      
      {/* Expert Details Modal */}
      {isModalOpen && selectedExpert && (
        <div className="modal-overlay" onClick={(e) => {
          // Only close modal if clicking on the overlay (not the content)
          if (e.target === e.currentTarget) {
            closeModal();
          }
        }}>
          <div 
            className="modal-content draggable-card" 
            onClick={(e) => e.stopPropagation()}
            style={{
              width: modalState.width,
              height: modalState.height,
              maxWidth: '90vw',
              maxHeight: '90vh',
              position: 'absolute',
              left: '50%',
              top: '50%',
              transform: 'translate(-50%, -50%)',
              margin: 0,
              zIndex: 2001
            }}
          >
            <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
              <div 
                className="window-header"
                onMouseDown={(e) => handleMouseDown(e, 'modal', 'drag')}
                style={{ cursor: 'move', userSelect: 'none' }}
              >
                <span>{isEditMode ? 'Uzmanı Düzenle' : 'Uzman Detayları'}</span>
                <div className="window-controls">
                  <button className="window-button" onClick={closeModal} aria-label="Kapat">×</button>
                </div>
              </div>
              <div className="window-content">
                {isEditMode ? (
                  // Edit form
                  <form onSubmit={handleUpdateExpert} className="edit-form">
                    <div className="form-group">
                      <label className="form-label">Ad:</label>
                      <input
                        type="text"
                        name="firstName"
                        className="form-input"
                        value={editFormData.firstName}
                        onChange={handleEditFormDataChange}
                        required
                      />
                    </div>
                    
                    <div className="form-group">
                      <label className="form-label">Soyad:</label>
                      <input
                        type="text"
                        name="lastName"
                        className="form-input"
                        value={editFormData.lastName}
                        onChange={handleEditFormDataChange}
                        required
                      />
                    </div>
                    
                    <div className="form-group">
                      <label className="form-label">Telefon:</label>
                      <input
                        type="text"
                        name="phone"
                        className="form-input"
                        value={editFormData.phone}
                        onChange={handleEditFormDataChange}
                        placeholder="5xx xxx xx xx"
                        required
                      />
                    </div>
                    
                    <div className="form-row">
                      <div className="form-col">
                        <div className="form-group">
                          <label className="form-label">Uzmanlık Sınıfı:</label>
                          <select
                            name="expertiseClass"
                            className="form-select"
                            value={editFormData.expertiseClass}
                            onChange={handleEditFormDataChange}
                          >
                            <option value="A">A</option>
                            <option value="B">B</option>
                            <option value="C">C</option>
                          </select>
                        </div>
                      </div>
                      <div className="form-col">
                        <div className="form-group">
                          <label className="form-label">Atanan Dakika:</label>
                          <input
                            type="number"
                            name="assignedMinutes"
                            className="form-input"
                            value={editFormData.assignedMinutes}
                            onChange={handleEditFormDataChange}
                            min="0"
                            max="11900"
                            required
                          />
                        </div>
                      </div>
                    </div>
                    
                    <div className="modal-actions">
                      <button type="submit" className="btn btn-primary">Güncelle</button>
                      <button type="button" className="btn" onClick={closeModal}>İptal</button>
                    </div>
                  </form>
                ) : (
                  // View mode
                  <>
                    <div className="expert-detail-info">
                      <div className="expert-detail-row">
                        <strong>Ad:</strong> {selectedExpert.firstName}
                      </div>
                      <div className="expert-detail-row">
                        <strong>Soyad:</strong> {selectedExpert.lastName}
                      </div>
                      <div className="expert-detail-row">
                        <strong>Telefon:</strong> {selectedExpert.phone}
                      </div>
                      <div className="expert-detail-row">
                        <strong>Uzmanlık Sınıfı:</strong> {selectedExpert.expertiseClass}
                      </div>
                      <div className="expert-detail-row">
                        <strong>Toplam Dakika:</strong> {selectedExpert.assignedMinutes}
                      </div>
                      <div className="expert-detail-row">
                        <strong>Atanan Dakika:</strong> 0
                      </div>
                      <div className="expert-detail-row">
                        <strong>Kalan Dakika:</strong> {selectedExpert.assignedMinutes}
                      </div>
                      
                      {/* Workplace assignment information */}
                      <div className="expert-detail-row">
                        <strong>Atandığı İş Yeri:</strong> {selectedExpert.assignedWorkplace ? selectedExpert.assignedWorkplace : 'Atanmamış'}
                      </div>
                    </div>
                    
                    <div className="modal-actions">
                      <button className="btn btn-primary" onClick={() => openEditExpert(selectedExpert)}>Düzenle</button>
                      <button className="btn btn-danger" onClick={() => {
                        const confirmed = window.confirm('Bu uzman kaydını silmek istediğinize emin misiniz?');
                        if (confirmed) {
                          handleDelete(selectedExpert.id);
                        }
                      }}>Sil</button>
                      <button className="btn btn-close" onClick={closeModal}>Kapat</button>
                    </div>
                  </>
                )}
              </div>
            </div>
            {/* Resize handles for modal */}
            <div 
              className="resize-handle resize-nw"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'top-left')}
            />
            <div 
              className="resize-handle resize-ne"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'top-right')}
            />
            <div 
              className="resize-handle resize-sw"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'bottom-left')}
            />
            <div 
              className="resize-handle resize-se"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'bottom-right')}
            />
            <div 
              className="resize-handle resize-n"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'top')}
            />
            <div 
              className="resize-handle resize-s"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'bottom')}
            />
            <div 
              className="resize-handle resize-w"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'left')}
            />
            <div 
              className="resize-handle resize-e"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'right')}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default ExpertCardPage;
