import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import Dashboard from './components/Dashboard';
import ExpertCardPage from './components/ExpertCardPage';
import DoctorCardPage from './components/DoctorCardPage';
import DspCardPage from './components/DspCardPage';
import WorkplacePage from './components/WorkplacePage';
import VisitsPage from './components/VisitsPage';
import LoginPage from './components/LoginPage';
import TestAuthPage from './components/TestAuthPage';
import Navbar from './components/Navbar';
import './App.css';

// Wrapper components to include Navbar with proper routing context
const DashboardPage = () => {
  const { isAuthenticated } = useAuth();
  if (!isAuthenticated) return <Navigate to="/login" />;
  
  return (
    <>
      <Navbar />
      <Dashboard />
    </>
  );
};

const ExpertPage = () => {
  const { isAuthenticated } = useAuth();
  if (!isAuthenticated) return <Navigate to="/login" />;
  
  return (
    <>
      <Navbar />
      <ExpertCardPage />
    </>
  );
};

const DoctorPage = () => {
  const { isAuthenticated } = useAuth();
  if (!isAuthenticated) return <Navigate to="/login" />;
  
  return (
    <>
      <Navbar />
      <DoctorCardPage />
    </>
  );
};

const DspPage = () => {
  const { isAuthenticated } = useAuth();
  if (!isAuthenticated) return <Navigate to="/login" />;
  
  return (
    <>
      <Navbar />
      <DspCardPage />
    </>
  );
};

const WorkplacePageWrapper = () => {
  const { isAuthenticated } = useAuth();
  if (!isAuthenticated) return <Navigate to="/login" />;
  
  return (
    <>
      <Navbar />
      <WorkplacePage />
    </>
  );
};

// Added VisitsPage wrapper
const VisitsPageWrapper = () => {
  const { isAuthenticated } = useAuth();
  if (!isAuthenticated) return <Navigate to="/login" />;
  
  return (
    <>
      <Navbar />
      <VisitsPage />
    </>
  );
};

// Login page wrapper
const LoginPageWrapper = () => {
  const { isAuthenticated } = useAuth();
  if (isAuthenticated) return <Navigate to="/" />;
  return <LoginPage />;
};

// Test auth page wrapper
const TestAuthPageWrapper = () => {
  const { isAuthenticated } = useAuth();
  if (!isAuthenticated) return <Navigate to="/login" />;
  return (
    <>
      <Navbar />
      <TestAuthPage />
    </>
  );
};

function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="app">
          <Routes>
            <Route path="/login" element={<LoginPageWrapper />} />
            <Route path="/test-auth" element={<TestAuthPageWrapper />} />
            <Route path="/" element={<DashboardPage />} />
            <Route path="/experts" element={<ExpertPage />} />
            <Route path="/doctors" element={<DoctorPage />} />
            <Route path="/dsps" element={<DspPage />} />
            <Route path="/workplaces" element={<WorkplacePageWrapper />} />
            <Route path="/visits" element={<VisitsPageWrapper />} />
          </Routes>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
