import React, { useState, useEffect, useRef } from 'react';
import { createPortal } from 'react-dom';
import Navbar from './Navbar';
import './Dashboard.css';
import { createWorkplace, updateWorkplace, getAvailableExperts, getAvailableDoctors, getAvailableDsps, getAllWorkplaces, deleteWorkplace, getAllExperts } from '../services/api';

// Define types
interface Workplace {
  id: number;
  name: string;
  sskRegistrationNo: string;
  address: string;
  price: string;
  taxOffice: string;
  taxNumber: string;
  riskLevel: string;
  employeeCount: string;
  source: string;
  assignedExpertId: string;
  assignedDoctorId: string;
  assignedDspId: string;
  registrationDate: string;
  approvalStatus: string;
}

const Dashboard: React.FC = () => {
  // State declarations
  const [formData, setFormData] = useState({
    name: '',
    sskRegistrationNo: '',
    address: '',
    price: '',
    taxOffice: '',
    taxNumber: '',
    riskLevel: 'low',
    employeeCount: '',
    source: '',
    assignedExpertId: '',
    assignedDoctorId: '',
    assignedDspId: '',
    registrationDate: '',
  });
  
  const [isKdvIncluded, setIsKdvIncluded] = useState<boolean>(false);
  const [workplaces, setWorkplaces] = useState<Workplace[]>([]);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [isChecked, setIsChecked] = useState<boolean>(false);
  const [isModalOpen, setIsModalOpen] = useState<boolean>(false);
  const [modalFormData, setModalFormData] = useState<Partial<Workplace>>({});
  const [sortMenuOpen, setSortMenuOpen] = useState<boolean>(false);
  const [sortCriteria, setSortCriteria] = useState<string>('name');
  const [showNoExpertPopup, setShowNoExpertPopup] = useState<boolean>(false);
  const [showNoDoctorPopup, setShowNoDoctorPopup] = useState<boolean>(false);
  const [showNoDspPopup, setShowNoDspPopup] = useState<boolean>(false);
  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState<boolean>(false);
  const [allExperts, setAllExperts] = useState<any[]>([]);

  const sortButtonRef = useRef<HTMLButtonElement>(null);
  
  // Constants
  const workplacesPerPage = 10;
  
  // Derived state
  const totalPages = Math.ceil(workplaces.length / workplacesPerPage);
  
  // Handlers
  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };
  
  const handlePriceChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    // Allow only numeric values
    if (value === '' || /^\d*\.?\d*$/.test(value)) {
      setFormData(prev => ({
        ...prev,
        price: value
      }));
    }
  };
  
  const handleTaxOfficeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    // Allow only letters and spaces, no numbers
    if (value === '' || /^[a-zA-ZğüşıöçĞÜŞİÖÇ\s]*$/.test(value)) {
      setFormData(prev => ({
        ...prev,
        taxOffice: value
      }));
    }
  };
  
  const handleTaxNumberChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    // Allow only numeric values
    if (value === '' || /^\d*$/.test(value)) {
      setFormData(prev => ({
        ...prev,
        taxNumber: value
      }));
    }
  };
  
  const handleEmployeeCountChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    // Allow only numeric values
    if (value === '' || /^\d*$/.test(value)) {
      setFormData(prev => ({
        ...prev,
        employeeCount: value
      }));
    }
  };
  
  const handleSourceChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    // Allow only letters and spaces, no numbers
    if (value === '' || /^[a-zA-ZğüşıöçĞÜŞİÖÇ\s]*$/.test(value)) {
      setFormData(prev => ({
        ...prev,
        source: value
      }));
    }
  };
  
  const handleAssignedExpertChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    // Allow only letters and spaces, no numbers
    if (value === '' || /^[a-zA-ZğüşıöçĞÜŞİÖÇ\s]*$/.test(value)) {
      setFormData(prev => ({
        ...prev,
        assignedExpertId: value
      }));
    }
  };
  
  const handleAssignedDoctorChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    // Allow only letters and spaces, no numbers
    if (value === '' || /^[a-zA-ZğüşıöçĞÜŞİÖÇ\s]*$/.test(value)) {
      setFormData(prev => ({
        ...prev,
        assignedDoctorId: value
      }));
    }
  };
  
  const handleAssignedDspChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    // Allow only letters and spaces, no numbers
    if (value === '' || /^[a-zA-ZğüşıöçĞÜŞİÖÇ\s]*$/.test(value)) {
      setFormData(prev => ({
        ...prev,
        assignedDspId: value
      }));
    }
  };

  // Format price with thousands separator
  const formatPrice = (price: string | number): string => {
    if (!price) return '0';
    const num = typeof price === 'string' ? parseFloat(price) : price;
    if (isNaN(num)) return '0';
    return num.toLocaleString('tr-TR', { maximumFractionDigits: 2 });
  };
  
  const handleSSkRegistrationNoChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    let value = e.target.value.replace(/\D/g, ''); // Remove non-digit characters
    
    // Format as "x xxxx xx xx xxxxx xxx xx xx xxx"
    if (value.length > 21) value = value.substring(0, 21);
    
    let formattedValue = '';
    for (let i = 0; i < value.length; i++) {
      if (i === 1 || i === 5 || i === 7 || i === 9 || i === 14 || i === 17 || i === 19) {
        formattedValue += ' ' + value[i];
      } else {
        formattedValue += value[i];
      }
    }
    
    setFormData(prev => ({
      ...prev,
      sskRegistrationNo: formattedValue
    }));
  };
  
  const handleCheckboxChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setIsChecked(e.target.checked);
  };
  
  const handleClose = () => {
    setFormData({
      name: '',
      sskRegistrationNo: '',
      address: '',
      price: '',
      taxOffice: '',
      taxNumber: '',
      riskLevel: 'low',
      employeeCount: '',
      source: '',
      assignedExpertId: '',
      assignedDoctorId: '',
      assignedDspId: '',
      registrationDate: '',
    });
    setIsChecked(false);
    setIsKdvIncluded(false);
  };
  
  // Function to calculate required minutes based on risk level and employee count
  const calculateRequiredMinutes = (riskLevel: string, employeeCount: number): number => {
    switch (riskLevel) {
      case 'low':
        return employeeCount * 10;
      case 'dangerous':
        return employeeCount * 20;
      case 'veryDangerous':
        return employeeCount * 40;
      default:
        return 0;
    }
  };
  
  // Function to filter experts based on risk level and required minutes
  const getFilteredExperts = (riskLevel: string, employeeCount: number) => {
    const requiredMinutes = calculateRequiredMinutes(riskLevel, employeeCount);
    
    return allExperts.filter(expert => {
      // Check if expert has enough minutes
      if (expert.assignedMinutes < requiredMinutes) {
        return false;
      }
      
      // Filter based on expertise class and risk level
      // A class experts can handle all risk levels
      // B class experts can handle low and dangerous risk levels
      // C class experts can only handle low risk level
      switch (expert.expertiseClass) {
        case 'A':
          return true; // A class can handle all risk levels
        case 'B':
          return riskLevel === 'low' || riskLevel === 'dangerous'; // B class can handle low and dangerous
        case 'C':
          return riskLevel === 'low'; // C class can only handle low risk
        default:
          return false;
      }
    });
  };
  
  // Function to check if expert assignment is valid
  const checkExpertAssignment = async (workplaceData: any) => {
    try {
      // Create a temporary workplace to get its ID
      const tempWorkplace = await createWorkplace({
        ...workplaceData,
        employeeCount: parseInt(workplaceData.employeeCount) || 0,
        price: parseFloat(workplaceData.price) || 0,
        approvalStatus: 'atama'
      });
      
      // Get available experts for this workplace
      const availableExperts = await getAvailableExperts(tempWorkplace.id);
      
      // Delete the temporary workplace
      await updateWorkplace(tempWorkplace.id, { ...tempWorkplace, deleted: true });
      
      // If no experts available, show popup
      if (availableExperts.length === 0) {
        setShowNoExpertPopup(true);
        return false;
      }
      
      return true;
    } catch (error) {
      console.error('Error checking expert assignment:', error);
      return true; // Allow assignment if there's an error
    }
  };
  
  // Function to check if doctor assignment is valid
  const checkDoctorAssignment = async (workplaceData: any) => {
    try {
      // Create a temporary workplace to get its ID
      const tempWorkplace = await createWorkplace({
        ...workplaceData,
        employeeCount: parseInt(workplaceData.employeeCount) || 0,
        price: parseFloat(workplaceData.price) || 0,
        approvalStatus: 'atama'
      });
      
      // Get available doctors for this workplace
      const availableDoctors = await getAvailableDoctors(tempWorkplace.id);
      
      // Delete the temporary workplace
      await updateWorkplace(tempWorkplace.id, { ...tempWorkplace, deleted: true });
      
      // If no doctors available, show popup
      if (availableDoctors.length === 0) {
        setShowNoDoctorPopup(true);
        return false;
      }
      
      return true;
    } catch (error) {
      console.error('Error checking doctor assignment:', error);
      return true; // Allow assignment if there's an error
    }
  };
  
  // Function to check if DSP assignment is valid
  const checkDspAssignment = async (workplaceData: any) => {
    try {
      // Create a temporary workplace to get its ID
      const tempWorkplace = await createWorkplace({
        ...workplaceData,
        employeeCount: parseInt(workplaceData.employeeCount) || 0,
        price: parseFloat(workplaceData.price) || 0,
        approvalStatus: 'atama'
      });
      
      // Get available DSPs for this workplace
      const availableDsps = await getAvailableDsps(tempWorkplace.id);
      
      // Delete the temporary workplace
      await updateWorkplace(tempWorkplace.id, { ...tempWorkplace, deleted: true });
      
      // If no DSPs available, show popup
      if (availableDsps.length === 0) {
        setShowNoDspPopup(true);
        return false;
      }
      
      return true;
    } catch (error) {
      console.error('Error checking DSP assignment:', error);
      return true; // Allow assignment if there's an error
    }
  };
  
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    try {
      // Ensure all required fields are included
      const workplaceData = {
        ...formData,
        employeeCount: parseInt(formData.employeeCount) || 0,
        price: parseFloat(formData.price) || 0,
        approvalStatus: 'atama'
      };
      
      // Check if personnel assignments are valid
      const isExpertValid = await checkExpertAssignment(workplaceData);
      const isDoctorValid = await checkDoctorAssignment(workplaceData);
      const isDspValid = await checkDspAssignment(workplaceData);
      
      // If any assignment is invalid, don't proceed with saving
      if (!isExpertValid || !isDoctorValid || !isDspValid) {
        return;
      }
      
      // Save to backend
      const newWorkplace = await createWorkplace(workplaceData);
      console.log('Created new workplace:', newWorkplace);
      
      // Add to local state with the id from the backend
      setWorkplaces(prev => {
        const updatedWorkplaces = [...prev, { ...newWorkplace, id: newWorkplace.id }];
        console.log('Updated workplaces state:', updatedWorkplaces);
        return updatedWorkplaces;
      });
      
      // Close the form
      handleClose();
    } catch (error) {
      console.error('Error saving workplace:', error);
      // Still add to local state even if backend fails for demo purposes
      const newWorkplace: Workplace = {
        ...formData,
        id: Date.now(),
        approvalStatus: 'atama'
      } as Workplace;
      
      setWorkplaces(prev => [...prev, newWorkplace]);
      handleClose();
    }
  };
  
  const handlePageChange = (pageNumber: number) => {
    setCurrentPage(pageNumber);
  };
  
  const getCurrentPageWorkplaces = (): Workplace[] => {
    const startIndex = (currentPage - 1) * workplacesPerPage;
    const endIndex = startIndex + workplacesPerPage;
    return workplaces.slice(startIndex, endIndex);
  };
  
  const handleRowClick = (workplace: Workplace) => {
    setModalFormData(workplace);
    setIsModalOpen(true);
    // Prevent background scrolling when modal is open
    document.body.style.overflow = 'hidden';
  };
  
  const handleModalChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    
    // Apply the same formatting logic for SGK registration number in the modal
    if (name === 'sskRegistrationNo') {
      let formattedValue = value.replace(/\D/g, ''); // Remove non-digit characters
      
      // Format as "x xxxx xx xx xxxxx xxx xx xx xxx"
      if (formattedValue.length > 21) formattedValue = formattedValue.substring(0, 21);
      
      let finalFormattedValue = '';
      for (let i = 0; i < formattedValue.length; i++) {
        if (i === 1 || i === 5 || i === 7 || i === 9 || i === 14 || i === 17 || i === 19) {
          finalFormattedValue += ' ' + formattedValue[i];
        } else {
          finalFormattedValue += formattedValue[i];
        }
      }
      
      setModalFormData(prev => ({
        ...prev,
        [name]: finalFormattedValue
      }));
    } else {
      setModalFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };
  
  const handleModalSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    try {
      if (modalFormData.id) {
        // Ensure all required fields are included
        const workplaceData = {
          ...modalFormData,
          employeeCount: parseInt(modalFormData.employeeCount as string) || 0,
          price: parseFloat(modalFormData.price as string) || 0
        };
        
        // Check if personnel assignments are valid
        const isExpertValid = await checkExpertAssignment(workplaceData);
        const isDoctorValid = await checkDoctorAssignment(workplaceData);
        const isDspValid = await checkDspAssignment(workplaceData);
        
        // If any assignment is invalid, don't proceed with saving
        if (!isExpertValid || !isDoctorValid || !isDspValid) {
          return;
        }
        
        // Update existing workplace
        const updatedWorkplace = await updateWorkplace(modalFormData.id, workplaceData);
        setWorkplaces(prev => 
          prev.map(workplace => 
            workplace.id === modalFormData.id ? updatedWorkplace : workplace
          )
        );
      }
      setIsModalOpen(false);
    } catch (error) {
      console.error('Error updating workplace:', error);
      // Fallback to local state update if backend fails
      setWorkplaces(prev => 
        prev.map(workplace => 
          workplace.id === modalFormData.id ? modalFormData as Workplace : workplace
        )
      );
      setIsModalOpen(false);
    }
  };
  
  const handleDeleteWorkplace = () => {
    // Show confirmation dialog before deleting
    setShowDeleteConfirmation(true);
  };

  // Function to confirm deletion
  const confirmDeleteWorkplace = async () => {
    try {
      if (modalFormData.id) {
        // Delete from backend
        await deleteWorkplace(modalFormData.id);
        
        // Delete from local state
        setWorkplaces(prev => prev.filter(workplace => workplace.id !== modalFormData.id));
      }
    } catch (error) {
      console.error('Error deleting workplace:', error);
      // Even if backend fails, still remove from local state for UI consistency
      setWorkplaces(prev => prev.filter(workplace => workplace.id !== modalFormData.id));
    }
    
    setShowDeleteConfirmation(false);
    closeModal();
  };

  // Function to cancel deletion
  const cancelDeleteWorkplace = () => {
    setShowDeleteConfirmation(false);
  };

  const handleApprovalStatusChange = (id: number, status: string) => {
    setWorkplaces(prev => 
      prev.map(workplace => 
        workplace.id === id ? { ...workplace, approvalStatus: status } : workplace
      )
    );
  };
  
  const formatDate = (dateString: string) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString('tr-TR');
  };
  
  const sortWorkplaces = (criteria: string) => {
    console.log('Sorting workplaces by:', criteria); // Add debug log
    setSortCriteria(criteria);
    setSortMenuOpen(false);
    
    const sortedWorkplaces = [...workplaces];
    
    switch (criteria) {
      case 'name':
        sortedWorkplaces.sort((a, b) => 
          (a.name || '').localeCompare(b.name || '')
        );
        break;
      case 'date':
        sortedWorkplaces.sort((a, b) => 
          new Date(b.registrationDate || '').getTime() - new Date(a.registrationDate || '').getTime()
        );
        break;
      case 'approval':
        const statusOrder: { [key: string]: number } = { 'atama': 1, 'bekliyor': 2, 'onaylandi': 3 };
        sortedWorkplaces.sort((a, b) => 
          (statusOrder[a.approvalStatus || 'atama'] || 0) - (statusOrder[b.approvalStatus || 'atama'] || 0)
        );
        break;
      default:
        break;
    }
    
    console.log('Sorted workplaces:', sortedWorkplaces); // Add debug log
    setWorkplaces(sortedWorkplaces);
  };
  
  // Close sort menu when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (sortButtonRef.current && !sortButtonRef.current.contains(event.target as Node)) {
        console.log('Click outside detected, closing sort menu'); // Debug log
        setSortMenuOpen(false);
      }
    };
    
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);
  
  // Load workplaces data from backend on component mount
  useEffect(() => {
    const fetchWorkplaces = async () => {
      try {
        console.log('Fetching workplaces from backend...');
        const data = await getAllWorkplaces();
        console.log('Received workplaces data:', data);
        setWorkplaces(data);
      } catch (error) {
        console.error('Error fetching workplaces:', error);
        // Fallback to sample data if backend fails
        const sampleWorkplaces: Workplace[] = [
          {
            id: 1,
            name: "Örnek İş Yeri 1",
            sskRegistrationNo: "1 2345 67 89 01234 567 89 01 234",
            address: "Örnek Adres 1",
            price: "999",
            taxOffice: "Örnek Vergi Dairesi 1",
            taxNumber: "1234567890",
            riskLevel: "low",
            employeeCount: "10",
            source: "Kaynak 1",
            assignedExpertId: "Uzman 1",
            assignedDoctorId: "Hekim 1",
            assignedDspId: "DSP 1",
            registrationDate: "2023-01-15",
            approvalStatus: "atama"
          },
          {
            id: 2,
            name: "Örnek İş Yeri 2",
            sskRegistrationNo: "2 3456 78 90 12345 678 90 12 345",
            address: "Örnek Adres 2",
            price: "1250",
            taxOffice: "Örnek Vergi Dairesi 2",
            taxNumber: "2345678901",
            riskLevel: "dangerous",
            employeeCount: "20",
            source: "Kaynak 2",
            assignedExpertId: "Uzman 2",
            assignedDoctorId: "Hekim 2",
            assignedDspId: "DSP 2",
            registrationDate: "2023-02-20",
            approvalStatus: "bekliyor"
          },
          {
            id: 3,
            name: "Örnek İş Yeri 3",
            sskRegistrationNo: "3 4567 89 01 23456 789 01 23 456",
            address: "Örnek Adres 3",
            price: "1000000",
            taxOffice: "Örnek Vergi Dairesi 3",
            taxNumber: "3456789012",
            riskLevel: "veryDangerous",
            employeeCount: "30",
            source: "Kaynak 3",
            assignedExpertId: "Uzman 3",
            assignedDoctorId: "Hekim 3",
            assignedDspId: "DSP 3",
            registrationDate: "2023-03-25",
            approvalStatus: "onaylandi"
          }
        ];
        
        setWorkplaces(sampleWorkplaces);
      }
    };
    
    const fetchExperts = async () => {
      try {
        console.log('Fetching experts from backend...');
        const data = await getAllExperts();
        console.log('Received experts data:', data);
        setAllExperts(data);
      } catch (error) {
        console.error('Error fetching experts:', error);
      }
    };
    
    fetchWorkplaces();
    fetchExperts();
  }, []);

  const closeModal = () => {
    setIsModalOpen(false);
    // Re-enable background scrolling when modal is closed
    document.body.style.overflow = 'auto';
  };

  // Handle body overflow when modal state changes
  useEffect(() => {
    if (isModalOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'auto';
    }
    
    // Cleanup function to reset overflow on unmount
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, [isModalOpen]);

  // Handle window resize to keep modal centered
  useEffect(() => {
    const handleResize = () => {
      if (isModalOpen) {
        // Force reflow to ensure modal stays centered
        const modalContent = document.querySelector('.modal-content');
        if (modalContent instanceof HTMLElement) {
          modalContent.style.transform = 'translate(-50%, -50%)';
        }
      }
    };

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [isModalOpen]);
  
  const handleEditWorkplace = () => {
    setWorkplaces(prev => 
      prev.map(workplace => 
        workplace.id === modalFormData.id ? modalFormData as Workplace : workplace
      )
    );
    closeModal();
  };
  
  return (
    <div className="dashboard-container">
      <Navbar />
      <div className="dashboard-header">
        <h2 className="dashboard-title">Yeni İş Yeri Kaydı</h2>
      </div>
      
      {/* No Expert Popup */}
      {showNoExpertPopup && (
        <div className="popup-overlay" onClick={() => setShowNoExpertPopup(false)}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()}>
            <div className="popup-header">
              <h3>Uygun Uzman Bulunmuyor</h3>
              <button className="popup-close" onClick={() => setShowNoExpertPopup(false)}>×</button>
            </div>
            <div className="popup-body">
              <p>Atanacak uygun uzman bulunmamaktadır.</p>
            </div>
            <div className="popup-footer">
              <button className="btn btn-primary" onClick={() => setShowNoExpertPopup(false)}>Tamam</button>
            </div>
          </div>
        </div>
      )}
      
      {/* No Doctor Popup */}
      {showNoDoctorPopup && (
        <div className="popup-overlay" onClick={() => setShowNoDoctorPopup(false)}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()}>
            <div className="popup-header">
              <h3>Uygun Hekim Bulunmuyor</h3>
              <button className="popup-close" onClick={() => setShowNoDoctorPopup(false)}>×</button>
            </div>
            <div className="popup-body">
              <p>Atanacak uygun hekim bulunmamaktadır.</p>
            </div>
            <div className="popup-footer">
              <button className="btn btn-primary" onClick={() => setShowNoDoctorPopup(false)}>Tamam</button>
            </div>
          </div>
        </div>
      )}
      
      {/* No DSP Popup */}
      {showNoDspPopup && (
        <div className="popup-overlay" onClick={() => setShowNoDspPopup(false)}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()}>
            <div className="popup-header">
              <h3>Uygun DSP Bulunmuyor</h3>
              <button className="popup-close" onClick={() => setShowNoDspPopup(false)}>×</button>
            </div>
            <div className="popup-body">
              <p>Atanacak uygun DSP bulunmamaktadır.</p>
            </div>
            <div className="popup-footer">
              <button className="btn btn-primary" onClick={() => setShowNoDspPopup(false)}>Tamam</button>
            </div>
          </div>
        </div>
      )}

      {/* Delete Confirmation Popup */}
      {showDeleteConfirmation && (
        <div className="popup-overlay" onClick={cancelDeleteWorkplace}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()}>
            <div className="popup-header">
              <h3>Kayıt Silme Onayı</h3>
              <button className="popup-close" onClick={cancelDeleteWorkplace}>×</button>
            </div>
            <div className="popup-body">
              <p>Kayıdı gerçekten silmek istiyor musunuz?</p>
            </div>
            <div className="popup-footer">
              <button className="btn btn-secondary" onClick={cancelDeleteWorkplace}>İptal</button>
              <button className="btn btn-danger" onClick={confirmDeleteWorkplace}>Sil</button>
            </div>
          </div>
        </div>
      )}

      <div className="dashboard-content">
        {/* Existing form */}
        <div className="form-container">
          <form onSubmit={handleSubmit} className="workplace-form">
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">İş Yeri Adı:</label>
                  <input
                    type="text"
                    name="name"
                    className="form-input"
                    value={formData.name}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">SGK Kayıt No:</label>
                  <input
                    type="text"
                    name="sskRegistrationNo"
                    className="form-input ssk-input"
                    value={formData.sskRegistrationNo}
                    onChange={handleSSkRegistrationNoChange}
                    placeholder="x xxxx xx xx xxxxx xxx xx xx xxx"
                    maxLength={26} // 21 digits + 4 spaces
                    required
                  />
                </div>
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Adres:</label>
                  <textarea
                    name="address"
                    className="form-textarea smaller-textarea"
                    value={formData.address}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Fiyat:</label>
                  <div className="price-input-container">
                    <input
                      type="text"
                      name="price"
                      className="form-input small-input price-input"
                      value={formData.price}
                      onChange={handlePriceChange}
                      placeholder="0"
                      required
                    />
                    <span className="price-suffix">TL</span>
                  </div>
                  <div className="kdv-checkbox-container">
                    <input
                      type="checkbox"
                      id="kdvIncluded"
                      className="kdv-checkbox"
                      checked={isKdvIncluded}
                      onChange={(e) => setIsKdvIncluded(e.target.checked)}
                    />
                    <label htmlFor="kdvIncluded" className="kdv-label">KDV dahil</label>
                  </div>
                </div>
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Vergi Dairesi:</label>
                  <input
                    type="text"
                    name="taxOffice"
                    className="form-input small-input"
                    value={formData.taxOffice}
                    onChange={handleTaxOfficeChange}
                    required
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Vergi No:</label>
                  <input
                    type="text"
                    name="taxNumber"
                    className="form-input small-input"
                    value={formData.taxNumber}
                    onChange={handleTaxNumberChange}
                    required
                  />
                </div>
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Tehlike Sınıfı:</label>
                  <select
                    name="riskLevel"
                    className="form-select small-select"
                    value={formData.riskLevel}
                    onChange={handleChange}
                  >
                    <option value="low">Az Tehlikeli</option>
                    <option value="dangerous">Tehlikeli</option>
                    <option value="veryDangerous">Çok Tehlikeli</option>
                  </select>
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Çalışan Sayısı:</label>
                  <input
                    type="text"
                    name="employeeCount"
                    className="form-input small-input"
                    value={formData.employeeCount}
                    onChange={handleEmployeeCountChange}
                    min="0"
                    required
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Kaynak:</label>
                  <input
                    type="text"
                    name="source"
                    className="form-input small-input"
                    value={formData.source}
                    onChange={handleSourceChange}
                  />
                </div>
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Atanan Uzman:</label>
                  <input
                    type="text"
                    name="assignedExpertId"
                    className="form-input small-input"
                    value={formData.assignedExpertId}
                    onChange={handleAssignedExpertChange}
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Atanan Hekim:</label>
                  <input
                    type="text"
                    name="assignedDoctorId"
                    className="form-input small-input"
                    value={formData.assignedDoctorId}
                    onChange={handleAssignedDoctorChange}
                  />
                </div>
              </div>
            </div>
            
            <div className={`form-row ${isChecked ? 'completed' : ''}`}>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Atanan DSP:</label>
                  <input
                    type="text"
                    name="assignedDspId"
                    className="form-input small-input"
                    value={formData.assignedDspId}
                    onChange={handleAssignedDspChange}
                  />
                </div>
              </div>
              <div className="form-col">
                <div className="form-group">
                  <label className="form-label">Kayıt Tarihi:</label>
                  <input
                    type="date"
                    name="registrationDate"
                    className="form-input small-input"
                    value={formData.registrationDate}
                    onChange={handleChange}
                  />
                </div>
              </div>
              <div className="form-col checkbox-col">
                <div className="form-group">
                  <label className="form-label">Tamamlandı:</label>
                  <input
                    type="checkbox"
                    name="completed"
                    className="form-checkbox"
                    checked={isChecked}
                    onChange={handleCheckboxChange}
                  />
                </div>
              </div>
              <div className="form-col sort-button-col">
                <button
                  type="button"
                  className="btn btn-danger sort-button"
                  onClick={() => setSortMenuOpen(!sortMenuOpen)}
                  ref={sortButtonRef}
                >
                  Sırala
                </button>
                {sortMenuOpen && (
                  <div className="sort-menu">
                    <button 
                      className="sort-option" 
                      onClick={(e) => {
                        e.stopPropagation();
                        sortWorkplaces('name');
                      }}
                    >
                      İş Yeri Adına Göre
                    </button>
                    <button 
                      className="sort-option" 
                      onClick={(e) => {
                        e.stopPropagation();
                        sortWorkplaces('date');
                      }}
                    >
                      Tarihe Göre
                    </button>
                    <button 
                      className="sort-option" 
                      onClick={(e) => {
                        e.stopPropagation();
                        sortWorkplaces('approval');
                      }}
                    >
                      Onay Durumuna Göre
                    </button>
                  </div>
                )}
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-col form-actions">
                <button type="button" className="btn btn-secondary" onClick={handleClose}>
                  İptal
                </button>
                <button type="submit" className="btn btn-primary">
                  Kaydet
                </button>
              </div>
            </div>
          </form>
        </div>
        
        {/* Rows with pagination - Approval status selection outside of compact row */}
        <div className="rows-container">
          {/* Display header row without container box */}
          <div className="workplaces-list-header">
            <div className="header-row-no-box">
              <span className="row-header-label">İş Yeri Adı</span>
              <span className="row-header-source">Kimden</span>
              <span className="row-header-date">Tarih</span>
              <span className="row-header-price">Fiyat</span>
              <span className="row-header-status">Onay Durumu</span>
            </div>
          </div>
          
          {/* Display current page rows */}
          <div className="workplaces-list">
            {getCurrentPageWorkplaces().map((workplace, index) => (
              <div 
                key={(currentPage - 1) * 10 + index} 
                className="row-item"
                onClick={() => handleRowClick(workplace)}
              >
                <span className="row-label">{workplace.name || 'İş Yeri Adı'}</span>
                <span className="row-source">{workplace.source || 'Belirtilmemiş'}</span>
                <span className="row-date">{formatDate(workplace.registrationDate)}</span>
                <span className="row-price">{formatPrice(workplace.price)} TL</span>
                <select
                  className={`inline-approval-status approval-status-${workplace.approvalStatus || 'atama'}`}
                  value={workplace.approvalStatus || 'atama'}
                  onClick={(e) => e.stopPropagation()}
                  onChange={(e) => handleApprovalStatusChange(workplace.id, (e.target as HTMLSelectElement).value)}
                >
                  <option value="atama">Atama yapılacak</option>
                  <option value="bekliyor">Onay bekleniyor</option>
                  <option value="onaylandi">Onaylandı</option>
                </select>
              </div>
            ))}
          </div>
          
          {/* Page numbers */}
          {totalPages > 1 && (
            <div className="pagination">
              {[...Array(totalPages)].map((_, index) => (
                <button
                  key={index}
                  className={`page-number ${currentPage === index + 1 ? 'active' : ''}`}
                  onClick={() => handlePageChange(index + 1)}
                >
                  {index + 1}
                </button>
              ))}
            </div>
          )}
        </div>
      </div>
      
      {/* Modal for workplace details */}
      {isModalOpen && createPortal(
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>İş Yeri Detayları</h3>
              <button className="modal-close" onClick={closeModal}>
                ×
              </button>
            </div>
            <div className="modal-body">
              <form onSubmit={handleModalSubmit}>
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">İş Yeri Adı:</label>
                      <input
                        type="text"
                        name="name"
                        className="form-input"
                        value={modalFormData.name || ''}
                        onChange={handleModalChange}
                        required
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Adres:</label>
                      <textarea
                        name="address"
                        className="form-textarea"
                        value={modalFormData.address || ''}
                        onChange={handleModalChange}
                        required
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">SGK Kayıt No:</label>
                      <input
                        type="text"
                        name="sskRegistrationNo"
                        className="form-input"
                        value={modalFormData.sskRegistrationNo || ''}
                        onChange={handleModalChange}
                        required
                      />
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Fiyat:</label>
                      <div className="price-input-container">
                        <input
                          type="text"
                          name="price"
                          className="form-input price-input"
                          value={modalFormData.price || ''}
                          onChange={(e) => {
                            const value = e.target.value;
                            // Allow only numeric values
                            if (value === '' || /^\d*\.?\d*$/.test(value)) {
                              setModalFormData(prev => ({
                                ...prev,
                                price: value
                              }));
                            }
                          }}
                          placeholder="0"
                          required
                        />
                        <span className="price-suffix">TL</span>
                      </div>
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Vergi Dairesi:</label>
                      <input
                        type="text"
                        name="taxOffice"
                        className="form-input"
                        value={modalFormData.taxOffice || ''}
                        onChange={(e) => {
                          const value = e.target.value;
                          // Allow only letters and spaces, no numbers
                          if (value === '' || /^[a-zA-ZğüşıöçĞÜŞİÖÇ\s]*$/.test(value)) {
                            setModalFormData(prev => ({
                              ...prev,
                              taxOffice: value
                            }));
                          }
                        }}
                        required
                      />
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Vergi No:</label>
                      <input
                        type="text"
                        name="taxNumber"
                        className="form-input"
                        value={modalFormData.taxNumber || ''}
                        onChange={(e) => {
                          const value = e.target.value;
                          // Allow only numeric values
                          if (value === '' || /^\d*$/.test(value)) {
                            setModalFormData(prev => ({
                              ...prev,
                              taxNumber: value
                            }));
                          }
                        }}
                        required
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Tehlike Sınıfı:</label>
                      <select
                        name="riskLevel"
                        className="form-select"
                        value={modalFormData.riskLevel || 'low'}
                        onChange={handleModalChange}
                      >
                        <option value="low">Az Tehlikeli</option>
                        <option value="dangerous">Tehlikeli</option>
                        <option value="veryDangerous">Çok Tehlikeli</option>
                      </select>
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Çalışan Sayısı:</label>
                      <input
                        type="number"
                        name="employeeCount"
                        className="form-input"
                        value={modalFormData.employeeCount || 0}
                        onChange={(e) => {
                          const value = e.target.value;
                          // Allow only numeric values
                          if (value === '' || /^\d*$/.test(value)) {
                            setModalFormData(prev => ({
                              ...prev,
                              employeeCount: value
                            }));
                          }
                        }}
                        min="0"
                        required
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Atanan Uzman:</label>
                      <input
                        type="text"
                        name="assignedExpertId"
                        className="form-input"
                        value={modalFormData.assignedExpertId || ''}
                        onChange={(e) => {
                          const value = e.target.value;
                          // Allow only letters and spaces, no numbers
                          if (value === '' || /^[a-zA-ZğüşıöçĞÜŞİÖÇ\s]*$/.test(value)) {
                            setModalFormData(prev => ({
                              ...prev,
                              assignedExpertId: value
                            }));
                          }
                        }}
                      />
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Atanan Hekim:</label>
                      <input
                        type="text"
                        name="assignedDoctorId"
                        className="form-input"
                        value={modalFormData.assignedDoctorId || ''}
                        onChange={(e) => {
                          const value = e.target.value;
                          // Allow only letters and spaces, no numbers
                          if (value === '' || /^[a-zA-ZğüşıöçĞÜŞİÖÇ\s]*$/.test(value)) {
                            setModalFormData(prev => ({
                              ...prev,
                              assignedDoctorId: value
                            }));
                          }
                        }}
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Atanan DSP:</label>
                      <input
                        type="text"
                        name="assignedDspId"
                        className="form-input"
                        value={modalFormData.assignedDspId || ''}
                        onChange={(e) => {
                          const value = e.target.value;
                          // Allow only letters and spaces, no numbers
                          if (value === '' || /^[a-zA-ZğüşıöçĞÜŞİÖÇ\s]*$/.test(value)) {
                            setModalFormData(prev => ({
                              ...prev,
                              assignedDspId: value
                            }));
                          }
                        }}
                      />
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Kayıt Tarihi:</label>
                      <input
                        type="date"
                        name="registrationDate"
                        className="form-input"
                        value={modalFormData.registrationDate || ''}
                        onChange={handleModalChange}
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Onay Durumu:</label>
                      <select
                        name="approvalStatus"
                        className="form-select"
                        value={modalFormData.approvalStatus || 'atama'}
                        onChange={handleModalChange}
                      >
                        <option value="atama">Atama yapılacak</option>
                        <option value="bekliyor">Onay bekliyor</option>
                        <option value="onaylandi">Onaylandı</option>
                      </select>
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Kaynak:</label>
                      <input
                        type="text"
                        name="source"
                        className="form-input"
                        value={modalFormData.source || ''}
                        onChange={(e) => {
                          const value = e.target.value;
                          // Allow only letters and spaces, no numbers
                          if (value === '' || /^[a-zA-ZğüşıöçĞÜŞİÖÇ\s]*$/.test(value)) {
                            setModalFormData(prev => ({
                              ...prev,
                              source: value
                            }));
                          }
                        }}
                      />
                    </div>
                  </div>
                </div>
                
                <div className="modal-actions">
                  <button type="button" className="btn btn-secondary" onClick={closeModal}>
                    İptal
                  </button>
                  <button type="button" className="btn btn-danger" onClick={handleDeleteWorkplace}>
                    Sil
                  </button>
                  <button type="button" className="btn btn-primary" onClick={handleEditWorkplace}>
                    Düzenle
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>,
        document.body
      )}
    </div>
  );
};

export default Dashboard;
