import React, { useState, useEffect } from 'react';
import { createDoctor, getAllDoctors, deleteDoctor, updateDoctor } from '../services/api';
import './DoctorCardPage.css';
import { useNavigate } from 'react-router-dom';

const DoctorCardPage = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    assignedMinutes: 11900
  });
  const [doctors, setDoctors] = useState<any[]>([]);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  
  // Load saved positions from localStorage or use defaults
  const getSavedCardState = (cardType: 'new' | 'list') => {
    try {
      const savedState = localStorage.getItem(`doctorCard_${cardType}_state`);
      if (savedState) {
        const parsedState = JSON.parse(savedState);
        // Validate that the parsed state has the required properties
        if (typeof parsedState === 'object' && parsedState !== null &&
            'x' in parsedState && 'y' in parsedState && 
            'width' in parsedState && 'height' in parsedState) {
          return {
            ...parsedState,
            isDragging: false,
            isResizing: false,
            resizeDirection: '',
            dragStartX: 0,
            dragStartY: 0,
            resizeStartX: 0,
            resizeStartY: 0
          };
        }
      }
    } catch (e) {
      // If there's an error parsing, we'll use the default state
      console.warn(`Failed to parse saved state for ${cardType} card, using defaults`);
    }
    
    // Default positions
    if (cardType === 'new') {
      return {
        x: 50,
        y: 150,
        width: 400,
        height: 500,
        isDragging: false,
        isResizing: false,
        resizeDirection: '',
        dragStartX: 0,
        dragStartY: 0,
        resizeStartX: 0,
        resizeStartY: 0,
        originalX: 50,
        originalY: 150,
        originalWidth: 400,
        originalHeight: 500
      };
    } else {
      return {
        x: 500,
        y: 150,
        width: 500,
        height: 500,
        isDragging: false,
        isResizing: false,
        resizeDirection: '',
        dragStartX: 0,
        dragStartY: 0,
        resizeStartX: 0,
        resizeStartY: 0,
        originalX: 500,
        originalY: 150,
        originalWidth: 500,
        originalHeight: 500
      };
    }
  };

  // Get saved modal state or use defaults
  const getSavedModalState = () => {
    try {
      const savedState = localStorage.getItem('doctorModal_state');
      if (savedState) {
        const parsedState = JSON.parse(savedState);
        // Validate that the parsed state has the required properties
        if (typeof parsedState === 'object' && parsedState !== null &&
            'width' in parsedState && 'height' in parsedState) {
          return {
            ...parsedState,
            isResizing: false,
            resizeDirection: '',
            resizeStartX: 0,
            resizeStartY: 0,
            originalWidth: parsedState.width,
            originalHeight: parsedState.height
          };
        }
      }
    } catch (e) {
      console.warn('Failed to parse saved modal state, using defaults');
    }
    
    // Default modal size
    return {
      width: 400,
      height: 500,
      isResizing: false,
      resizeDirection: '',
      resizeStartX: 0,
      resizeStartY: 0,
      originalWidth: 400,
      originalHeight: 500
    };
  };

  // State for "Yeni Hekim Kayıt" card
  const [newCard, setNewCard] = useState(getSavedCardState('new'));
  
  // State for "Kayıtlı Hekimler" card
  const [listCard, setListCard] = useState(getSavedCardState('list'));
  
  // State for Doctor Modal
  const [modalState, setModalState] = useState(getSavedModalState);
  
  const navigate = useNavigate();

  useEffect(() => {
    fetchDoctors();
  }, []);

  const fetchDoctors = async () => {
    try {
      const data = await getAllDoctors();
      setDoctors(data);
    } catch (err) {
      console.error('Hekimler getirilirken hata oluştu:', err);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'assignedMinutes' ? parseInt(value) : value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (formData.assignedMinutes > 11900) {
      setError('Atanan dakika 11.900\'ü geçemez');
      return;
    }
    
    try {
      await createDoctor(formData);
      setSuccess('Hekim başarıyla kaydedildi!');
      setFormData({
        firstName: '',
        lastName: '',
        phone: '',
        assignedMinutes: 11900
      });
      // Refresh the doctors list
      fetchDoctors();
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccess('');
      }, 3000);
    } catch (err: any) {
      console.error('Create doctor error:', err);
      const errorMessage = err.message || 'Hekim kaydedilirken hata oluştu';
      setError(errorMessage);
      
      // Clear error message after 5 seconds
      setTimeout(() => {
        setError('');
      }, 5000);
    }
  };

  const handleDelete = async (id: number) => {
    // Show confirmation dialog before deleting
    const confirmed = window.confirm('Bu hekim kaydını silmek istediğinize emin misiniz?');
    if (!confirmed) {
      return; // User cancelled the deletion
    }
    
    try {
      await deleteDoctor(id);
      setSuccess('Hekim başarıyla silindi!');
      // Refresh the doctors list
      fetchDoctors();
      
      // Close modal if it was open for this doctor
      if (selectedDoctor && selectedDoctor.id === id) {
        setIsModalOpen(false);
        setSelectedDoctor(null);
      }
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccess('');
      }, 3000);
    } catch (err: any) {
      console.error('Delete error:', err);
      const errorMessage = err.message || 'Hekim silinirken hata oluştu';
      setError(errorMessage);
      
      // Clear error message after 5 seconds
      setTimeout(() => {
        setError('');
      }, 5000);
    }
  };

  const handleClose = () => {
    // Redirect to dashboard
    navigate('/dashboard');
  };

  // Function to open modal with doctor details
  const openDoctorDetails = (doctor: any) => {
    setSelectedDoctor(doctor);
    setIsEditMode(false);
    setIsModalOpen(true);
  };

  // Function to open modal in edit mode
  const openEditDoctor = (doctor: any) => {
    setSelectedDoctor(doctor);
    setEditFormData({
      firstName: doctor.firstName || '',
      lastName: doctor.lastName || '',
      phone: doctor.phone || '',
      assignedMinutes: doctor.assignedMinutes || 11900
    });
    setIsEditMode(true);
    setIsModalOpen(true);
  };

  // Function to close modal
  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedDoctor(null);
    setIsEditMode(false);
  };

  // State for modal dialog
  const [selectedDoctor, setSelectedDoctor] = useState<any>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isEditMode, setIsEditMode] = useState(false);
  
  // State for edit form data
  const [editFormData, setEditFormData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    assignedMinutes: 11900
  });
  
  const handleEditFormDataChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setEditFormData(prev => ({
      ...prev,
      [name]: name === 'assignedMinutes' ? parseInt(value) : value
    }));
  };
  
  const handleUpdateDoctor = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedDoctor) return;
    
    try {
      await updateDoctor(selectedDoctor.id, editFormData);
      setSuccess('Hekim başarıyla güncellendi!');
      closeModal();
      fetchDoctors(); // Refresh the doctors list
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccess('');
      }, 3000);
    } catch (err: any) {
      console.error('Update error:', err);
      const errorMessage = err.message || 'Hekim güncellenirken hata oluştu';
      setError(errorMessage);
      
      // Clear error message after 5 seconds
      setTimeout(() => {
        setError('');
      }, 5000);
    }
  };

  // Mouse event handlers
  const handleMouseDown = (e: React.MouseEvent, cardType: 'new' | 'list' | 'modal', action: 'drag' | 'resize', direction: string = '') => {
    e.preventDefault();
    e.stopPropagation();
    
    if (action === 'drag') {
      if (cardType === 'new') {
        setNewCard((prev: typeof newCard) => ({
          ...prev,
          isDragging: true,
          dragStartX: e.clientX - prev.x,
          dragStartY: e.clientY - prev.y
        }));
      } else if (cardType === 'list') {
        setListCard((prev: typeof listCard) => ({
          ...prev,
          isDragging: true,
          dragStartX: e.clientX - prev.x,
          dragStartY: e.clientY - prev.y
        }));
      } else if (cardType === 'modal') {
        // For modal dragging, we need to get the current position
        setModalState((prev: typeof modalState) => ({
          ...prev,
          isDragging: true,
          dragStartX: e.clientX - (prev.originalX || 0),
          dragStartY: e.clientY - (prev.originalY || 0)
        }));
      }
    } else if (action === 'resize') {
      if (cardType === 'new') {
        setNewCard((prev: typeof newCard) => ({
          ...prev,
          isResizing: true,
          resizeDirection: direction,
          resizeStartX: e.clientX,
          resizeStartY: e.clientY,
          originalX: prev.x,
          originalY: prev.y,
          originalWidth: prev.width,
          originalHeight: prev.height
        }));
      } else if (cardType === 'list') {
        setListCard((prev: typeof listCard) => ({
          ...prev,
          isResizing: true,
          resizeDirection: direction,
          resizeStartX: e.clientX,
          resizeStartY: e.clientY,
          originalX: prev.x,
          originalY: prev.y,
          originalWidth: prev.width,
          originalHeight: prev.height
        }));
      } else if (cardType === 'modal') {
        // Get the current modal position relative to the viewport
        const modalElement = e.currentTarget.closest('.modal-content');
        if (modalElement) {
          const rect = modalElement.getBoundingClientRect();
          const currentX = rect.left;
          const currentY = rect.top;
          
          setModalState((prev: typeof modalState) => ({
            ...prev,
            isResizing: true,
            resizeDirection: direction,
            resizeStartX: e.clientX,
            resizeStartY: e.clientY,
            originalX: currentX,
            originalY: currentY,
            originalWidth: prev.width,
            originalHeight: prev.height
          }));
        }
      }
    }
  };

  const handleMouseMove = (e: MouseEvent) => {
    // Handle dragging for "Yeni Hekim Kayıt" card
    if (newCard.isDragging) {
      const newX = e.clientX - newCard.dragStartX;
      const newY = e.clientY - newCard.dragStartY;
      setNewCard((prev: typeof newCard) => ({
        ...prev,
        x: newX,
        y: newY,
        originalX: newX,
        originalY: newY
      }));
    }
    
    // Handle dragging for "Kayıtlı Hekimler" card
    if (listCard.isDragging) {
      const newX = e.clientX - listCard.dragStartX;
      const newY = e.clientY - listCard.dragStartY;
      setListCard((prev: typeof listCard) => ({
        ...prev,
        x: newX,
        y: newY,
        originalX: newX,
        originalY: newY
      }));
    }
    
    // Handle resizing for "Yeni Hekim Kayıt" card
    if (newCard.isResizing) {
      const deltaX = e.clientX - newCard.resizeStartX;
      const deltaY = e.clientY - newCard.resizeStartY;
      
      let newX = newCard.originalX;
      let newY = newCard.originalY;
      let newWidth = newCard.originalWidth;
      let newHeight = newCard.originalHeight;
      
      // Handle different resize directions
      if (newCard.resizeDirection.includes('right')) {
        newWidth = Math.max(300, newCard.originalWidth + deltaX);
      }
      if (newCard.resizeDirection.includes('left')) {
        newWidth = Math.max(300, newCard.originalWidth - deltaX);
        newX = newCard.originalX + deltaX;
      }
      if (newCard.resizeDirection.includes('bottom')) {
        newHeight = Math.max(200, newCard.originalHeight + deltaY);
      }
      if (newCard.resizeDirection.includes('top')) {
        newHeight = Math.max(200, newCard.originalHeight - deltaY);
        newY = newCard.originalY + deltaY;
      }
      
      setNewCard((prev: typeof newCard) => ({
        ...prev,
        x: newX,
        y: newY,
        width: newWidth,
        height: newHeight,
        originalX: newX,
        originalY: newY,
        originalWidth: newWidth,
        originalHeight: newHeight
      }));
    }
    
    // Handle resizing for "Kayıtlı Hekimler" card
    if (listCard.isResizing) {
      const deltaX = e.clientX - listCard.resizeStartX;
      const deltaY = e.clientY - listCard.resizeStartY;
      
      let newX = listCard.originalX;
      let newY = listCard.originalY;
      let newWidth = listCard.originalWidth;
      let newHeight = listCard.originalHeight;
      
      // Handle different resize directions
      if (listCard.resizeDirection.includes('right')) {
        newWidth = Math.max(300, listCard.originalWidth + deltaX);
      }
      if (listCard.resizeDirection.includes('left')) {
        newWidth = Math.max(300, listCard.originalWidth - deltaX);
        newX = listCard.originalX + deltaX;
      }
      if (listCard.resizeDirection.includes('bottom')) {
        newHeight = Math.max(200, listCard.originalHeight + deltaY);
      }
      if (listCard.resizeDirection.includes('top')) {
        newHeight = Math.max(200, listCard.originalHeight - deltaY);
        newY = listCard.originalY + deltaY;
      }
      
      setListCard((prev: typeof listCard) => ({
        ...prev,
        x: newX,
        y: newY,
        width: newWidth,
        height: newHeight,
        originalX: newX,
        originalY: newY,
        originalWidth: newWidth,
        originalHeight: newHeight
      }));
    }
    
    // Handle resizing for Doctor Modal
    if (modalState.isResizing) {
      const deltaX = e.clientX - modalState.resizeStartX;
      const deltaY = e.clientY - modalState.resizeStartY;
      
      let newWidth = modalState.originalWidth;
      let newHeight = modalState.originalHeight;
      let newX = modalState.originalX || 0;
      let newY = modalState.originalY || 0;
      
      // Handle different resize directions
      if (modalState.resizeDirection.includes('right')) {
        newWidth = Math.max(300, modalState.originalWidth + deltaX);
      }
      if (modalState.resizeDirection.includes('left')) {
        newWidth = Math.max(300, modalState.originalWidth - deltaX);
        newX = modalState.originalX + deltaX;
      }
      if (modalState.resizeDirection.includes('bottom')) {
        newHeight = Math.max(200, modalState.originalHeight + deltaY);
      }
      if (modalState.resizeDirection.includes('top')) {
        newHeight = Math.max(200, modalState.originalHeight - deltaY);
        newY = modalState.originalY + deltaY;
      }
      
      setModalState((prev: typeof modalState) => ({
        ...prev,
        width: newWidth,
        height: newHeight,
        originalWidth: newWidth,
        originalHeight: newHeight,
        originalX: newX,
        originalY: newY
      }));
    }
    
    // Handle dragging for Doctor Modal
    if (modalState.isDragging) {
      const newX = e.clientX - modalState.dragStartX;
      const newY = e.clientY - modalState.dragStartY;
      setModalState((prev: typeof modalState) => ({
        ...prev,
        originalX: newX,
        originalY: newY
      }));
    }
  };

  const handleMouseUp = () => {
    setNewCard((prev: typeof newCard) => {
      const newState = { ...prev, isDragging: false, isResizing: false, resizeDirection: '' };
      // Save the new card state to localStorage
      localStorage.setItem('doctorCard_new_state', JSON.stringify(newState));
      return newState;
    });
    
    setListCard((prev: typeof listCard) => {
      const newState = { ...prev, isDragging: false, isResizing: false, resizeDirection: '' };
      // Save the list card state to localStorage
      localStorage.setItem('doctorCard_list_state', JSON.stringify(newState));
      return newState;
    });
    
    setModalState((prev: typeof modalState) => {
      const newState = { ...prev, isDragging: false, isResizing: false, resizeDirection: '' };
      // Save the modal state to localStorage
      localStorage.setItem('doctorModal_state', JSON.stringify(newState));
      return newState;
    });
  };

  useEffect(() => {
    // Define cleanup function
    const cleanup = () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
    
    // Always remove any existing listeners first to ensure clean state
    cleanup();
    
    // Only add global event listeners when actually needed
    if (newCard.isDragging || listCard.isDragging || newCard.isResizing || listCard.isResizing || modalState.isDragging || modalState.isResizing) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    }
    
    // Cleanup function to remove event listeners when component unmounts or dependencies change
    return cleanup;
  }, [newCard.isDragging, listCard.isDragging, newCard.isResizing, listCard.isResizing, modalState.isDragging, modalState.isResizing]);

  return (
    <div className="doctor-card-page">
      {/* "Yeni Hekim Kayıt" card (draggable and resizable) */}
      <div 
        className="draggable-card"
        style={{
          position: 'absolute',
          left: newCard.x,
          top: newCard.y,
          width: newCard.width,
          height: newCard.height,
          zIndex: 1000,
          cursor: newCard.isDragging ? 'grabbing' : 'default'
        }}
      >
        <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
          <div 
            className="window-header"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'drag')}
            style={{ cursor: 'move', userSelect: 'none' }}
          >
            <span>Yeni Hekim Kayıt</span>
            <div className="window-controls">
              <button className="window-button" onClick={handleClose}>×</button>
            </div>
          </div>
          <div className="window-content" style={{ flex: 1, overflow: 'visible' }}>
            {success && <div className="alert alert-info">{success}</div>}
            {error && <div className="alert alert-warning">{error}</div>}
            
            <form onSubmit={handleSubmit} className="registration-form">
              <div className="form-row">
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Ad:</label>
                    <input
                      type="text"
                      name="firstName"
                      className="form-input"
                      value={formData.firstName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
                <div className="form-col">
                  <div className="form-group">
                    <label className="form-label">Soyad:</label>
                    <input
                      type="text"
                      name="lastName"
                      className="form-input"
                      value={formData.lastName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
              </div>
              
              <div className="form-group">
                <label className="form-label">Telefon:</label>
                <input
                  type="text"
                  name="phone"
                  className="form-input"
                  value={formData.phone}
                  onChange={handleChange}
                  required
                />
              </div>
              
              <div className="form-group">
                <label className="form-label">Atanan Dakika:</label>
                <input
                  type="number"
                  name="assignedMinutes"
                  className="form-input"
                  value={formData.assignedMinutes}
                  onChange={handleChange}
                  min="0"
                  max="11900"
                  required
                />
              </div>
              
              <button type="submit" className="btn btn-primary">Hekimi Kaydet</button>
            </form>
          </div>
          {/* Resize handles */}
          <div 
            className="resize-handle resize-nw"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'top-left')}
          />
          <div 
            className="resize-handle resize-ne"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'top-right')}
          />
          <div 
            className="resize-handle resize-sw"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'bottom-left')}
          />
          <div 
            className="resize-handle resize-se"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'bottom-right')}
          />
          <div 
            className="resize-handle resize-n"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'top')}
          />
          <div 
            className="resize-handle resize-s"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'bottom')}
          />
          <div 
            className="resize-handle resize-w"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'left')}
          />
          <div 
            className="resize-handle resize-e"
            onMouseDown={(e) => handleMouseDown(e, 'new', 'resize', 'right')}
          />
        </div>
      </div>
      
      {/* "Kayıtlı Hekimler" card (draggable and resizable) */}
      <div 
        className="draggable-card"
        style={{
          position: 'absolute',
          left: listCard.x,
          top: listCard.y,
          width: listCard.width,
          height: listCard.height,
          zIndex: 1000,
          cursor: listCard.isDragging ? 'grabbing' : 'default'
        }}
      >
        <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
          <div 
            className="window-header"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'drag')}
            style={{ cursor: 'move', userSelect: 'none' }}
          >
            <span>Kayıtlı Hekimler</span>
          </div>
          <div className="window-content" style={{ flex: 1, overflow: 'visible' }}>
            {doctors.length === 0 ? (
              <p>Kayıtlı hekim bulunmamaktadır.</p>
            ) : (
              <div className="doctor-list">
                <div className="doctor-header">
                  <div className="doctor-header-item">Ad Soyad</div>
                  <div className="doctor-header-item">İletişim</div>
                  <div className="doctor-header-item minutes-vertical">Tanımlı Dakika</div>
                  <div className="doctor-header-item assigned-vertical">Atanan Dakika</div>
                  <div className="doctor-header-item minutes-shifted">Kalan</div>
                  <div className="doctor-header-item">İşlemler</div>
                </div>
                {doctors.map((doctor) => (
                  <div key={doctor.id} className="doctor-row" onClick={() => openDoctorDetails(doctor)}>
                    <div className="doctor-info">
                      <div className="doctor-name">{doctor.firstName} {doctor.lastName}</div>
                      <div className="doctor-contact">{doctor.phone}</div>
                      <div className="doctor-minutes">{doctor.assignedMinutes}</div>
                      <div className="doctor-assigned">0</div>
                      <div className="doctor-remaining">{doctor.assignedMinutes}</div>
                    </div>
                    <div className="doctor-actions">
                      <button className="btn-icon edit" onClick={(e) => { e.stopPropagation(); openEditDoctor(doctor); }}>✏️</button>
                      <button className="btn-icon delete" onClick={(e) => { e.stopPropagation(); handleDelete(doctor.id); }}>🗑️</button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
          {/* Resize handles */}
          <div 
            className="resize-handle resize-nw"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'top-left')}
          />
          <div 
            className="resize-handle resize-ne"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'top-right')}
          />
          <div 
            className="resize-handle resize-sw"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'bottom-left')}
          />
          <div 
            className="resize-handle resize-se"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'bottom-right')}
          />
          <div 
            className="resize-handle resize-n"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'top')}
          />
          <div 
            className="resize-handle resize-s"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'bottom')}
          />
          <div 
            className="resize-handle resize-w"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'left')}
          />
          <div 
            className="resize-handle resize-e"
            onMouseDown={(e) => handleMouseDown(e, 'list', 'resize', 'right')}
          />
        </div>
      </div>
      
      {/* Doctor Details Modal */}
      {isModalOpen && selectedDoctor && (
        <div className="modal-overlay" onClick={(e) => {
          // Only close modal if clicking on the overlay (not the content)
          if (e.target === e.currentTarget) {
            closeModal();
          }
        }}>
          <div 
            className="modal-content draggable-card" 
            onClick={(e) => e.stopPropagation()}
            style={{
              width: modalState.width,
              height: modalState.height,
              maxWidth: '90%',
              maxHeight: '90%',
              position: 'absolute',
              left: modalState.originalX !== undefined ? modalState.originalX : '50%',
              top: modalState.originalY !== undefined ? modalState.originalY : '50%',
              transform: modalState.originalX === undefined || modalState.originalY === undefined ? 'translate(-50%, -50%)' : 'none',
              margin: 0,
              zIndex: 2001
            }}
          >
            <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
              <div 
                className="window-header"
                onMouseDown={(e) => handleMouseDown(e, 'modal', 'drag')}
                style={{ cursor: 'move', userSelect: 'none' }}
              >
                <span>{isEditMode ? 'Hekimi Düzenle' : 'Hekim Detayları'}</span>
                <div className="window-controls">
                  <button className="window-button" onClick={closeModal} aria-label="Kapat">×</button>
                </div>
              </div>
              <div className="window-content">
                {isEditMode ? (
                  // Edit form
                  <form onSubmit={handleUpdateDoctor} className="edit-form">
                    <div className="form-group">
                      <label className="form-label">Ad:</label>
                      <input
                        type="text"
                        name="firstName"
                        className="form-input"
                        value={editFormData.firstName}
                        onChange={handleEditFormDataChange}
                        required
                      />
                    </div>
                    
                    <div className="form-group">
                      <label className="form-label">Soyad:</label>
                      <input
                        type="text"
                        name="lastName"
                        className="form-input"
                        value={editFormData.lastName}
                        onChange={handleEditFormDataChange}
                        required
                      />
                    </div>
                    
                    <div className="form-group">
                      <label className="form-label">Telefon:</label>
                      <input
                        type="text"
                        name="phone"
                        className="form-input"
                        value={editFormData.phone}
                        onChange={handleEditFormDataChange}
                        required
                      />
                    </div>
                    
                    <div className="form-group">
                      <label className="form-label">Atanan Dakika:</label>
                      <input
                        type="number"
                        name="assignedMinutes"
                        className="form-input"
                        value={editFormData.assignedMinutes}
                        onChange={handleEditFormDataChange}
                        min="0"
                        max="11900"
                        required
                      />
                    </div>
                    
                    <div className="modal-actions">
                      <button type="submit" className="btn btn-primary">Güncelle</button>
                      <button type="button" className="btn" onClick={closeModal}>İptal</button>
                    </div>
                  </form>
                ) : (
                  // View mode
                  <>
                    <div className="doctor-detail-info">
                      <div className="doctor-detail-row">
                        <strong>Ad:</strong> {selectedDoctor.firstName}
                      </div>
                      <div className="doctor-detail-row">
                        <strong>Soyad:</strong> {selectedDoctor.lastName}
                      </div>
                      <div className="doctor-detail-row">
                        <strong>Telefon:</strong> {selectedDoctor.phone}
                      </div>
                      <div className="doctor-detail-row">
                        <strong>Toplam Dakika:</strong> {selectedDoctor.assignedMinutes}
                      </div>
                      <div className="doctor-detail-row">
                        <strong>Atanan Dakika:</strong> 0
                      </div>
                      <div className="doctor-detail-row">
                        <strong>Kalan Dakika:</strong> {selectedDoctor.assignedMinutes}
                      </div>
                      
                      {/* Workplace assignment information */}
                      <div className="doctor-detail-row">
                        <strong>Atandığı İş Yeri:</strong> {selectedDoctor.assignedWorkplace ? selectedDoctor.assignedWorkplace : 'Atanmamış'}
                      </div>
                    </div>
                    
                    <div className="modal-actions">
                      <button className="btn btn-primary" onClick={() => openEditDoctor(selectedDoctor)}>Düzenle</button>
                      <button className="btn btn-danger" onClick={() => {
                        const confirmed = window.confirm('Bu hekim kaydını silmek istediğinize emin misiniz?');
                        if (confirmed) {
                          handleDelete(selectedDoctor.id);
                        }
                      }}>Sil</button>
                      <button className="btn btn-close" onClick={closeModal}>Kapat</button>
                    </div>
                  </>
                )}
              </div>
            </div>
            {/* Resize handles for modal */}
            <div 
              className="resize-handle resize-nw"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'top-left')}
            />
            <div 
              className="resize-handle resize-ne"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'top-right')}
            />
            <div 
              className="resize-handle resize-sw"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'bottom-left')}
            />
            <div 
              className="resize-handle resize-se"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'bottom-right')}
            />
            <div 
              className="resize-handle resize-n"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'top')}
            />
            <div 
              className="resize-handle resize-s"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'bottom')}
            />
            <div 
              className="resize-handle resize-w"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'left')}
            />
            <div 
              className="resize-handle resize-e"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'right')}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default DoctorCardPage;
