import React, { useState, useEffect } from 'react';
import { createDoctor, getAllDoctors, deleteDoctor, updateDoctor } from '../services/api';
import './DoctorCardPage.css';
import { useNavigate } from 'react-router-dom';

const DoctorCardPage = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    assignedMinutes: 11900
  });
  const [doctors, setDoctors] = useState<any[]>([]);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  
  // Get saved modal state or use defaults
  const getSavedModalState = () => {
    try {
      const savedState = localStorage.getItem('doctorModal_state');
      if (savedState) {
        const parsedState = JSON.parse(savedState);
        // Validate that the parsed state has the required properties
        if (typeof parsedState === 'object' && parsedState !== null &&
            'width' in parsedState && 'height' in parsedState) {
          return {
            ...parsedState,
            isResizing: false,
            resizeDirection: '',
            resizeStartX: 0,
            resizeStartY: 0,
            originalWidth: parsedState.width,
            originalHeight: parsedState.height
          };
        }
      }
    } catch (e) {
      console.warn('Failed to parse saved modal state, using defaults');
    }
    
    // Default modal size
    return {
      width: 400,
      height: 500,
      isResizing: false,
      resizeDirection: '',
      resizeStartX: 0,
      resizeStartY: 0,
      originalWidth: 400,
      originalHeight: 500
    };
  };

  const navigate = useNavigate();

  useEffect(() => {
    fetchDoctors();
  }, []);

  const fetchDoctors = async () => {
    try {
      const data = await getAllDoctors();
      setDoctors(data);
    } catch (err) {
      console.error('Hekimler getirilirken hata oluştu:', err);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData((prev: typeof formData) => ({
      ...prev,
      [name]: name === 'assignedMinutes' ? parseInt(value) : value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (formData.assignedMinutes > 11900) {
      setError('Atanan dakika 11.900\'ü geçemez');
      clearNotifications();
      return;
    }
    
    try {
      await createDoctor(formData);
      setSuccess('Hekim başarıyla kaydedildi!');
      setFormData({
        firstName: '',
        lastName: '',
        phone: '',
        assignedMinutes: 11900
      });
      // Refresh the doctors list
      fetchDoctors();
      clearNotifications();
    } catch (err: any) {
      console.error('Create doctor error:', err);
      const errorMessage = err.message || 'Hekim kaydedilirken hata oluştu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  // Clear notifications after timeout
  const clearNotifications = () => {
    setTimeout(() => {
      setSuccess('');
      setError('');
    }, 3000);
  };

  const handleDelete = async (id: number) => {
    // Show confirmation dialog before deleting
    const confirmed = window.confirm('Bu hekim kaydını silmek istediğinize emin misiniz?');
    if (!confirmed) {
      return; // User cancelled the deletion
    }
    
    try {
      await deleteDoctor(id);
      setSuccess('Hekim başarıyla silindi!');
      // Refresh the doctors list
      fetchDoctors();
      
      // Close modal if it was open for this doctor
      if (selectedDoctor && selectedDoctor.id === id) {
        setIsModalOpen(false);
        setSelectedDoctor(null);
      }
      
      clearNotifications();
    } catch (err: any) {
      console.error('Delete error:', err);
      const errorMessage = err.message || 'Hekim silinirken hata oluştu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  const handleClose = () => {
    // Redirect to dashboard
    navigate('/dashboard');
  };

  // Function to open modal with doctor details
  const openDoctorDetails = (doctor: any) => {
    setSelectedDoctor(doctor);
    setIsEditMode(false);
    setIsModalOpen(true);
  };

  // Function to open modal in edit mode
  const openEditDoctor = (doctor: any) => {
    setSelectedDoctor(doctor);
    setEditFormData({
      firstName: doctor.firstName || '',
      lastName: doctor.lastName || '',
      phone: doctor.phone || '',
      assignedMinutes: doctor.assignedMinutes || 11900
    });
    setIsEditMode(true);
    setIsModalOpen(true);
  };

  // Function to close modal
  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedDoctor(null);
    setIsEditMode(false);
  };

  // State for modal dialog
  const [selectedDoctor, setSelectedDoctor] = useState<any>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isEditMode, setIsEditMode] = useState(false);
  
  // State for edit form data
  const [editFormData, setEditFormData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    assignedMinutes: 11900
  });
  
  const [modalState, setModalState] = useState(getSavedModalState);
  
  const handleEditFormDataChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setEditFormData((prev: typeof editFormData) => ({
      ...prev,
      [name]: name === 'assignedMinutes' ? parseInt(value) : value
    }));
  };
  
  const handleUpdateDoctor = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedDoctor) return;
    
    try {
      await updateDoctor(selectedDoctor.id, editFormData);
      setSuccess('Hekim başarıyla güncellendi!');
      closeModal();
      fetchDoctors(); // Refresh the doctors list
      
      clearNotifications();
    } catch (err: any) {
      console.error('Update error:', err);
      const errorMessage = err.message || 'Hekim güncellenirken hata oluştu';
      setError(errorMessage);
      clearNotifications();
    }
  };

  // Mouse event handlers
  const handleMouseDown = (e: React.MouseEvent, cardType: 'modal', action: 'drag' | 'resize', direction: string = '') => {
    e.preventDefault();
    e.stopPropagation();
    
    if (action === 'drag') {
      if (cardType === 'modal') {
        // For modal dragging, we need to get the current position
        setModalState((prev: typeof modalState) => ({
          ...prev,
          isDragging: true,
          dragStartX: e.clientX - (prev.originalX || 0),
          dragStartY: e.clientY - (prev.originalY || 0)
        }));
      }
    } else if (action === 'resize') {
      if (cardType === 'modal') {
        // Get the current modal position relative to the viewport
        const modalElement = e.currentTarget.closest('.modal-content');
        if (modalElement) {
          const rect = modalElement.getBoundingClientRect();
          const currentX = rect.left;
          const currentY = rect.top;
          
          setModalState((prev: typeof modalState) => ({
            ...prev,
            isResizing: true,
            resizeDirection: direction,
            resizeStartX: e.clientX,
            resizeStartY: e.clientY,
            originalX: currentX,
            originalY: currentY,
            originalWidth: prev.width,
            originalHeight: prev.height
          }));
        }
      }
    }
    
    // Ensure global event listeners are attached
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
  };

  const handleMouseMove = (e: MouseEvent) => {
    // Handle resizing for Doctor Modal
    if (modalState.isResizing) {
      const deltaX = e.clientX - modalState.resizeStartX;
      const deltaY = e.clientY - modalState.resizeStartY;
      
      let newWidth = modalState.originalWidth;
      let newHeight = modalState.originalHeight;
      let newX = modalState.originalX || 0;
      let newY = modalState.originalY || 0;
      
      // Handle different resize directions
      if (modalState.resizeDirection.includes('right')) {
        newWidth = Math.max(300, modalState.originalWidth + deltaX);
      }
      if (modalState.resizeDirection.includes('left')) {
        newWidth = Math.max(300, modalState.originalWidth - deltaX);
        newX = modalState.originalX + deltaX;
      }
      if (modalState.resizeDirection.includes('bottom')) {
        newHeight = Math.max(200, modalState.originalHeight + deltaY);
      }
      if (modalState.resizeDirection.includes('top')) {
        newHeight = Math.max(200, modalState.originalHeight - deltaY);
        newY = modalState.originalY + deltaY;
      }
      
      setModalState((prev: typeof modalState) => ({
        ...prev,
        width: newWidth,
        height: newHeight,
        originalWidth: newWidth,
        originalHeight: newHeight,
        originalX: newX,
        originalY: newY
      }));
    }
    
    // Handle dragging for Doctor Modal
    if (modalState.isDragging) {
      const newX = e.clientX - modalState.dragStartX;
      const newY = e.clientY - modalState.dragStartY;
      setModalState((prev: typeof modalState) => ({
        ...prev,
        originalX: newX,
        originalY: newY
      }));
    }
  };

  const handleMouseUp = () => {
    setModalState((prev: typeof modalState) => {
      const newState = { ...prev, isDragging: false, isResizing: false, resizeDirection: '' };
      // Save the modal state to localStorage
      localStorage.setItem('doctorModal_state', JSON.stringify(newState));
      return newState;
    });
  };

  useEffect(() => {
    // Define cleanup function
    const cleanup = () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
    
    // Always remove any existing listeners first to ensure clean state
    cleanup();
    
    // Only add global event listeners when actually needed
    if (modalState.isDragging || modalState.isResizing) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    }
    
    // Cleanup function to remove event listeners when component unmounts or dependencies change
    return cleanup;
  }, [modalState.isDragging, modalState.isResizing]);

  return (
    <div className="doctor-card-page">
      {/* Popup notifications */}
      {success && (
        <div className="popup-notification alert alert-info">
          {success}
        </div>
      )}
      {error && (
        <div className="popup-notification alert alert-warning">
          {error}
        </div>
      )}
      
      {/* Doctor Details Modal */}
      {isModalOpen && selectedDoctor && (
        <div className="modal-overlay" onClick={(e) => {
          // Only close modal if clicking on the overlay (not the content)
          if (e.target === e.currentTarget) {
            closeModal();
          }
        }}>
          <div 
            className="modal-content draggable-card" 
            onClick={(e) => e.stopPropagation()}
            style={{
              width: modalState.width,
              height: modalState.height,
              maxWidth: '90%',
              maxHeight: '90%',
              position: 'absolute',
              left: modalState.originalX !== undefined ? modalState.originalX : '50%',
              top: modalState.originalY !== undefined ? modalState.originalY : '50%',
              transform: modalState.originalX === undefined || modalState.originalY === undefined ? 'translate(-50%, -50%)' : 'none',
              margin: 0,
              zIndex: 2001
            }}
          >
            <div className="window" style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
              <div 
                className="window-header"
                onMouseDown={(e) => handleMouseDown(e, 'modal', 'drag')}
                style={{ cursor: 'move', userSelect: 'none' }}
              >
                <span>{isEditMode ? 'Hekimi Düzenle' : 'Hekim Detayları'}</span>
                <div className="window-controls">
                  <button className="window-button" onClick={closeModal} aria-label="Kapat">×</button>
                </div>
              </div>
              <div className="window-content">
                {isEditMode ? (
                  // Edit form
                  <form onSubmit={handleUpdateDoctor} className="edit-form">
                    <div className="form-group">
                      <label className="form-label">Ad:</label>
                      <input
                        type="text"
                        name="firstName"
                        className="form-input"
                        value={editFormData.firstName}
                        onChange={handleEditFormDataChange}
                        required
                      />
                    </div>
                    
                    <div className="form-group">
                      <label className="form-label">Soyad:</label>
                      <input
                        type="text"
                        name="lastName"
                        className="form-input"
                        value={editFormData.lastName}
                        onChange={handleEditFormDataChange}
                        required
                      />
                    </div>
                    
                    <div className="form-group">
                      <label className="form-label">Telefon:</label>
                      <input
                        type="text"
                        name="phone"
                        className="form-input"
                        value={editFormData.phone}
                        onChange={handleEditFormDataChange}
                        required
                      />
                    </div>
                    
                    <div className="form-group">
                      <label className="form-label">Atanan Dakika:</label>
                      <input
                        type="number"
                        name="assignedMinutes"
                        className="form-input"
                        value={editFormData.assignedMinutes}
                        onChange={handleEditFormDataChange}
                        min="0"
                        max="11900"
                        required
                      />
                    </div>
                    
                    <div className="modal-actions">
                      <button type="submit" className="btn btn-primary">Güncelle</button>
                      <button type="button" className="btn" onClick={closeModal}>İptal</button>
                    </div>
                  </form>
                ) : (
                  // View mode
                  <>
                    <div className="doctor-detail-info">
                      <div className="doctor-detail-row">
                        <strong>Ad:</strong> {selectedDoctor.firstName}
                      </div>
                      <div className="doctor-detail-row">
                        <strong>Soyad:</strong> {selectedDoctor.lastName}
                      </div>
                      <div className="doctor-detail-row">
                        <strong>Telefon:</strong> {selectedDoctor.phone}
                      </div>
                      <div className="doctor-detail-row">
                        <strong>Toplam Dakika:</strong> {selectedDoctor.assignedMinutes}
                      </div>
                      <div className="doctor-detail-row">
                        <strong>Atanan Dakika:</strong> 0
                      </div>
                      <div className="doctor-detail-row">
                        <strong>Kalan Dakika:</strong> {selectedDoctor.assignedMinutes}
                      </div>
                      
                      {/* Workplace assignment information */}
                      <div className="doctor-detail-row">
                        <strong>Atandığı İş Yeri:</strong> {selectedDoctor.assignedWorkplace ? selectedDoctor.assignedWorkplace : 'Atanmamış'}
                      </div>
                    </div>
                    
                    <div className="modal-actions">
                      <button className="btn btn-primary" onClick={() => openEditDoctor(selectedDoctor)}>Düzenle</button>
                      <button className="btn btn-danger" onClick={() => {
                        const confirmed = window.confirm('Bu hekim kaydını silmek istediğinize emin misiniz?');
                        if (confirmed) {
                          handleDelete(selectedDoctor.id);
                        }
                      }}>Sil</button>
                      <button className="btn btn-close" onClick={closeModal}>Kapat</button>
                    </div>
                  </>
                )}
              </div>
            </div>
            {/* Resize handles for modal */}
            <div 
              className="resize-handle resize-nw"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'top-left')}
            />
            <div 
              className="resize-handle resize-ne"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'top-right')}
            />
            <div 
              className="resize-handle resize-sw"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'bottom-left')}
            />
            <div 
              className="resize-handle resize-se"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'bottom-right')}
            />
            <div 
              className="resize-handle resize-n"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'top')}
            />
            <div 
              className="resize-handle resize-s"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'bottom')}
            />
            <div 
              className="resize-handle resize-w"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'left')}
            />
            <div 
              className="resize-handle resize-e"
              onMouseDown={(e) => handleMouseDown(e, 'modal', 'resize', 'right')}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default DoctorCardPage;
