import React, { useState, useEffect, useCallback } from 'react';
import { useLocation } from 'react-router-dom';
import './Dashboard.css';
import { 
  createWorkplace, 
  updateWorkplace, 
  getAvailableExperts, 
  getAvailableDoctors, 
  getAvailableDsps, 
  getAllWorkplaces, 
  deleteWorkplace, 
  getAllExperts, 
  getAllDoctors, 
  getAllDsps 
} from '../services/api';

interface Workplace {
  id: number;
  name: string;
  sskRegistrationNo: string;
  address: string;
  price: string;
  taxOffice: string;
  taxNumber: string;
  riskLevel: string;
  employeeCount: string;
  source: string;
  assignedExpertId: string;
  assignedDoctorId: string;
  assignedDspId: string;
  trackingExpertId: string;
  registrationDate: string;
  approvalStatus: string;
  Expert?: {
    id: number;
    firstName: string;
    lastName: string;
  } | null;
  Doctor?: {
    id: number;
    firstName: string;
    lastName: string;
  } | null;
  Dsp?: {
    id: number;
    firstName: string;
    lastName: string;
  } | null;
}

const Dashboard: React.FC = () => {
  const location = useLocation();
  
  // State
  const [formData, setFormData] = useState({
    name: '',
    sskRegistrationNo: '',
    address: '',
    price: '',
    taxOffice: '',
    taxNumber: '',
    riskLevel: 'low',
    employeeCount: '',
    source: '',
    assignedExpertId: '',
    assignedDoctorId: '',
    assignedDspId: '',
    trackingExpertId: '',
    registrationDate: '',
  });
  
  const [workplaces, setWorkplaces] = useState<Workplace[]>([]);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [isModalOpen, setIsModalOpen] = useState<boolean>(false);
  const [modalFormData, setModalFormData] = useState<Partial<Workplace>>({});
  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState<boolean>(false);
  
  const [showNoExpertPopup, setShowNoExpertPopup] = useState<boolean>(false);
  const [showNoDoctorPopup, setShowNoDoctorPopup] = useState<boolean>(false);
  const [showNoDspPopup, setShowNoDspPopup] = useState<boolean>(false);
  const [showDspNotAllowedPopup, setShowDspNotAllowedPopup] = useState<boolean>(false);
  
  const [allExperts, setAllExperts] = useState<any[]>([]);
  const [allDoctors, setAllDoctors] = useState<any[]>([]);
  const [allDsps, setAllDsps] = useState<any[]>([]);
  
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  const [duplicateWarning, setDuplicateWarning] = useState<{show: boolean, workplace: Workplace | null}>({show: false, workplace: null});
  
  const [sortConfig, setSortConfig] = useState<{ key: string; direction: 'asc' | 'desc' } | null>(null);
  const [isPriceFocused, setIsPriceFocused] = useState(false);
  const [isModalPriceFocused, setIsModalPriceFocused] = useState(false);

  const workplacesPerPage = 10;
  const totalPages = Math.ceil(workplaces.length / workplacesPerPage);

  // Helper functions
  const isValidTurkishText = (text: string): boolean => {
    return /^([a-zA-ZÄŸÃ¼ÅŸÄ±Ã¶Ã§ÄžÃœÅžÄ°Ã–Ã‡\s]*)$/.test(text);
  };

  const isNumericOnly = (text: string): boolean => {
    return /^([0-9]*)$/.test(text);
  };

  const formatPriceForDisplay = (price: string): string => {
    if (!price) return '';
    if (!/^[0-9]*$/.test(price)) return price;
    return parseInt(price).toLocaleString('tr-TR');
  };

  const parsePrice = (formattedPrice: string): string => {
    if (!formattedPrice) return '';
    const cleaned = formattedPrice.replace(/\./g, '');
    return /^[0-9]*$/.test(cleaned) ? cleaned : '';
  };

  const formatDate = (dateString: string) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString('tr-TR');
  };

  const isDspAssignmentAllowed = (riskLevel: string, employeeCount: number): boolean => {
    if (riskLevel === 'low' || riskLevel === 'dangerous') {
      return false;
    }
    if (riskLevel === 'veryDangerous' && employeeCount < 10) {
      return false;
    }
    return true;
  };

  const getExpertName = (expertId: string, workplace?: Workplace) => {
    if (workplace?.Expert && workplace.Expert.firstName && workplace.Expert.lastName) {
      return `${workplace.Expert.firstName} ${workplace.Expert.lastName}`;
    }
    if (!expertId) return 'Atanmamış';
    const expert = allExperts.find((exp: any) => exp.id === parseInt(expertId));
    return expert ? `${expert.firstName} ${expert.lastName}` : 'Bilinmeyen Uzman';
  };

  const getTrackingExpertName = (trackingExpertId: string, workplace?: Workplace) => {
    if (workplace?.Expert && workplace.Expert.firstName && workplace.Expert.lastName) {
      return `${workplace.Expert.firstName} ${workplace.Expert.lastName}`;
    }
    if (!trackingExpertId) return 'Atanmamış';
    const expert = allExperts.find((exp: any) => exp.id === parseInt(trackingExpertId));
    return expert ? `${expert.firstName} ${expert.lastName}` : 'Atanmamış';
  };

  const getDoctorName = (doctorId: string, workplace?: Workplace) => {
    if (workplace?.Doctor && workplace.Doctor.firstName && workplace.Doctor.lastName) {
      return `${workplace.Doctor.firstName} ${workplace.Doctor.lastName}`;
    }
    if (!doctorId) return 'Atanmamış';
    const doctor = allDoctors.find((doc: any) => doc.id === parseInt(doctorId));
    return doctor ? `${doctor.firstName} ${doctor.lastName}` : 'Bilinmeyen Hekim';
  };

  const getDspName = (dspId: string, workplace?: Workplace) => {
    if (workplace?.Dsp && workplace.Dsp.firstName && workplace.Dsp.lastName) {
      return `${workplace.Dsp.firstName} ${workplace.Dsp.lastName}`;
    }
    if (!dspId) return 'Atanmamış';
    const dsp = allDsps.find((d: any) => d.id === parseInt(dspId));
    return dsp ? `${dsp.firstName} ${dsp.lastName}` : 'Bilinmeyen DSP';
  };

  const calculateRequiredMinutes = (riskLevel: string, employeeCount: number): number => {
    const multipliers: Record<string, number> = { 'low': 10, 'dangerous': 20, 'veryDangerous': 40 };
    return employeeCount * (multipliers[riskLevel] || 0);
  };

  const calculateRequiredDoctorMinutes = (riskLevel: string, employeeCount: number): number => {
    const multipliers: Record<string, number> = { 'low': 5, 'dangerous': 10, 'veryDangerous': 15 };
    return employeeCount * (multipliers[riskLevel] || 0);
  };

  const getFilteredExperts = (riskLevel: string, employeeCount: number) => {
    const requiredMinutes = calculateRequiredMinutes(riskLevel, employeeCount);
    return allExperts.filter(expert => {
      if (expert.assignedMinutes < requiredMinutes) return false;
      switch (expert.expertiseClass) {
        case 'A': return true;
        case 'B': return riskLevel === 'low' || riskLevel === 'dangerous';
        case 'C': return riskLevel === 'low';
        default: return false;
      }
    });
  };

  const getFilteredDoctors = (riskLevel: string, employeeCount: number) => {
    const requiredMinutes = calculateRequiredDoctorMinutes(riskLevel, employeeCount);
    return allDoctors.filter(doctor => doctor.assignedMinutes >= requiredMinutes);
  };

  const getFilteredDsps = (riskLevel: string, employeeCount: number) => {
    if (!isDspAssignmentAllowed(riskLevel, employeeCount)) {
      return [];
    }
    const isDspRequired = riskLevel === 'veryDangerous' && employeeCount >= 10;
    if (!isDspRequired) {
      return allDsps.filter(dsp => dsp.assignedMinutes > 0);
    }
    const requiredMinutes = employeeCount * 5;
    return allDsps.filter(dsp => dsp.assignedMinutes >= requiredMinutes);
  };

  const getSortedWorkplaces = () => {
    if (!sortConfig) {
      return [...workplaces].sort((a, b) => {
        return new Date(b.registrationDate || '').getTime() - new Date(a.registrationDate || '').getTime();
      });
    }

    return [...workplaces].sort((a, b) => {
      const key = sortConfig.key as keyof Workplace;
      let aValue: any, bValue: any;

      switch (key) {
        case 'name':
        case 'source':
          aValue = a[key] || '';
          bValue = b[key] || '';
          break;
        case 'registrationDate':
          aValue = new Date(a[key] || '').getTime();
          bValue = new Date(b[key] || '').getTime();
          break;
        case 'riskLevel':
          const riskOrder: Record<string, number> = { 'low': 1, 'dangerous': 2, 'veryDangerous': 3 };
          aValue = riskOrder[a[key]] || 0;
          bValue = riskOrder[b[key]] || 0;
          break;
        case 'approvalStatus':
          const approvalOrder: Record<string, number> = { 'atama': 1, 'bekliyor': 2, 'onaylandi': 3 };
          aValue = approvalOrder[a[key]] || 0;
          bValue = approvalOrder[b[key]] || 0;
          break;
        case 'assignedExpertId':
          aValue = getExpertName(a[key]);
          bValue = getExpertName(b[key]);
          break;
        case 'assignedDoctorId':
          aValue = getDoctorName(a[key]);
          bValue = getDoctorName(b[key]);
          break;
        case 'assignedDspId':
          aValue = getDspName(a[key]);
          bValue = getDspName(b[key]);
          break;
        default:
          aValue = a[key];
          bValue = b[key];
      }

      if (aValue < bValue) return sortConfig.direction === 'asc' ? -1 : 1;
      if (aValue > bValue) return sortConfig.direction === 'asc' ? 1 : -1;
      return 0;
    });
  };

  // API calls
  const fetchAllData = useCallback(async () => {
    try {
      const [workplacesData, expertsData, doctorsData, dspsData] = await Promise.all([
        getAllWorkplaces(),
        getAllExperts(),
        getAllDoctors(),
        getAllDsps()
      ]);
      
      setWorkplaces(workplacesData);
      setAllExperts(expertsData);
      setAllDoctors(doctorsData);
      setAllDsps(dspsData);
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Veri alınırken hata oluştu');
    }
  }, []);

  useEffect(() => {
    fetchAllData();
  }, [fetchAllData]);

  useEffect(() => {
    fetchAllData();
  }, [location.pathname, fetchAllData]);

  useEffect(() => {
    const handleFocus = () => fetchAllData();
    window.addEventListener('focus', handleFocus);
    return () => window.removeEventListener('focus', handleFocus);
  }, [fetchAllData]);

  // Handlers
  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;

    if (name === 'assignedDspId' && value) {
      const employeeCount = parseInt(formData.employeeCount) || 0;
      if (!isDspAssignmentAllowed(formData.riskLevel, employeeCount)) {
        setShowDspNotAllowedPopup(true);
        return;
      }
    }

    if (name === 'taxOffice' && value && !isValidTurkishText(value)) return;
    if (name === 'taxNumber' && value && !isNumericOnly(value)) return;

    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handlePriceChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    if (value === '' || /^[0-9]*$/.test(value)) {
      setFormData(prev => ({ ...prev, price: value }));
    }
  };

  const handleModalPriceChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    if (value === '' || /^[0-9]*$/.test(value)) {
      setModalFormData(prev => ({ ...prev, price: value }));
    }
  };

  const handlePriceFocus = () => {
    setIsPriceFocused(true);
    setFormData(prev => ({ ...prev, price: parsePrice(prev.price) }));
  };

  const handlePriceBlur = () => {
    setIsPriceFocused(false);
    setFormData(prev => ({ ...prev, price: formatPriceForDisplay(prev.price) }));
  };

  const handleModalPriceFocus = () => {
    setIsModalPriceFocused(true);
    setModalFormData(prev => ({
      ...prev,
      price: typeof prev.price === 'string' ? parsePrice(prev.price) : String(prev.price || '')
    }));
  };

  const handleModalPriceBlur = () => {
    setIsModalPriceFocused(false);
    setModalFormData(prev => ({
      ...prev,
      price: typeof prev.price === 'string' ? formatPriceForDisplay(prev.price) : String(prev.price || '')
    }));
  };

  const handleSSkRegistrationNoChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    let value = e.target.value.replace(/\D/g, '');
    if (value.length > 21) value = value.substring(0, 21);
    
    let formattedValue = '';
    for (let i = 0; i < value.length; i++) {
      if ([1, 5, 7, 9, 14, 17, 19].includes(i)) {
        formattedValue += ' ' + value[i];
      } else {
        formattedValue += value[i];
      }
    }
    
    setFormData(prev => ({ ...prev, sskRegistrationNo: formattedValue }));
  };

  const handleClose = () => {
    setFormData({
      name: '', sskRegistrationNo: '', address: '', price: '', taxOffice: '',
      taxNumber: '', riskLevel: 'low', employeeCount: '', source: '',
      assignedExpertId: '', assignedDoctorId: '', assignedDspId: '', trackingExpertId: '', registrationDate: '',
    });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      const formattedSskNo = formData.sskRegistrationNo.replace(/\s/g, '');
      const existingWorkplace = workplaces.find(workplace => 
        workplace.sskRegistrationNo?.replace(/\s/g, '') === formattedSskNo
      );

      if (existingWorkplace) {
        setDuplicateWarning({ show: true, workplace: existingWorkplace });
        setTimeout(() => setDuplicateWarning({ show: false, workplace: null }), 5000);
        return;
      }

      const workplaceData = {
        ...formData,
        employeeCount: parseInt(formData.employeeCount) || 0,
        price: parseInt(parsePrice(formData.price)) || 0,
        approvalStatus: 'atama',
        assignedExpertId: formData.assignedExpertId || null,
        assignedDoctorId: formData.assignedDoctorId || null,
        assignedDspId: formData.assignedDspId || null
      };

      const newWorkplace = await createWorkplace(workplaceData);
      setSuccess('İş yeri başarıyla kaydedildi!');
      handleClose();
      await fetchAllData();
      setCurrentPage(1);
      setTimeout(() => setSuccess(''), 3000);
    } catch (error: any) {
      setError(`İş yeri kaydedilirken hata oluştu: ${error.message || ''}`);
      setTimeout(() => setError(''), 3000);
    }
  };

  const handleRowClick = (workplace: Workplace) => {
    setModalFormData(workplace);
    setIsModalOpen(true);
    document.body.style.overflow = 'hidden';
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setModalFormData({});
    document.body.style.overflow = 'auto';
  };

  const handleModalChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;

    if (name === 'taxOffice' && value && !isValidTurkishText(value)) return;
    if (name === 'taxNumber' && value && !isNumericOnly(value)) return;

    if (name === 'sskRegistrationNo') {
      let formattedValue = value.replace(/\D/g, '');
      if (formattedValue.length > 21) formattedValue = formattedValue.substring(0, 21);
      let finalValue = '';
      for (let i = 0; i < formattedValue.length; i++) {
        if ([1, 5, 7, 9, 14, 17, 19].includes(i)) {
          finalValue += ' ' + formattedValue[i];
        } else {
          finalValue += formattedValue[i];
        }
      }
      setModalFormData(prev => ({ ...prev, [name]: finalValue }));
    } else {
      setModalFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleModalSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      if (modalFormData.id) {
        const workplaceData = {
          ...modalFormData,
          employeeCount: parseInt(modalFormData.employeeCount as string) || 0,
          price: parseInt(parsePrice(modalFormData.price as string)) || 0,
          assignedExpertId: modalFormData.assignedExpertId || null,
          assignedDoctorId: modalFormData.assignedDoctorId || null,
          assignedDspId: modalFormData.assignedDspId || null
        };

        await updateWorkplace(modalFormData.id, workplaceData);
        setSuccess('İş yeri başarıyla güncellendi!');
        closeModal();
        await fetchAllData();
        setTimeout(() => setSuccess(''), 3000);
      }
    } catch (error: any) {
      setError(`İş yeri güncellenirken hata oluştu: ${error.message || ''}`);
      setTimeout(() => setError(''), 3000);
    }
  };

  const handleDelete = async (id: number) => {
    try {
      await deleteWorkplace(id);
      setSuccess('İş yeri başarıyla silindi!');
      closeModal();
      await fetchAllData();
      setTimeout(() => setSuccess(''), 3000);
    } catch (error: any) {
      setError(`İş yeri silinirken hata oluştu: ${error.message || ''}`);
      setTimeout(() => setError(''), 3000);
    }
  };

  const handleApprovalStatusChange = async (id: number, status: string) => {
    try {
      setWorkplaces(prev => 
        prev.map(w => w.id === id ? { ...w, approvalStatus: status } : w)
      );
      const workplace = workplaces.find(w => w.id === id);
      if (workplace) {
        await updateWorkplace(id, { ...workplace, approvalStatus: status });
      }
    } catch (error) {
      await fetchAllData();
    }
  };

  const sortWorkplaces = (key: string) => {
    let direction: 'asc' | 'desc' = 'asc';
    if (sortConfig?.key === key && sortConfig.direction === 'asc') {
      direction = 'desc';
    }
    setSortConfig({ key, direction });
  };

  const getCurrentPageWorkplaces = () => {
    const startIndex = (currentPage - 1) * workplacesPerPage;
    return getSortedWorkplaces().slice(startIndex, startIndex + workplacesPerPage);
  };

  return (
    <div className="dashboard-container">
      {success && <div className="popup-notification alert alert-info">{success}</div>}
      {error && <div className="popup-notification alert alert-warning">{error}</div>}
      {duplicateWarning.show && (
        <div className="duplicate-warning-popup">
          <h3>Kayıt Zaten Mevcut</h3>
          <p>Bu kayıt daha önce girilmiş.</p>
        </div>
      )}

      <div className="form-container">
        <form onSubmit={handleSubmit} className="workplace-form">
          <div className="form-row">
            <div className="form-col">
              <label>İş Yeri Adı:</label>
              <input type="text" name="name" value={formData.name} onChange={handleChange} required />
            </div>
            <div className="form-col">
              <label>SGK İşyeri Sicil No:</label>
              <input type="text" name="sskRegistrationNo" value={formData.sskRegistrationNo} onChange={handleSSkRegistrationNoChange} required />
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-col">
              <label>Adres:</label>
              <textarea name="address" value={formData.address} onChange={handleChange} required />
            </div>
          </div>

          <div className="form-row">
            <div className="form-col">
              <label>Fiyat:</label>
              <input
                type="text"
                name="price"
                value={isPriceFocused ? formData.price : formatPriceForDisplay(formData.price)}
                onChange={handlePriceChange}
                onFocus={handlePriceFocus}
                onBlur={handlePriceBlur}
                required
              />
            </div>
            <div className="form-col">
              <label>Çalışan Sayısı:</label>
              <input type="text" name="employeeCount" value={formData.employeeCount} onChange={handleChange} required />
            </div>
            <div className="form-col">
              <label>Takip Edecek Uzman:</label>
              <select name="trackingExpertId" value={formData.trackingExpertId} onChange={handleChange}>
                <option value="">Seçiniz</option>
                {allExperts.map(e => <option key={e.id} value={e.id}>{e.firstName} {e.lastName}</option>)}
              </select>
            </div>
          </div>

          <div className="form-row">
            <div className="form-col">
              <label>Vergi Dairesi:</label>
              <input type="text" name="taxOffice" value={formData.taxOffice} onChange={handleChange} required />
            </div>
            <div className="form-col">
              <label>Vergi Numarası:</label>
              <input type="text" name="taxNumber" value={formData.taxNumber} onChange={handleChange} required />
            </div>
          </div>

          <div className="form-row">
            <div className="form-col">
              <label>Tehlike Sınıfı:</label>
              <select name="riskLevel" value={formData.riskLevel} onChange={handleChange} required>
                <option value="low">Az Tehlikeli</option>
                <option value="dangerous">Tehlikeli</option>
                <option value="veryDangerous">Çok Tehlikeli</option>
              </select>
            </div>
            <div className="form-col">
              <label>Kaynak:</label>
              <input type="text" name="source" value={formData.source} onChange={handleChange} required />
            </div>
          </div>

          <div className="form-row">
            <div className="form-col">
              <label>Atanan Uzman:</label>
              <select name="assignedExpertId" value={formData.assignedExpertId} onChange={handleChange}>
                <option value="">Seçiniz</option>
                {getFilteredExperts(formData.riskLevel, parseInt(formData.employeeCount) || 0).map(e => 
                  <option key={e.id} value={e.id}>{e.firstName} {e.lastName}</option>
                )}
              </select>
            </div>
            <div className="form-col">
              <label>Atanan Hekim:</label>
              <select name="assignedDoctorId" value={formData.assignedDoctorId} onChange={handleChange}>
                <option value="">Seçiniz</option>
                {getFilteredDoctors(formData.riskLevel, parseInt(formData.employeeCount) || 0).map(d => 
                  <option key={d.id} value={d.id}>{d.firstName} {d.lastName}</option>
                )}
              </select>
            </div>
          </div>

          <div className="form-row">
            <div className="form-col">
              <label>Atanan DSP:</label>
              <select 
                name="assignedDspId" 
                value={formData.assignedDspId} 
                onChange={handleChange}
                disabled={!isDspAssignmentAllowed(formData.riskLevel, parseInt(formData.employeeCount) || 0)}
              >
                <option value="">Seçiniz</option>
                {getFilteredDsps(formData.riskLevel, parseInt(formData.employeeCount) || 0).map(d => 
                  <option key={d.id} value={d.id}>{d.firstName} {d.lastName}</option>
                )}
              </select>
            </div>
            <div className="form-col">
              <label>Kayıt Tarihi:</label>
              <input type="date" name="registrationDate" value={formData.registrationDate} onChange={handleChange} required />
            </div>
            <div className="form-col">
              <button type="button" className="btn btn-secondary" onClick={handleClose}>Temizle</button>
              <button type="submit" className="btn btn-primary">Kaydet</button>
            </div>
          </div>
        </form>
      </div>

      <div className="rows-container">
        <div className="header-row">
          <button onClick={() => sortWorkplaces('name')}>İş Yeri Adı</button>
          <button onClick={() => sortWorkplaces('source')}>Kaynak</button>
          <button onClick={() => sortWorkplaces('registrationDate')}>Kayıt Tarihi</button>
          <button onClick={() => sortWorkplaces('trackingExpertId')}>Takip Edecek</button>
          <button onClick={() => sortWorkplaces('approvalStatus')}>Durum</button>
        </div>

        {getCurrentPageWorkplaces().map(workplace => (
          <div key={workplace.id} className="row-item" onClick={() => handleRowClick(workplace)}>
            <div>{workplace.name}</div>
            <div>{workplace.source}</div>
            <div>{formatDate(workplace.registrationDate)}</div>
            <div>{getTrackingExpertName(workplace.trackingExpertId, workplace)}</div>
            <select 
              value={workplace.approvalStatus}
              onChange={(e) => handleApprovalStatusChange(workplace.id, e.target.value)}
              onClick={(e) => e.stopPropagation()}
            >
              <option value="atama">Atama yapılacak</option>
              <option value="bekliyor">Onay bekleniyor</option>
              <option value="onaylandi">Onaylandı</option>
            </select>
          </div>
        ))}

        {totalPages > 1 && (
          <div className="pagination">
            <button onClick={() => handlePageChange(Math.max(1, currentPage - 1))}>Önceki</button>
            <span>Sayfa {currentPage} / {totalPages}</span>
            <button onClick={() => handlePageChange(Math.min(totalPages, currentPage + 1))}>Sonraki</button>
          </div>
        )}
      </div>

      {isModalOpen && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>İş Yeri Düzenle</h3>
              <button className="modal-close" onClick={closeModal}>×</button>
            </div>
            <div className="modal-body">
              <form onSubmit={handleModalSubmit}>
                <div className="form-row">
                  <div className="form-col">
                    <label>İş Yeri Adı:</label>
                    <input type="text" name="name" value={modalFormData.name || ''} onChange={handleModalChange} required />
                  </div>
                  <div className="form-col">
                    <label>SGK İşyeri Sicil No:</label>
                    <input type="text" name="sskRegistrationNo" value={modalFormData.sskRegistrationNo || ''} onChange={handleModalChange} required />
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-col">
                    <label>Adres:</label>
                    <textarea name="address" value={modalFormData.address || ''} onChange={handleModalChange} required />
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-col">
                    <label>Fiyat:</label>
                    <input
                      type="text"
                      name="price"
                      value={isModalPriceFocused ? (modalFormData.price || '') : formatPriceForDisplay(modalFormData.price as string || '')}
                      onChange={handleModalPriceChange}
                      onFocus={handleModalPriceFocus}
                      onBlur={handleModalPriceBlur}
                      required
                    />
                  </div>
                  <div className="form-col">
                    <label>Çalışan Sayısı:</label>
                    <input type="text" name="employeeCount" value={modalFormData.employeeCount || ''} onChange={handleModalChange} required />
                  </div>
                  <div className="form-col">
                    <label>Takip Edecek:</label>
                    <select name="trackingExpertId" value={modalFormData.trackingExpertId || ''} onChange={handleModalChange}>
                      <option value="">Seçiniz</option>
                      {allExperts.map(e => <option key={e.id} value={e.id}>{e.firstName} {e.lastName}</option>)}
                    </select>
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-col">
                    <label>Vergi Dairesi:</label>
                    <input type="text" name="taxOffice" value={modalFormData.taxOffice || ''} onChange={handleModalChange} required />
                  </div>
                  <div className="form-col">
                    <label>Vergi Numarası:</label>
                    <input type="text" name="taxNumber" value={modalFormData.taxNumber || ''} onChange={handleModalChange} required />
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-col">
                    <label>Tehlike Sınıfı:</label>
                    <select name="riskLevel" value={modalFormData.riskLevel || ''} onChange={handleModalChange} required>
                      <option value="low">Az Tehlikeli</option>
                      <option value="dangerous">Tehlikeli</option>
                      <option value="veryDangerous">Çok Tehlikeli</option>
                    </select>
                  </div>
                  <div className="form-col">
                    <label>Kaynak:</label>
                    <input type="text" name="source" value={modalFormData.source || ''} onChange={handleModalChange} required />
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-col">
                    <label>Atanan Uzman:</label>
                    <select name="assignedExpertId" value={modalFormData.assignedExpertId || ''} onChange={handleModalChange}>
                      <option value="">Seçiniz</option>
                      {allExperts.map(e => <option key={e.id} value={e.id}>{e.firstName} {e.lastName}</option>)}
                    </select>
                  </div>
                  <div className="form-col">
                    <label>Atanan Hekim:</label>
                    <select name="assignedDoctorId" value={modalFormData.assignedDoctorId || ''} onChange={handleModalChange}>
                      <option value="">Seçiniz</option>
                      {allDoctors.map(d => <option key={d.id} value={d.id}>{d.firstName} {d.lastName}</option>)}
                    </select>
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-col">
                    <label>Atanan DSP:</label>
                    <select name="assignedDspId" value={modalFormData.assignedDspId || ''} onChange={handleModalChange}>
                      <option value="">Seçiniz</option>
                      {allDsps.map(d => <option key={d.id} value={d.id}>{d.firstName} {d.lastName}</option>)}
                    </select>
                  </div>
                  <div className="form-col">
                    <label>Kayıt Tarihi:</label>
                    <input type="date" name="registrationDate" value={modalFormData.registrationDate || ''} onChange={handleModalChange} required />
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-col">
                    <label>Onay Durumu:</label>
                    <select name="approvalStatus" value={modalFormData.approvalStatus || ''} onChange={handleModalChange}>
                      <option value="atama">Atama yapılacak</option>
                      <option value="bekliyor">Onay bekleniyor</option>
                      <option value="onaylandi">Onaylandı</option>
                    </select>
                  </div>
                </div>

                <div className="modal-actions">
                  <button type="button" className="btn btn-danger" onClick={() => {
                    setShowDeleteConfirmation(true);
                  }}>
                    Sil
                  </button>
                  <button type="button" className="btn btn-secondary" onClick={closeModal}>
                    İptal
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Güncelle
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {showDeleteConfirmation && (
        <div className="modal-overlay" onClick={() => setShowDeleteConfirmation(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Kayıt Silme Onayı</h3>
              <button className="modal-close" onClick={() => setShowDeleteConfirmation(false)}>×</button>
            </div>
            <div className="modal-body">
              <p>Kayıtı gerçekten silmek istiyor musunuz?</p>
            </div>
            <div className="modal-actions">
              <button className="btn btn-secondary" onClick={() => setShowDeleteConfirmation(false)}>
                İptal
              </button>
              <button className="btn btn-danger" onClick={() => {
                if (modalFormData.id) {
                  handleDelete(modalFormData.id);
                  setShowDeleteConfirmation(false);
                }
              }}>
                Sil
              </button>
            </div>
          </div>
        </div>
      )}

      {showDspNotAllowedPopup && (
        <div className="modal-overlay" onClick={() => setShowDspNotAllowedPopup(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>DSP Atanamaz</h3>
              <button className="modal-close" onClick={() => setShowDspNotAllowedPopup(false)}>×</button>
            </div>
            <div className="modal-body">
              <p>Seçilen iş yerine DSP atanamaz.</p>
            </div>
            <div className="modal-actions">
              <button className="btn btn-primary" onClick={() => setShowDspNotAllowedPopup(false)}>
                Tamam
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
