import React, { useState, useEffect, useRef, useCallback } from 'react';
import { createPortal } from 'react-dom';
import { useLocation } from 'react-router-dom';
import './Dashboard.css';
import { createWorkplace, updateWorkplace, getAvailableExperts, getAvailableDoctors, getAvailableDsps, getAllWorkplaces, deleteWorkplace, getAllExperts, getAllDoctors, getAllDsps } from '../services/api';

// Define types
interface Workplace {
  id: number;
  name: string;
  sskRegistrationNo: string;
  address: string;
  price: string;
  taxOffice: string;
  taxNumber: string;
  riskLevel: string;
  employeeCount: string;
  source: string;
  assignedExpertId: string;
  assignedDoctorId: string;
  assignedDspId: string;
  registrationDate: string;
  approvalStatus: string;
}

const Dashboard: React.FC = () => {
  console.log('Dashboard component rendered at:', new Date().toISOString());
  const location = useLocation();
  
  // State declarations
  const [formData, setFormData] = useState({
    name: '',
    sskRegistrationNo: '',
    address: '',
    price: '',
    taxOffice: '',
    taxNumber: '',
    riskLevel: 'low',
    employeeCount: '',
    source: '',
    assignedExpertId: '',
    assignedDoctorId: '',
    assignedDspId: '',
    registrationDate: '',
  });
  
  const [isKdvIncluded, setIsKdvIncluded] = useState<boolean>(false);
  const [workplaces, setWorkplaces] = useState<Workplace[]>([]);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [isModalOpen, setIsModalOpen] = useState<boolean>(false);
  const [modalFormData, setModalFormData] = useState<Partial<Workplace>>({});
  const [sortMenuOpen, setSortMenuOpen] = useState<boolean>(false);
  const [sortCriteria, setSortCriteria] = useState<string>('name');
  const [showNoExpertPopup, setShowNoExpertPopup] = useState<boolean>(false);
  const [showNoDoctorPopup, setShowNoDoctorPopup] = useState<boolean>(false);
  const [showNoDspPopup, setShowNoDspPopup] = useState<boolean>(false);
  const [showDspNotAllowedPopup, setShowDspNotAllowedPopup] = useState<boolean>(false);
  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState<boolean>(false);
  const [allExperts, setAllExperts] = useState<any[]>([]);
  const [allDoctors, setAllDoctors] = useState<any[]>([]);
  const [allDsps, setAllDsps] = useState<any[]>([]);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');

  const sortButtonRef = useRef<HTMLButtonElement>(null);
  
  // Constants
  const workplacesPerPage = 10;
  
  // Derived state
  const totalPages = Math.ceil(workplaces.length / workplacesPerPage);
  
  // Add a useEffect to log when component mounts and unmounts
  useEffect(() => {
    console.log('Dashboard component mounted at:', new Date().toISOString());
    
    // Cleanup function
    return () => {
      console.log('Dashboard component unmounted at:', new Date().toISOString());
    };
  }, []);
  
  // Function to fetch all data - similar to ExpertCardPage approach
  const fetchAllData = useCallback(async () => {
    try {
      console.log('Fetching all data from backend...');
      
      // Fetch all data in parallel
      const [workplacesData, expertsData, doctorsData, dspsData] = await Promise.all([
        getAllWorkplaces(),
        getAllExperts(),
        getAllDoctors(),
        getAllDsps()
      ]);
      
      console.log('Received all data:', { 
        workplacesCount: workplacesData.length, 
        expertsCount: expertsData.length, 
        doctorsCount: doctorsData.length, 
        dspsCount: dspsData.length 
      });
      
      // Update all state
      setWorkplaces(workplacesData);
      setAllExperts(expertsData);
      setAllDoctors(doctorsData);
      setAllDsps(dspsData);
      console.log('State updated after data fetch');
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Veri alınırken hata oluştu');
    }
  }, []);

  // Load all data from backend on component mount - similar to ExpertCardPage approach
  useEffect(() => {
    console.log('Dashboard useEffect running - fetching all data at:', new Date().toISOString());
    fetchAllData();
  }, []);

  // Refresh data when location changes
  useEffect(() => {
    console.log('Location changed to:', location.pathname, 'at:', new Date().toISOString());
    fetchAllData();
  }, [location.pathname]);

  // Refresh data when window regains focus
  useEffect(() => {
    const handleFocus = () => {
      console.log('Window focused, refreshing all data...');
      fetchAllData();
    };

