import React, { useState, useEffect, useRef } from 'react';
import { createExpert, getAllExperts, deleteExpert } from '../services/api';
import './ExpertCardPage.css';
import { useNavigate } from 'react-router-dom';

const ExpertCardPage = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    expertiseClass: 'A',
    assignedMinutes: 11900
  });
  const [experts, setExperts] = useState<any[]>([]);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  
  // Drag states for "Yeni Uzman Kayƒ±t" card
  const [isDraggingNew, setIsDraggingNew] = useState(false);
  const [dragOffsetNew, setDragOffsetNew] = useState({ x: 0, y: 0 });
  const [newCardPosition, setNewCardPosition] = useState({ x: 50, y: 150 });
  const newCardRef = useRef<HTMLDivElement>(null);
  
  // Drag states for "Kayƒ±tlƒ± Uzmanlar" card
  const [isDraggingList, setIsDraggingList] = useState(false);
  const [dragOffsetList, setDragOffsetList] = useState({ x: 0, y: 0 });
  const [listCardPosition, setListCardPosition] = useState({ x: 400, y: 150 });
  const listCardRef = useRef<HTMLDivElement>(null);
  
  // Resize states for "Yeni Uzman Kayƒ±t" card
  const [isResizingNew, setIsResizingNew] = useState(false);
  const [resizeStartNew, setResizeStartNew] = useState({ x: 0, y: 0 });
  const [newCardSize, setNewCardSize] = useState({ width: 400, height: 500 });
  
  // Resize states for "Kayƒ±tlƒ± Uzmanlar" card
  const [isResizingList, setIsResizingList] = useState(false);
  const [resizeStartList, setResizeStartList] = useState({ x: 0, y: 0 });
  const [listCardSize, setListCardSize] = useState({ width: 500, height: 500 });
  
  const navigate = useNavigate();

  useEffect(() => {
    fetchExperts();
  }, []);

  const fetchExperts = async () => {
    try {
      const data = await getAllExperts();
      setExperts(data);
    } catch (err) {
      console.error('Uzmanlar getirilirken hata olu≈ütu:', err);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'assignedMinutes' ? parseInt(value) : value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (formData.assignedMinutes > 11900) {
      setError('Atanan dakika 11.900\'√º ge√ßemez');
      return;
    }
    
    try {
      await createExpert(formData);
      setSuccess('Uzman ba≈üarƒ±yla kaydedildi!');
      setFormData({
        firstName: '',
        lastName: '',
        phone: '',
        expertiseClass: 'A',
        assignedMinutes: 11900
      });
      // Refresh the experts list
      fetchExperts();
    } catch (err) {
      setError('Uzman kaydedilirken hata olu≈ütu');
    }
  };

  const handleDelete = async (id: number) => {
    try {
      await deleteExpert(id);
      setSuccess('Uzman ba≈üarƒ±yla silindi!');
      // Refresh the experts list
      fetchExperts();
    } catch (err) {
      setError('Uzman silinirken hata olu≈ütu');
    }
  };

  const handleClose = () => {
    // Redirect to dashboard
    navigate('/dashboard');
  };

  // Drag functions for "Yeni Uzman Kayƒ±t" card
  const handleMouseDownNew = (e: React.MouseEvent) => {
    if (newCardRef.current && e.target === e.currentTarget) {
      const rect = newCardRef.current.getBoundingClientRect();
      setIsDraggingNew(true);
      setDragOffsetNew({
        x: e.clientX - rect.left,
        y: e.clientY - rect.top
      });
    }
  };

  // Drag functions for "Kayƒ±tlƒ± Uzmanlar" card
  const handleMouseDownList = (e: React.MouseEvent) => {
    if (listCardRef.current && e.target === e.currentTarget) {
      const rect = listCardRef.current.getBoundingClientRect();
      setIsDraggingList(true);
      setDragOffsetList({
        x: e.clientX - rect.left,
        y: e.clientY - rect.top
      });
    }
  };

  const handleMouseMove = (e: MouseEvent) => {
    // Handle dragging for "Yeni Uzman Kayƒ±t" card
    if (isDraggingNew) {
      setNewCardPosition({
        x: e.clientX - dragOffsetNew.x,
        y: e.clientY - dragOffsetNew.y
      });
    }
    
    // Handle dragging for "Kayƒ±tlƒ± Uzmanlar" card
    if (isDraggingList) {
      setListCardPosition({
        x: e.clientX - dragOffsetList.x,
        y: e.clientY - dragOffsetList.y
      });
    }
    
    // Handle resizing for "Yeni Uzman Kayƒ±t" card
    if (isResizingNew) {
      const width = Math.max(300, e.clientX - resizeStartNew.x);
      const height = Math.max(200, e.clientY - resizeStartNew.y);
      setNewCardSize({ width, height });
    }
    
    // Handle resizing for "Kayƒ±tlƒ± Uzmanlar" card
    if (isResizingList) {
      const width = Math.max(300, e.clientX - resizeStartList.x);
      const height = Math.max(200, e.clientY - resizeStartList.y);
      setListCardSize({ width, height });
    }
  };

  const handleMouseUp = () => {
    setIsDraggingNew(false);
    setIsDraggingList(false);
    setIsResizingNew(false);
    setIsResizingList(false);
  };

  // Resize functions for "Yeni Uzman Kayƒ±t" card
  const handleResizeStartNew = (e: React.MouseEvent) => {
    e.stopPropagation();
    setIsResizingNew(true);
    setResizeStartNew({ x: e.clientX, y: e.clientY });
  };

  // Resize functions for "Kayƒ±tlƒ± Uzmanlar" card
  const handleResizeStartList = (e: React.MouseEvent) => {
    e.stopPropagation();
    setIsResizingList(true);
    setResizeStartList({ x: e.clientX, y: e.clientY });
  };

  useEffect(() => {
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
    
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isDraggingNew, isDraggingList, isResizingNew, isResizingList]);

  return (
    <div className="expert-card-page">
      <div className="expert-card-layout">
        {/* "Yeni Uzman Kayƒ±t" card (draggable and resizable) */}
        <div 
          className="draggable-card"
          ref={newCardRef}
          style={{
            position: 'absolute',
            left: newCardPosition.x,
            top: newCardPosition.y,
            width: newCardSize.width,
            height: newCardSize.height,
            zIndex: 1000,
            cursor: isDraggingNew ? 'grabbing' : 'grab'
          }}
        >
          <div className="window">
            <div 
              className="window-header"
              onMouseDown={handleMouseDownNew}
              style={{ cursor: 'move' }}
            >
              <span>Yeni Uzman Kayƒ±t</span>
              <div className="window-controls">
                <button className="window-button" onClick={handleClose}>√ó</button>
              </div>
            </div>
            <div className="window-content" style={{ height: 'calc(100% - 40px)' }}>
              {success && <div className="alert alert-info">{success}</div>}
              {error && <div className="alert alert-warning">{error}</div>}
              
              <form onSubmit={handleSubmit} className="registration-form">
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Ad:</label>
                      <input
                        type="text"
                        name="firstName"
                        className="form-input"
                        value={formData.firstName}
                        onChange={handleChange}
                        required
                      />
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Soyad:</label>
                      <input
                        type="text"
                        name="lastName"
                        className="form-input"
                        value={formData.lastName}
                        onChange={handleChange}
                        required
                      />
                    </div>
                  </div>
                </div>
                
                <div className="form-group">
                  <label className="form-label">Telefon:</label>
                  <input
                    type="text"
                    name="phone"
                    className="form-input"
                    value={formData.phone}
                    onChange={handleChange}
                    required
                  />
                </div>
                
                <div className="form-row">
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Uzmanlƒ±k Sƒ±nƒ±fƒ±:</label>
                      <select
                        name="expertiseClass"
                        className="form-select"
                        value={formData.expertiseClass}
                        onChange={handleChange}
                      >
                        <option value="A">A</option>
                        <option value="B">B</option>
                        <option value="C">C</option>
                      </select>
                    </div>
                  </div>
                  <div className="form-col">
                    <div className="form-group">
                      <label className="form-label">Atanan Dakika:</label>
                      <input
                        type="number"
                        name="assignedMinutes"
                        className="form-input"
                        value={formData.assignedMinutes}
                        onChange={handleChange}
                        min="0"
                        max="11900"
                        required
                      />
                    </div>
                  </div>
                </div>
                
                <button type="submit" className="btn btn-primary">Uzmanƒ± Kaydet</button>
              </form>
            </div>
            <div 
              className="resize-handle"
              onMouseDown={handleResizeStartNew}
            />
          </div>
        </div>
        
        {/* "Kayƒ±tlƒ± Uzmanlar" card (draggable and resizable) */}
        <div 
          className="draggable-card"
          ref={listCardRef}
          style={{
            position: 'absolute',
            left: listCardPosition.x,
            top: listCardPosition.y,
            width: listCardSize.width,
            height: listCardSize.height,
            zIndex: 1000,
            cursor: isDraggingList ? 'grabbing' : 'grab'
          }}
        >
          <div className="window">
            <div 
              className="window-header"
              onMouseDown={handleMouseDownList}
              style={{ cursor: 'move' }}
            >
              <span>Kayƒ±tlƒ± Uzmanlar</span>
            </div>
            <div className="window-content" style={{ height: 'calc(100% - 40px)', overflow: 'auto' }}>
              {experts.length === 0 ? (
                <p>Kayƒ±tlƒ± uzman bulunmamaktadƒ±r.</p>
              ) : (
                <div className="expert-list">
                  {experts.map((expert) => (
                    <div key={expert.id} className="expert-card">
                      <div className="expert-info">
                        <span>{expert.firstName} {expert.lastName} - {expert.phone} - Sƒ±nƒ±f {expert.expertiseClass} - {expert.assignedMinutes} dakika</span>
                      </div>
                      <div className="expert-actions">
                        <button className="btn-icon edit">‚úèÔ∏è</button>
                        <button className="btn-icon delete" onClick={() => handleDelete(expert.id)}>üóëÔ∏è</button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
            <div 
              className="resize-handle"
              onMouseDown={handleResizeStartList}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default ExpertCardPage;
