import React, { createContext, useState, useContext, useEffect, ReactNode } from 'react';
import { getCurrentUser, setAuthToken, setOrganizationId } from '../services/api';

// Define types
interface User {
  id: number;
  username: string;
  fullName: string;
  role: string;
  organizationId: number | null;
}

interface AuthContextType {
  user: User | null;
  login: (userData: User) => void;
  logout: () => void;
  isAuthenticated: boolean;
  organizationId: number | null;
}

// Create context
const AuthContext = createContext<AuthContextType | undefined>(undefined);

// AuthProvider component
export const AuthProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [organizationId, setOrgId] = useState<number | null>(null);

  // Check if user is authenticated on app load
  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (token) {
      setAuthToken(token);
      fetchCurrentUser();
    }
  }, []);

  const fetchCurrentUser = async () => {
    try {
      const userData = await getCurrentUser();
      setUser({
        id: userData.id,
        username: userData.username,
        fullName: userData.fullName,
        role: userData.role,
        organizationId: userData.organizationId
      });
      
      if (userData.organizationId) {
        setOrgId(userData.organizationId);
        setOrganizationId(userData.organizationId);
      }
    } catch (error) {
      console.error('Failed to fetch current user:', error);
      logout();
    }
  };

  const login = (userData: User) => {
    setUser(userData);
    if (userData.organizationId) {
      setOrgId(userData.organizationId);
      setOrganizationId(userData.organizationId);
    }
  };

  const logout = () => {
    setUser(null);
    setOrgId(null);
    localStorage.removeItem('authToken');
    setAuthToken('');
    setOrganizationId(0);
  };

  const isAuthenticated = !!user;

  return (
    <AuthContext.Provider value={{ user, login, logout, isAuthenticated, organizationId }}>
      {children}
    </AuthContext.Provider>
  );
};

// Custom hook to use auth context
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};
