import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllExperts, getExpertAssignedWorkplaces, getExpertVisits, updateVisitStatus } from '../services/api';
import './VisitsPage.css';

interface Expert {
  id: number;
  firstName: string;
  lastName: string;
  phone: string;
  expertiseClass: string;
}

interface Workplace {
  id: number;
  name: string;
  address: string;
  sskRegistrationNo: string;
  // Add other workplace properties as needed
}

interface VisitStatus {
  [workplaceId: number]: {
    [month: string]: boolean; // true if visited, false if not
  };
}

interface VisitSummary {
  [workplaceId: number]: {
    workplaceName: string;
    visits: {
      [month: string]: {
        visited: boolean;
        visitDate: string | null;
      };
    };
  };
}

const VisitsPage: React.FC = () => {
  const navigate = useNavigate();
  const [experts, setExperts] = useState<Expert[]>([]);
  const [selectedExpert, setSelectedExpert] = useState<Expert | null>(null);
  const [workplaces, setWorkplaces] = useState<Workplace[]>([]);
  const [visitStatus, setVisitStatus] = useState<VisitStatus>({});
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string>('');
  const [sortOption, setSortOption] = useState<'all' | 'visited' | 'not-visited'>('all');
  const [currentMonthOffset, setCurrentMonthOffset] = useState<number>(0);

  // Get month names for the current, previous, and next months
  const getMonthNames = () => {
    const months = [];
    const currentDate = new Date();
    
    for (let i = -1; i <= 1; i++) {
      const date = new Date(currentDate.getFullYear(), currentDate.getMonth() + currentMonthOffset + i, 1);
      const monthName = date.toLocaleDateString('tr-TR', { month: 'long', year: 'numeric' });
      months.push({
        name: monthName,
        key: `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`
      });
    }
    
    return months;
  };

  const months = getMonthNames();

  useEffect(() => {
    fetchExperts();
  }, []);

  const fetchExperts = async () => {
    try {
      setLoading(true);
      const data = await getAllExperts();
      setExperts(data);
      setError('');
    } catch (err: any) {
      console.error('Error fetching experts:', err);
      setError('Uzmanlar getirilirken hata oluştu: ' + (err.message || ''));
    } finally {
      setLoading(false);
    }
  };

  const fetchWorkplacesForExpert = async (expertId: number) => {
    try {
      setLoading(true);
      const [workplacesData, visitsData] = await Promise.all([
        getExpertAssignedWorkplaces(expertId),
        getExpertVisits(expertId)
      ]);
      
      setWorkplaces(workplacesData);
      setError('');
      
      // Initialize visit status from backend data
      const initialStatus: VisitStatus = {};
      workplacesData.forEach((workplace: Workplace) => {
        initialStatus[workplace.id] = {};
        months.forEach(month => {
          // Check if we have visit data for this workplace and month
          if (visitsData[workplace.id] && visitsData[workplace.id].visits[month.key]) {
            initialStatus[workplace.id][month.key] = visitsData[workplace.id].visits[month.key].visited;
          } else {
            // Default to not visited if no data exists
            initialStatus[workplace.id][month.key] = false;
          }
        });
      });
      setVisitStatus(initialStatus);
    } catch (err: any) {
      console.error('Error fetching data:', err);
      setError('Veriler getirilirken hata oluştu: ' + (err.message || ''));
    } finally {
      setLoading(false);
    }
  };

  const handleExpertSelect = (expert: Expert) => {
    setSelectedExpert(expert);
    fetchWorkplacesForExpert(expert.id);
  };

  const handleBackToExperts = () => {
    setSelectedExpert(null);
    setWorkplaces([]);
    setVisitStatus({});
  };

  const toggleVisitStatus = (workplaceId: number, monthKey: string) => {
    setVisitStatus(prev => ({
      ...prev,
      [workplaceId]: {
        ...prev[workplaceId],
        [monthKey]: !prev[workplaceId]?.[monthKey]
      }
    }));
  };

  const filteredWorkplaces = workplaces.filter(workplace => {
    if (sortOption === 'all') return true;
    
    const hasVisitedMonth = months.some(month => 
      visitStatus[workplace.id]?.[month.key] === true
    );
    
    if (sortOption === 'visited') return hasVisitedMonth;
    if (sortOption === 'not-visited') return !hasVisitedMonth;
    
    return true;
  });

  const handleMonthChange = (offset: number) => {
    setCurrentMonthOffset(prev => prev + offset);
  };

  if (loading) {
    return (
      <div className="visits-page-container">
        <div className="page-header">
          <h2>Ziyaretler</h2>
          <p>Ziyaret takibi ve yönetimi</p>
        </div>
        <div className="loading">Yükleniyor...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="visits-page-container">
        <div className="page-header">
          <h2>Ziyaretler</h2>
          <p>Ziyaret takibi ve yönetimi</p>
        </div>
        <div className="error">{error}</div>
        <button className="btn btn-primary" onClick={() => navigate('/')}>
          Ana Sayfaya Dön
        </button>
      </div>
    );
  }

  return (
    <div className="visits-page-container">
      <div className="page-header">
        <h2>Ziyaretler</h2>
        <p>Ziyaret takibi ve yönetimi</p>
      </div>

      {!selectedExpert ? (
        // Experts list view
        <div className="experts-list">
          <h3>Kayıtlı Uzmanlar</h3>
          {experts.length === 0 ? (
            <div className="no-workplaces">Kayıtlı uzman bulunamadı</div>
          ) : (
            experts.map(expert => (
              <div 
                key={expert.id} 
                className="expert-item"
                onClick={() => handleExpertSelect(expert)}
              >
                <div className="expert-info">
                  <div className="expert-name">{expert.firstName} {expert.lastName}</div>
                  <div className="expert-phone">{expert.phone}</div>
                </div>
                <div className={`expert-class ${expert.expertiseClass.toLowerCase()}`}>
                  {expert.expertiseClass} Sınıfı
                </div>
              </div>
            ))
          )}
        </div>
      ) : (
        // Visits details view
        <div className="visits-details">
          <div className="visits-header">
            <h3>{selectedExpert.firstName} {selectedExpert.lastName} - Ziyaret Detayları</h3>
            <button className="back-button" onClick={handleBackToExperts}>
              ← Uzman Listesine Dön
            </button>
          </div>

          <div className="month-navigation">
            <button 
              className="month-button" 
              onClick={() => handleMonthChange(-1)}
            >
              ← Önceki Ay
            </button>
            <div className="current-month">
              {months[1]?.name || 'Geçerli Ay'}
            </div>
            <button 
              className="month-button" 
              onClick={() => handleMonthChange(1)}
            >
              Sonraki Ay →
            </button>
          </div>

          <div className="sort-controls">
            <button 
              className={`sort-button ${sortOption === 'all' ? 'active' : ''}`}
              onClick={() => setSortOption('all')}
            >
              Tüm İş Yerleri
            </button>
            <button 
              className={`sort-button ${sortOption === 'visited' ? 'active' : ''}`}
              onClick={() => setSortOption('visited')}
            >
              Ziyaret Edilenler
            </button>
            <button 
              className={`sort-button ${sortOption === 'not-visited' ? 'active' : ''}`}
              onClick={() => setSortOption('not-visited')}
            >
              Ziyaret Edilmeyenler
            </button>
          </div>

          {filteredWorkplaces.length === 0 ? (
            <div className="no-workplaces">
              {sortOption === 'all' 
                ? 'Bu uzmana atanmış iş yeri bulunamadı' 
                : sortOption === 'visited' 
                  ? 'Henüz ziyaret edilen iş yeri bulunmamaktadır' 
                  : 'Ziyaret edilmemiş iş yeri bulunmamaktadır'}
            </div>
          ) : (
            <div className="workplaces-grid">
              {filteredWorkplaces.map(workplace => (
                <div key={workplace.id} className="workplace-card">
                  <div className="workplace-name">{workplace.name}</div>
                  <div className="months-container">
                    {months.map(month => (
                      <div key={month.key} className="month-section">
                        <div className="month-header">{month.name}</div>
                        <div className="visit-status-container">
                          <div 
                            className={`visit-status ${
                              visitStatus[workplace.id]?.[month.key] 
                                ? 'visited' 
                                : visitStatus[workplace.id]?.[month.key] === false 
                                  ? 'not-visited' 
                                  : 'pending'
                            }`}
                            onClick={() => toggleVisitStatus(workplace.id, month.key)}
                            title={
                              visitStatus[workplace.id]?.[month.key] 
                                ? 'Ziyaret edildi (değiştirmek için tıklayın)' 
                                : visitStatus[workplace.id]?.[month.key] === false 
                                  ? 'Ziyaret edilmedi (değiştirmek için tıklayın)' 
                                  : 'Durum belirsiz (değiştirmek için tıklayın)'
                            }
                          >
                            {visitStatus[workplace.id]?.[month.key] ? '✓' : '✕'}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default VisitsPage;
