import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { io, Socket } from 'socket.io-client';
import { getAllExperts, getExpertAssignedWorkplaces, getExpertVisitsByMonth } from '../services/api';
import './VisitsPage.css';

interface Expert {
  id: number;
  firstName: string;
  lastName: string;
  phone: string;
  expertiseClass: string;
  assignedMinutes: number;
  usedMinutes: number;
}

interface ExpertStats {
  expert: Expert;
  assignedWorkplaceCount: number;
  trackedWorkplaceCount: number;
  totalWorkplaceCount: number;
  currentMonthVisitCount: number;
  remainingWorkplaceCount: number;
}

interface Visit {
  id: number;
  expertId: number;
  workplaceId: number;
  visitMonth: string;
  visited: boolean;
  visitDate: string;
  Workplace?: {
    id: number;
    name: string;
  };
}

// Function to get the current month name in Turkish
const getCurrentMonthName = () => {
  const months = [
    'Ocak', 'Şubat', 'Mart', 'Nisan', 'Mayıs', 'Haziran',
    'Temmuz', 'Ağustos', 'Eylül', 'Ekim', 'Kasım', 'Aralık'
  ];
  const currentMonthIndex = new Date().getMonth();
  return months[currentMonthIndex];
};

// Function to get the current month in YYYY-MM format
const getCurrentMonth = () => {
  const now = new Date();
  const year = now.getFullYear();
  const month = String(now.getMonth() + 1).padStart(2, '0');
  return `${year}-${month}`;
};

// Function to get all months for the past year
const getPastYearMonths = () => {
  const months = [];
  const now = new Date();
  
  // Add current month first
  const currentYear = now.getFullYear();
  const currentMonth = String(now.getMonth() + 1).padStart(2, '0');
  const currentMonthName = [
    'Ocak', 'Şubat', 'Mart', 'Nisan', 'Mayıs', 'Haziran',
    'Temmuz', 'Ağustos', 'Eylül', 'Ekim', 'Kasım', 'Aralık'
  ][now.getMonth()];
  
  months.push({
    value: `${currentYear}-${currentMonth}`,
    label: `${currentMonthName} ${currentYear} (Mevcut Ay)`
  });
  
  // Add previous months
  for (let i = 1; i < 12; i++) {
    const date = new Date(now.getFullYear(), now.getMonth() - i, 1);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const monthName = [
      'Ocak', 'Şubat', 'Mart', 'Nisan', 'Mayıs', 'Haziran',
      'Temmuz', 'Ağustos', 'Eylül', 'Ekim', 'Kasım', 'Aralık'
    ][date.getMonth()];
    
    months.push({
      value: `${year}-${month}`,
      label: `${monthName} ${year}`
    });
  }
  
  return months;
};

// Function to format date
const formatDate = (dateString: string) => {
  if (!dateString) return 'Ziyaret yapılmadı';
  const date = new Date(dateString);
  return date.toLocaleDateString('tr-TR');
};

const VisitsPage: React.FC = () => {
  const navigate = useNavigate();
  const [experts, setExperts] = useState<Expert[]>([]);
  const [expertStats, setExpertStats] = useState<ExpertStats[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string>('');
  const socketRef = useRef<Socket | null>(null);
  const currentMonthName = getCurrentMonthName();

  // State for expert popup modal
  const [isExpertModalOpen, setIsExpertModalOpen] = useState<boolean>(false);
  const [selectedExpert, setSelectedExpert] = useState<Expert | null>(null);
  const [selectedMonth, setSelectedMonth] = useState<string>(getCurrentMonth());
  const [months] = useState<any[]>(getPastYearMonths());
  const [expertVisits, setExpertVisits] = useState<Visit[]>([]);
  const [visitsLoading, setVisitsLoading] = useState<boolean>(false);

  useEffect(() => {
    fetchExpertsWithStats();
    
    // Initialize WebSocket connection
    socketRef.current = io('http://localhost:5001');
    
    // Clean up WebSocket connection
    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, []);

  useEffect(() => {
    if (isExpertModalOpen && selectedExpert) {
      fetchExpertVisits(selectedExpert.id, selectedMonth);
    }
  }, [isExpertModalOpen, selectedExpert, selectedMonth]);

  const fetchExpertsWithStats = async () => {
    try {
      setLoading(true);
      const expertsData = await getAllExperts();
      setExperts(expertsData);
      
      // Fetch stats for each expert
      const statsPromises = expertsData.map(async (expert: Expert) => {
        try {
          // Get assigned workplaces
          const assignedWorkplaces = await getExpertAssignedWorkplaces(expert.id);
          
          // For demo purposes, we'll use placeholder values since we don't have real data
          // In a real implementation, you would calculate these values based on actual data
          return {
            expert,
            assignedWorkplaceCount: assignedWorkplaces.length,
            trackedWorkplaceCount: Math.floor(assignedWorkplaces.length * 0.8), // 80% for demo
            totalWorkplaceCount: assignedWorkplaces.length,
            currentMonthVisitCount: Math.floor(assignedWorkplaces.length * 0.6), // 60% for demo
            remainingWorkplaceCount: Math.ceil(assignedWorkplaces.length * 0.4) // 40% for demo
          };
        } catch (err) {
          // Return default values if there's an error fetching stats
          return {
            expert,
            assignedWorkplaceCount: 0,
            trackedWorkplaceCount: 0,
            totalWorkplaceCount: 0,
            currentMonthVisitCount: 0,
            remainingWorkplaceCount: 0
          };
        }
      });
      
      const statsData = await Promise.all(statsPromises);
      setExpertStats(statsData);
      setError('');
    } catch (err: any) {
      console.error('Error fetching experts:', err);
      setError('Uzmanlar getirilirken hata oluştu: ' + (err.message || ''));
    } finally {
      setLoading(false);
    }
  };

  const fetchExpertVisits = async (expertId: number, month: string) => {
    try {
      setVisitsLoading(true);
      const visitsData = await getExpertVisitsByMonth(expertId, month);
      setExpertVisits(visitsData);
    } catch (err: any) {
      console.error('Error fetching expert visits:', err);
      setError('Ziyaretler getirilirken hata oluştu: ' + (err.message || ''));
    } finally {
      setVisitsLoading(false);
    }
  };

  // Function to open expert modal
  const openExpertModal = (expert: Expert) => {
    setSelectedExpert(expert);
    setSelectedMonth(getCurrentMonth());
    setIsExpertModalOpen(true);
  };

  // Function to close expert modal
  const closeExpertModal = () => {
    setIsExpertModalOpen(false);
    setSelectedExpert(null);
    setExpertVisits([]);
  };

  // Handle month change
  const handleMonthChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedMonth(event.target.value);
  };

  // Calculate total statistics
  const calculateTotalStats = () => {
    return expertStats.reduce((totals, stat) => {
      return {
        totalWorkplaces: totals.totalWorkplaces + stat.totalWorkplaceCount,
        visitedWorkplaces: totals.visitedWorkplaces + stat.currentMonthVisitCount,
        remainingWorkplaces: totals.remainingWorkplaces + stat.remainingWorkplaceCount
      };
    }, { totalWorkplaces: 0, visitedWorkplaces: 0, remainingWorkplaces: 0 });
  };

  const totalStats = calculateTotalStats();

  if (loading) {
    return (
      <div className="visits-page-container">
        <div className="page-header">
          <h2>Ziyaretler</h2>
        </div>
        <div className="loading">Yükleniyor...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="visits-page-container">
        <div className="page-header">
          <h2>Ziyaretler</h2>
        </div>
        <div className="error">{error}</div>
        <button className="btn btn-primary" onClick={() => navigate('/')}>
          Ana Sayfaya Dön
        </button>
      </div>
    );
  }

  return (
    <div className="visits-page-container">
      <div className="page-header">
        <h2>Ziyaretler</h2>
      </div>

      <div className="column-headers">
        <span className="name-header">Ad Soyad</span>
        <div className="class-header-container">
          <span className="class-header">Sınıfı</span>
          <div className="class-badges-placeholder"></div>
        </div>
        <div className="assigned-workplaces-header">
          <span>Atanan</span>
          <span>İşyerleri</span>
        </div>
        <div className="tracked-workplaces-header">
          <span>Takip edilecek</span>
          <span>İşyerleri</span>
        </div>
        <div className="remaining-visits-header">
          <span>Gerçekleşen</span>
          <span>Ziyaretler</span>
        </div>
        <div className="remaining-visits-count-header">
          <span>Kalan</span>
          <span>Ziyaretler</span>
        </div>
      </div>

      <div className="visits-content-wrapper">
        <div className="experts-table-container">
          <table className="experts-table">
            <tbody>
              {expertStats.map((stat) => (
                <tr key={stat.expert.id} className="expert-row">
                  <td 
                    className="expert-name-cell"
                    onClick={() => openExpertModal(stat.expert)}
                  >
                    {stat.expert.firstName} {stat.expert.lastName}
                  </td>
                  <td>
                    <span className={`expert-class-badge ${stat.expert.expertiseClass.toLowerCase()}`}>
                      {stat.expert.expertiseClass} Sınıfı
                    </span>
                  </td>
                  <td>{stat.assignedWorkplaceCount}</td>
                  <td>{stat.trackedWorkplaceCount}</td>
                  <td>{stat.currentMonthVisitCount}</td>
                  <td>{stat.remainingWorkplaceCount}</td>
                </tr>
              ))}
            </tbody>
          </table>
          
          {expertStats.length === 0 && (
            <div className="no-workplaces">Kayıtlı uzman bulunamadı</div>
          )}
        </div>

        <div className="statistics-panel">
          <h3 className="statistics-header">Ziyaret İstatistikleri</h3>
          <div className="statistics-content">
            <div className="stat-item">
              <span className="stat-label">{currentMonthName} ayında ziyaret edilmesi gereken</span>
              
              <span className="stat-value">{totalStats.totalWorkplaces}</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">{currentMonthName} ayından ziyareti yapılan işletme sayısı:</span>
              <span className="stat-value">{totalStats.visitedWorkplaces}</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">{currentMonthName} ayında ziyareti kalan işletme sayısı:</span>
              <span className="stat-value">{totalStats.remainingWorkplaces}</span>
            </div>
          </div>
        </div>
      </div>

      {/* Expert Modal Popup */}
      {isExpertModalOpen && selectedExpert && (
        <div className="expert-modal-overlay" onClick={closeExpertModal}>
          <div className="expert-modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="expert-modal-header">
              <h2 className="expert-name-display">{selectedExpert.firstName} {selectedExpert.lastName}</h2>
              <button className="expert-modal-close" onClick={closeExpertModal}>×</button>
            </div>
            <div className="expert-modal-body">
              <div className="month-selector">
                <label htmlFor="month-select">Ay Seçin:</label>
                <select 
                  id="month-select"
                  value={selectedMonth}
                  onChange={handleMonthChange}
                  className="month-select-dropdown"
                >
                  {months.map((month) => (
                    <option key={month.value} value={month.value}>
                      {month.label}
                    </option>
                  ))}
                </select>
              </div>
              <div className="visit-details">
                {visitsLoading ? (
                  <p>Yükleniyor...</p>
                ) : error ? (
                  <p className="error">{error}</p>
                ) : (
                  <>
                    <h3>Ziyaret Detayları</h3>
                    {expertVisits.length > 0 ? (
                      <table className="visit-details-table">
                        <thead>
                          <tr>
                            <th>İş Yeri Adı</th>
                            <th>Ziyaret Durumu</th>
                            <th>Ziyaret Tarihi</th>
                          </tr>
                        </thead>
                        <tbody>
                          {expertVisits.map((visit) => (
                            <tr key={visit.id}>
                              <td>{visit.Workplace?.name || 'Bilinmeyen İş Yeri'}</td>
                              <td>
                                <span className={`visit-status ${visit.visited ? 'visited' : 'not-visited'}`}>
                                  {visit.visited ? 'Ziyaret Edildi' : 'Ziyaret Edilmedi'}
                                </span>
                              </td>
                              <td>{formatDate(visit.visitDate)}</td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    ) : (
                      <p>Seçilen ay için ziyaret kaydı bulunamadı.</p>
                    )}
                  </>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default VisitsPage;
